INSERT INTO `indicatorTemplate` (`dashboardId`, `name`, `category`, `subCategory`, `framework`, `frameworkVersion`, `definition`, `notes`, `template`, `active`, `locked`, `shared`, `metricSetName`, `metricLabel`) VALUES (@dashboardId1,'Active Patients','General (Click on the pie chart to see the patients included in the pie chart)','Patient Population','DoBC CPQI PSP Panel','2017-08-02','% of active patients in the practice last seen < 3 years, 3-5 years, 5+ years and never seen.','','<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<indicatorTemplateXML>\n	<author>OSCAR BC</author>\n	<uid></uid>\n	<heading>\n		<category>General (Click on the pie chart to see the patients included in the pie chart)</category>\n		<subCategory>Patient Population</subCategory>\n		<name>Active Patients</name>\n		<definition>% of active patients in the practice last seen &lt; 3 years, 3-5 years, 5+ years and never seen.</definition>\n		<framework>DoBC CPQI PSP Panel</framework>\n		<frameworkVersion>08-02-2017</frameworkVersion>\n		<notes></notes>\n	</heading>\n	<indicatorQuery>\n		<version>03-13-2018</version>\n		<params>\n			<!-- \n				Use this parameter in the query as '${provider}'\n				This parameter should be used for fetching patient\'s assigned to a MRP.\n				ie: WHERE demographic.provider_no = ${provider}\n			-->\n			<parameter id=\"provider\" name=\"provider_no\" value=\"loggedInProvider\" />\n			<parameter id=\"pstatus\" name=\"Patient Status\" value=\"\'AC\'\" />\n		</params>\n		<query>\n		  <!-- Indicator SQL Query here -->\nSELECT\n	SUM(fin.THREE) AS \"% Active, Seen within 3 years\",\n	SUM(fin.FIVETHREE) AS \"% Active, Seen 3-5 years ago\",\n	SUM(fin.FIVEPLUS) AS \"% Active, not seen in 5+ years\",\n	SUM(fin.NONE) AS \"% Active, no documented encounter\"\nFROM (\n	SELECT\n		IF( DATE_SUB(CURDATE() , INTERVAL 3 YEAR) &lt;= DATE( MAX(cmn.observation_date) ) ,1 ,0 ) AS THREE, \n		IF(  DATE_SUB(CURDATE() , INTERVAL 5 YEAR) &lt;= DATE( MAX(cmn.observation_date) ) AND\n		DATE( MAX(cmn.observation_date) ) &lt; DATE_SUB(CURDATE(), INTERVAL 3 YEAR), 1,0 ) AS FIVETHREE,\n		IF( DATE( MAX( cmn.observation_date ) ) &lt; DATE_SUB(CURDATE() , INTERVAL 5 YEAR) ,1 ,0 ) AS FIVEPLUS,\n		IF( cmn.note_id IS NULL ,1, 0 ) AS NONE\n	FROM demographic d\n	LEFT JOIN casemgmt_note cmn\n	ON (  d.demographic_no = cmn.demographic_no AND cmn.signed = 1 AND cmn.provider_no != \'-1\'  )\n	WHERE d.provider_no LIKE ${provider}\n	AND d.patient_status LIKE ${pstatus}\n	GROUP BY d.demographic_no HAVING count(d.demographic_no) > -1\n) fin;\n		</query>\n	</indicatorQuery>\n	<drillDownQuery>\n		<version>03-13-2018</version>\n		<params>\n			<parameter id=\"provider\" name=\"provider_no\" value=\"loggedInProvider\" />	\n			<parameter id=\"pstatus\" name=\"Patient Status\" value=\"\'AC\'\" />\n		</params>\n		<displayColumns>\n			<column id=\"demographic\" name=\"d.demographic_no\" title=\"Patient Id\" primary=\"true\" />\n			<column id=\"name\" name=\"CONCAT( d.last_name, \', \', d.first_name )\" title=\"Patient Name\" primary=\"false\" />\n			<column id=\"dob\" name=\"DATE_FORMAT( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth), \'%m-%d-%Y\' )\" title=\"Date of Birth (mm-dd-yy)\" primary=\"false\" />\n			<column id=\"age\" name=\"TIMESTAMPDIFF(YEAR, DATE( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth) ), CURDATE() )\" title=\"Age\" primary=\"false\" />	\n			<column id=\"lastEncounter\" name=\"IF(MAX(cmn.observation_date) IS NOT NULL,MAX(cmn.observation_date),\'\')\" title=\"Last Encounter\" primary=\"false\" />\n			<column id=\"patientStatus\" name=\"d.patient_status\" title=\"Patient Status\" primary=\"false\" />\n		</displayColumns>\n		<exportColumns>\n			<column id=\"demographic\" name=\"d.demographic_no\" title=\"Patient Id\" primary=\"true\" />\n			<column id=\"name\" name=\"CONCAT( d.last_name, \', \', d.first_name )\" title=\"Patient Name\" primary=\"false\" />\n			<column id=\"dob\" name=\"DATE_FORMAT( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth), \'%m-%d-%Y\' )\" title=\"Date of Birth (mm-dd-yy)\" primary=\"false\" />\n			<column id=\"age\" name=\"TIMESTAMPDIFF(YEAR, DATE( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth) ), CURDATE() )\" title=\"Age\" primary=\"false\" />	\n			<column id=\"lastEncounter\" name=\"IF(MAX(cmn.observation_date) IS NOT NULL,MAX(cmn.observation_date),\'\')\" title=\"Last Encounter\" primary=\"false\" />\n			<column id=\"patientStatus\" name=\"d.patient_status\" title=\"Patient Status\" primary=\"false\" />\n		</exportColumns>\n		<query>\n			<!-- Drilldown SQL Query here -->\nSELECT\n    d.*,\n    d.patient_status AS pt_status,\n    max(cmn.observation_date) as last_encounter\nFROM demographic d\nLEFT JOIN casemgmt_note cmn\nON (  d.demographic_no = cmn.demographic_no AND cmn.signed = 1 AND cmn.provider_no != \'-1\'  )\nWHERE d.provider_no LIKE ${provider}\nAND d.patient_status LIKE ${pstatus}\nGROUP BY d.demographic_no HAVING count(d.demographic_no) > -1			\n		</query>\n	</drillDownQuery>\n	<shared>false</shared>\n	<sharedMetricSetName>Home - Patient status</sharedMetricSetName>\n	<sharedMetricDataId>Status</sharedMetricDataId>\n	<sharedMappings>\n		<sharedMapping fromLabel=\"% Active, Seen within 3 years\" toLabel=\"Active, seen within 3 years\"/>\n		<sharedMapping fromLabel=\"% Active, Seen 3-5 years ago\" toLabel=\"Active, seen within 3-5 yrs\"/>\n		<sharedMapping fromLabel=\"% Active, not seen in 5+ years\" toLabel=\"Active, not seen in 5+ yrs\"/>\n	</sharedMappings>	\n</indicatorTemplateXML>\n','','\0',0,NULL,NULL);
INSERT INTO `indicatorTemplate` (`dashboardId`, `name`, `category`, `subCategory`, `framework`, `frameworkVersion`, `definition`, `notes`, `template`, `active`, `locked`, `shared`, `metricSetName`, `metricLabel`) VALUES (@dashboardId1,'No Assigned Primary','General (Click on the pie chart to see the patients included in the pie chart)','Patient Population','DoBC CPQI PSP Panel','2017-08-02','# of active patients without an assigned primary\n		','\n		  i)  # of active patients without an assigned primary\n		  ii) # all active patients for all providers\n		','<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<indicatorTemplateXML>\n	<author>OSCAR BC</author>\n	<uid></uid>\n	<heading>\n		<category>General (Click on the pie chart to see the patients included in the pie chart)</category>\n		<subCategory>Patient Population</subCategory>\n		<name>No Assigned Primary</name>\n		<definition># of active patients without an assigned primary\n		</definition>\n		<framework>DoBC CPQI PSP Panel</framework>\n		<frameworkVersion>08-02-2017</frameworkVersion>\n		<notes>\n		  i)  # of active patients without an assigned primary\n		  ii) # all active patients for all providers\n		</notes>\n	</heading>\n	<indicatorQuery>\n		<version>03-13-2018</version>\n		<params>\n			<!-- \n				Use this parameter in the query as '${provider}'\n				This parameter should be used for fetching patient\'s assigned to a MRP.\n				ie: WHERE demographic.provider_no = ${provider}\n			-->\n			<parameter id=\"provider\" name=\"provider_no\" value=\"loggedInProvider\" />\n			<parameter id=\"pstatus\" name=\"Patient Status\" value=\"\'AC\'\" />\n		</params>\n		<query>\n		  <!-- Indicator SQL Query here -->\nSELECT\n	SUM(fin.NO_ASSIGNED_PRIMARY) AS \"% Active, No Assigned Primary\",\n	SUM(fin.OTHER_PATIENTS) AS \"% Active, Assigned Primary\"\nFROM (\n	SELECT\n		IF( d.provider_no IS NULL OR d.provider_no = \'\' OR p.provider_no IS NULL ,1 ,0 ) AS NO_ASSIGNED_PRIMARY,\n		IF( d.provider_no IS NOT NULL AND d.provider_no != \'\' AND p.provider_no is NOT NULL,1 ,0 ) AS OTHER_PATIENTS\n	FROM demographic d\n	LEFT JOIN provider p\n	ON d.provider_no = p.provider_no\n	WHERE d.patient_status LIKE ${pstatus}\n) fin;\n		</query>\n	</indicatorQuery>\n	<drillDownQuery>\n		<version>03-13-2018</version>\n		<params>\n			<parameter id=\"provider\" name=\"provider_no\" value=\"loggedInProvider\" />	\n			<parameter id=\"pstatus\" name=\"Patient Status\" value=\"\'AC\'\" />\n		</params>\n		<displayColumns>\n			<column id=\"demographic\" name=\"d.demographic_no\" title=\"Patient Id\" primary=\"true\" />\n			<column id=\"name\" name=\"CONCAT( d.last_name, \', \', d.first_name )\" title=\"Patient Name\" primary=\"false\" />\n			<column id=\"dob\" name=\"DATE_FORMAT( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth), \'%m-%d-%Y\' )\" title=\"Date of Birth (mm-dd-yy)\" primary=\"false\" />\n			<column id=\"age\" name=\"TIMESTAMPDIFF(YEAR, DATE( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth) ), CURDATE() )\" title=\"Age\" primary=\"false\" />\n			<column id=\"phone\" name=\"d.phone\" title=\"Phone#\" primary=\"false\" />\n			<column id=\"provider\" name=\"d.provider_no\" title=\"Missing/Invalid Provider\" primary=\"false\" />\n		</displayColumns>\n		<exportColumns>\n		        <column id=\"demographic\" name=\"d.demographic_no\" title=\"Patient Id\" primary=\"true\" />\n		        <column id=\"firstName\" name=\"d.first_name\" title=\"First Name\" primary=\"false\" />\n                        <column id=\"lastName\" name=\"d.last_name\" title=\"Last Name\" primary=\"false\" />\n			<column id=\"dob\" name=\"DATE_FORMAT( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth), \'%m-%d-%Y\' )\" title=\"Date of Birth (mm-dd-yy)\" primary=\"false\" />\n			<column id=\"age\" name=\"TIMESTAMPDIFF(YEAR, DATE( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth) ), CURDATE() )\" title=\"Age\" primary=\"false\" />\n			<column id=\"phone\" name=\"d.phone\" title=\"Phone#\" primary=\"false\" />\n			<column id=\"provider\" name=\"d.provider_no\" title=\"Missing/Invalid Provider\" primary=\"false\" />\n\n		</exportColumns>\n		<query>\n			<!-- Drilldown SQL Query here -->\nSELECT\nd.*\nFROM demographic d\nLEFT JOIN provider p\nON d.provider_no = p.provider_no\nWHERE d.patient_status LIKE ${pstatus}\nAND (d.provider_no IS NULL OR d.provider_no = \'\' OR p.provider_no IS NULL)\nORDER BY d.provider_no DESC;\n		</query>\n	</drillDownQuery>\n</indicatorTemplateXML>\n','','\0',0,NULL,NULL);
INSERT INTO `indicatorTemplate` (`dashboardId`, `name`, `category`, `subCategory`, `framework`, `frameworkVersion`, `definition`, `notes`, `template`, `active`, `locked`, `shared`, `metricSetName`, `metricLabel`) VALUES (@dashboardId1,'Patient Contact','General (Click on the pie chart to see the patients included in the pie chart)','Patient Population','DoBC CPQI PSP Panel','2017-08-02','# of active patients with incomplete contact information in comparison to # all active patients\n		','\n		  i)  # of active patients with incomplete contact information\n		  ii) # of active patients with contact information\n		','<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<indicatorTemplateXML>\n	<author>OSCAR BC</author>\n	<uid></uid>\n	<heading>\n		<category>General (Click on the pie chart to see the patients included in the pie chart)</category>\n		<subCategory>Patient Population</subCategory>\n		<name>Patient Contact</name>\n		<definition># of active patients with incomplete contact information in comparison to # all active patients\n		</definition>\n		<framework>DoBC CPQI PSP Panel</framework>\n		<frameworkVersion>08-02-2017</frameworkVersion>\n		<notes>\n		  i)  # of active patients with incomplete contact information\n		  ii) # of active patients with contact information\n		</notes>\n	</heading>\n	<indicatorQuery>\n		<version>03-13-2018</version>\n		<params>\n			<!-- \n				Use this parameter in the query as '${provider}'\n				This parameter should be used for fetching patient\'s assigned to a MRP.\n				ie: WHERE demographic.provider_no = ${provider}\n			-->\n			<parameter id=\"provider\" name=\"provider_no\" value=\"loggedInProvider\" />\n			<parameter id=\"pstatus\" name=\"Patient Status\" value=\"\'AC\'\" />\n		</params>\n		<query>\n		  <!-- Indicator SQL Query here -->\nSELECT\n	SUM(fin.INCOMPLETE_CONTACT_INFO) AS \"Active, Incomplete Contact Information\",\n	SUM(fin.HAS_CONTACT_INFO) AS \"Active, With Contact Information\"\nFROM (\n	SELECT\n		IF( (LENGTH(phone) &lt; LENGTH(\"###-###-####\") AND LENGTH(phone2) &lt; LENGTH(\"###-###-####\") AND LENGTH(e.value) &lt; LENGTH(\"###-###-####\")) OR (address is NULL OR address=\"\") OR (city is NULL OR city=\"\") ,1 ,0 ) AS INCOMPLETE_CONTACT_INFO,\n		IF( (LENGTH(phone) &lt; LENGTH(\"###-###-####\") AND LENGTH(phone2) &lt; LENGTH(\"###-###-####\") AND LENGTH(e.value) &lt; LENGTH(\"###-###-####\")) OR (address is NULL OR address=\"\") OR (city is NULL OR city=\"\") ,0 ,1 ) AS HAS_CONTACT_INFO\n	FROM demographic d\n	LEFT JOIN demographicExt e ON d.demographic_no=e.demographic_no AND\n	  e.key_val=\'demo_cell\' AND e.date_time=(SELECT MAX(de.date_time) FROM demographicExt de WHERE e.demographic_no=de.demographic_no)\n	WHERE d.provider_no LIKE ${provider}\n	AND d.patient_status LIKE ${pstatus}\n) fin;\n		</query>\n	</indicatorQuery>\n	<drillDownQuery>\n		<version>03-13-2018</version>\n		<params>\n			<parameter id=\"provider\" name=\"provider_no\" value=\"loggedInProvider\" />	\n			<parameter id=\"pstatus\" name=\"Patient Status\" value=\"\'AC\'\" />\n		</params>\n		<displayColumns>\n			<column id=\"demographic\" name=\"d.demographic_no\" title=\"Patient Id\" primary=\"true\" />\n			<column id=\"name\" name=\"CONCAT( d.last_name, \', \', d.first_name )\" title=\"Patient Name\" primary=\"false\" />\n			<column id=\"dob\" name=\"DATE_FORMAT( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth), \'%m-%d-%Y\' )\" title=\"Date of Birth (mm-dd-yy)\" primary=\"false\" />\n			<column id=\"age\" name=\"TIMESTAMPDIFF(YEAR, DATE( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth) ), CURDATE() )\" title=\"Age\" primary=\"false\" />\n			<column id=\"phone\" name=\"IF(d.phone IS NULL OR d.phone=\'\', \'N/A\', d.phone )\" title=\"Phone(H)\" primary=\"false\" />\n			<column id=\"phone2\" name=\"IF(d.phone2 IS NULL OR d.phone2=\'\', \'N/A\', d.phone2)\" title=\"Phone(W)\" primary=\"false\" />\n			<column id=\"phone3\" name=\"IF(e.value IS NULL OR e.value=\'\', \'N/A\', e.value)\" title=\"Cell Phone\" primary=\"false\" />\n			<column id=\"address\" name=\"IF(d.address IS NULL OR d.address=\'\', \'N/A\', d.address )\" title=\"Address\" primary=\"false\" />\n			<column id=\"city\" name=\"IF(d.city IS NULL OR d.city=\'\', \'N/A\', d.city )\" title=\"City\" primary=\"false\" />\n\n		</displayColumns>\n		<exportColumns>\n		        <column id=\"demographic\" name=\"d.demographic_no\" title=\"Patient Id\" primary=\"true\" />\n		  	<column id=\"firstName\" name=\"d.first_name\" title=\"First Name\" primary=\"false\" />\n                        <column id=\"lastName\" name=\"d.last_name\" title=\"Last Name\" primary=\"false\" />\n			<column id=\"dob\" name=\"DATE_FORMAT( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth), \'%m-%d-%Y\' )\" title=\"Date of Birth (mm-dd-yy)\" primary=\"false\" />\n			<column id=\"age\" name=\"TIMESTAMPDIFF(YEAR, DATE( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth) ), CURDATE() )\" title=\"Age\" primary=\"false\" />\n			<column id=\"phone\" name=\"IF(d.phone IS NULL OR d.phone=\'\', \'N/A\', d.phone )\" title=\"Phone(H)\" primary=\"false\" />\n			<column id=\"phone2\" name=\"IF(d.phone2 IS NULL OR d.phone2=\'\', \'N/A\', d.phone2)\" title=\"Phone(W)\" primary=\"false\" />\n			<column id=\"phone3\" name=\"IF(e.value IS NULL OR e.value=\'\', \'N/A\', e.value)\" title=\"Cell Phone\" primary=\"false\" />\n			<column id=\"address\" name=\"IF(d.address IS NULL OR d.address=\'\', \'N/A\', d.address )\" title=\"Address\" primary=\"false\" />\n			<column id=\"city\" name=\"IF(d.city IS NULL OR d.city=\'\', \'N/A\', d.city )\" title=\"City\" primary=\"false\" />\n\n		</exportColumns>\n		<query>\n			<!-- Drilldown SQL Query here -->\nSELECT\nd.*\nFROM demographic d\nLEFT JOIN demographicExt e ON d.demographic_no=e.demographic_no AND e.key_val=\'demo_cell\'\n  AND e.date_time=(SELECT MAX(de.date_time) FROM demographicExt de WHERE e.demographic_no=de.demographic_no)	\nWHERE d.provider_no LIKE ${provider}\nAND d.patient_status LIKE ${pstatus}\nAND ((LENGTH(phone) &lt; LENGTH(\"###-###-####\") AND LENGTH(phone2) &lt; LENGTH(\"###-###-####\" ) AND LENGTH(e.value) &lt; LENGTH(\"###-###-####\")) OR (address is NULL OR address=\"\") OR (city is NULL OR city=\"\"));\n		</query>\n	</drillDownQuery>\n</indicatorTemplateXML>\n','','\0',0,NULL,NULL);
INSERT INTO `indicatorTemplate` (`dashboardId`, `name`, `category`, `subCategory`, `framework`, `frameworkVersion`, `definition`, `notes`, `template`, `active`, `locked`, `shared`, `metricSetName`, `metricLabel`) VALUES (@dashboardId1,'Active Med Count','Home (Click on the pie chart to see the patients included in the pie chart)','Polypharmacy','DoBC CPQI PSP Panel','2017-08-02','% of active patients with an active continuous medication count of 0, 1-4, 5-9 or 10+.','','<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<indicatorTemplateXML>\n	<author>OSCAR BC</author>\n	<uid></uid>\n	<heading>\n		<category>Home (Click on the pie chart to see the patients included in the pie chart)</category>\n		<subCategory>Polypharmacy</subCategory>\n		<name>Active Med Count</name>\n		<definition>% of active patients with an active continuous medication count of 0, 1-4, 5-9 or 10+.</definition>\n		<framework>DoBC CPQI PSP Panel</framework>\n		<frameworkVersion>08-02-2017</frameworkVersion>\n		<notes></notes>\n	</heading>\n	<indicatorQuery>\n		<version>03-13-2018</version>\n		<params>\n			<!-- \n				Use this parameter in the query as '${provider}'\n				This parameter should be used for fetching patient\'s assigned to a MRP.\n				ie: WHERE demographic.provider_no = ${provider}\n			-->\n			<parameter id=\"provider\" name=\"provider_no\" value=\"loggedInProvider\" />\n			<parameter id=\"pstatus\" name=\"Patient Status\" value=\"\'AC\'\" />\n		</params>\n		<query>\n			<!-- Indicator SQL Query here -->\nSELECT\nIF( COUNT(patient) > 0,\nSUM( IF(patient_status = ${pstatus} AND med_COUNT >= 10,1,0)),0) AS \'10+ meds\',\nIF( COUNT(patient) > 0,\nSUM( IF(patient_status = ${pstatus} AND med_COUNT BETWEEN 5 AND 9, 1, 0)),0) AS \'5-9 meds\',\nIF( COUNT(patient) > 0,\nSUM( IF(patient_status = ${pstatus} AND med_COUNT BETWEEN 1 AND 4, 1, 0)),0) AS \'1-4 meds\',\nIF( COUNT(patient) > 0,\nSUM( IF(patient_status = ${pstatus} AND med_COUNT = 0,1,0)),0) AS \'0 meds\'\nFROM (\nSELECT d.demographic_no AS patient, d.patient_status, COUNT(DISTINCT meds.regional_identifier) AS med_count\nFROM demographic d LEFT JOIN(\nSELECT dr.demographic_no, dr.regional_identifier FROM drugs AS dr\nWHERE dr.archived = 0 AND dr.regional_identifier IS NOT NULL AND\ndr.regional_identifier != \'\' AND rx_date &lt;= NOW() AND\n(DATE_ADD(dr.rx_date, INTERVAL(DATEDIFF(dr.end_date,dr.rx_date)*1.0)\nDAY)) >= NOW() ORDER BY dr.demographic_no) meds\non (d.demographic_no = meds.demographic_no) where d.provider_no LIKE ${provider}\nGROUP BY d.demographic_no\nORDER BY COUNT(DISTINCT meds.regional_identifier) DESC\n) counts\n		</query>\n	</indicatorQuery>\n	<drillDownQuery>\n		<version>03-13-2018</version>\n		<params>\n			<parameter id=\"provider\" name=\"provider_no\" value=\"loggedInProvider\" />	\n			<parameter id=\"pstatus\" name=\"Patient Status\" value=\"\'AC\'\" />\n		</params>\n		<displayColumns>\n			<column id=\"demographic\" name=\"demographic_no\" title=\"Patient Id\" primary=\"true\" />\n			<column id=\"name\" name=\"CONCAT( last_name, \', \', first_name )\" title=\"Patient Name\" primary=\"false\" />\n			<column id=\"dob\" name=\"DATE_FORMAT( CONCAT(year_of_birth,\'-\',month_of_birth,\'-\',date_of_birth), \'%m-%d-%Y\' )\" title=\"Date of Birth (mm-dd-yy)\" primary=\"false\" />\n			<column id=\"age\" name=\"TIMESTAMPDIFF(YEAR, DATE( CONCAT(year_of_birth,\'-\',month_of_birth,\'-\',date_of_birth) ), CURDATE() )\" title=\"Age\" primary=\"false\" />\n			<column id=\"activeMedCount\" name=\"counts.med_count\" title=\"Active Med Count\" primary=\"false\" />\n			<column id=\"patientStatus\" name=\"patient_status\" title=\"Patient Status\" primary=\"false\" />\n		</displayColumns>\n		<exportColumns>\n			<column id=\"demographic\" name=\"demographic_no\" title=\"Patient Id\" primary=\"true\" />\n			<column id=\"name\" name=\"CONCAT( last_name, \', \', first_name )\" title=\"Patient Name\" primary=\"false\" />\n			<column id=\"dob\" name=\"DATE_FORMAT( CONCAT(year_of_birth,\'-\',month_of_birth,\'-\',date_of_birth), \'%m-%d-%Y\' )\" title=\"Date of Birth (mm-dd-yy)\" primary=\"false\" />\n			<column id=\"age\" name=\"TIMESTAMPDIFF(YEAR, DATE( CONCAT(year_of_birth,\'-\',month_of_birth,\'-\',date_of_birth) ), CURDATE() )\" title=\"Age\" primary=\"false\" />\n			<column id=\"activeMedCount\" name=\"counts.med_count\" title=\"Active Med Count\" primary=\"false\" />\n			<column id=\"patientStatus\" name=\"patient_status\" title=\"Patient Status\" primary=\"false\" />\n		</exportColumns>\n		<query>\n			<!-- Drilldown SQL Query here -->\nSELECT demographic_no, patient_status, counts.med_count\nFROM\n  (SELECT d.*, COUNT(DISTINCT meds.regional_identifier) AS med_count\n  FROM demographic d\n  LEFT JOIN(\n    SELECT dr.demographic_no, dr.regional_identifier FROM drugs AS dr\n    WHERE dr.archived = 0 AND dr.regional_identifier IS NOT NULL AND\n      dr.regional_identifier != \'\' AND rx_date &lt;= NOW() AND\n      (DATE_ADD(dr.rx_date, INTERVAL(DATEDIFF(dr.end_date,dr.rx_date)*1.0)\n      DAY)) >= NOW()\n    ORDER BY dr.demographic_no\n    ) meds\n  ON (d.demographic_no = meds.demographic_no) where d.provider_no LIKE ${provider}\n  GROUP BY d.demographic_no\n  ORDER BY COUNT(DISTINCT meds.regional_identifier) DESC ,\n    d.demographic_no ASC\n  ) counts\nWHERE patient_status=${pstatus} AND med_count > 0\n		</query>\n	</drillDownQuery>\n	<shared>false</shared>\n	<sharedMetricSetName>General - Polypharmacy</sharedMetricSetName>\n	<sharedMetricDataId></sharedMetricDataId>\n	<sharedMappings>\n		<sharedMapping fromLabel=\"10+ meds\" toLabel=\"10+ Rx\"/>\n		<sharedMapping fromLabel=\"5-9 meds\" toLabel=\"5-9 Rx\"/>\n		<sharedMapping fromLabel=\"1-4 meds\" toLabel=\"1-4 Rx\"/>\n		<sharedMapping fromLabel=\"0 meds\" toLabel=\"0 Rx\"/>\n	</sharedMappings>	\n</indicatorTemplateXML>\n','','\0',0,NULL,NULL);
INSERT INTO `indicatorTemplate` (`dashboardId`, `name`, `category`, `subCategory`, `framework`, `frameworkVersion`, `definition`, `notes`, `template`, `active`, `locked`, `shared`, `metricSetName`, `metricLabel`) VALUES (@dashboardId1,'Advanced Care Planning Prevalence','CDM (Click on the pie chart to see the patients included in the pie chart)','Advanced Care Planning','DoBC CPQI PSP Panel','2017-08-02','Proportion of active patients in the practice coded with Advanced Care Planning.','\ni) Advanced care patients: \"Patient Status: Active\nAND has a Current Problem List/Profile item code of 105499002,103735009 or starting with V66\"\nii) Other patients: \"Patient Status: Active\nAND does not have a Current Problem List/Profile item code of 105','<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<indicatorTemplateXML>\n	<author>OSCAR BC</author>\n	<uid></uid>\n	<heading>\n		<category>CDM (Click on the pie chart to see the patients included in the pie chart)</category>\n		<subCategory>Advanced Care Planning</subCategory>\n		<name>Advanced Care Planning Prevalence</name>\n		<definition>Proportion of active patients in the practice coded with Advanced Care Planning.</definition>\n		<framework>DoBC CPQI PSP Panel</framework>\n		<frameworkVersion>08-02-2017</frameworkVersion>\n		<notes>\ni) Advanced care patients: \"Patient Status: Active\nAND has a Current Problem List/Profile item code of 105499002,103735009 or starting with V66\"\nii) Other patients: \"Patient Status: Active\nAND does not have a Current Problem List/Profile item code of 105499002,103735009 or starting with V66\"\n		</notes>\n	</heading>\n	<indicatorQuery>\n		<version>03-13-2018</version>\n		<params>\n			<!-- \n				Required parameter provider. Value options are: \n					[ an individual providerNo, or provider range ] ie: value=\"370, 1001\" \n					\"all\" ie: value=\"all\" including null.\n					\"loggedInProvider\" ie:\n				Default is \"loggedInProvider\"\n				Use this parameter in the query as '${provider}'\n				This parameter should be used for fetching patient\'s assigned to a MRP.\n				ie: WHERE demographic.provider_no = ${provider}\n			-->\n			<parameter id=\"provider\" name=\"provider_no\" value=\"loggedInProvider\" />\n			<parameter id=\"pstatus\" name=\"Patient Status\" value=\"\'AC\'\" />\n			<parameter id=\"dxcodesICD\" name=\"Dx Codes (ICD)\" value=\"V66\" />\n			<parameter id=\"dxcodesSysICD\" name=\"DX CodeSystem (ICD)\" value=\"\'icd9\'\" />\n			<parameter id=\"dxcodesSnoMed\" name=\"Dx Codes (SnoMed)\" value=\"105499002,103735009\" />\n			<parameter id=\"dxcodesSysSnoMed\" name=\"Dx CodeSystem (SnoMed)\" value=\"\'snomed%\'\" />\n			<parameter id=\"excludedPatient\" name=\"Excluded Patient\" value=\"excludedPatient\" />\n		</params>\n		<query>\n			<!-- Indicator SQL Query here -->\nSELECT\n -- TOTAL NUMBER OF PATIENTS NOTED AS ACTIVE WITH CURRENT ADVANCED CARE PLANNING\n IF( COUNT(fin.patient) &gt; 0, SUM( IF( dxresearch_code is not NULL , 1, 0) ) , 0 ) AS \"Number Active, Current Advanced Care Planning\",\n -- TOTAL NUMBER OF PATIENTS NOTED AS ACTIVE WITHOUT CURRENT ADVANCED CARE PLANNING\n IF( COUNT(fin.patient) &gt; 0, SUM( IF( dxresearch_code is NULL , 1, 0) ) , 0 ) AS \"Number Active, No Current Advanced Care Planning\"\nFROM (\n SELECT d.demographic_no AS patient, d.patient_status AS pt_status, dxresearch_code\n FROM demographic d\n LEFT JOIN (\n  SELECT demographic_no, dxresearch_code, COUNT(dxresearch_code)\n  FROM dxresearch\n  WHERE status=\'A\' AND\n  (\n   (LEFT(dxresearch_code, 3) IN (\'V66\') AND coding_system = ${dxcodesSysICD}) OR\n   (dxresearch_code IN (\'105499002\', \'103735009\') AND coding_system LIKE ${dxcodesSysSnoMed})\n   )\n  GROUP BY demographic_no\n  ORDER BY demographic_no\n ) problems\n ON d.demographic_no = problems.demographic_no\n WHERE d.provider_no LIKE ${provider} AND d.patient_status = ${pstatus}\n AND d.demographic_no NOT IN ${excludedPatient}\n) fin\n		</query>\n	</indicatorQuery>\n	<drillDownQuery>\n		<version>03-13-2018</version>\n		<params>\n			<parameter id=\"provider\" name=\"provider_no\" value=\"loggedInProvider\" />	\n			<parameter id=\"pstatus\" name=\"Patient Status\" value=\"\'AC\'\" />\n			<parameter id=\"dxcodesICD\" name=\"Dx Codes (ICD)\" value=\"V66\" />\n			<parameter id=\"dxcodesSysICD\" name=\"DX CodeSystem (ICD)\" value=\"\'icd9\'\" />\n			<parameter id=\"dxcodesSnoMed\" name=\"Dx Codes (SnoMed)\" value=\"105499002,103735009\" />\n			<parameter id=\"dxcodesSysSnoMed\" name=\"Dx CodeSystem (SnoMed)\" value=\"\'snomed%\'\" />\n			<parameter id=\"excludedPatient\" name=\"Excluded Patient\" value=\"excludedPatient\" />\n		</params>\n		<displayColumns>\n			<column id=\"demographic\" name=\"d.demographic_no\" title=\"Patient Id\" primary=\"true\" />\n			<column id=\"name\" name=\"CONCAT( d.last_name, \', \', d.first_name )\" title=\"Patient Name\" primary=\"false\" />\n			<column id=\"dob\" name=\"DATE_FORMAT( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth), \'%m-%d-%Y\' )\" title=\"Date of Birth (mm-dd-yy)\" primary=\"false\" />\n			<column id=\"age\" name=\"TIMESTAMPDIFF(YEAR, DATE( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth) ), CURDATE() )\" title=\"Age\" primary=\"false\" />\n			<column id=\"dxcode\" title=\"Dx System/Code\" name=\"CONCAT(MIN(dxr.coding_system),\'/\',MIN(dxr.dxresearch_code),IF( MIN(dxr.dxresearch_code) != MAX(dxr.dxresearch_code),CONCAT( \' &amp; \', MAX(dxr.coding_system), \'/\', MAX(dxr.dxresearch_code) ),\'\') )\" primary=\"false\" />\n		</displayColumns>\n		<exportColumns>\n			<column id=\"demographic\" name=\"d.demographic_no\" title=\"Patient Id\" primary=\"true\" />\n			<column id=\"firstName\" name=\"d.first_name\" title=\"First Name\" primary=\"false\" />\n			<column id=\"lastName\" name=\"d.last_name\" title=\"Last Name\" primary=\"false\" />\n			<column id=\"dob\" name=\"DATE_FORMAT( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth), \'%m-%d-%Y\' )\" title=\"Date of Birth (mm-dd-yy)\" primary=\"false\" />\n			<column id=\"age\" name=\"TIMESTAMPDIFF(YEAR, DATE( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth) ), CURDATE() )\" title=\"Age\" primary=\"false\" />\n			<column id=\"dxcode\" title=\"Dx System/Code\" name=\"CONCAT(MIN(dxr.coding_system),\'/\',MIN(dxr.dxresearch_code),IF( MIN(dxr.dxresearch_code) != MAX(dxr.dxresearch_code),CONCAT( \' &amp; \', MAX(dxr.coding_system), \'/\', MAX(dxr.dxresearch_code) ),\'\') )\" primary=\"false\" />\n		</exportColumns>\n		<drillDownActions>\n			<action id=\"tickler\" name=\"Tickler\"/>\n			<action id=\"demoExcl\" name=\"Patient Exclusion\"/>\n		</drillDownActions>\n		<query>\n			<!-- Drilldown SQL Query here -->\nSELECT\n*			\nFROM demographic d\nLEFT JOIN dxresearch dxr\nON d.demographic_no = dxr.demographic_no\nWHERE d.patient_status=${pstatus}\nAND\n(\n (LEFT(dxr.dxresearch_code, 3) IN (\'V66\') AND dxr.coding_system = ${dxcodesSysICD}) OR\n (dxr.dxresearch_code IN (\'105499002\', \'103735009\') AND dxr.coding_system LIKE ${dxcodesSysSnoMed})\n )\nAND dxr.status = \'A\'\nAND d.provider_no LIKE ${provider}\nAND d.demographic_no NOT IN ${excludedPatient}\nGROUP BY d.demographic_no\nORDER BY COUNT(d.demographic_no) DESC, d.demographic_no\n		</query>\n	</drillDownQuery>\n</indicatorTemplateXML>\n','','\0',0,NULL,NULL);
INSERT INTO `indicatorTemplate` (`dashboardId`, `name`, `category`, `subCategory`, `framework`, `frameworkVersion`, `definition`, `notes`, `template`, `active`, `locked`, `shared`, `metricSetName`, `metricLabel`) VALUES (@dashboardId1,'Consider Advanced Care Planning','CDM (Click on the pie chart to see the patients included in the pie chart)','Advanced Care Planning','DoBC CPQI PSP Panel','2017-08-02','\n			Patients with advanced care planning dx in 2+ bills or 2+ visit encounters within last 3 years or PPS < 50\n		','\n		  Created for Doctors of BC\n		  i) Advanced Care Planning dx in 2+ bills: \"Patient Status: Active\nAND does not have a Current Problem List/Profile item code of 105499002,103735009 or starting with V66\nAND with >=2 Bills with the billing code starting w','<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<indicatorTemplateXML>\n	<author>OSCAR BC</author>\n	<uid></uid>\n	<heading>\n		<category>CDM (Click on the pie chart to see the patients included in the pie chart)</category>\n		<subCategory>Advanced Care Planning</subCategory>\n		<name>Consider Advanced Care Planning</name>\n		<definition>\n			Patients with advanced care planning dx in 2+ bills or 2+ visit encounters within last 3 years or PPS &lt; 50\n		</definition>\n		<framework>DoBC CPQI PSP Panel</framework>\n		<frameworkVersion>08-02-2017</frameworkVersion>\n		<notes>\n		  Created for Doctors of BC\n		  i) Advanced Care Planning dx in 2+ bills: \"Patient Status: Active\nAND does not have a Current Problem List/Profile item code of 105499002,103735009 or starting with V66\nAND with >=2 Bills with the billing code starting with V66 in the last 3 years\"\n		  ii) Advanced Care Planning dx in 2+ visits: \"Patient Status: Active\nAND does not have a Current Problem List/Profile item code of 105499002,103735009 or starting with V66\nAND with >=2 Visit Encounters with Snomed diagnosis code 105499002 or the ICD9 diagnosis code starting with V66 in the last 3 years\"\n                  iii) PPS &lt; 50: \"Patient Status: Active\nAND does not have a Current Problem List/Profile item code of 105499002 or starting with V66\nAND with most recent Palliative Performance Scale score &lt; 50\n		</notes>\n	</heading>\n	<indicatorQuery>\n		<version>03-13-2018</version>\n		<params>\n			<parameter id=\"provider\" name=\"Provider Number\" value=\"loggedInProvider\" />\n			<parameter id=\"active\" name=\"Active Patients\" value=\"\'AC\'\" />\n			<parameter id=\"dxcodesICD\" name=\"Dx Codes (ICD)\" value=\"V66\" />\n                        <parameter id=\"dxcodesSysICD\" name=\"DX CodeSystem (ICD)\" value=\"\'icd9\'\" />\n                        <parameter id=\"dxcodesSnoMed\" name=\"Dx Codes (SnoMed)\" value=\"105499002,103735009\" />\n                        <parameter id=\"dxcodesSysSnoMed\" name=\"Dx CodeSystem (SnoMed)\" value=\"\'snomed%\'\" />\n			<parameter id=\"billingCode\" name=\"ICD9 Billing Code\" value=\"V66\" />\n			<parameter id=\"numOfBills\" name=\"Number of ICD9 V66 Bills\" value=\"2\" />\n			<parameter id=\"numOfVisits\" name=\"Number of Visit Encounters coded V66/ICD9\" value=\"2\" />\n			<parameter id=\"obsName\" name=\"Observation Name\" value=\"\'PPS\'\" />\n			<parameter id=\"obsResult\" name=\"Observation Result\" value=\"50\" />\n			<parameter id=\"excludedPatient\" name=\"Excluded Patient\" value=\"excludedPatient\" />\n		</params>\n		<range>\n			<lowerLimit id=\"billingdate\" label=\"From Date\" name=\"Date\" value=\"DATE_SUB( NOW(), INTERVAL 3 YEAR )\" />\n			<upperLimit id=\"billingdate\" label=\"Date Today\" name=\"Date\" value=\"NOW()\" />\n			<lowerLimit id=\"visitdate\" label=\"From Date\" name=\"Date\" value=\"DATE_SUB( NOW(), INTERVAL 3 YEAR )\" />\n			<upperLimit id=\"visitdate\" label=\"Date Today\" name=\"Date\" value=\"NOW()\" />\n		</range>\n		<query>\nSELECT\n  IF ( COUNT(fin.patient) &gt; 0, SUM( CASE WHEN (fin.dx IS NULL AND fin.billcount &gt;= ${numOfBills}) THEN 1 ELSE 0 END ) , 0) AS \"Number with Advanced Care Planning dx in ${numOfBills}+ bills\",\n  IF ( COUNT(fin.patient) &gt; 0, SUM( CASE WHEN (fin.dx IS NULL AND fin.visitcount &gt;= ${numOfVisits}) THEN 1 ELSE 0 END ) , 0) AS \"Number with Advanced Care Planning dx in ${numOfVisits}+ visits\",\n  IF ( COUNT(fin.patient) &gt; 0, SUM( CASE WHEN (fin.dx IS NULL AND fin.result &lt; ${obsResult}) THEN 1 ELSE 0 END ) , 0) AS \"Number with PPS &lt; ${obsResult}%\"\nFROM ( \n  SELECT \n    d.demographic_no AS patient, \n    BILLING.billcount AS billcount,\n    VISIT.visitcount AS visitcount,\n    OBSLAST.result AS result,\n    dxr.dxresearch_code AS dx,\n    dxr.coding_system AS dxs\n  FROM demographic d \n  LEFT JOIN (\n    SELECT demographic_no, dxresearch_code, coding_system, COUNT(dxresearch_code)\n    FROM dxresearch\n    WHERE status=\'A\' AND\n    (\n      (LEFT(dxresearch_code, 3) IN (\'V66\') AND coding_system = ${dxcodesSysICD}) OR\n      (dxresearch_code IN (\'105499002\',\'103735009\') AND coding_system LIKE ${dxcodesSysSnoMed})\n    )\n    GROUP BY demographic_no\n    ORDER BY demographic_no\n  ) dxr\n  ON d.demographic_no = dxr.demographic_no\n  -- Billed V66 in last 3 years\n  LEFT JOIN (\n    SELECT b.demographic_no AS demographic_no, b.dx_code1 AS dxcode1, b.dx_code2 AS dxcode2, b.dx_code3 AS dxcode3, COUNT(b.demographic_no) AS billcount\n    FROM billingmaster b\n    WHERE ( LEFT(b.dx_code1, 3) IN (\'V66\') OR LEFT(b.dx_code2, 3) IN (\'V66\') OR LEFT(b.dx_code3, 3) IN (\'V66\') )\n    AND DATE(b.createdate) &gt; ${lowerLimit.billingdate}\n    AND b.demographic_no &gt; 0\n    GROUP BY b.demographic_no HAVING COUNT(b.demographic_no) &gt; -1\n  ) BILLING\n  ON (d.demographic_no = BILLING.demographic_no)  \n  -- Coded dx during visit encounter in last 3 years\n  LEFT JOIN(\n    SELECT ASSIGNED.demographic_no AS demographic_no, observation_date AS visitdate, uuid, COUNT(ASSIGNED.demographic_no) AS visitcount\n    FROM casemgmt_note note\n    INNER JOIN (\n      SELECT note_id, CODED.demo_no AS demographic_no FROM casemgmt_issue_notes\n      INNER JOIN (\n        SELECT ci.id AS id, ci.update_date, ci.demographic_no demo_no FROM issue i\n        INNER JOIN casemgmt_issue ci\n        ON i.issue_id = ci.issue_id\n        WHERE\n	(i.type LIKE ${dxcodesSysICD} AND LEFT(i.code, 3) IN (\'V66\')) OR\n	(i.type LIKE ${dxcodesSysSnoMed} AND i.code IN (\'105499002\',\'103735009\'))\n      ) CODED\n      ON (casemgmt_issue_notes.id = CODED.id)\n      ORDER BY CODED.demo_no\n    ) ASSIGNED\n    ON note.note_id = ASSIGNED.note_id AND\n    -- GET LAST REVISION OF NOTE\n    note.note_id= (SELECT MAX(cmn.note_id) FROM casemgmt_note cmn WHERE cmn.uuid = note.uuid) AND\n    DATE(note.observation_date) &gt; ${lowerLimit.visitdate}\n    GROUP BY demographic_no\n    ORDER BY demographic_no\n  ) VISIT\n  ON (d.demographic_no = VISIT.demographic_no)\n  -- Consider Observation\n  LEFT JOIN ( \n    SELECT m.demographicNo AS demographic_no, m.dataField AS result, m.dateObserved AS dateObserved\n    FROM measurements m\n    INNER JOIN (\n      SELECT demographicNo, MAX(dateObserved) AS dateObserved\n      FROM measurements\n      WHERE type = ${obsName}\n      GROUP BY demographicNo\n    ) mlast\n    ON m.demographicNo = mlast.demographicNo\n    WHERE m.type = ${obsName}\n    AND m.dateObserved = mlast.dateObserved\n    AND m.demographicNo &gt; 0\n    GROUP BY m.demographicNo\n  ) OBSLAST\n  ON (d.demographic_no = OBSLAST.demographic_no)  \n  WHERE d.patient_status LIKE ${active}\n  AND d.demographic_no &gt; 0\n  AND d.provider_no LIKE '${provider}'\n  AND d.demographic_no NOT IN ${excludedPatient}\n) fin\n		</query>\n	</indicatorQuery>\n	<drillDownQuery>\n		<version>03-13-2018</version>\n		<params>\n			<parameter id=\"provider\" name=\"Provider Number\" value=\"loggedInProvider\" />\n			<parameter id=\"active\" name=\"Active Patients\" value=\"\'AC\'\" />\n			<parameter id=\"dxcodesICD\" name=\"Dx Codes (ICD)\" value=\"V66\" />\n                        <parameter id=\"dxcodesSysICD\" name=\"DX CodeSystem (ICD)\" value=\"\'icd9\'\" />\n                        <parameter id=\"dxcodesSnoMed\" name=\"Dx Codes (SnoMed)\" value=\"105499002,103735009\" />\n                        <parameter id=\"dxcodesSysSnoMed\" name=\"Dx CodeSystem (SnoMed)\" value=\"\'snomed%\'\" />\n			<parameter id=\"billingCode\" name=\"ICD9 Billing Code\" value=\"V66\" />\n			<parameter id=\"numOfBills\" name=\"Number of ICD9 V66 Bills\" value=\"2\" />\n			<parameter id=\"numOfVisits\" name=\"Number of Visit Encounters coded V66/ICD9\" value=\"2\" />\n			<parameter id=\"obsName\" name=\"Observation Name\" value=\"\'PPS\'\" />\n			<parameter id=\"obsResult\" name=\"Observation Result\" value=\"50\" />\n			<parameter id=\"excludedPatient\" name=\"Excluded Patient\" value=\"excludedPatient\" />\n		</params>\n		\n		<range>\n			<lowerLimit id=\"billingdate\" label=\"From Date\" name=\"Date\" value=\"DATE_SUB( NOW(), INTERVAL 3 YEAR )\" />\n			<upperLimit id=\"billingdate\" label=\"Date Today\" name=\"Date\" value=\"NOW()\" />\n			<lowerLimit id=\"visitdate\" label=\"From Date\" name=\"Date\" value=\"DATE_SUB( NOW(), INTERVAL 3 YEAR )\" />\n			<upperLimit id=\"visitdate\" label=\"Date Today\" name=\"Date\" value=\"NOW()\" />\n		</range>\n		\n		<displayColumns>\n			<column id=\"demographic\" name=\"d.demographic_no\" title=\"Patient Id\" primary=\"true\" />\n			<column id=\"name\" name=\"CONCAT( d.last_name, \', \', d.first_name )\" title=\"Patient Name\" primary=\"false\" />\n			<column id=\"dob\" name=\"DATE_FORMAT( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth), \'%m-%d-%Y\' )\" title=\"Date of Birth\" primary=\"false\" />\n			<column id=\"age\" name=\"TIMESTAMPDIFF(YEAR, DATE( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth) ), CURDATE() )\" title=\"Age\" primary=\"false\" />\n			<column id=\"billings\" name=\"IFNULL( BILLING.billcount, \'0\')\" title=\"Billings\" primary=\"false\" />\n			<column id=\"visits\" name=\"IFNULL( VISIT.visitcount, \'0\')\" title=\"Visits\" primary=\"false\" />\n			<column id=\"observation\" name=\"IFNULL( OBSLAST.result, \'NA\')\" title=\"PPS\" primary=\"false\" />\n		</displayColumns>\n		\n		<exportColumns>\n			<column id=\"demographic\" name=\"d.demographic_no\" title=\"Patient Id\" primary=\"true\" />\n                        <column id=\"firstName\" name=\"d.first_name\" title=\"First Name\" primary=\"false\" />\n                        <column id=\"lastName\" name=\"d.last_name\" title=\"Last Name\" primary=\"false\" />\n			<column id=\"dob\" name=\"DATE_FORMAT( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth), \'%m-%d-%Y\' )\" title=\"Date of Birth\" primary=\"false\" />\n			<column id=\"age\" name=\"TIMESTAMPDIFF(YEAR, DATE( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth) ), CURDATE() )\" title=\"Age\" primary=\"false\" />\n			<column id=\"billings\" name=\"IFNULL( BILLING.billcount, \'0\')\" title=\"Billings\" primary=\"false\" />\n			<column id=\"visits\" name=\"IFNULL( VISIT.visitcount, \'0\')\" title=\"Visits\" primary=\"false\" />\n			<column id=\"observation\" name=\"IFNULL( OBSLAST.result, \'NA\')\" title=\"PPS\" primary=\"false\" />\n		</exportColumns>\n		<drillDownActions>\n			<action id=\"tickler\" name=\"Tickler\"/>\n			<action id=\"dxUpdate\" name=\"Disease Registry Update\" value=\"V66\"/>\n			<action id=\"demoExcl\" name=\"Patient Exclusion\"/>\n		</drillDownActions>\n		<query>\n  SELECT *	\n  FROM demographic d \n  LEFT JOIN (\n    SELECT demographic_no, dxresearch_code, coding_system, COUNT(dxresearch_code)\n    FROM dxresearch\n    WHERE status=\'A\' AND\n    (\n      (LEFT(dxresearch_code, 3) IN (\'V66\') AND coding_system = ${dxcodesSysICD}) OR\n      (dxresearch_code IN (\'105499002\',\'103735009\') AND coding_system LIKE ${dxcodesSysSnoMed})\n    )\n    GROUP BY demographic_no\n    ORDER BY demographic_no\n  ) dxr\n  ON d.demographic_no = dxr.demographic_no\n  -- Billed V66 in last 3 years\n  LEFT JOIN (\n    SELECT b.demographic_no AS demographic_no, b.dx_code1 AS dxcode1, b.dx_code2 AS dxcode2, b.dx_code3 AS dxcode3, COUNT(b.demographic_no) AS billcount\n    FROM billingmaster b\n    WHERE ( LEFT(b.dx_code1, 3) IN (\'V66\') OR LEFT(b.dx_code2, 3) IN (\'V66\') OR LEFT(b.dx_code3, 3) IN (\'V66\') )\n    AND DATE(b.createdate) &gt; ${lowerLimit.billingdate}\n    AND b.demographic_no &gt; 0\n    GROUP BY b.demographic_no HAVING COUNT(b.demographic_no) &gt; -1\n  ) BILLING\n  ON (d.demographic_no = BILLING.demographic_no)  \n  -- Coded dx during visit encounter in last 3 years\n  LEFT JOIN(\n    SELECT ASSIGNED.demographic_no AS demographic_no, observation_date AS visitdate, uuid, COUNT(ASSIGNED.demographic_no) AS visitcount\n    FROM casemgmt_note note\n    INNER JOIN (\n      SELECT note_id, CODED.demo_no AS demographic_no FROM casemgmt_issue_notes\n      INNER JOIN (\n        SELECT ci.id AS id, ci.update_date, ci.demographic_no demo_no FROM issue i\n        INNER JOIN casemgmt_issue ci\n        ON i.issue_id = ci.issue_id\n        WHERE\n	(i.type LIKE ${dxcodesSysICD} AND LEFT(i.code, 3) IN (\'V66\')) OR\n	(i.type LIKE ${dxcodesSysSnoMed} AND i.code IN (\'105499002\',\'103735009\'))\n      ) CODED\n      ON (casemgmt_issue_notes.id = CODED.id)\n      ORDER BY CODED.demo_no\n    ) ASSIGNED\n    ON note.note_id = ASSIGNED.note_id AND\n    -- GET LAST REVISION OF NOTE\n    note.note_id= (SELECT MAX(cmn.note_id) FROM casemgmt_note cmn WHERE cmn.uuid = note.uuid) AND\n    DATE(note.observation_date) &gt; ${lowerLimit.visitdate}\n    GROUP BY demographic_no\n    ORDER BY demographic_no\n  ) VISIT\n  ON (d.demographic_no = VISIT.demographic_no)  \n  -- Consider OBS here if needed\n  LEFT JOIN ( \n    SELECT m.demographicNo AS demographic_no, m.dataField AS result, m.dateObserved AS dateObserved\n    FROM measurements m\n    INNER JOIN (\n      SELECT demographicNo, MAX(dateObserved) AS dateObserved\n      FROM measurements\n      WHERE type = ${obsName}\n      GROUP BY demographicNo\n    ) mlast\n    ON m.demographicNo = mlast.demographicNo\n    WHERE m.type = ${obsName}\n    AND m.dateObserved = mlast.dateObserved\n    AND m.demographicNo &gt; 0\n    GROUP BY m.demographicNo\n  ) OBSLAST\n  ON (d.demographic_no = OBSLAST.demographic_no)  \n  WHERE d.patient_status LIKE ${active}\n  AND d.demographic_no &gt; 0\n  AND ( dxr.dxresearch_code IS NULL AND ((BILLING.billcount IS NOT NULL AND BILLING.billcount &gt;= ${numOfBills}) OR (VISIT.visitcount IS NOT NULL AND VISIT.visitcount &gt;= ${numOfVisits}) OR (OBSLAST.result IS NOT NULL AND OBSLAST.result &lt; ${obsResult})) )\n  AND d.provider_no LIKE ${provider}\n  AND d.demographic_no NOT IN ${excludedPatient}\n  ORDER BY d.demographic_no\n		</query>\n	</drillDownQuery>\n</indicatorTemplateXML>\n','','\0',0,NULL,NULL);
INSERT INTO `indicatorTemplate` (`dashboardId`, `name`, `category`, `subCategory`, `framework`, `frameworkVersion`, `definition`, `notes`, `template`, `active`, `locked`, `shared`, `metricSetName`, `metricLabel`) VALUES (@dashboardId1,'Frailty Prevalence','CDM (Click on the pie chart to see the patients included in the pie chart)','Frailty','DoBC CPQI PSP Panel','2017-08-02','Proportion of active patients in the practice coded with Frailty.','\ni) Frailty patients: \"Patient Status: Active\nAND has a Current Problem List/Profile item code of 248279007, 404904002, V15, 797\"\nii) Other patients: \"Patient Status: Active\nAND does not have a Current Problem List/Profile item code of 248279007, 40490400','<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<indicatorTemplateXML>\n	<author>OSCAR BC</author>\n	<uid></uid>\n	<heading>\n		<category>CDM (Click on the pie chart to see the patients included in the pie chart)</category>\n		<subCategory>Frailty</subCategory>\n		<name>Frailty Prevalence</name>\n		<definition>Proportion of active patients in the practice coded with Frailty.</definition>\n		<framework>DoBC CPQI PSP Panel</framework>\n		<frameworkVersion>08-02-2017</frameworkVersion>\n		<notes>\ni) Frailty patients: \"Patient Status: Active\nAND has a Current Problem List/Profile item code of 248279007, 404904002, V15, 797\"\nii) Other patients: \"Patient Status: Active\nAND does not have a Current Problem List/Profile item code of 248279007, 404904002, V15, 797\"\n		</notes>\n	</heading>\n	<indicatorQuery>\n		<version>03-13-2018</version>\n		<params>\n			<!-- \n				Required parameter provider. Value options are: \n					[ an individual providerNo, or provider range ] ie: value=\"370, 1001\" \n					\"all\" ie: value=\"all\" including null.\n					\"loggedInProvider\" ie:\n				Default is \"loggedInProvider\"\n				Use this parameter in the query as '${provider}'\n				This parameter should be used for fetching patient\'s assigned to a MRP.\n				ie: WHERE demographic.provider_no = ${provider}\n			-->\n			<parameter id=\"provider\" name=\"provider_no\" value=\"loggedInProvider\" />\n			<parameter id=\"pstatus\" name=\"Patient Status\" value=\"\'AC\'\" />\n			<parameter id=\"dxcodesICD\" name=\"Dx Codes (ICD)\" value=\"V15,797\" />\n			<parameter id=\"dxcodesSysICD\" name=\"DX CodeSystem (ICD)\" value=\"\'icd9\'\" />\n			<parameter id=\"dxcodesSnoMed\" name=\"Dx Codes (SnoMed)\" value=\"248279007,404904002\" />\n			<parameter id=\"dxcodesSysSnoMed\" name=\"Dx CodeSystem (SnoMed)\" value=\"\'snomed%\'\" />\n			<parameter id=\"excludedPatient\" name=\"Excluded Patient\" value=\"excludedPatient\" />\n		</params>\n		<query>\n			<!-- Indicator SQL Query here -->\nSELECT\n -- TOTAL NUMBER OF PATIENTS NOTED AS ACTIVE WITH CURRENT FRAILTY\n IF( COUNT(fin.patient) &gt; 0, SUM( IF( dxresearch_code is not NULL , 1, 0) ) , 0 ) AS \"Number Active, Current Frailty\",\n -- TOTAL NUMBER OF PATIENTS NOTED AS ACTIVE WITHOUT CURRENT FRAILTY\n IF( COUNT(fin.patient) &gt; 0, SUM( IF( dxresearch_code is NULL , 1, 0) ) , 0 ) AS \"Number Active, No Current Frailty\"\nFROM (\n SELECT d.demographic_no AS patient, d.patient_status AS pt_status, dxresearch_code\n FROM demographic d\n LEFT JOIN (\n  SELECT demographic_no, dxresearch_code, COUNT(dxresearch_code)\n  FROM dxresearch\n  WHERE status=\'A\' AND\n  (\n   (dxresearch_code IN (\'V15\', \'797\') AND coding_system = ${dxcodesSysICD}) OR\n   (dxresearch_code IN (\'248279007\', \'404904002\') AND coding_system LIKE ${dxcodesSysSnoMed})\n   )\n  GROUP BY demographic_no\n  ORDER BY demographic_no\n ) problems\n ON d.demographic_no = problems.demographic_no\n WHERE d.provider_no LIKE ${provider} AND d.patient_status = ${pstatus}\n AND d.demographic_no NOT IN ${excludedPatient}\n) fin\n		</query>\n	</indicatorQuery>\n	<drillDownQuery>\n		<version>03-13-2018</version>\n		<params>\n			<parameter id=\"provider\" name=\"provider_no\" value=\"loggedInProvider\" />	\n			<parameter id=\"pstatus\" name=\"Patient Status\" value=\"\'AC\'\" />\n			<parameter id=\"dxcodesICD\" name=\"Dx Codes (ICD)\" value=\"V15,797\" />\n			<parameter id=\"dxcodesSysICD\" name=\"DX CodeSystem (ICD)\" value=\"\'icd9\'\" />\n			<parameter id=\"dxcodesSnoMed\" name=\"Dx Codes (SnoMed)\" value=\"248279007,404904002\" />\n			<parameter id=\"dxcodesSysSnoMed\" name=\"Dx CodeSystem (SnoMed)\" value=\"\'snomed%\'\" />\n			<parameter id=\"excludedPatient\" name=\"Excluded Patient\" value=\"excludedPatient\" />\n		</params>\n		<displayColumns>\n			<column id=\"demographic\" name=\"d.demographic_no\" title=\"Patient Id\" primary=\"true\" />\n			<column id=\"name\" name=\"CONCAT( d.last_name, \', \', d.first_name )\" title=\"Patient Name\" primary=\"false\" />\n			<column id=\"dob\" name=\"DATE_FORMAT( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth), \'%m-%d-%Y\' )\" title=\"Date of Birth (mm-dd-yy)\" primary=\"false\" />\n			<column id=\"age\" name=\"TIMESTAMPDIFF(YEAR, DATE( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth) ), CURDATE() )\" title=\"Age\" primary=\"false\" />\n			<column id=\"dxcode\" title=\"Dx System/Code\" name=\"CONCAT(MIN(dxr.coding_system),\'/\',MIN(dxr.dxresearch_code),IF( MIN(dxr.dxresearch_code) != MAX(dxr.dxresearch_code),CONCAT( \' &amp; \', MAX(dxr.coding_system), \'/\', MAX(dxr.dxresearch_code) ),\'\') )\" primary=\"false\" />\n		</displayColumns>\n		<exportColumns>\n			<column id=\"demographic\" name=\"d.demographic_no\" title=\"Patient Id\" primary=\"true\" />\n			<column id=\"firstName\" name=\"d.first_name\" title=\"First Name\" primary=\"false\" />\n			<column id=\"lastName\" name=\"d.last_name\" title=\"Last Name\" primary=\"false\" />\n			<column id=\"dob\" name=\"DATE_FORMAT( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth), \'%m-%d-%Y\' )\" title=\"Date of Birth (mm-dd-yy)\" primary=\"false\" />\n			<column id=\"age\" name=\"TIMESTAMPDIFF(YEAR, DATE( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth) ), CURDATE() )\" title=\"Age\" primary=\"false\" />\n			<column id=\"dxcode\" title=\"Dx System/Code\" name=\"CONCAT(MIN(dxr.coding_system),\'/\',MIN(dxr.dxresearch_code),IF( MIN(dxr.dxresearch_code) != MAX(dxr.dxresearch_code),CONCAT( \' &amp; \', MAX(dxr.coding_system), \'/\', MAX(dxr.dxresearch_code) ),\'\') )\" primary=\"false\" />\n		</exportColumns>\n		<drillDownActions>\n			<action id=\"tickler\" name=\"Tickler\"/>\n			<action id=\"demoExcl\" name=\"Patient Exclusion\"/>\n		</drillDownActions>\n		<query>\n			<!-- Drilldown SQL Query here -->\nSELECT\n*			\nFROM demographic d\nLEFT JOIN dxresearch dxr\nON d.demographic_no = dxr.demographic_no\nWHERE d.patient_status=${pstatus}\nAND\n(\n (dxr.dxresearch_code IN (\'V15\', \'797\') AND dxr.coding_system = ${dxcodesSysICD}) OR\n (dxr.dxresearch_code IN (\'248279007\', \'404904002\') AND dxr.coding_system LIKE ${dxcodesSysSnoMed})\n )\nAND dxr.status = \'A\'\nAND d.provider_no LIKE ${provider}\nAND d.demographic_no NOT IN ${excludedPatient}\nGROUP BY d.demographic_no\nORDER BY COUNT(d.demographic_no) DESC, d.demographic_no\n		</query>\n	</drillDownQuery>\n</indicatorTemplateXML>\n','','\0',0,NULL,NULL);
INSERT INTO `indicatorTemplate` (`dashboardId`, `name`, `category`, `subCategory`, `framework`, `frameworkVersion`, `definition`, `notes`, `template`, `active`, `locked`, `shared`, `metricSetName`, `metricLabel`) VALUES (@dashboardId1,'Consider Frailty','CDM (Click on the pie chart to see the patients included in the pie chart)','Frailty','DoBC CPQI PSP Panel','2017-08-02','\n			Patients with frailty dx in 2+ bills or 2+ visit encounters within last 3 years\n		','\n		  Created for Doctors of BC\n		  i) Frailty dx in 2+ bills: \"Patient Status: Active\nAND does not have a Current Problem List/Profile item code of 248279007, 404904002, V15, 797 \nAND with >=2 Bills with the billing code of V15 in the last 3 years\"\n		  ii','<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<indicatorTemplateXML>\n	<author>OSCAR BC</author>\n	<uid></uid>\n	<heading>\n		<category>CDM (Click on the pie chart to see the patients included in the pie chart)</category>\n		<subCategory>Frailty</subCategory>\n		<name>Consider Frailty</name>\n		<definition>\n			Patients with frailty dx in 2+ bills or 2+ visit encounters within last 3 years\n		</definition>\n		<framework>DoBC CPQI PSP Panel</framework>\n		<frameworkVersion>08-02-2017</frameworkVersion>\n		<notes>\n		  Created for Doctors of BC\n		  i) Frailty dx in 2+ bills: \"Patient Status: Active\nAND does not have a Current Problem List/Profile item code of 248279007, 404904002, V15, 797 \nAND with >=2 Bills with the billing code of V15 in the last 3 years\"\n		  ii) Frailty dx in 2+ visits: \"Patient Status: Active\nAND does not have a Current Problem List/Profile item code of 248279007, 404904002, V15, 797 \nAND with >=2 Visit Encounters with Snomed diagnosis code 248279007, 404904002 or the ICD9 diagnosis code of V15, 797 in the last 3 years\"\n		</notes>\n	</heading>\n	<indicatorQuery>\n		<version>03-13-2018</version>\n		<params>\n			<parameter id=\"provider\" name=\"Provider Number\" value=\"loggedInProvider\" />\n			<parameter id=\"active\" name=\"Active Patients\" value=\"\'AC\'\" />\n			<parameter id=\"dxcodesICD\" name=\"Dx Codes (ICD)\" value=\"V15,797\" />\n                        <parameter id=\"dxcodesSysICD\" name=\"DX CodeSystem (ICD)\" value=\"\'icd9\'\" />\n                        <parameter id=\"dxcodesSnoMed\" name=\"Dx Codes (SnoMed)\" value=\"248279007,404904002\" />\n                        <parameter id=\"dxcodesSysSnoMed\" name=\"Dx CodeSystem (SnoMed)\" value=\"\'snomed%\'\" />\n			<parameter id=\"billingCode\" name=\"ICD9 Billing Code\" value=\"V15,797\" />\n			<parameter id=\"numOfBills\" name=\"Number of ICD9 V15,797 Bills\" value=\"2\" />\n			<parameter id=\"numOfVisits\" name=\"Number of Visit Encounters coded V15,797/ICD9\" value=\"2\" />\n			<parameter id=\"excludedPatient\" name=\"Excluded Patient\" value=\"excludedPatient\" />\n		</params>\n		<range>\n			<lowerLimit id=\"billingdate\" label=\"From Date\" name=\"Date\" value=\"DATE_SUB( NOW(), INTERVAL 3 YEAR )\" />\n			<upperLimit id=\"billingdate\" label=\"Date Today\" name=\"Date\" value=\"NOW()\" />\n			<lowerLimit id=\"visitdate\" label=\"From Date\" name=\"Date\" value=\"DATE_SUB( NOW(), INTERVAL 3 YEAR )\" />\n			<upperLimit id=\"visitdate\" label=\"Date Today\" name=\"Date\" value=\"NOW()\" />\n		</range>\n		<query>\nSELECT\n  IF ( COUNT(fin.patient) &gt; 0, SUM( CASE WHEN (fin.dx IS NULL AND fin.billcount &gt;= ${numOfBills}) THEN 1 ELSE 0 END ) , 0) AS \"Number with Frailty dx in ${numOfBills}+ bills\",\n  IF ( COUNT(fin.patient) &gt; 0, SUM( CASE WHEN (fin.dx IS NULL AND fin.visitcount &gt;= ${numOfVisits}) THEN 1 ELSE 0 END ) , 0) AS \"Number with Frailty dx in ${numOfVisits}+ visits\"\n-- Consider OBS here if needed  \nFROM ( \n  SELECT \n    d.demographic_no AS patient, \n    BILLING.billcount AS billcount,\n    VISIT.visitcount AS visitcount,\n    dxr.dxresearch_code AS dx,\n    dxr.coding_system AS dxs\n  FROM demographic d \n  LEFT JOIN (\n    SELECT demographic_no, dxresearch_code, coding_system, COUNT(dxresearch_code)\n    FROM dxresearch\n    WHERE status=\'A\' AND\n    (\n      (dxresearch_code IN (\'V15\',\'797\') AND coding_system = ${dxcodesSysICD}) OR\n      (dxresearch_code IN (\'248279007\',\'404904002\') AND coding_system LIKE ${dxcodesSysSnoMed})\n    )\n    GROUP BY demographic_no\n    ORDER BY demographic_no\n  ) dxr\n  ON d.demographic_no = dxr.demographic_no\n  -- Billed V15,797 in last 3 years\n  LEFT JOIN (\n    SELECT b.demographic_no AS demographic_no, b.dx_code1 AS dxcode1, b.dx_code2 AS dxcode2, b.dx_code3 AS dxcode3, COUNT(b.demographic_no) AS billcount\n    FROM billingmaster b\n    WHERE ( b.dx_code1 IN (\'V15\',\'797\') OR b.dx_code2 IN (\'V15\',\'797\') OR b.dx_code3 IN (\'V15\',\'797\') )\n    AND DATE(b.createdate) &gt; ${lowerLimit.billingdate}\n    AND b.demographic_no &gt; 0\n    GROUP BY b.demographic_no HAVING COUNT(b.demographic_no) &gt; -1\n  ) BILLING\n  ON (d.demographic_no = BILLING.demographic_no)  \n  -- Coded dx during visit encounter in last 3 years\n  LEFT JOIN(\n    SELECT ASSIGNED.demographic_no AS demographic_no, observation_date AS visitdate, uuid, COUNT(ASSIGNED.demographic_no) AS visitcount\n    FROM casemgmt_note note\n    INNER JOIN (\n      SELECT note_id, CODED.demo_no AS demographic_no FROM casemgmt_issue_notes\n      INNER JOIN (\n        SELECT ci.id AS id, ci.update_date, ci.demographic_no demo_no FROM issue i\n        INNER JOIN casemgmt_issue ci\n        ON i.issue_id = ci.issue_id\n        WHERE\n	(i.type LIKE ${dxcodesSysICD} AND i.code IN (\'V15\',\'797\')) OR\n	(i.type LIKE ${dxcodesSysSnoMed} AND i.code IN (\'248279007\',\'404904002\'))\n      ) CODED\n      ON (casemgmt_issue_notes.id = CODED.id)\n      ORDER BY CODED.demo_no\n    ) ASSIGNED\n    ON note.note_id = ASSIGNED.note_id AND\n    -- GET LAST REVISION OF NOTE\n    note.note_id= (SELECT MAX(cmn.note_id) FROM casemgmt_note cmn WHERE cmn.uuid = note.uuid) AND\n    DATE(note.observation_date) &gt; ${lowerLimit.visitdate}\n    GROUP BY demographic_no\n    ORDER BY demographic_no\n  ) VISIT\n  ON (d.demographic_no = VISIT.demographic_no)  \n  WHERE d.patient_status LIKE ${active}\n  AND d.demographic_no &gt; 0\n  AND d.provider_no LIKE '${provider}'\n  AND d.demographic_no NOT IN ${excludedPatient}\n) fin\n		</query>\n	</indicatorQuery>\n	<drillDownQuery>\n		<version>03-13-2018</version>\n		<params>\n			<parameter id=\"provider\" name=\"Provider Number\" value=\"loggedInProvider\" />\n			<parameter id=\"active\" name=\"Active Patients\" value=\"\'AC\'\" />\n			<parameter id=\"dxcodesICD\" name=\"Dx Codes (ICD)\" value=\"V15,797\" />\n                        <parameter id=\"dxcodesSysICD\" name=\"DX CodeSystem (ICD)\" value=\"\'icd9\'\" />\n                        <parameter id=\"dxcodesSnoMed\" name=\"Dx Codes (SnoMed)\" value=\"248279007,404904002\" />\n                        <parameter id=\"dxcodesSysSnoMed\" name=\"Dx CodeSystem (SnoMed)\" value=\"\'snomed%\'\" />\n			<parameter id=\"billingCode\" name=\"ICD9 Billing Code\" value=\"V15,797\" />\n			<parameter id=\"numOfBills\" name=\"Number of ICD9 V15,797 Bills\" value=\"2\" />\n			<parameter id=\"numOfVisits\" name=\"Number of Visit Encounters coded V15,797/ICD9\" value=\"2\" />\n			<parameter id=\"excludedPatient\" name=\"Excluded Patient\" value=\"excludedPatient\" />\n		</params>\n		\n		<range>\n			<lowerLimit id=\"billingdate\" label=\"From Date\" name=\"Date\" value=\"DATE_SUB( NOW(), INTERVAL 3 YEAR )\" />\n			<upperLimit id=\"billingdate\" label=\"Date Today\" name=\"Date\" value=\"NOW()\" />\n			<lowerLimit id=\"visitdate\" label=\"From Date\" name=\"Date\" value=\"DATE_SUB( NOW(), INTERVAL 3 YEAR )\" />\n			<upperLimit id=\"visitdate\" label=\"Date Today\" name=\"Date\" value=\"NOW()\" />\n		</range>\n		\n		<displayColumns>\n			<column id=\"demographic\" name=\"d.demographic_no\" title=\"Patient Id\" primary=\"true\" />\n			<column id=\"name\" name=\"CONCAT( d.last_name, \', \', d.first_name )\" title=\"Patient Name\" primary=\"false\" />\n			<column id=\"dob\" name=\"DATE_FORMAT( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth), \'%m-%d-%Y\' )\" title=\"Date of Birth\" primary=\"false\" />\n			<column id=\"age\" name=\"TIMESTAMPDIFF(YEAR, DATE( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth) ), CURDATE() )\" title=\"Age\" primary=\"false\" />\n			<column id=\"billings\" name=\"IFNULL( BILLING.billcount, \'0\')\" title=\"Billings\" primary=\"false\" />\n			<column id=\"visits\" name=\"IFNULL( VISIT.visitcount, \'0\')\" title=\"Visits\" primary=\"false\" />\n		</displayColumns>\n		\n		<exportColumns>\n			<column id=\"demographic\" name=\"d.demographic_no\" title=\"Patient Id\" primary=\"true\" />\n                        <column id=\"firstName\" name=\"d.first_name\" title=\"First Name\" primary=\"false\" />\n                        <column id=\"lastName\" name=\"d.last_name\" title=\"Last Name\" primary=\"false\" />\n			<column id=\"dob\" name=\"DATE_FORMAT( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth), \'%m-%d-%Y\' )\" title=\"Date of Birth\" primary=\"false\" />\n			<column id=\"age\" name=\"TIMESTAMPDIFF(YEAR, DATE( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth) ), CURDATE() )\" title=\"Age\" primary=\"false\" />\n			<column id=\"billings\" name=\"IFNULL( BILLING.billcount, \'0\')\" title=\"Billings\" primary=\"false\" />\n			<column id=\"visits\" name=\"IFNULL( VISIT.visitcount, \'0\')\" title=\"Visits\" primary=\"false\" />\n		</exportColumns>\n		<drillDownActions>\n			<action id=\"tickler\" name=\"Tickler\"/>\n			<action id=\"dxUpdate\" name=\"Disease Registry Update\" value=\"V15\"/>\n			<action id=\"demoExcl\" name=\"Patient Exclusion\"/>\n		</drillDownActions>\n		<query>\n  SELECT *	\n  FROM demographic d \n  LEFT JOIN (\n    SELECT demographic_no, dxresearch_code, coding_system, COUNT(dxresearch_code)\n    FROM dxresearch\n    WHERE status=\'A\' AND\n    (\n      (dxresearch_code IN (\'V15\',\'797\') AND coding_system = ${dxcodesSysICD}) OR\n      (dxresearch_code IN (\'248279007\',\'404904002\') AND coding_system LIKE ${dxcodesSysSnoMed})\n    )\n    GROUP BY demographic_no\n    ORDER BY demographic_no\n  ) dxr\n  ON d.demographic_no = dxr.demographic_no\n  -- Billed V15,797 in last 3 years\n  LEFT JOIN (\n    SELECT b.demographic_no AS demographic_no, b.dx_code1 AS dxcode1, b.dx_code2 AS dxcode2, b.dx_code3 AS dxcode3, COUNT(b.demographic_no) AS billcount\n    FROM billingmaster b\n    WHERE ( b.dx_code1 IN (\'V15\',\'797\') OR b.dx_code2 IN (\'V15\',\'797\') OR b.dx_code3 IN (\'V15\',\'797\') )\n    AND DATE(b.createdate) &gt; ${lowerLimit.billingdate}\n    AND b.demographic_no &gt; 0\n    GROUP BY b.demographic_no HAVING COUNT(b.demographic_no) &gt; -1\n  ) BILLING\n  ON (d.demographic_no = BILLING.demographic_no)  \n  -- Coded dx during visit encounter in last 3 years\n  LEFT JOIN(\n    SELECT ASSIGNED.demographic_no AS demographic_no, observation_date AS visitdate, uuid, COUNT(ASSIGNED.demographic_no) AS visitcount\n    FROM casemgmt_note note\n    INNER JOIN (\n      SELECT note_id, CODED.demo_no AS demographic_no FROM casemgmt_issue_notes\n      INNER JOIN (\n        SELECT ci.id AS id, ci.update_date, ci.demographic_no demo_no FROM issue i\n        INNER JOIN casemgmt_issue ci\n        ON i.issue_id = ci.issue_id\n        WHERE\n	(i.type LIKE ${dxcodesSysICD} AND i.code IN (\'V15\',\'797\')) OR\n	(i.type LIKE ${dxcodesSysSnoMed} AND i.code IN (\'248279007\',\'404904002\'))\n      ) CODED\n      ON (casemgmt_issue_notes.id = CODED.id)\n      ORDER BY CODED.demo_no\n    ) ASSIGNED\n    ON note.note_id = ASSIGNED.note_id AND\n    -- GET LAST REVISION OF NOTE\n    note.note_id= (SELECT MAX(cmn.note_id) FROM casemgmt_note cmn WHERE cmn.uuid = note.uuid) AND\n    DATE(note.observation_date) &gt; ${lowerLimit.visitdate}\n    GROUP BY demographic_no\n    ORDER BY demographic_no\n  ) VISIT\n  ON (d.demographic_no = VISIT.demographic_no)  \n-- Consider OBS here if needed  \n  WHERE d.patient_status LIKE ${active}\n  AND d.demographic_no &gt; 0\n  AND ( dxr.dxresearch_code IS NULL AND ((BILLING.billcount IS NOT NULL AND BILLING.billcount &gt;= ${numOfBills}) OR (VISIT.visitcount IS NOT NULL AND VISIT.visitcount &gt;= ${numOfVisits})))\n  AND d.provider_no LIKE ${provider}\n  AND d.demographic_no NOT IN ${excludedPatient}\n  ORDER BY d.demographic_no\n		</query>\n	</drillDownQuery>\n</indicatorTemplateXML>\n','','\0',0,NULL,NULL);
INSERT INTO `indicatorTemplate` (`dashboardId`, `name`, `category`, `subCategory`, `framework`, `frameworkVersion`, `definition`, `notes`, `template`, `active`, `locked`, `shared`, `metricSetName`, `metricLabel`) VALUES (@dashboardId2,'Chronic Kidney Disease Prevalence','CDM (Click on the pie chart to see the patients included in the pie chart)','Chronic Kidney Disease','DoBC CPQI PSP Panel','2017-08-02','Proportion of active patients in the practice coded with Chronic Kidney Disease.','\ni) CKD patients: \"Patient Status: Active\nAND has a Current Problem List/Profile item code of 90688005, 709044004 OR starting with 582, 585\"\nii) Other patients: \"Patient Status: Active\nAND does not have a Current Problem List/Profile item code of 90688005','<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<indicatorTemplateXML>\n	<author>OSCAR BC</author>\n	<uid></uid>\n	<heading>\n		<category>CDM (Click on the pie chart to see the patients included in the pie chart)</category>\n		<subCategory>Chronic Kidney Disease</subCategory>\n		<name>Chronic Kidney Disease Prevalence</name>\n		<definition>Proportion of active patients in the practice coded with Chronic Kidney Disease.</definition>\n		<framework>DoBC CPQI PSP Panel</framework>\n		<frameworkVersion>08-02-2017</frameworkVersion>\n		<notes>\ni) CKD patients: \"Patient Status: Active\nAND has a Current Problem List/Profile item code of 90688005, 709044004 OR starting with 582, 585\"\nii) Other patients: \"Patient Status: Active\nAND does not have a Current Problem List/Profile item code of 90688005, 709044004 OR starting with 582, 585\"\n		</notes>\n	</heading>\n	<indicatorQuery>\n		<version>03-13-2018</version>\n		<params>\n			<!-- \n				Required parameter provider. Value options are: \n					[ an individual providerNo, or provider range ] ie: value=\"370, 1001\" \n					\"all\" ie: value=\"all\" including null.\n					\"loggedInProvider\" ie:\n				Default is \"loggedInProvider\"\n				Use this parameter in the query as '${provider}'\n				This parameter should be used for fetching patient\'s assigned to a MRP.\n				ie: WHERE demographic.provider_no = ${provider}\n			-->\n			<parameter id=\"provider\" name=\"provider_no\" value=\"loggedInProvider\" />\n			<parameter id=\"pstatus\" name=\"Patient Status\" value=\"\'AC\'\" />\n			<parameter id=\"dxcodesICD\" name=\"Dx Codes (ICD)\" value=\"582,585\" />\n			<parameter id=\"dxcodesSysICD\" name=\"DX CodeSystem (ICD)\" value=\"\'icd9\'\" />\n			<parameter id=\"dxcodesSnoMed\" name=\"Dx Codes (SnoMed)\" value=\"90688005,709044004\" />\n			<parameter id=\"dxcodesSysSnoMed\" name=\"Dx CodeSystem (SnoMed)\" value=\"\'snomed%\'\" />\n			<parameter id=\"excludedPatient\" name=\"Excluded Patient\" value=\"excludedPatient\" />\n		</params>\n		<query>\n			<!-- Indicator SQL Query here -->\nSELECT\n -- TOTAL NUMBER OF PATIENTS NOTED AS ACTIVE WITH CURRENT CHRONIC KIDNEY DISEASE\n IF( COUNT(fin.patient) &gt; 0, SUM( IF( dxresearch_code is not NULL , 1, 0) ) , 0 ) AS \"Number Active, Current Chronic Kidney Disease\",\n -- TOTAL NUMBER OF PATIENTS NOTED AS ACTIVE WITHOUT CURRENT CHRONIC KIDNEY DISEASE\n IF( COUNT(fin.patient) &gt; 0, SUM( IF( dxresearch_code is NULL , 1, 0) ) , 0 ) AS \"Number Active, No Current Chronic Kidney Disease\"\nFROM (\n SELECT d.demographic_no AS patient, d.patient_status AS pt_status, dxresearch_code\n FROM demographic d\n LEFT JOIN (\n  SELECT demographic_no, dxresearch_code, COUNT(dxresearch_code)\n  FROM dxresearch\n  WHERE status=\'A\' AND\n  (\n   (LEFT(dxresearch_code, 3) IN (\'582\', \'585\') AND coding_system = ${dxcodesSysICD}) OR\n   (dxresearch_code IN (\'90688005\', \'709044004\') AND coding_system LIKE ${dxcodesSysSnoMed})\n   )\n  GROUP BY demographic_no\n  ORDER BY demographic_no\n ) problems\n ON d.demographic_no = problems.demographic_no\n WHERE d.provider_no LIKE ${provider} AND d.patient_status = ${pstatus}\n AND d.demographic_no NOT IN ${excludedPatient}\n) fin\n		</query>\n	</indicatorQuery>\n	<drillDownQuery>\n		<version>03-13-2018</version>\n		<params>\n			<parameter id=\"provider\" name=\"provider_no\" value=\"loggedInProvider\" />	\n			<parameter id=\"pstatus\" name=\"Patient Status\" value=\"\'AC\'\" />\n			<parameter id=\"dxcodesICD\" name=\"Dx Codes (ICD)\" value=\"582,585\" />\n			<parameter id=\"dxcodesSysICD\" name=\"DX CodeSystem (ICD)\" value=\"\'icd9\'\" />\n			<parameter id=\"dxcodesSnoMed\" name=\"Dx Codes (SnoMed)\" value=\"90688005,709044004\" />\n			<parameter id=\"dxcodesSysSnoMed\" name=\"Dx CodeSystem (SnoMed)\" value=\"\'snomed%\'\" />\n			<parameter id=\"excludedPatient\" name=\"Excluded Patient\" value=\"excludedPatient\" />\n		</params>\n		<displayColumns>\n			<column id=\"demographic\" name=\"d.demographic_no\" title=\"Patient Id\" primary=\"true\" />\n			<column id=\"name\" name=\"CONCAT( d.last_name, \', \', d.first_name )\" title=\"Patient Name\" primary=\"false\" />\n			<column id=\"dob\" name=\"DATE_FORMAT( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth), \'%m-%d-%Y\' )\" title=\"Date of Birth (mm-dd-yy)\" primary=\"false\" />\n			<column id=\"age\" name=\"TIMESTAMPDIFF(YEAR, DATE( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth) ), CURDATE() )\" title=\"Age\" primary=\"false\" />\n			<column id=\"dxcode\" title=\"Dx System/Code\" name=\"CONCAT(MIN(dxr.coding_system),\'/\',MIN(dxr.dxresearch_code),IF( MIN(dxr.dxresearch_code) != MAX(dxr.dxresearch_code),CONCAT( \' &amp; \', MAX(dxr.coding_system), \'/\', MAX(dxr.dxresearch_code) ),\'\') )\" primary=\"false\" />\n		</displayColumns>\n		<exportColumns>\n			<column id=\"demographic\" name=\"d.demographic_no\" title=\"Patient Id\" primary=\"true\" />\n			<column id=\"firstName\" name=\"d.first_name\" title=\"First Name\" primary=\"false\" />\n			<column id=\"lastName\" name=\"d.last_name\" title=\"Last Name\" primary=\"false\" />\n			<column id=\"dob\" name=\"DATE_FORMAT( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth), \'%m-%d-%Y\' )\" title=\"Date of Birth (mm-dd-yy)\" primary=\"false\" />\n			<column id=\"age\" name=\"TIMESTAMPDIFF(YEAR, DATE( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth) ), CURDATE() )\" title=\"Age\" primary=\"false\" />\n			<column id=\"dxcode\" title=\"Dx System/Code\" name=\"CONCAT(MIN(dxr.coding_system),\'/\',MIN(dxr.dxresearch_code),IF( MIN(dxr.dxresearch_code) != MAX(dxr.dxresearch_code),CONCAT( \' &amp; \', MAX(dxr.coding_system), \'/\', MAX(dxr.dxresearch_code) ),\'\') )\" primary=\"false\" />\n		</exportColumns>\n		<drillDownActions>\n			<action id=\"tickler\" name=\"Tickler\"/>\n			<action id=\"demoExcl\" name=\"Patient Exclusion\"/>\n		</drillDownActions>\n		<query>\n			<!-- Drilldown SQL Query here -->\nSELECT\n*			\nFROM demographic d\nLEFT JOIN dxresearch dxr\nON d.demographic_no = dxr.demographic_no\nWHERE d.patient_status=${pstatus}\nAND\n(\n (LEFT(dxr.dxresearch_code, 3) IN (\'582\', \'585\') AND dxr.coding_system = ${dxcodesSysICD}) OR\n (dxr.dxresearch_code IN (\'90688005\', \'709044004\') AND dxr.coding_system LIKE ${dxcodesSysSnoMed})\n )\nAND dxr.status = \'A\'\nAND d.provider_no LIKE ${provider}\nAND d.demographic_no NOT IN ${excludedPatient}\nGROUP BY d.demographic_no\nORDER BY COUNT(d.demographic_no) DESC, d.demographic_no\n		</query>\n	</drillDownQuery>\n</indicatorTemplateXML>\n','','\0',0,NULL,NULL);
INSERT INTO `indicatorTemplate` (`dashboardId`, `name`, `category`, `subCategory`, `framework`, `frameworkVersion`, `definition`, `notes`, `template`, `active`, `locked`, `shared`, `metricSetName`, `metricLabel`) VALUES (@dashboardId2,'Consider Chronic Kidney Disease','CDM (Click on the pie chart to see the patients included in the pie chart)','Chronic Kidney Disease','DoBC CPQI PSP Panel','2017-08-02','\n			Patients with chronic kidney disease dx in 2+ bills or 2+ visit encounters within last 3 years or eGFR <60 or ACR >=3\n		','\n		  Created for Doctors of BC\n		  i) Chronic Kidney Disease dx in 2+ bills: \"Patient Status: Active\nAND does not have a Current Problem List/Profile item code of 90688005, 709044004 OR starting with 582, 585\nAND with >= 2 Bills with the ICD9 diagnosis co','<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<indicatorTemplateXML>\n	<author>OSCAR BC</author>\n	<uid></uid>\n	<heading>\n		<category>CDM (Click on the pie chart to see the patients included in the pie chart)</category>\n		<subCategory>Chronic Kidney Disease</subCategory>\n		<name>Consider Chronic Kidney Disease</name>\n		<definition>\n			Patients with chronic kidney disease dx in 2+ bills or 2+ visit encounters within last 3 years or eGFR &lt;60 or ACR &gt;=3\n		</definition>\n		<framework>DoBC CPQI PSP Panel</framework>\n		<frameworkVersion>08-02-2017</frameworkVersion>\n		<notes>\n		  Created for Doctors of BC\n		  i) Chronic Kidney Disease dx in 2+ bills: \"Patient Status: Active\nAND does not have a Current Problem List/Profile item code of 90688005, 709044004 OR starting with 582, 585\nAND with >= 2 Bills with the ICD9 diagnosis code starting with 585 in the last 3 years\"\n		  ii) Chronic Kidney Disease dx in 2+ visits: \"Patient Status: Active\nAND does not have a Current Problem List/Profile item code of 90688005, 709044004 OR starting with 582, 585\nAND with >= 2 Visit Encounters with Snomed diagnosis code 90688005, 709044004 or the ICD9 diagnosis code starting with 585 in the last 3 years\"\n		  iii) eGFR &lt;60 or ACR &gt;=3: \"Patient Status: Active\nAND does not have a Current Problem List/Profile item code of 90688005, 709044004 OR starting with 585\nAND with last eGFR (estimated Glomerular Filtration Rate) lab result &lt;60 OR a last ACR (Albumin:creatinine ratio) lab result &gt;=3 OR  2 or more eGFR &lt;60 or ACR &gt;=3 in the last 2 years\"\n		</notes>\n	</heading>\n	<indicatorQuery>\n		<version>03-13-2018</version>\n		<params>\n			<parameter id=\"provider\" name=\"Provider Number\" value=\"loggedInProvider\" />\n			<parameter id=\"active\" name=\"Active Patients\" value=\"\'AC\'\" />\n			<parameter id=\"dxcodesICD\" name=\"Dx Codes (ICD)\" value=\"582,585\" />\n                        <parameter id=\"dxcodesSysICD\" name=\"DX CodeSystem (ICD)\" value=\"\'icd9\'\" />\n                        <parameter id=\"dxcodesSnoMed\" name=\"Dx Codes (SnoMed)\" value=\"90688005,709044004\" />\n                        <parameter id=\"dxcodesSysSnoMed\" name=\"Dx CodeSystem (SnoMed)\" value=\"\'snomed%\'\" />\n			<parameter id=\"billingCode\" name=\"ICD9 Billing Code\" value=\"582,585\" />\n			<parameter id=\"numOfBills\" name=\"Number of ICD9 582,585 Bills\" value=\"2\" />\n			<parameter id=\"numOfVisits\" name=\"Number of Visit Encounters coded 582,585/ICD9\" value=\"2\" />\n			<parameter id=\"obs1Name\" name=\"Obs 1 Name\" value=\"\'EGFR\'\" />\n			<parameter id=\"obs1Result\" name=\"Obs 1 Result\" value=\"60\" />\n			<parameter id=\"obs2Name\" name=\"Obs 2 Name\" value=\"\'ACR\'\" />\n			<parameter id=\"obs2Result\" name=\"Obs 2 Result\" value=\"3\" />\n			<parameter id=\"numOfObs\" name=\"Number of measurements\" value=\"2\" />\n			<parameter id=\"excludedPatient\" name=\"Excluded Patient\" value=\"excludedPatient\" />\n		</params>\n		<range>\n			<lowerLimit id=\"billingdate\" label=\"From Date\" name=\"Date\" value=\"DATE_SUB( NOW(), INTERVAL 3 YEAR )\" />\n			<upperLimit id=\"billingdate\" label=\"Date Today\" name=\"Date\" value=\"NOW()\" />\n			<lowerLimit id=\"visitdate\" label=\"From Date\" name=\"Date\" value=\"DATE_SUB( NOW(), INTERVAL 3 YEAR )\" />\n			<upperLimit id=\"visitdate\" label=\"Date Today\" name=\"Date\" value=\"NOW()\" />\n			<lowerLimit id=\"labdate\" label=\"From Date\" name=\"Date\" value=\"DATE_SUB( NOW(), INTERVAL 2 YEAR )\" />\n			<upperLimit id=\"labdate\" label=\"Date Today\" name=\"Date\" value=\"NOW()\" />\n		</range>\n		<query>\nSELECT\n  IF ( COUNT(fin.patient) &gt; 0, SUM( CASE WHEN (fin.dx IS NULL AND fin.billcount &gt;= ${numOfBills}) THEN 1 ELSE 0 END ) , 0) AS \"Number with Chronic Kidney Disease dx in ${numOfBills}+ bills\",\n  IF ( COUNT(fin.patient) &gt; 0, SUM( CASE WHEN (fin.dx IS NULL AND fin.visitcount &gt;= ${numOfVisits}) THEN 1 ELSE 0 END ) , 0) AS \"Number with Chronic Kidney Disease dx in ${numOfVisits}+ visits\",\n  IF ( COUNT(fin.patient) &gt; 0, SUM( CASE WHEN (fin.dx IS NULL AND (fin.result1 &lt; ${obs1Result} OR fin.result2 &gt;= ${obs2Result} OR fin.cntResult1 &gt;= ${numOfObs} OR fin.cntResult2 &gt;= ${numOfObs})) THEN 1 ELSE 0 END ) , 0) AS \"Number with eGFR &lt; ${obs1Result} or ACR &gt;= ${obs2Result}\"  \nFROM ( \n  SELECT \n    d.demographic_no AS patient, \n    BILLING.billcount AS billcount,\n    VISIT.visitcount AS visitcount,\n    OBS1LAST.result AS result1,\n    OBS2LAST.result AS result2,\n    OBS1CNT.countOBS AS cntResult1,\n    OBS2CNT.countOBS AS cntResult2,\n    dxr.dxresearch_code AS dx,\n    dxr.coding_system AS dxs\n  FROM demographic d \n  LEFT JOIN (\n    SELECT demographic_no, dxresearch_code, coding_system, COUNT(dxresearch_code)\n    FROM dxresearch\n    WHERE status=\'A\' AND\n    (\n      (LEFT(dxresearch_code, 3) IN (\'582\',\'585\') AND coding_system = ${dxcodesSysICD}) OR\n      (dxresearch_code IN (\'90688005\',\'709044004\') AND coding_system LIKE ${dxcodesSysSnoMed})\n    )\n    GROUP BY demographic_no\n    ORDER BY demographic_no\n  ) dxr\n  ON d.demographic_no = dxr.demographic_no\n  -- Billed 582,585 in last 3 years\n  LEFT JOIN (\n    SELECT b.demographic_no AS demographic_no, b.dx_code1 AS dxcode1, b.dx_code2 AS dxcode2, b.dx_code3 AS dxcode3, COUNT(b.demographic_no) AS billcount\n    FROM billingmaster b\n    WHERE ( LEFT(b.dx_code1, 3) IN (\'582\',\'585\') OR LEFT(b.dx_code2, 3) IN (\'582\',\'585\') OR LEFT(b.dx_code3, 3) IN (\'582\',\'585\') )\n    AND DATE(b.createdate) &gt; ${lowerLimit.billingdate}\n    AND b.demographic_no &gt; 0\n    GROUP BY b.demographic_no HAVING COUNT(b.demographic_no) &gt; -1\n  ) BILLING\n  ON (d.demographic_no = BILLING.demographic_no)  \n  -- Coded dx during visit encounter in last 3 years\n  LEFT JOIN(\n    SELECT ASSIGNED.demographic_no AS demographic_no, observation_date AS visitdate, uuid, COUNT(ASSIGNED.demographic_no) AS visitcount\n    FROM casemgmt_note note\n    INNER JOIN (\n      SELECT note_id, CODED.demo_no AS demographic_no FROM casemgmt_issue_notes\n      INNER JOIN (\n        SELECT ci.id AS id, ci.update_date, ci.demographic_no demo_no FROM issue i\n        INNER JOIN casemgmt_issue ci\n        ON i.issue_id = ci.issue_id\n        WHERE\n	(i.type LIKE ${dxcodesSysICD} AND LEFT(i.code, 3) IN (\'582\',\'585\')) OR\n	(i.type LIKE ${dxcodesSysSnoMed} AND i.code IN (\'90688005\',\'709044004\'))\n      ) CODED\n      ON (casemgmt_issue_notes.id = CODED.id)\n      ORDER BY CODED.demo_no\n    ) ASSIGNED\n    ON note.note_id = ASSIGNED.note_id AND\n    -- GET LAST REVISION OF NOTE\n    note.note_id= (SELECT MAX(cmn.note_id) FROM casemgmt_note cmn WHERE cmn.uuid = note.uuid) AND\n    DATE(note.observation_date) &gt; ${lowerLimit.visitdate}\n    GROUP BY demographic_no\n    ORDER BY demographic_no\n  ) VISIT\n  ON (d.demographic_no = VISIT.demographic_no)\n  LEFT JOIN ( \n    SELECT m.demographicNo AS demographic_no, m.dataField as result, m.dateObserved AS dateObserved\n    FROM measurements m\n    INNER JOIN (\n      SELECT demographicNo, MAX(dateObserved) AS dateObserved\n      FROM measurements\n      WHERE type = ${obs1Name}\n      GROUP BY demographicNo\n    ) mlast\n    ON m.demographicNo = mlast.demographicNo\n    WHERE m.type = ${obs1Name}\n    AND m.dataField NOT LIKE \'>%\'\n    AND m.dateObserved = mlast.dateObserved\n    AND m.demographicNo &gt; 0\n    GROUP BY m.demographicNo HAVING COUNT(m.demographicNo) &gt; -1\n  ) OBS1LAST\n  ON (d.demographic_no = OBS1LAST.demographic_no) \n  LEFT JOIN ( \n    SELECT m.demographicNo AS demographic_no, m.dataField as result, m.dateObserved AS dateObserved\n    FROM measurements m\n    INNER JOIN (\n      SELECT demographicNo, MAX(dateObserved) AS dateObserved\n      FROM measurements\n      WHERE type = ${obs2Name}\n      GROUP BY demographicNo\n    ) mlast\n    ON m.demographicNo = mlast.demographicNo\n    WHERE m.type = ${obs2Name}\n    AND m.dateObserved = mlast.dateObserved\n    AND m.demographicNo &gt; 0\n    GROUP BY m.demographicNo HAVING COUNT(m.demographicNo) &gt; -1\n  ) OBS2LAST\n  ON (d.demographic_no = OBS2LAST.demographic_no)\n  LEFT JOIN ( \n    SELECT m.demographicNo AS demographic_no, m.dataField, count(m.dataField) as countOBS\n    FROM measurements m\n    WHERE m.type = ${obs1Name}\n    AND m.dataField NOT LIKE \'>%\' AND m.dataField &lt; ${obs1Result}\n    AND DATE(m.dateObserved) &gt; ${lowerLimit.labdate}\n    AND m.demographicNo &gt; 0\n    GROUP BY m.demographicNo HAVING COUNT(m.demographicNo) &gt; -1\n  ) OBS1CNT\n  ON (d.demographic_no = OBS1CNT.demographic_no) \n  LEFT JOIN ( \n    SELECT m.demographicNo AS demographic_no, m.dataField, count(m.dataField) as countOBS\n    FROM measurements m\n    WHERE m.type = ${obs2Name}\n    AND m.dataField &gt;= ${obs2Result}\n    AND DATE(m.dateObserved) &gt; ${lowerLimit.labdate}\n    AND m.demographicNo &gt; 0\n    GROUP BY m.demographicNo HAVING COUNT(m.demographicNo) &gt; -1\n  ) OBS2CNT\n  ON (d.demographic_no = OBS2CNT.demographic_no)\n  WHERE d.patient_status LIKE ${active}\n  AND d.demographic_no &gt; 0\n  AND d.provider_no LIKE '${provider}'\n  AND d.demographic_no NOT IN ${excludedPatient}\n) fin\n		</query>\n	</indicatorQuery>\n	<drillDownQuery>\n		<version>03-13-2018</version>\n		<params>\n			<parameter id=\"provider\" name=\"Provider Number\" value=\"loggedInProvider\" />\n			<parameter id=\"active\" name=\"Active Patients\" value=\"\'AC\'\" />\n			<parameter id=\"dxcodesICD\" name=\"Dx Codes (ICD)\" value=\"582,585\" />\n                        <parameter id=\"dxcodesSysICD\" name=\"DX CodeSystem (ICD)\" value=\"\'icd9\'\" />\n                        <parameter id=\"dxcodesSnoMed\" name=\"Dx Codes (SnoMed)\" value=\"90688005,709044004\" />\n                        <parameter id=\"dxcodesSysSnoMed\" name=\"Dx CodeSystem (SnoMed)\" value=\"\'snomed%\'\" />\n			<parameter id=\"billingCode\" name=\"ICD9 Billing Code\" value=\"582,585\" />\n			<parameter id=\"numOfBills\" name=\"Number of ICD9 582,585 Bills\" value=\"2\" />\n			<parameter id=\"numOfVisits\" name=\"Number of Visit Encounters coded 582,585/ICD9\" value=\"2\" />\n			<parameter id=\"obs1Name\" name=\"Obs 1 Name\" value=\"\'EGFR\'\" />\n			<parameter id=\"obs1Result\" name=\"Obs 1 Result\" value=\"60\" />\n			<parameter id=\"obs2Name\" name=\"Obs 2 Name\" value=\"\'ACR\'\" />\n			<parameter id=\"obs2Result\" name=\"Obs 2 Result\" value=\"3\" />\n			<parameter id=\"numOfObs\" name=\"Number of measurements\" value=\"2\" />\n			<parameter id=\"excludedPatient\" name=\"Excluded Patient\" value=\"excludedPatient\" />\n		</params>\n		\n		<range>\n			<lowerLimit id=\"billingdate\" label=\"From Date\" name=\"Date\" value=\"DATE_SUB( NOW(), INTERVAL 3 YEAR )\" />\n			<upperLimit id=\"billingdate\" label=\"Date Today\" name=\"Date\" value=\"NOW()\" />\n			<lowerLimit id=\"visitdate\" label=\"From Date\" name=\"Date\" value=\"DATE_SUB( NOW(), INTERVAL 3 YEAR )\" />\n			<upperLimit id=\"visitdate\" label=\"Date Today\" name=\"Date\" value=\"NOW()\" />\n			<lowerLimit id=\"labdate\" label=\"From Date\" name=\"Date\" value=\"DATE_SUB( NOW(), INTERVAL 2 YEAR )\" />\n			<upperLimit id=\"labdate\" label=\"Date Today\" name=\"Date\" value=\"NOW()\" />\n		</range>\n		\n		<displayColumns>\n			<column id=\"demographic\" name=\"d.demographic_no\" title=\"Patient Id\" primary=\"true\" />\n			<column id=\"name\" name=\"CONCAT( d.last_name, \', \', d.first_name )\" title=\"Patient Name\" primary=\"false\" />\n			<column id=\"dob\" name=\"DATE_FORMAT( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth), \'%m-%d-%Y\' )\" title=\"Date of Birth\" primary=\"false\" />\n			<column id=\"age\" name=\"TIMESTAMPDIFF(YEAR, DATE( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth) ), CURDATE() )\" title=\"Age\" primary=\"false\" />\n			<column id=\"billings\" name=\"IFNULL( BILLING.billcount, \'0\')\" title=\"Billings\" primary=\"false\" />\n			<column id=\"visits\" name=\"IFNULL( VISIT.visitcount, \'0\')\" title=\"Visits\" primary=\"false\" />\n			<column id=\"lastOBS1\" name=\"IFNULL( OBS1LAST.result, \'NA\')\" title=\"Last EGFR\" primary=\"false\" />\n			<column id=\"testDateOBS1\" name=\"IFNULL( DATE_FORMAT( OBS1LAST.dateObserved, \'%m-%d-%Y\' ), \'NA\')\" title=\"EGFR Date (yyyy-mm-dd)\" primary=\"false\" />\n			<column id=\"numOBS1s\" name=\"IFNULL( OBS1CNT.countOBS, \'0\')\" title=\"Num. eGFR &lt;60 Last 2yrs\" primary=\"false\" />\n			<column id=\"lastOBS2\" name=\"IFNULL( OBS2LAST.result, \'NA\')\" title=\"Last ACR\" primary=\"false\" />\n			<column id=\"testDateOBS2\" name=\"IFNULL( DATE_FORMAT( OBS2LAST.dateObserved, \'%m-%d-%Y\' ), \'NA\')\" title=\"ACR Date (yyyy-mm-dd)\" primary=\"false\" />\n			<column id=\"numOBS2s\" name=\"IFNULL( OBS2CNT.countOBS, \'0\')\" title=\"Num. ACR &gt;=3 Last 2yrs\" primary=\"false\" />\n		</displayColumns>\n		\n		<exportColumns>\n			<column id=\"demographic\" name=\"d.demographic_no\" title=\"Patient Id\" primary=\"true\" />\n                        <column id=\"firstName\" name=\"d.first_name\" title=\"First Name\" primary=\"false\" />\n                        <column id=\"lastName\" name=\"d.last_name\" title=\"Last Name\" primary=\"false\" />\n			<column id=\"dob\" name=\"DATE_FORMAT( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth), \'%m-%d-%Y\' )\" title=\"Date of Birth\" primary=\"false\" />\n			<column id=\"age\" name=\"TIMESTAMPDIFF(YEAR, DATE( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth) ), CURDATE() )\" title=\"Age\" primary=\"false\" />\n			<column id=\"billings\" name=\"IFNULL( BILLING.billcount, \'0\')\" title=\"Billings\" primary=\"false\" />\n			<column id=\"visits\" name=\"IFNULL( VISIT.visitcount, \'0\')\" title=\"Visits\" primary=\"false\" />\n			<column id=\"lastOBS1\" name=\"IFNULL( OBS1LAST.result, \'NA\')\" title=\"Last EGFR\" primary=\"false\" />\n			<column id=\"testDateOBS1\" name=\"IFNULL( DATE_FORMAT( OBS1LAST.dateObserved, \'%m-%d-%Y\' ), \'NA\')\" title=\"EGFR Date (yyyy-mm-dd)\" primary=\"false\" />\n			<column id=\"numOBS1s\" name=\"IFNULL( OBS1CNT.countOBS, \'0\')\" title=\"Num. eGFR &lt;60 Last 2yrs\" primary=\"false\" />\n			<column id=\"lastOBS2\" name=\"IFNULL( OBS2LAST.result, \'NA\')\" title=\"Last ACR\" primary=\"false\" />\n			<column id=\"testDateOBS2\" name=\"IFNULL( DATE_FORMAT( OBS2LAST.dateObserved, \'%m-%d-%Y\' ), \'NA\')\" title=\"ACR Date (yyyy-mm-dd)\" primary=\"false\" />\n			<column id=\"numOBS2s\" name=\"IFNULL( OBS2CNT.countOBS, \'0\')\" title=\"Num. ACR &gt;=3 Last 2yrs\" primary=\"false\" />\n		</exportColumns>\n		<drillDownActions>\n			<action id=\"tickler\" name=\"Tickler\"/>\n			<action id=\"dxUpdate\" name=\"Disease Registry Update\" value=\"585\"/>\n			<action id=\"demoExcl\" name=\"Patient Exclusion\"/>\n		</drillDownActions>\n		<query>\n  SELECT *	\n  FROM demographic d \n  LEFT JOIN (\n    SELECT demographic_no, dxresearch_code, coding_system, COUNT(dxresearch_code)\n    FROM dxresearch\n    WHERE status=\'A\' AND\n    (\n      (LEFT(dxresearch_code, 3) IN (\'582\',\'585\') AND coding_system = ${dxcodesSysICD}) OR\n      (dxresearch_code IN (\'90688005\',\'709044004\') AND coding_system LIKE ${dxcodesSysSnoMed})\n    )\n    GROUP BY demographic_no\n    ORDER BY demographic_no\n  ) dxr\n  ON d.demographic_no = dxr.demographic_no\n  -- Billed 582,585 in last 3 years\n  LEFT JOIN (\n    SELECT b.demographic_no AS demographic_no, b.dx_code1 AS dxcode1, b.dx_code2 AS dxcode2, b.dx_code3 AS dxcode3, COUNT(b.demographic_no) AS billcount\n    FROM billingmaster b\n    WHERE ( LEFT(b.dx_code1, 3) IN (\'582\',\'585\') OR LEFT(b.dx_code2, 3) IN (\'582\',\'585\') OR LEFT(b.dx_code3, 3) IN (\'582\',\'585\') )\n    AND DATE(b.createdate) &gt; ${lowerLimit.billingdate}\n    AND b.demographic_no &gt; 0\n    GROUP BY b.demographic_no HAVING COUNT(b.demographic_no) &gt; -1\n  ) BILLING\n  ON (d.demographic_no = BILLING.demographic_no)  \n  -- Coded dx during visit encounter in last 3 years\n  LEFT JOIN(\n    SELECT ASSIGNED.demographic_no AS demographic_no, observation_date AS visitdate, uuid, COUNT(ASSIGNED.demographic_no) AS visitcount\n    FROM casemgmt_note note\n    INNER JOIN (\n      SELECT note_id, CODED.demo_no AS demographic_no FROM casemgmt_issue_notes\n      INNER JOIN (\n        SELECT ci.id AS id, ci.update_date, ci.demographic_no demo_no FROM issue i\n        INNER JOIN casemgmt_issue ci\n        ON i.issue_id = ci.issue_id\n        WHERE\n	(i.type LIKE ${dxcodesSysICD} AND LEFT(i.code, 3) IN (\'582\',\'585\')) OR\n	(i.type LIKE ${dxcodesSysSnoMed} AND i.code IN (\'90688005\',\'709044004\'))\n      ) CODED\n      ON (casemgmt_issue_notes.id = CODED.id)\n      ORDER BY CODED.demo_no\n    ) ASSIGNED\n    ON note.note_id = ASSIGNED.note_id AND\n    -- GET LAST REVISION OF NOTE\n    note.note_id= (SELECT MAX(cmn.note_id) FROM casemgmt_note cmn WHERE cmn.uuid = note.uuid) AND\n    DATE(note.observation_date) &gt; ${lowerLimit.visitdate}\n    GROUP BY demographic_no\n    ORDER BY demographic_no\n  ) VISIT\n  ON (d.demographic_no = VISIT.demographic_no)  \n  -- Consider OBS here if needed\n  LEFT JOIN ( \n    SELECT m.demographicNo AS demographic_no, m.dataField as result, m.dateObserved AS dateObserved\n    FROM measurements m\n    INNER JOIN (\n      SELECT demographicNo, MAX(dateObserved) AS dateObserved\n      FROM measurements\n      WHERE type = ${obs1Name}\n      GROUP BY demographicNo\n    ) mlast\n    ON m.demographicNo = mlast.demographicNo\n    WHERE m.type = ${obs1Name}\n    AND m.dataField NOT LIKE \'>%\'\n    AND m.dateObserved = mlast.dateObserved\n    AND m.demographicNo &gt; 0\n    GROUP BY m.demographicNo HAVING COUNT(m.demographicNo) &gt; -1\n  ) OBS1LAST\n  ON (d.demographic_no = OBS1LAST.demographic_no) \n  LEFT JOIN (\n    SELECT m.demographicNo AS demographic_no, m.dataField as result, m.dateObserved AS dateObserved\n    FROM measurements m\n    INNER JOIN (\n      SELECT demographicNo, MAX(dateObserved) AS dateObserved\n      FROM measurements\n      WHERE type = ${obs2Name}\n      GROUP BY demographicNo\n    ) mlast\n    ON m.demographicNo = mlast.demographicNo\n    WHERE m.type = ${obs2Name}\n    AND m.dateObserved = mlast.dateObserved\n    AND m.demographicNo &gt; 0\n    GROUP BY m.demographicNo HAVING COUNT(m.demographicNo) &gt; -1\n  ) OBS2LAST\n  ON (d.demographic_no = OBS2LAST.demographic_no)\n  LEFT JOIN ( \n    SELECT m.demographicNo AS demographic_no, m.dataField, count(m.dataField) as countOBS\n    FROM measurements m\n    WHERE m.type = ${obs1Name}\n    AND m.dataField NOT LIKE \'>%\' AND m.dataField &lt; ${obs1Result}\n    AND DATE(m.dateObserved) &gt; ${lowerLimit.labdate}\n    AND m.demographicNo &gt; 0\n    GROUP BY m.demographicNo HAVING COUNT(m.demographicNo) &gt; -1\n  ) OBS1CNT\n  ON (d.demographic_no = OBS1CNT.demographic_no) \n  LEFT JOIN ( \n    SELECT m.demographicNo AS demographic_no, m.dataField, count(m.dataField) as countOBS\n    FROM measurements m\n    WHERE m.type = ${obs2Name}\n    AND m.dataField &gt;= ${obs2Result}\n    AND DATE(m.dateObserved) &gt; ${lowerLimit.labdate}\n    AND m.demographicNo &gt; 0\n    GROUP BY m.demographicNo HAVING COUNT(m.demographicNo) &gt; -1\n  ) OBS2CNT\n  ON (d.demographic_no = OBS2CNT.demographic_no)  \n  WHERE d.patient_status LIKE ${active}\n  AND d.demographic_no &gt; 0\n  AND ( dxr.dxresearch_code IS NULL AND\n  ( (BILLING.billcount IS NOT NULL AND BILLING.billcount &gt;= ${numOfBills}) OR\n    (VISIT.visitcount IS NOT NULL AND VISIT.visitcount &gt;= ${numOfVisits}) OR\n    ( (OBS1LAST.result IS NOT NULL AND OBS1LAST.result &lt; ${obs1Result}) OR\n      (OBS2LAST.result IS NOT NULL AND OBS2LAST.result &gt;= ${obs2Result}) OR\n      (OBS1CNT.countOBS IS NOT NULL AND OBS1CNT.countOBS &gt;= ${numOfObs}) OR\n      (OBS2CNT.countOBS IS NOT NULL AND OBS2CNT.countOBS &gt;= ${numOfObs})) )\n  )\n  AND d.provider_no LIKE ${provider}\n  AND d.demographic_no NOT IN ${excludedPatient}\n  ORDER BY d.demographic_no\n		</query>\n	</drillDownQuery>\n</indicatorTemplateXML>\n','','\0',0,NULL,NULL);
INSERT INTO `indicatorTemplate` (`dashboardId`, `name`, `category`, `subCategory`, `framework`, `frameworkVersion`, `definition`, `notes`, `template`, `active`, `locked`, `shared`, `metricSetName`, `metricLabel`) VALUES (@dashboardId2,'Chronic Liver Disease Prevalence','CDM (Click on the pie chart to see the patients included in the pie chart)','Chronic Liver Disease','DoBC CPQI PSP Panel','2017-08-02','Proportion of active patients in the practice coded with Chronic Liver Disease.','\ni) Chronic Liver Disease: \"Patient Status: Active\nAND has a Current Problem List/Profile item code of 328383001 or starting with 571, 573\"\nii) Other patients: \"Patient Status: Active\nAND does not have a Current Problem List/Profile item code of 328383001','<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<indicatorTemplateXML>\n	<author>OSCAR BC</author>\n	<uid></uid>\n	<heading>\n		<category>CDM (Click on the pie chart to see the patients included in the pie chart)</category>\n		<subCategory>Chronic Liver Disease</subCategory>\n		<name>Chronic Liver Disease Prevalence</name>\n		<definition>Proportion of active patients in the practice coded with Chronic Liver Disease.</definition>\n		<framework>DoBC CPQI PSP Panel</framework>\n		<frameworkVersion>08-02-2017</frameworkVersion>\n		<notes>\ni) Chronic Liver Disease: \"Patient Status: Active\nAND has a Current Problem List/Profile item code of 328383001 or starting with 571, 573\"\nii) Other patients: \"Patient Status: Active\nAND does not have a Current Problem List/Profile item code of 328383001 or starting with 571, 573\"\n		</notes>\n	</heading>\n	<indicatorQuery>\n		<version>03-13-2018</version>\n		<params>\n			<!-- \n				Required parameter provider. Value options are: \n					[ an individual providerNo, or provider range ] ie: value=\"370, 1001\" \n					\"all\" ie: value=\"all\" including null.\n					\"loggedInProvider\" ie:\n				Default is \"loggedInProvider\"\n				Use this parameter in the query as '${provider}'\n				This parameter should be used for fetching patient\'s assigned to a MRP.\n				ie: WHERE demographic.provider_no = ${provider}\n			-->\n			<parameter id=\"provider\" name=\"provider_no\" value=\"loggedInProvider\" />\n			<parameter id=\"pstatus\" name=\"Patient Status\" value=\"\'AC\'\" />\n			<parameter id=\"dxcodesICD\" name=\"Dx Codes (ICD)\" value=\"571,573\" />\n			<parameter id=\"dxcodesSysICD\" name=\"DX CodeSystem (ICD)\" value=\"\'icd9\'\" />\n			<parameter id=\"dxcodesSnoMed\" name=\"Dx Codes (SnoMed)\" value=\"328383001\" />\n			<parameter id=\"dxcodesSysSnoMed\" name=\"Dx CodeSystem (SnoMed)\" value=\"\'snomed%\'\" />\n			<parameter id=\"excludedPatient\" name=\"Excluded Patient\" value=\"excludedPatient\" />\n		</params>\n		<query>\n			<!-- Indicator SQL Query here -->\nSELECT\n -- TOTAL NUMBER OF PATIENTS NOTED AS ACTIVE WITH CURRENT CHRONIC LIVER DISEASE\n IF( COUNT(fin.patient) &gt; 0, SUM( IF( dxresearch_code is not NULL , 1, 0) ) , 0 ) AS \"Number Active, Current Chronic Liver Disease\",\n -- TOTAL NUMBER OF PATIENTS NOTED AS ACTIVE WITHOUT CURRENT CHRONIC LIVER DISEASE\n IF( COUNT(fin.patient) &gt; 0, SUM( IF( dxresearch_code is NULL , 1, 0) ) , 0 ) AS \"Number Active, No Current Chronic Liver Disease\"\nFROM (\n SELECT d.demographic_no AS patient, d.patient_status AS pt_status, dxresearch_code\n FROM demographic d\n LEFT JOIN (\n  SELECT demographic_no, dxresearch_code, COUNT(dxresearch_code)\n  FROM dxresearch\n  WHERE status=\'A\' AND\n  (\n   (LEFT(dxresearch_code, 3) IN (\'571\', \'573\') AND coding_system = ${dxcodesSysICD}) OR\n   (dxresearch_code IN (\'328383001\') AND coding_system LIKE ${dxcodesSysSnoMed})\n   )\n  GROUP BY demographic_no\n  ORDER BY demographic_no\n ) problems\n ON d.demographic_no = problems.demographic_no\n WHERE d.provider_no LIKE ${provider} AND d.patient_status = ${pstatus}\n AND d.demographic_no NOT IN ${excludedPatient}\n) fin\n		</query>\n	</indicatorQuery>\n	<drillDownQuery>\n		<version>03-13-2018</version>\n		<params>\n			<parameter id=\"provider\" name=\"provider_no\" value=\"loggedInProvider\" />	\n			<parameter id=\"pstatus\" name=\"Patient Status\" value=\"\'AC\'\" />\n			<parameter id=\"dxcodesICD\" name=\"Dx Codes (ICD)\" value=\"571,573\" />\n			<parameter id=\"dxcodesSysICD\" name=\"DX CodeSystem (ICD)\" value=\"\'icd9\'\" />\n			<parameter id=\"dxcodesSnoMed\" name=\"Dx Codes (SnoMed)\" value=\"328383001\" />\n			<parameter id=\"dxcodesSysSnoMed\" name=\"Dx CodeSystem (SnoMed)\" value=\"\'snomed%\'\" />\n			<parameter id=\"excludedPatient\" name=\"Excluded Patient\" value=\"excludedPatient\" />\n		</params>\n		<displayColumns>\n			<column id=\"demographic\" name=\"d.demographic_no\" title=\"Patient Id\" primary=\"true\" />\n			<column id=\"name\" name=\"CONCAT( d.last_name, \', \', d.first_name )\" title=\"Patient Name\" primary=\"false\" />\n			<column id=\"dob\" name=\"DATE_FORMAT( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth), \'%m-%d-%Y\' )\" title=\"Date of Birth (mm-dd-yy)\" primary=\"false\" />\n			<column id=\"age\" name=\"TIMESTAMPDIFF(YEAR, DATE( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth) ), CURDATE() )\" title=\"Age\" primary=\"false\" />\n			<column id=\"dxcode\" title=\"Dx System/Code\" name=\"CONCAT(MIN(dxr.coding_system),\'/\',MIN(dxr.dxresearch_code),IF( MIN(dxr.dxresearch_code) != MAX(dxr.dxresearch_code),CONCAT( \' &amp; \', MAX(dxr.coding_system), \'/\', MAX(dxr.dxresearch_code) ),\'\') )\" primary=\"false\" />\n		</displayColumns>\n		<exportColumns>\n			<column id=\"demographic\" name=\"d.demographic_no\" title=\"Patient Id\" primary=\"true\" />\n			<column id=\"firstName\" name=\"d.first_name\" title=\"First Name\" primary=\"false\" />\n			<column id=\"lastName\" name=\"d.last_name\" title=\"Last Name\" primary=\"false\" />\n			<column id=\"dob\" name=\"DATE_FORMAT( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth), \'%m-%d-%Y\' )\" title=\"Date of Birth (mm-dd-yy)\" primary=\"false\" />\n			<column id=\"age\" name=\"TIMESTAMPDIFF(YEAR, DATE( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth) ), CURDATE() )\" title=\"Age\" primary=\"false\" />\n			<column id=\"dxcode\" title=\"Dx System/Code\" name=\"CONCAT(MIN(dxr.coding_system),\'/\',MIN(dxr.dxresearch_code),IF( MIN(dxr.dxresearch_code) != MAX(dxr.dxresearch_code),CONCAT( \' &amp; \', MAX(dxr.coding_system), \'/\', MAX(dxr.dxresearch_code) ),\'\') )\" primary=\"false\" />\n		</exportColumns>\n		<drillDownActions>\n			<action id=\"tickler\" name=\"Tickler\"/>\n			<action id=\"demoExcl\" name=\"Patient Exclusion\"/>\n		</drillDownActions>\n		<query>\n			<!-- Drilldown SQL Query here -->\nSELECT\n*			\nFROM demographic d\nLEFT JOIN dxresearch dxr\nON d.demographic_no = dxr.demographic_no\nWHERE d.patient_status=${pstatus}\nAND\n(\n (LEFT(dxr.dxresearch_code, 3) IN (\'571\', \'573\') AND dxr.coding_system = ${dxcodesSysICD}) OR\n (dxr.dxresearch_code IN (\'328383001\') AND dxr.coding_system LIKE ${dxcodesSysSnoMed})\n )\nAND dxr.status = \'A\'\nAND d.provider_no LIKE ${provider}\nAND d.demographic_no NOT IN ${excludedPatient}\nGROUP BY d.demographic_no\nORDER BY COUNT(d.demographic_no) DESC, d.demographic_no\n		</query>\n	</drillDownQuery>\n</indicatorTemplateXML>\n','','\0',0,NULL,NULL);
INSERT INTO `indicatorTemplate` (`dashboardId`, `name`, `category`, `subCategory`, `framework`, `frameworkVersion`, `definition`, `notes`, `template`, `active`, `locked`, `shared`, `metricSetName`, `metricLabel`) VALUES (@dashboardId2,'Consider Chronic Liver Disease','CDM (Click on the pie chart to see the patients included in the pie chart)','Chronic Liver Disease','DoBC CPQI PSP Panel','2017-08-02','\n			Patients with chronic liver disease dx in 2+ bills or 2+ visit encounters within last 3 years\n		','\n		  Created for Doctors of BC\n		  i) Chronic Liver Disease dx in 2+ bills: \"Patient Status: Active\nAND does not have a Current Problem List/Profile item code of 328383001 or starting with 571, 573\nAND with >=2 Bills with the billing code starting with 57','<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<indicatorTemplateXML>\n	<author>OSCAR BC</author>\n	<uid></uid>\n	<heading>\n		<category>CDM (Click on the pie chart to see the patients included in the pie chart)</category>\n		<subCategory>Chronic Liver Disease</subCategory>\n		<name>Consider Chronic Liver Disease</name>\n		<definition>\n			Patients with chronic liver disease dx in 2+ bills or 2+ visit encounters within last 3 years\n		</definition>\n		<framework>DoBC CPQI PSP Panel</framework>\n		<frameworkVersion>08-02-2017</frameworkVersion>\n		<notes>\n		  Created for Doctors of BC\n		  i) Chronic Liver Disease dx in 2+ bills: \"Patient Status: Active\nAND does not have a Current Problem List/Profile item code of 328383001 or starting with 571, 573\nAND with >=2 Bills with the billing code starting with 571, 573 in the last 3 years\"\n		  ii) Chronic Liver Disease dx in 2+ visits: \"Patient Status: Active\nAND does not have a Current Problem List/Profile item code of 328383001 or starting with 571, 573\nAND with >=2 Visit Encounters with the Snomed diagnosis code 328383001 or the ICD9 diagnosis code starting with 571, 573 in the last 3 years\"\n		</notes>\n	</heading>\n	<indicatorQuery>\n		<version>03-13-2018</version>\n		<params>\n			<parameter id=\"provider\" name=\"Provider Number\" value=\"loggedInProvider\" />\n			<parameter id=\"active\" name=\"Active Patients\" value=\"\'AC\'\" />\n			<parameter id=\"dxcodesICD\" name=\"Dx Codes (ICD)\" value=\"571,573\" />\n                        <parameter id=\"dxcodesSysICD\" name=\"DX CodeSystem (ICD)\" value=\"\'icd9\'\" />\n                        <parameter id=\"dxcodesSnoMed\" name=\"Dx Codes (SnoMed)\" value=\"328383001\" />\n                        <parameter id=\"dxcodesSysSnoMed\" name=\"Dx CodeSystem (SnoMed)\" value=\"\'snomed%\'\" />\n			<parameter id=\"billingCode\" name=\"ICD9 Billing Code\" value=\"571,573\" />\n			<parameter id=\"numOfBills\" name=\"Number of ICD9 571,573 Bills\" value=\"2\" />\n			<parameter id=\"numOfVisits\" name=\"Number of Visit Encounters coded 571,573/ICD9\" value=\"2\" />\n			<parameter id=\"excludedPatient\" name=\"Excluded Patient\" value=\"excludedPatient\" />\n		</params>\n		<range>\n			<lowerLimit id=\"billingdate\" label=\"From Date\" name=\"Date\" value=\"DATE_SUB( NOW(), INTERVAL 3 YEAR )\" />\n			<upperLimit id=\"billingdate\" label=\"Date Today\" name=\"Date\" value=\"NOW()\" />\n			<lowerLimit id=\"visitdate\" label=\"From Date\" name=\"Date\" value=\"DATE_SUB( NOW(), INTERVAL 3 YEAR )\" />\n			<upperLimit id=\"visitdate\" label=\"Date Today\" name=\"Date\" value=\"NOW()\" />\n		</range>\n		<query>\nSELECT\n  IF ( COUNT(fin.patient) &gt; 0, SUM( CASE WHEN (fin.dx IS NULL AND fin.billcount &gt;= ${numOfBills}) THEN 1 ELSE 0 END ) , 0) AS \"Number with Chronic Liver Disease dx in ${numOfBills}+ bills\",\n  IF ( COUNT(fin.patient) &gt; 0, SUM( CASE WHEN (fin.dx IS NULL AND fin.visitcount &gt;= ${numOfVisits}) THEN 1 ELSE 0 END ) , 0) AS \"Number with Chronic Liver Disease dx in ${numOfVisits}+ visits\"\n-- Consider OBS here if needed  \nFROM ( \n  SELECT \n    d.demographic_no AS patient, \n    BILLING.billcount AS billcount,\n    VISIT.visitcount AS visitcount,\n    dxr.dxresearch_code AS dx,\n    dxr.coding_system AS dxs\n  FROM demographic d \n  LEFT JOIN (\n    SELECT demographic_no, dxresearch_code, coding_system, COUNT(dxresearch_code)\n    FROM dxresearch\n    WHERE status=\'A\' AND\n    (\n      (LEFT(dxresearch_code, 3) IN (\'571\',\'573\') AND coding_system = ${dxcodesSysICD}) OR\n      (dxresearch_code IN (\'328383001\') AND coding_system LIKE ${dxcodesSysSnoMed})\n    )\n    GROUP BY demographic_no\n    ORDER BY demographic_no\n  ) dxr\n  ON d.demographic_no = dxr.demographic_no\n  -- Billed 571,573 in last 3 years\n  LEFT JOIN (\n    SELECT b.demographic_no AS demographic_no, b.dx_code1 AS dxcode1, b.dx_code2 AS dxcode2, b.dx_code3 AS dxcode3, COUNT(b.demographic_no) AS billcount\n    FROM billingmaster b\n    WHERE ( LEFT(b.dx_code1, 3) IN (\'571\',\'573\') OR LEFT(b.dx_code2, 3) IN (\'571\',\'573\') OR LEFT(b.dx_code3, 3) IN (\'571\',\'573\') )\n    AND DATE(b.createdate) &gt; ${lowerLimit.billingdate}\n    AND b.demographic_no &gt; 0\n    GROUP BY b.demographic_no HAVING COUNT(b.demographic_no) &gt; -1\n  ) BILLING\n  ON (d.demographic_no = BILLING.demographic_no)  \n  -- Coded dx during visit encounter in last 3 years\n  LEFT JOIN(\n    SELECT ASSIGNED.demographic_no AS demographic_no, observation_date AS visitdate, uuid, COUNT(ASSIGNED.demographic_no) AS visitcount\n    FROM casemgmt_note note\n    INNER JOIN (\n      SELECT note_id, CODED.demo_no AS demographic_no FROM casemgmt_issue_notes\n      INNER JOIN (\n        SELECT ci.id AS id, ci.update_date, ci.demographic_no demo_no FROM issue i\n        INNER JOIN casemgmt_issue ci\n        ON i.issue_id = ci.issue_id\n        WHERE\n	(i.type LIKE ${dxcodesSysICD} AND LEFT(i.code, 3) IN (\'571\',\'573\')) OR\n	(i.type LIKE ${dxcodesSysSnoMed} AND i.code IN (\'328383001\'))\n      ) CODED\n      ON (casemgmt_issue_notes.id = CODED.id)\n      ORDER BY CODED.demo_no\n    ) ASSIGNED\n    ON note.note_id = ASSIGNED.note_id AND\n    -- GET LAST REVISION OF NOTE\n    note.note_id= (SELECT MAX(cmn.note_id) FROM casemgmt_note cmn WHERE cmn.uuid = note.uuid) AND\n    DATE(note.observation_date) &gt; ${lowerLimit.visitdate}\n    GROUP BY demographic_no\n    ORDER BY demographic_no\n  ) VISIT\n  ON (d.demographic_no = VISIT.demographic_no)  \n  WHERE d.patient_status LIKE ${active}\n  AND d.demographic_no &gt; 0\n  AND d.provider_no LIKE '${provider}'\n  AND d.demographic_no NOT IN ${excludedPatient}\n) fin\n		</query>\n	</indicatorQuery>\n	<drillDownQuery>\n		<version>03-13-2018</version>\n		<params>\n			<parameter id=\"provider\" name=\"Provider Number\" value=\"loggedInProvider\" />\n			<parameter id=\"active\" name=\"Active Patients\" value=\"\'AC\'\" />\n			<parameter id=\"dxcodesICD\" name=\"Dx Codes (ICD)\" value=\"571,573\" />\n                        <parameter id=\"dxcodesSysICD\" name=\"DX CodeSystem (ICD)\" value=\"\'icd9\'\" />\n                        <parameter id=\"dxcodesSnoMed\" name=\"Dx Codes (SnoMed)\" value=\"328383001\" />\n                        <parameter id=\"dxcodesSysSnoMed\" name=\"Dx CodeSystem (SnoMed)\" value=\"\'snomed%\'\" />\n			<parameter id=\"billingCode\" name=\"ICD9 Billing Code\" value=\"571,573\" />\n			<parameter id=\"numOfBills\" name=\"Number of ICD9 571,573 Bills\" value=\"2\" />\n			<parameter id=\"numOfVisits\" name=\"Number of Visit Encounters coded 571,573/ICD9\" value=\"2\" />\n			<parameter id=\"excludedPatient\" name=\"Excluded Patient\" value=\"excludedPatient\" />\n		</params>\n		\n		<range>\n			<lowerLimit id=\"billingdate\" label=\"From Date\" name=\"Date\" value=\"DATE_SUB( NOW(), INTERVAL 3 YEAR )\" />\n			<upperLimit id=\"billingdate\" label=\"Date Today\" name=\"Date\" value=\"NOW()\" />\n			<lowerLimit id=\"visitdate\" label=\"From Date\" name=\"Date\" value=\"DATE_SUB( NOW(), INTERVAL 3 YEAR )\" />\n			<upperLimit id=\"visitdate\" label=\"Date Today\" name=\"Date\" value=\"NOW()\" />\n		</range>\n		\n		<displayColumns>\n			<column id=\"demographic\" name=\"d.demographic_no\" title=\"Patient Id\" primary=\"true\" />\n			<column id=\"name\" name=\"CONCAT( d.last_name, \', \', d.first_name )\" title=\"Patient Name\" primary=\"false\" />\n			<column id=\"dob\" name=\"DATE_FORMAT( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth), \'%m-%d-%Y\' )\" title=\"Date of Birth\" primary=\"false\" />\n			<column id=\"age\" name=\"TIMESTAMPDIFF(YEAR, DATE( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth) ), CURDATE() )\" title=\"Age\" primary=\"false\" />\n			<column id=\"billings\" name=\"IFNULL( BILLING.billcount, \'0\')\" title=\"Billings\" primary=\"false\" />\n			<column id=\"visits\" name=\"IFNULL( VISIT.visitcount, \'0\')\" title=\"Visits\" primary=\"false\" />\n		</displayColumns>\n		\n		<exportColumns>\n			<column id=\"demographic\" name=\"d.demographic_no\" title=\"Patient Id\" primary=\"true\" />\n                        <column id=\"firstName\" name=\"d.first_name\" title=\"First Name\" primary=\"false\" />\n                        <column id=\"lastName\" name=\"d.last_name\" title=\"Last Name\" primary=\"false\" />\n			<column id=\"dob\" name=\"DATE_FORMAT( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth), \'%m-%d-%Y\' )\" title=\"Date of Birth\" primary=\"false\" />\n			<column id=\"age\" name=\"TIMESTAMPDIFF(YEAR, DATE( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth) ), CURDATE() )\" title=\"Age\" primary=\"false\" />\n			<column id=\"billings\" name=\"IFNULL( BILLING.billcount, \'0\')\" title=\"Billings\" primary=\"false\" />\n			<column id=\"visits\" name=\"IFNULL( VISIT.visitcount, \'0\')\" title=\"Visits\" primary=\"false\" />\n		</exportColumns>\n		<drillDownActions>\n			<action id=\"tickler\" name=\"Tickler\"/>\n			<action id=\"dxUpdate\" name=\"Disease Registry Update\" value=\"571\"/>\n			<action id=\"demoExcl\" name=\"Patient Exclusion\"/>\n		</drillDownActions>\n		<query>\n  SELECT *	\n  FROM demographic d \n  LEFT JOIN (\n    SELECT demographic_no, dxresearch_code, coding_system, COUNT(dxresearch_code)\n    FROM dxresearch\n    WHERE status=\'A\' AND\n    (\n      (LEFT(dxresearch_code, 3) IN (\'571\',\'573\') AND coding_system = ${dxcodesSysICD}) OR\n      (dxresearch_code IN (\'328383001\') AND coding_system LIKE ${dxcodesSysSnoMed})\n    )\n    GROUP BY demographic_no\n    ORDER BY demographic_no\n  ) dxr\n  ON d.demographic_no = dxr.demographic_no\n  -- Billed 571,573 in last 3 years\n  LEFT JOIN (\n    SELECT b.demographic_no AS demographic_no, b.dx_code1 AS dxcode1, b.dx_code2 AS dxcode2, b.dx_code3 AS dxcode3, COUNT(b.demographic_no) AS billcount\n    FROM billingmaster b\n    WHERE ( LEFT(b.dx_code1, 3) IN (\'571\',\'573\') OR LEFT(b.dx_code2, 3) IN (\'571\',\'573\') OR LEFT(b.dx_code3, 3) IN (\'571\',\'573\') )\n    AND DATE(b.createdate) &gt; ${lowerLimit.billingdate}\n    AND b.demographic_no &gt; 0\n    GROUP BY b.demographic_no HAVING COUNT(b.demographic_no) &gt; -1\n  ) BILLING\n  ON (d.demographic_no = BILLING.demographic_no)  \n  -- Coded dx during visit encounter in last 3 years\n  LEFT JOIN(\n    SELECT ASSIGNED.demographic_no AS demographic_no, observation_date AS visitdate, uuid, COUNT(ASSIGNED.demographic_no) AS visitcount\n    FROM casemgmt_note note\n    INNER JOIN (\n      SELECT note_id, CODED.demo_no AS demographic_no FROM casemgmt_issue_notes\n      INNER JOIN (\n        SELECT ci.id AS id, ci.update_date, ci.demographic_no demo_no FROM issue i\n        INNER JOIN casemgmt_issue ci\n        ON i.issue_id = ci.issue_id\n        WHERE\n	(i.type LIKE ${dxcodesSysICD} AND LEFT(i.code, 3) IN (\'571\',\'573\')) OR\n	(i.type LIKE ${dxcodesSysSnoMed} AND i.code IN (\'328383001\'))\n      ) CODED\n      ON (casemgmt_issue_notes.id = CODED.id)\n      ORDER BY CODED.demo_no\n    ) ASSIGNED\n    ON note.note_id = ASSIGNED.note_id AND\n    -- GET LAST REVISION OF NOTE\n    note.note_id= (SELECT MAX(cmn.note_id) FROM casemgmt_note cmn WHERE cmn.uuid = note.uuid) AND\n    DATE(note.observation_date) &gt; ${lowerLimit.visitdate}\n    GROUP BY demographic_no\n    ORDER BY demographic_no\n  ) VISIT\n  ON (d.demographic_no = VISIT.demographic_no)  \n-- Consider OBS here if needed  \n  WHERE d.patient_status LIKE ${active}\n  AND d.demographic_no &gt; 0\n  AND ( dxr.dxresearch_code IS NULL AND ((BILLING.billcount IS NOT NULL AND BILLING.billcount &gt;= ${numOfBills}) OR (VISIT.visitcount IS NOT NULL AND VISIT.visitcount &gt;= ${numOfVisits})))\n  AND d.provider_no LIKE ${provider}\n  AND d.demographic_no NOT IN ${excludedPatient}\n  ORDER BY d.demographic_no\n		</query>\n	</drillDownQuery>\n</indicatorTemplateXML>\n','','\0',0,NULL,NULL);
INSERT INTO `indicatorTemplate` (`dashboardId`, `name`, `category`, `subCategory`, `framework`, `frameworkVersion`, `definition`, `notes`, `template`, `active`, `locked`, `shared`, `metricSetName`, `metricLabel`) VALUES (@dashboardId2,'COPD Prevalence','CDM (Click on the pie chart to see the patients included in the pie chart)','COPD','DoBC CPQI PSP Panel','2017-08-02','Proportion of active patients in the practice coded with COPD.','\ni) COPD patients: \"Patient Status: Active\nAND has a Current Problem List/Profile item code of 32398004, 63480004, 61937009, 74417001, 185086009, 49158009, 12295008, 13645005   OR starting with 490-492, 494, 496\"\nii) Other patients: \"Patient Status: Activ','<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<indicatorTemplateXML>\n	<author>OSCAR BC</author>\n	<uid></uid>\n	<heading>\n		<category>CDM (Click on the pie chart to see the patients included in the pie chart)</category>\n		<subCategory>COPD</subCategory>\n		<name>COPD Prevalence</name>\n		<definition>Proportion of active patients in the practice coded with COPD.</definition>\n		<framework>DoBC CPQI PSP Panel</framework>\n		<frameworkVersion>08-02-2017</frameworkVersion>\n		<notes>\ni) COPD patients: \"Patient Status: Active\nAND has a Current Problem List/Profile item code of 32398004, 63480004, 61937009, 74417001, 185086009, 49158009, 12295008, 13645005   OR starting with 490-492, 494, 496\"\nii) Other patients: \"Patient Status: Active\nAND does not have a Current Problem List/Profile item code of 32398004, 63480004, 61937009, 74417001, 185086009, 49158009, 12295008, 13645005   OR starting with 490-492, 494, 496\"\n		</notes>\n	</heading>\n	<indicatorQuery>\n		<version>03-13-2018</version>\n		<params>\n			<!-- \n				Required parameter provider. Value options are: \n					[ an individual providerNo, or provider range ] ie: value=\"370, 1001\" \n					\"all\" ie: value=\"all\" including null.\n					\"loggedInProvider\" ie:\n				Default is \"loggedInProvider\"\n				Use this parameter in the query as '${provider}'\n				This parameter should be used for fetching patient\'s assigned to a MRP.\n				ie: WHERE demographic.provider_no = ${provider}\n			-->\n			<parameter id=\"provider\" name=\"provider_no\" value=\"loggedInProvider\" />\n			<parameter id=\"pstatus\" name=\"Patient Status\" value=\"\'AC\'\" />\n			<parameter id=\"dxcodesICD\" name=\"Dx Codes (ICD)\" value=\"490,491,492,494,496\" />\n			<parameter id=\"dxcodesSysICD\" name=\"DX CodeSystem (ICD)\" value=\"\'icd9\'\" />\n			<parameter id=\"dxcodesSnoMed\" name=\"Dx Codes (SnoMed)\" value=\"32398004,63480004,61937009,74417001,185086009,49158009,12295008,13645005\" />\n			<parameter id=\"dxcodesSysSnoMed\" name=\"Dx CodeSystem (SnoMed)\" value=\"\'snomed%\'\" />\n			<parameter id=\"excludedPatient\" name=\"Excluded Patient\" value=\"excludedPatient\" />\n		</params>\n		<query>\n			<!-- Indicator SQL Query here -->\nSELECT\n -- TOTAL NUMBER OF PATIENTS NOTED AS ACTIVE WITH CURRENT COPD\n IF( COUNT(fin.patient) &gt; 0, SUM( IF( dxresearch_code is not NULL , 1, 0) ) , 0 ) AS \"Number Active, Current COPD\",\n -- TOTAL NUMBER OF PATIENTS NOTED AS ACTIVE WITHOUT CURRENT COPD\n IF( COUNT(fin.patient) &gt; 0, SUM( IF( dxresearch_code is NULL , 1, 0) ) , 0 ) AS \"Number Active, No Current COPD\"\nFROM (\n SELECT d.demographic_no AS patient, d.patient_status AS pt_status, dxresearch_code\n FROM demographic d\n LEFT JOIN (\n  SELECT demographic_no, dxresearch_code, COUNT(dxresearch_code)\n  FROM dxresearch\n  WHERE status=\'A\' AND\n  (\n   (LEFT(dxresearch_code, 3) IN (\'490\', \'491\', \'492\', \'494\', \'496\') AND coding_system = ${dxcodesSysICD}) OR\n   (dxresearch_code IN (\'32398004\', \'63480004\', \'61937009\', \'74417001\', \'185086009\', \'49158009\', \'12295008\', \'13645005\') AND coding_system LIKE ${dxcodesSysSnoMed})\n   )\n  GROUP BY demographic_no\n  ORDER BY demographic_no\n ) problems\n ON d.demographic_no = problems.demographic_no\n WHERE d.provider_no LIKE ${provider} AND d.patient_status = ${pstatus}\n AND d.demographic_no NOT IN ${excludedPatient}\n) fin\n		</query>\n	</indicatorQuery>\n	<drillDownQuery>\n		<version>03-13-2018</version>\n		<params>\n			<parameter id=\"provider\" name=\"provider_no\" value=\"loggedInProvider\" />	\n			<parameter id=\"pstatus\" name=\"Patient Status\" value=\"\'AC\'\" />\n			<parameter id=\"dxcodesICD\" name=\"Dx Codes (ICD)\" value=\"490,491,492,494,496\" />\n			<parameter id=\"dxcodesSysICD\" name=\"DX CodeSystem (ICD)\" value=\"\'icd9\'\" />\n			<parameter id=\"dxcodesSnoMed\" name=\"Dx Codes (SnoMed)\" value=\"32398004,63480004,61937009,74417001,185086009,49158009,12295008,13645005\" />\n			<parameter id=\"dxcodesSysSnoMed\" name=\"Dx CodeSystem (SnoMed)\" value=\"\'snomed%\'\" />\n			<parameter id=\"excludedPatient\" name=\"Excluded Patient\" value=\"excludedPatient\" />\n		</params>\n		<displayColumns>\n			<column id=\"demographic\" name=\"d.demographic_no\" title=\"Patient Id\" primary=\"true\" />\n			<column id=\"name\" name=\"CONCAT( d.last_name, \', \', d.first_name )\" title=\"Patient Name\" primary=\"false\" />\n			<column id=\"dob\" name=\"DATE_FORMAT( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth), \'%m-%d-%Y\' )\" title=\"Date of Birth (mm-dd-yy)\" primary=\"false\" />\n			<column id=\"age\" name=\"TIMESTAMPDIFF(YEAR, DATE( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth) ), CURDATE() )\" title=\"Age\" primary=\"false\" />\n			<column id=\"dxcode\" title=\"Dx System/Code\" name=\"CONCAT(MIN(dxr.coding_system),\'/\',MIN(dxr.dxresearch_code),IF( MIN(dxr.dxresearch_code) != MAX(dxr.dxresearch_code),CONCAT( \' &amp; \', MAX(dxr.coding_system), \'/\', MAX(dxr.dxresearch_code) ),\'\') )\" primary=\"false\" />\n		</displayColumns>\n		<exportColumns>\n			<column id=\"demographic\" name=\"d.demographic_no\" title=\"Patient Id\" primary=\"true\" />\n			<column id=\"firstName\" name=\"d.first_name\" title=\"First Name\" primary=\"false\" />\n			<column id=\"lastName\" name=\"d.last_name\" title=\"Last Name\" primary=\"false\" />\n			<column id=\"dob\" name=\"DATE_FORMAT( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth), \'%m-%d-%Y\' )\" title=\"Date of Birth (mm-dd-yy)\" primary=\"false\" />\n			<column id=\"age\" name=\"TIMESTAMPDIFF(YEAR, DATE( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth) ), CURDATE() )\" title=\"Age\" primary=\"false\" />\n			<column id=\"dxcode\" title=\"Dx System/Code\" name=\"CONCAT(MIN(dxr.coding_system),\'/\',MIN(dxr.dxresearch_code),IF( MIN(dxr.dxresearch_code) != MAX(dxr.dxresearch_code),CONCAT( \' &amp; \', MAX(dxr.coding_system), \'/\', MAX(dxr.dxresearch_code) ),\'\') )\" primary=\"false\" />\n		</exportColumns>\n		<drillDownActions>\n			<action id=\"tickler\" name=\"Tickler\"/>\n			<action id=\"demoExcl\" name=\"Patient Exclusion\"/>\n		</drillDownActions>\n		<query>\n			<!-- Drilldown SQL Query here -->\nSELECT\n*			\nFROM demographic d\nLEFT JOIN dxresearch dxr\nON d.demographic_no = dxr.demographic_no\nWHERE d.patient_status=${pstatus}\nAND\n(\n (LEFT(dxr.dxresearch_code, 3) IN (\'490\', \'491\', \'492\', \'494\', \'496\') AND dxr.coding_system = ${dxcodesSysICD}) OR\n (dxr.dxresearch_code IN (\'32398004\', \'63480004\', \'61937009\', \'74417001\', \'185086009\', \'49158009\', \'12295008\', \'13645005\') AND dxr.coding_system LIKE ${dxcodesSysSnoMed})\n )\nAND dxr.status = \'A\'\nAND d.provider_no LIKE ${provider}\nAND d.demographic_no NOT IN ${excludedPatient}\nGROUP BY d.demographic_no\nORDER BY COUNT(d.demographic_no) DESC, d.demographic_no\n		</query>\n	</drillDownQuery>\n</indicatorTemplateXML>\n','','\0',0,NULL,NULL);
INSERT INTO `indicatorTemplate` (`dashboardId`, `name`, `category`, `subCategory`, `framework`, `frameworkVersion`, `definition`, `notes`, `template`, `active`, `locked`, `shared`, `metricSetName`, `metricLabel`) VALUES (@dashboardId2,'Consider COPD','CDM (Click on the pie chart to see the patients included in the pie chart)','COPD','DoBC CPQI PSP Panel','2017-08-02','\n			Patients with copd dx in 2+ bills or 2+ visit encounters within last 3 years or FEV1/FVC < 0.7 OR Current Tiotroprium medication\n		','\n		  Created for Doctors of BC\n		  i) COPD dx in 2+ bills: \"Patient Status: Active\nAND does not have a Current Problem List/Profile item code of 32398004, 63480004, 61937009, 74417001, 185086009, 49158009, 12295008, 13645005   OR starting with 490-492, 49','<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<indicatorTemplateXML>\n	<author>OSCAR BC</author>\n	<uid></uid>\n	<heading>\n		<category>CDM (Click on the pie chart to see the patients included in the pie chart)</category>\n		<subCategory>COPD</subCategory>\n		<name>Consider COPD</name>\n		<definition>\n			Patients with copd dx in 2+ bills or 2+ visit encounters within last 3 years or FEV1/FVC &lt; 0.7 OR Current Tiotroprium medication\n		</definition>\n		<framework>DoBC CPQI PSP Panel</framework>\n		<frameworkVersion>08-02-2017</frameworkVersion>\n		<notes>\n		  Created for Doctors of BC\n		  i) COPD dx in 2+ bills: \"Patient Status: Active\nAND does not have a Current Problem List/Profile item code of 32398004, 63480004, 61937009, 74417001, 185086009, 49158009, 12295008, 13645005   OR starting with 490-492, 494, 496\nAND with >= 2 Bills with the ICD9 diagnosis code starting with  490-492, 494, 496 in the last 3 years\"\n		  ii) COPD dx in 2+ visits: \"Patient Status: Active\nAND does not have a Current Problem List/Profile item code of 32398004, 63480004, 61937009, 74417001, 185086009, 49158009, 12295008, 13645005   OR starting with 490-492, 494, 496\nAND with >= 2 Visit Encounters with Snomed diagnosis code 32398004, 63480004, 61937009, 74417001, 185086009, 49158009, 12295008, 13645005 or the ICD9 diagnosis code starting with 490-492, 494, 496 in the last 3 years\"\n		  iii) \"Patient Status: Active\nAND does not have a Current Problem List/Profile item code of 32398004, 63480004, 61937009, 74417001, 185086009, 49158009, 12295008, 13645005   OR starting with 490-492, 494, 496\nAND has a FEV1 &lt; 0.7 OR Current Tiotroprium medication (ATC code R03BB04)\"\n		</notes>\n	</heading>\n	<indicatorQuery>\n		<version>03-13-2018</version>\n		<params>\n			<parameter id=\"provider\" name=\"Provider Number\" value=\"loggedInProvider\" />\n			<parameter id=\"active\" name=\"Active Patients\" value=\"\'AC\'\" />\n			<parameter id=\"dxcodesICD\" name=\"Dx Codes (ICD)\" value=\"490,491,492,494,496\" />\n                        <parameter id=\"dxcodesSysICD\" name=\"DX CodeSystem (ICD)\" value=\"\'icd9\'\" />\n                        <parameter id=\"dxcodesSnoMed\" name=\"Dx Codes (SnoMed)\" value=\"32398004,63480004,61937009,74417001,185086009,49158009,12295008,13645005\" />\n                        <parameter id=\"dxcodesSysSnoMed\" name=\"Dx CodeSystem (SnoMed)\" value=\"\'snomed%\'\" />\n			<parameter id=\"billingCode\" name=\"ICD9 Billing Code\" value=\"490,491,492,494,496\" />\n			<parameter id=\"numOfBills\" name=\"Number of ICD9 490,491,492,494,496 Bills\" value=\"2\" />\n			<parameter id=\"numOfVisits\" name=\"Number of Visit Encounters coded 490,491,492,494,496/ICD9\" value=\"2\" />\n			<parameter id=\"fev1_fvc\" name=\"FEV1/FVC Result\" value=\"0.7\" />\n			<parameter id=\"drug\" name=\"ATC Code/Class\" value=\"\'R03BB04\'\" />\n			<parameter id=\"numOfMeds\" name=\"Opioid Medications\" value=\"1\" />\n			<parameter id=\"excludedPatient\" name=\"Excluded Patient\" value=\"excludedPatient\" />\n		</params>\n		<range>\n			<lowerLimit id=\"billingdate\" label=\"From Date\" name=\"Date\" value=\"DATE_SUB( NOW(), INTERVAL 3 YEAR )\" />\n			<upperLimit id=\"billingdate\" label=\"Date Today\" name=\"Date\" value=\"NOW()\" />\n			<lowerLimit id=\"visitdate\" label=\"From Date\" name=\"Date\" value=\"DATE_SUB( NOW(), INTERVAL 3 YEAR )\" />\n			<upperLimit id=\"visitdate\" label=\"Date Today\" name=\"Date\" value=\"NOW()\" />\n		</range>\n		<query>\nSELECT\n  IF ( COUNT(fin.patient) &gt; 0, SUM( CASE WHEN (fin.dx IS NULL AND fin.billcount &gt;= ${numOfBills}) THEN 1 ELSE 0 END ) , 0) AS \"Number with COPD dx in ${numOfBills}+ bills\",\n  IF ( COUNT(fin.patient) &gt; 0, SUM( CASE WHEN (fin.dx IS NULL AND fin.visitcount &gt;= ${numOfVisits}) THEN 1 ELSE 0 END ) , 0) AS \"Number with COPD dx in ${numOfVisits}+ visits\",\n  IF ( COUNT(fin.patient) &gt; 0, SUM( CASE WHEN (fin.dx IS NULL AND (fin.fev1_fvc &lt; ${fev1_fvc} OR (fin.fev1_fvc &gt; 1 AND fin.fev1_fvc &lt; 100*${fev1_fvc}) OR fin.medcount &gt;= ${numOfMeds})) THEN 1 ELSE 0 END ) , 0) AS \"Number with FEV1/FVC &lt; ${fev1_fvc}% OR Tiotroprium\"\nFROM ( \n  SELECT \n    d.demographic_no AS patient, \n    BILLING.billcount AS billcount,\n    VISIT.visitcount AS visitcount,\n    OBS.fev1_fvc AS fev1_fvc,\n    MEDS.numOfMeds as medcount,\n    dxr.dxresearch_code AS dx,\n    dxr.coding_system AS dxs\n  FROM demographic d \n  LEFT JOIN (\n    SELECT demographic_no, dxresearch_code, coding_system, COUNT(dxresearch_code)\n    FROM dxresearch\n    WHERE status=\'A\' AND\n    (\n      (LEFT(dxresearch_code, 3) IN ${dxcodesICD} AND coding_system = ${dxcodesSysICD}) OR\n      (dxresearch_code IN ${dxcodesSnoMed} AND coding_system LIKE ${dxcodesSysSnoMed})\n    )\n    GROUP BY demographic_no\n    ORDER BY demographic_no\n  ) dxr\n  ON d.demographic_no = dxr.demographic_no\n  -- Billed 490,491,492,494,496 in last 3 years\n  LEFT JOIN (\n    SELECT b.demographic_no AS demographic_no, b.dx_code1 AS dxcode1, b.dx_code2 AS dxcode2, b.dx_code3 AS dxcode3, COUNT(b.demographic_no) AS billcount\n    FROM billingmaster b\n    WHERE b.dx_code1 IN ${billingCode} OR b.dx_code2 IN ${billingCode} OR b.dx_code3 IN ${billingCode}\n    AND DATE(b.createdate) &gt; ${lowerLimit.billingdate}\n    AND b.demographic_no &gt; 0\n    GROUP BY b.demographic_no HAVING COUNT(b.demographic_no) &gt; -1\n  ) BILLING\n  ON (d.demographic_no = BILLING.demographic_no)  \n  -- Coded dx during visit encounter in last 3 years\n  LEFT JOIN(\n    SELECT ASSIGNED.demographic_no AS demographic_no, observation_date AS visitdate, uuid, COUNT(ASSIGNED.demographic_no) AS visitcount\n    FROM casemgmt_note note\n    INNER JOIN (\n      SELECT note_id, CODED.demo_no AS demographic_no FROM casemgmt_issue_notes\n      INNER JOIN (\n        SELECT ci.id AS id, ci.update_date, ci.demographic_no demo_no FROM issue i\n        INNER JOIN casemgmt_issue ci\n        ON i.issue_id = ci.issue_id\n        WHERE\n	(i.type LIKE ${dxcodesSysICD} AND LEFT(i.code,3) IN ${dxcodesICD}) OR\n	(i.type LIKE ${dxcodesSysSnoMed} AND i.code IN ${dxcodesSnoMed})\n      ) CODED\n      ON (casemgmt_issue_notes.id = CODED.id)\n      ORDER BY CODED.demo_no\n    ) ASSIGNED\n    ON note.note_id = ASSIGNED.note_id AND\n    -- GET LAST REVISION OF NOTE\n    note.note_id= (SELECT MAX(cmn.note_id) FROM casemgmt_note cmn WHERE cmn.uuid = note.uuid) AND\n    DATE(note.observation_date) &gt; ${lowerLimit.visitdate}\n    GROUP BY demographic_no\n    ORDER BY demographic_no\n  ) VISIT\n  ON (d.demographic_no = VISIT.demographic_no)  \n  -- FEV1/FVC measurement\n  LEFT JOIN (\n    SELECT m.demographicNo AS demographic_no, m.dataField AS fev1_fvc, m.dateObserved AS dateObserved\n    FROM measurements m\n    INNER JOIN (\n      SELECT demographicNo, MAX(dateObserved) AS dateObserved\n      FROM measurements\n      WHERE type = \'FEV1\'\n      GROUP BY demographicNo\n    ) mlast\n    ON m.demographicNo = mlast.demographicNo\n    WHERE m.type = \'FEV1\'\n    AND m.dateObserved = mlast.dateObserved\n    AND m.demographicNo &gt; 0\n    GROUP BY m.demographicNo\n  ) OBS\n  ON (d.demographic_no = OBS.demographic_no)\n  LEFT JOIN (\n    SELECT dr.demographic_no, count(DISTINCT dr.atc) AS numOfMeds FROM drugs AS dr\n    WHERE dr.archived = 0 AND dr.ATC LIKE ${drug}\n    AND (dr.long_term OR (dr.rx_date &lt;= NOW() AND (DATE_ADD(dr.rx_date, INTERVAL(DATEDIFF(dr.end_date,dr.rx_date)*1.0) DAY)) >= NOW()))\n    GROUP BY dr.demographic_no\n  ) MEDS\n  ON (d.demographic_no = MEDS.demographic_no)  \n  WHERE d.patient_status LIKE ${active}\n  AND d.demographic_no &gt; 0\n  AND d.provider_no LIKE '${provider}'\n  AND d.demographic_no NOT IN ${excludedPatient}\n) fin\n		</query>\n	</indicatorQuery>\n	<drillDownQuery>\n		<version>03-13-2018</version>\n		<params>\n			<parameter id=\"provider\" name=\"Provider Number\" value=\"loggedInProvider\" />\n			<parameter id=\"active\" name=\"Active Patients\" value=\"\'AC\'\" />\n			<parameter id=\"dxcodesICD\" name=\"Dx Codes (ICD)\" value=\"490,491,492,494,496\" />\n                        <parameter id=\"dxcodesSysICD\" name=\"DX CodeSystem (ICD)\" value=\"\'icd9\'\" />\n                        <parameter id=\"dxcodesSnoMed\" name=\"Dx Codes (SnoMed)\" value=\"32398004,63480004,61937009,74417001,185086009,49158009,12295008,13645005\" />\n                        <parameter id=\"dxcodesSysSnoMed\" name=\"Dx CodeSystem (SnoMed)\" value=\"\'snomed%\'\" />\n			<parameter id=\"billingCode\" name=\"ICD9 Billing Code\" value=\"490,491,492,494,496\" />\n			<parameter id=\"numOfBills\" name=\"Number of ICD9 490,491,492,494,496 Bills\" value=\"2\" />\n			<parameter id=\"numOfVisits\" name=\"Number of Visit Encounters coded 490,491,492,494,496/ICD9\" value=\"2\" />\n			<parameter id=\"fev1_fvc\" name=\"FEV1/FVC Result\" value=\"0.7\" />\n			<parameter id=\"drug\" name=\"ATC Code/Class\" value=\"\'R03BB04\'\" />\n			<parameter id=\"numOfMeds\" name=\"Opioid Medications\" value=\"1\" />\n			<parameter id=\"excludedPatient\" name=\"Excluded Patient\" value=\"excludedPatient\" />\n		</params>\n		\n		<range>\n			<lowerLimit id=\"billingdate\" label=\"From Date\" name=\"Date\" value=\"DATE_SUB( NOW(), INTERVAL 3 YEAR )\" />\n			<upperLimit id=\"billingdate\" label=\"Date Today\" name=\"Date\" value=\"NOW()\" />\n			<lowerLimit id=\"visitdate\" label=\"From Date\" name=\"Date\" value=\"DATE_SUB( NOW(), INTERVAL 3 YEAR )\" />\n			<upperLimit id=\"visitdate\" label=\"Date Today\" name=\"Date\" value=\"NOW()\" />\n		</range>\n		\n		<displayColumns>\n			<column id=\"demographic\" name=\"d.demographic_no\" title=\"Patient Id\" primary=\"true\" />\n			<column id=\"name\" name=\"CONCAT( d.last_name, \', \', d.first_name )\" title=\"Patient Name\" primary=\"false\" />\n			<column id=\"dob\" name=\"DATE_FORMAT( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth), \'%m-%d-%Y\' )\" title=\"Date of Birth\" primary=\"false\" />\n			<column id=\"age\" name=\"TIMESTAMPDIFF(YEAR, DATE( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth) ), CURDATE() )\" title=\"Age\" primary=\"false\" />\n			<column id=\"billings\" name=\"IFNULL( BILLING.billcount, \'0\')\" title=\"Billings\" primary=\"false\" />\n			<column id=\"visits\" name=\"IFNULL( VISIT.visitcount, \'0\')\" title=\"Visits\" primary=\"false\" />\n			<column id=\"fev1_fvc\" name=\"IFNULL( OBS.fev1_fvc, \'NA\')\" title=\"FEV1/FVC\" primary=\"false\" />\n			<column id=\"numOfMeds\" name=\"IFNULL( MEDS.numOfMeds, \'NA\')\" title=\"Tiotroprium Medications\" primary=\"false\" />			\n		</displayColumns>\n		\n		<exportColumns>\n			<column id=\"demographic\" name=\"d.demographic_no\" title=\"Patient Id\" primary=\"true\" />\n                        <column id=\"firstName\" name=\"d.first_name\" title=\"First Name\" primary=\"false\" />\n                        <column id=\"lastName\" name=\"d.last_name\" title=\"Last Name\" primary=\"false\" />\n			<column id=\"dob\" name=\"DATE_FORMAT( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth), \'%m-%d-%Y\' )\" title=\"Date of Birth\" primary=\"false\" />\n			<column id=\"age\" name=\"TIMESTAMPDIFF(YEAR, DATE( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth) ), CURDATE() )\" title=\"Age\" primary=\"false\" />\n			<column id=\"billings\" name=\"IFNULL( BILLING.billcount, \'0\')\" title=\"Billings\" primary=\"false\" />\n			<column id=\"visits\" name=\"IFNULL( VISIT.visitcount, \'0\')\" title=\"Visits\" primary=\"false\" />\n			<column id=\"fev1_fvc\" name=\"IFNULL( OBS.fev1_fvc, \'NA\')\" title=\"FEV1/FVC\" primary=\"false\" />\n			<column id=\"numOfMeds\" name=\"IFNULL( MEDS.numOfMeds, \'NA\')\" title=\"Tiotroprium Medications\" primary=\"false\" />			\n		</exportColumns>\n		<drillDownActions>\n			<action id=\"tickler\" name=\"Tickler\"/>\n			<action id=\"dxUpdate\" name=\"Disease Registry Update\" value=\"496\"/>\n			<action id=\"demoExcl\" name=\"Patient Exclusion\"/>\n		</drillDownActions>\n		<query>\n  SELECT *	\n  FROM demographic d \n  LEFT JOIN (\n    SELECT demographic_no, dxresearch_code, coding_system, COUNT(dxresearch_code)\n    FROM dxresearch\n    WHERE status=\'A\' AND\n    (\n      (LEFT(dxresearch_code,3) IN ${dxcodesICD} AND coding_system = ${dxcodesSysICD}) OR\n      (dxresearch_code IN ${dxcodesSnoMed} AND coding_system LIKE ${dxcodesSysSnoMed})\n    )\n    GROUP BY demographic_no\n    ORDER BY demographic_no\n  ) dxr\n  ON d.demographic_no = dxr.demographic_no\n  -- Billed 490,491,492,494,496 in last 3 years\n  LEFT JOIN (\n    SELECT b.demographic_no AS demographic_no, b.dx_code1 AS dxcode1, b.dx_code2 AS dxcode2, b.dx_code3 AS dxcode3, COUNT(b.demographic_no) AS billcount\n    FROM billingmaster b\n    WHERE b.dx_code1 IN ${billingCode} OR b.dx_code2 IN ${billingCode} OR b.dx_code3 IN ${billingCode}\n    AND DATE(b.createdate) &gt; ${lowerLimit.billingdate}\n    AND b.demographic_no &gt; 0\n    GROUP BY b.demographic_no HAVING COUNT(b.demographic_no) &gt; -1\n  ) BILLING\n  ON (d.demographic_no = BILLING.demographic_no)  \n  -- Coded dx during visit encounter in last 3 years\n  LEFT JOIN(\n    SELECT ASSIGNED.demographic_no AS demographic_no, observation_date AS visitdate, uuid, COUNT(ASSIGNED.demographic_no) AS visitcount\n    FROM casemgmt_note note\n    INNER JOIN (\n      SELECT note_id, CODED.demo_no AS demographic_no FROM casemgmt_issue_notes\n      INNER JOIN (\n        SELECT ci.id AS id, ci.update_date, ci.demographic_no demo_no FROM issue i\n        INNER JOIN casemgmt_issue ci\n        ON i.issue_id = ci.issue_id\n        WHERE\n	(i.type LIKE ${dxcodesSysICD} AND LEFT(i.code,3) IN ${dxcodesICD}) OR\n	(i.type LIKE ${dxcodesSysSnoMed} AND i.code IN ${dxcodesSnoMed})\n      ) CODED\n      ON (casemgmt_issue_notes.id = CODED.id)\n      ORDER BY CODED.demo_no\n    ) ASSIGNED\n    ON note.note_id = ASSIGNED.note_id AND\n    -- GET LAST REVISION OF NOTE\n    note.note_id= (SELECT MAX(cmn.note_id) FROM casemgmt_note cmn WHERE cmn.uuid = note.uuid) AND\n    DATE(note.observation_date) &gt; ${lowerLimit.visitdate}\n    GROUP BY demographic_no\n    ORDER BY demographic_no\n  ) VISIT\n  ON (d.demographic_no = VISIT.demographic_no)  \n  -- FEV1/FVC measurement\n  LEFT JOIN (\n    SELECT m.demographicNo AS demographic_no, m.dataField AS fev1_fvc, m.dateObserved AS dateObserved\n    FROM measurements m\n    INNER JOIN (\n      SELECT demographicNo, MAX(dateObserved) AS dateObserved\n      FROM measurements\n      WHERE type = \'FEV1\'\n      GROUP BY demographicNo\n    ) mlast\n    ON m.demographicNo = mlast.demographicNo\n    WHERE m.type = \'FEV1\'\n    AND m.dateObserved = mlast.dateObserved\n    AND m.demographicNo &gt; 0\n    GROUP BY m.demographicNo\n  ) OBS\n  ON (d.demographic_no = OBS.demographic_no)\n  LEFT JOIN (\n    SELECT dr.demographic_no, count(DISTINCT dr.atc) AS numOfMeds FROM drugs AS dr\n    WHERE dr.archived = 0 AND dr.ATC LIKE ${drug}\n    AND (dr.long_term OR (dr.rx_date &lt;= NOW() AND (DATE_ADD(dr.rx_date, INTERVAL(DATEDIFF(dr.end_date,dr.rx_date)*1.0) DAY)) >= NOW()))\n    GROUP BY dr.demographic_no\n  ) MEDS\n  ON (d.demographic_no = MEDS.demographic_no)  \n  WHERE d.patient_status LIKE ${active}\n  AND d.demographic_no &gt; 0\n  AND dxr.dxresearch_code IS NULL AND ((BILLING.billcount IS NOT NULL AND BILLING.billcount &gt;= ${numOfBills}) OR (VISIT.visitcount IS NOT NULL AND VISIT.visitcount &gt;= ${numOfVisits}) OR (OBS.fev1_fvc IS NOT NULL AND (OBS.fev1_fvc &lt; ${fev1_fvc} OR (OBS.fev1_fvc &gt; 1 AND OBS.fev1_fvc &lt; 100*${fev1_fvc}))) OR (MEDS.numOfMeds IS NOT NULL AND MEDS.numOfMeds &gt;= ${numOfMeds}))\n  AND d.provider_no LIKE ${provider}\n  AND d.demographic_no NOT IN ${excludedPatient}\n  ORDER BY d.demographic_no\n		</query>\n	</drillDownQuery>\n</indicatorTemplateXML>\n','','\0',0,NULL,NULL);
INSERT INTO `indicatorTemplate` (`dashboardId`, `name`, `category`, `subCategory`, `framework`, `frameworkVersion`, `definition`, `notes`, `template`, `active`, `locked`, `shared`, `metricSetName`, `metricLabel`) VALUES (@dashboardId2,'Diabetes Prevalence','CDM (Click on the pie chart to see the patients included in the pie chart)','Diabetes','DoBC CPQI PSP Panel','2017-08-02','Proportion of active patients in the practice coded with Diabetes.','\ni) Diabetes patients: \"Patient Status: Active\nAND has a Current Problem List/Profile item code of 73211009 OR starting with 250\"\nii) Other patients: \"Patient Status: Active\nAND does not have a Current Problem List/Profile item code of 73211009 OR startin','<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<indicatorTemplateXML>\n	<author>OSCAR BC</author>\n	<uid></uid>\n	<heading>\n		<category>CDM (Click on the pie chart to see the patients included in the pie chart)</category>\n		<subCategory>Diabetes</subCategory>\n		<name>Diabetes Prevalence</name>\n		<definition>Proportion of active patients in the practice coded with Diabetes.</definition>\n		<framework>DoBC CPQI PSP Panel</framework>\n		<frameworkVersion>08-02-2017</frameworkVersion>\n		<notes>\ni) Diabetes patients: \"Patient Status: Active\nAND has a Current Problem List/Profile item code of 73211009 OR starting with 250\"\nii) Other patients: \"Patient Status: Active\nAND does not have a Current Problem List/Profile item code of 73211009 OR starting with 250\"\n		</notes>\n	</heading>\n	<indicatorQuery>\n		<version>03-13-2018</version>\n		<params>\n			<!-- \n				Required parameter provider. Value options are: \n					[ an individual providerNo, or provider range ] ie: value=\"370, 1001\" \n					\"all\" ie: value=\"all\" including null.\n					\"loggedInProvider\" ie:\n				Default is \"loggedInProvider\"\n				Use this parameter in the query as '${provider}'\n				This parameter should be used for fetching patient\'s assigned to a MRP.\n				ie: WHERE demographic.provider_no = ${provider}\n			-->\n			<parameter id=\"provider\" name=\"provider_no\" value=\"loggedInProvider\" />\n			<parameter id=\"pstatus\" name=\"Patient Status\" value=\"\'AC\'\" />\n			<parameter id=\"dxcodesICD\" name=\"Dx Codes (ICD)\" value=\"250\" />\n			<parameter id=\"dxcodesSysICD\" name=\"DX CodeSystem (ICD)\" value=\"\'icd9\'\" />\n			<parameter id=\"dxcodesSnoMed\" name=\"Dx Codes (SnoMed)\" value=\"73211009\" />\n			<parameter id=\"dxcodesSysSnoMed\" name=\"Dx CodeSystem (SnoMed)\" value=\"\'snomed%\'\" />\n			<parameter id=\"excludedPatient\" name=\"Excluded Patient\" value=\"excludedPatient\" />\n		</params>\n		<query>\n			<!-- Indicator SQL Query here -->\nSELECT\n -- TOTAL NUMBER OF PATIENTS NOTED AS ACTIVE WITH CURRENT DIABETES\n IF( COUNT(fin.patient) &gt; 0, SUM( IF( dxresearch_code is not NULL , 1, 0) ) , 0 ) AS \"Number Active, Current Diabetes\",\n -- TOTAL NUMBER OF PATIENTS NOTED AS ACTIVE WITHOUT CURRENT DIABETES\n IF( COUNT(fin.patient) &gt; 0, SUM( IF( dxresearch_code is NULL , 1, 0) ) , 0 ) AS \"Number Active, No Current Diabetes\"\nFROM (\n SELECT d.demographic_no AS patient, d.patient_status AS pt_status, dxresearch_code\n FROM demographic d\n LEFT JOIN (\n  SELECT demographic_no, dxresearch_code, COUNT(dxresearch_code)\n  FROM dxresearch\n  WHERE status=\'A\' AND\n  (\n   (LEFT(dxresearch_code, 3) IN (\'250\') AND coding_system = ${dxcodesSysICD}) OR\n   (dxresearch_code IN (\'73211009\') AND coding_system LIKE ${dxcodesSysSnoMed})\n   )\n  GROUP BY demographic_no\n  ORDER BY demographic_no\n ) problems\n ON d.demographic_no = problems.demographic_no\n WHERE d.provider_no LIKE ${provider} AND d.patient_status = ${pstatus}\n AND d.demographic_no NOT IN ${excludedPatient}\n) fin\n		</query>\n	</indicatorQuery>\n	<drillDownQuery>\n		<version>03-13-2018</version>\n		<params>\n			<parameter id=\"provider\" name=\"provider_no\" value=\"loggedInProvider\" />	\n			<parameter id=\"pstatus\" name=\"Patient Status\" value=\"\'AC\'\" />\n			<parameter id=\"dxcodesICD\" name=\"Dx Codes (ICD)\" value=\"250\" />\n			<parameter id=\"dxcodesSysICD\" name=\"DX CodeSystem (ICD)\" value=\"\'icd9\'\" />\n			<parameter id=\"dxcodesSnoMed\" name=\"Dx Codes (SnoMed)\" value=\"73211009\" />\n			<parameter id=\"dxcodesSysSnoMed\" name=\"Dx CodeSystem (SnoMed)\" value=\"\'snomed%\'\" />\n			<parameter id=\"excludedPatient\" name=\"Excluded Patient\" value=\"excludedPatient\" />\n		</params>\n		<displayColumns>\n			<column id=\"demographic\" name=\"d.demographic_no\" title=\"Patient Id\" primary=\"true\" />\n			<column id=\"name\" name=\"CONCAT( d.last_name, \', \', d.first_name )\" title=\"Patient Name\" primary=\"false\" />\n			<column id=\"dob\" name=\"DATE_FORMAT( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth), \'%m-%d-%Y\' )\" title=\"Date of Birth (mm-dd-yy)\" primary=\"false\" />\n			<column id=\"age\" name=\"TIMESTAMPDIFF(YEAR, DATE( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth) ), CURDATE() )\" title=\"Age\" primary=\"false\" />\n			<column id=\"dxcode\" title=\"Dx System/Code\" name=\"CONCAT(MIN(dxr.coding_system),\'/\',MIN(dxr.dxresearch_code),IF( MIN(dxr.dxresearch_code) != MAX(dxr.dxresearch_code),CONCAT( \' &amp; \', MAX(dxr.coding_system), \'/\', MAX(dxr.dxresearch_code) ),\'\') )\" primary=\"false\" />\n		</displayColumns>\n		<exportColumns>\n			<column id=\"demographic\" name=\"d.demographic_no\" title=\"Patient Id\" primary=\"true\" />\n			<column id=\"firstName\" name=\"d.first_name\" title=\"First Name\" primary=\"false\" />\n			<column id=\"lastName\" name=\"d.last_name\" title=\"Last Name\" primary=\"false\" />\n			<column id=\"dob\" name=\"DATE_FORMAT( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth), \'%m-%d-%Y\' )\" title=\"Date of Birth (mm-dd-yy)\" primary=\"false\" />\n			<column id=\"age\" name=\"TIMESTAMPDIFF(YEAR, DATE( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth) ), CURDATE() )\" title=\"Age\" primary=\"false\" />\n			<column id=\"dxcode\" title=\"Dx System/Code\" name=\"CONCAT(MIN(dxr.coding_system),\'/\',MIN(dxr.dxresearch_code),IF( MIN(dxr.dxresearch_code) != MAX(dxr.dxresearch_code),CONCAT( \' &amp; \', MAX(dxr.coding_system), \'/\', MAX(dxr.dxresearch_code) ),\'\') )\" primary=\"false\" />\n		</exportColumns>\n		<drillDownActions>\n			<action id=\"tickler\" name=\"Tickler\"/>\n			<action id=\"demoExcl\" name=\"Patient Exclusion\"/>\n		</drillDownActions>\n		<query>\n			<!-- Drilldown SQL Query here -->\nSELECT\n*			\nFROM demographic d\nLEFT JOIN dxresearch dxr\nON d.demographic_no = dxr.demographic_no\nWHERE d.patient_status=${pstatus}\nAND\n(\n (LEFT(dxr.dxresearch_code, 3) IN (\'250\') AND dxr.coding_system = ${dxcodesSysICD}) OR\n (dxr.dxresearch_code IN (\'73211009\') AND dxr.coding_system LIKE ${dxcodesSysSnoMed})\n )\nAND dxr.status = \'A\'\nAND d.provider_no LIKE ${provider}\nAND d.demographic_no NOT IN ${excludedPatient}\nGROUP BY d.demographic_no\nORDER BY COUNT(d.demographic_no) DESC, d.demographic_no\n		</query>\n	</drillDownQuery>\n</indicatorTemplateXML>\n','','\0',0,NULL,NULL);
INSERT INTO `indicatorTemplate` (`dashboardId`, `name`, `category`, `subCategory`, `framework`, `frameworkVersion`, `definition`, `notes`, `template`, `active`, `locked`, `shared`, `metricSetName`, `metricLabel`) VALUES (@dashboardId2,'Consider Diabetes','CDM (Click on the pie chart to see the patients included in the pie chart)','Diabetes','DoBC CPQI PSP Panel','2017-08-02','\n		  Patients with diabetes dx in 2+ bills or 2+ visit\n		  encounters within last 3 years or most recent HbA1C\n		  >= 6.5% or Glucose Fasting > 7% or current\n		  Insulin (ATC code A10A) or Oral hypoglycemics (ATC\n		  code A10B) medication\n		','\n		  Created for Doctors of BC\n		  i) Diabetes dx in 2+ bills: \"Patient Status: Active\nAND does not have a Current Problem List/Profile item code of 73211009 OR starting with 250\nAND with >= 2 Bills with the ICD9 diagnosis code starting with 250 in the la','<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<indicatorTemplateXML>\n	<author>OSCAR BC</author>\n	<uid></uid>\n	<heading>\n		<category>CDM (Click on the pie chart to see the patients included in the pie chart)</category>\n		<subCategory>Diabetes</subCategory>\n		<name>Consider Diabetes</name>\n		<definition>\n		  Patients with diabetes dx in 2+ bills or 2+ visit\n		  encounters within last 3 years or most recent HbA1C\n		  &gt;= 6.5% or Glucose Fasting &gt; 7% or current\n		  Insulin (ATC code A10A) or Oral hypoglycemics (ATC\n		  code A10B) medication\n		</definition>\n		<framework>DoBC CPQI PSP Panel</framework>\n		<frameworkVersion>08-02-2017</frameworkVersion>\n		<notes>\n		  Created for Doctors of BC\n		  i) Diabetes dx in 2+ bills: \"Patient Status: Active\nAND does not have a Current Problem List/Profile item code of 73211009 OR starting with 250\nAND with >= 2 Bills with the ICD9 diagnosis code starting with 250 in the last 3 years\"\n		  ii) Diabetes dx in 2+ visits: \"Patient Status: Active\nAND does not have a Current Problem List/Profile item code of 73211009 OR starting with 250\nAND with >= 2 Visit Encounters with Snomed diagnosis code 73211009 or the ICD9 diagnosis code starting with 250 in the last 3 years\"\n                  iii) HbA1C >= 6.5% or Glucose Fasting > 7% or\n                  current Insulin (ATC code A10A) or Oral\n                  hypoglycemics (ATC code A10B) medication: \"Patient\n                  Status: Active AND does not have a Current Problem\n                  List/Profile item code of 73211009 OR starting with\n                  250 AND ( (WITH a most recent [HbA1c >= 6.5% OR (>=\n                  0.065 AND NOT > 1)] OR [Fasting Glucose > 7%] in the\n                  past 1 year) OR (with two or more HbA1c >= 6.5% in\n                  last 5 years) OR (current medication with ATC code\n                  starting with A10A or A10B) )\"\n		</notes>\n	</heading>\n	<indicatorQuery>\n		<version>03-13-2018</version>\n		<params>\n			<parameter id=\"provider\" name=\"Provider Number\" value=\"loggedInProvider\" />\n			<parameter id=\"active\" name=\"Active Patients\" value=\"\'AC\'\" />\n			<parameter id=\"dxcodesICD\" name=\"Dx Codes (ICD)\" value=\"250\" />\n                        <parameter id=\"dxcodesSysICD\" name=\"DX CodeSystem (ICD)\" value=\"\'icd9\'\" />\n                        <parameter id=\"dxcodesSnoMed\" name=\"Dx Codes (SnoMed)\" value=\"73211009\" />\n                        <parameter id=\"dxcodesSysSnoMed\" name=\"Dx CodeSystem (SnoMed)\" value=\"\'snomed%\'\" />\n			<parameter id=\"billingCode\" name=\"ICD9 Billing Code\" value=\"250\" />\n			<parameter id=\"numOfBills\" name=\"Number of ICD9 250 Bills\" value=\"2\" />\n			<parameter id=\"numOfVisits\" name=\"Number of Visit Encounters coded 250/ICD9\" value=\"2\" />\n                        <parameter id=\"obs1Name\" name=\"Obs 1 Name\" value=\"\'A1C\'\" />\n                        <parameter id=\"obs1Result\" name=\"Obs 1 Result\" value=\"6.5\" />\n                        <parameter id=\"obs2Name\" name=\"Obs 2 Name\" value=\"\'FBS\'\" />\n                        <parameter id=\"obs2Result\" name=\"Obs 2 Result\" value=\"7.0\" />\n                        <parameter id=\"numOfObs\" name=\"Number of measurements\" value=\"2\" />\n                        <parameter id=\"drugs\" name=\"ATC Code/Class\" value=\"A10A,A10B\" />\n                        <parameter id=\"numOfMeds\" name=\"Heart Failure Medications\" value=\"1\" />\n			<parameter id=\"excludedPatient\" name=\"Excluded Patient\" value=\"excludedPatient\" />\n		</params>\n		<range>\n			<lowerLimit id=\"billingdate\" label=\"From Date\" name=\"Date\" value=\"DATE_SUB( NOW(), INTERVAL 3 YEAR )\" />\n			<upperLimit id=\"billingdate\" label=\"Date Today\" name=\"Date\" value=\"NOW()\" />\n			<lowerLimit id=\"visitdate\" label=\"From Date\" name=\"Date\" value=\"DATE_SUB( NOW(), INTERVAL 3 YEAR )\" />\n                        <upperLimit id=\"visitdate\" label=\"Date Today\" name=\"Date\" value=\"NOW()\" />\n			<lowerLimit id=\"labdatelast\" label=\"From Date\" name=\"Date\" value=\"DATE_SUB( NOW(), INTERVAL 12 MONTH )\" />\n			<upperLimit id=\"labdatelast\" label=\"Date Today\" name=\"Date\" value=\"NOW()\" />\n			<lowerLimit id=\"labdateObs\" label=\"From Date\" name=\"Date\" value=\"DATE_SUB( NOW(), INTERVAL 5 YEAR )\" />\n			<upperLimit id=\"labdateObs\" label=\"Date Today\" name=\"Date\" value=\"NOW()\" />\n		</range>\n		<query>\nSELECT\n  IF ( COUNT(fin.patient) &gt; 0, SUM( CASE WHEN (fin.dx IS NULL AND fin.billcount &gt;= ${numOfBills}) THEN 1 ELSE 0 END ) , 0) AS \"Number with Diabetes dx in ${numOfBills}+ bills\",\n  IF ( COUNT(fin.patient) &gt; 0, SUM( CASE WHEN (fin.dx IS NULL AND fin.visitcount &gt;= ${numOfVisits}) THEN 1 ELSE 0 END ) , 0) AS \"Number with Diabetes dx in ${numOfVisits}+ visits\",\n  IF ( COUNT(fin.patient) &gt; 0, SUM( CASE WHEN (fin.dx IS NULL AND\n  (fin.result1 &gt; ${obs1Result} OR (fin.result1 &gt; 0.01*${obs1Result} AND fin.result1 &lt; 1) OR fin.result2 &gt; ${obs2Result} OR fin.cntResult1 &gt;= ${numOfObs} OR fin.medcount &gt;= ${numOfMeds})\n  ) THEN 1 ELSE 0 END ) , 0) AS \"Number with Diabetes Obs/Medication\"\nFROM ( \n  SELECT \n    d.demographic_no AS patient, \n    BILLING.billcount AS billcount,\n    VISIT.visitcount AS visitcount,\n    OBS1LAST.result AS result1,\n    OBS2LAST.result AS result2,\n    OBS1CNT.countOBS AS cntResult1,\n    MEDS.numOfMeds AS medcount,\n    dxr.dxresearch_code AS dx,\n    dxr.coding_system AS dxs\n  FROM demographic d \n  LEFT JOIN (\n    SELECT demographic_no, dxresearch_code, coding_system, COUNT(dxresearch_code)\n    FROM dxresearch\n    WHERE status=\'A\' AND\n    (\n      (LEFT(dxresearch_code, 3) IN (\'250\') AND coding_system = ${dxcodesSysICD}) OR\n      (dxresearch_code IN (\'73211009\') AND coding_system LIKE ${dxcodesSysSnoMed})\n    )\n    GROUP BY demographic_no\n    ORDER BY demographic_no\n  ) dxr\n  ON d.demographic_no = dxr.demographic_no\n  -- Billed 250 in last 3 years\n  LEFT JOIN (\n    SELECT b.demographic_no AS demographic_no, b.dx_code1 AS dxcode1, b.dx_code2 AS dxcode2, b.dx_code3 AS dxcode3, COUNT(b.demographic_no) AS billcount\n    FROM billingmaster b\n    WHERE ( LEFT(b.dx_code1, 3) IN (\'250\') OR LEFT(b.dx_code2, 3) IN (\'250\') OR LEFT(b.dx_code3, 3) IN (\'250\') )\n    AND DATE(b.createdate) &gt; ${lowerLimit.billingdate}\n    AND b.demographic_no &gt; 0\n    GROUP BY b.demographic_no HAVING COUNT(b.demographic_no) &gt; -1\n  ) BILLING\n  ON (d.demographic_no = BILLING.demographic_no)  \n  -- Coded dx during visit encounter in last 3 years\n  LEFT JOIN(\n    SELECT ASSIGNED.demographic_no AS demographic_no, observation_date AS visitdate, uuid, COUNT(ASSIGNED.demographic_no) AS visitcount\n    FROM casemgmt_note note\n    INNER JOIN (\n      SELECT note_id, CODED.demo_no AS demographic_no FROM casemgmt_issue_notes\n      INNER JOIN (\n        SELECT ci.id AS id, ci.update_date, ci.demographic_no demo_no FROM issue i\n        INNER JOIN casemgmt_issue ci\n        ON i.issue_id = ci.issue_id\n        WHERE\n	(i.type LIKE ${dxcodesSysICD} AND LEFT(i.code, 3) IN (\'250\')) OR\n	(i.type LIKE ${dxcodesSysSnoMed} AND i.code IN (\'73211009\'))\n      ) CODED\n      ON (casemgmt_issue_notes.id = CODED.id)\n      ORDER BY CODED.demo_no\n    ) ASSIGNED\n    ON note.note_id = ASSIGNED.note_id AND\n    -- GET LAST REVISION OF NOTE\n    note.note_id= (SELECT MAX(cmn.note_id) FROM casemgmt_note cmn WHERE cmn.uuid = note.uuid) AND\n    DATE(note.observation_date) &gt; ${lowerLimit.visitdate}\n    GROUP BY demographic_no\n    ORDER BY demographic_no\n  ) VISIT\n  ON (d.demographic_no = VISIT.demographic_no)\n  -- HbA1C measurement\n  LEFT JOIN ( \n    SELECT m.demographicNo AS demographic_no, m.dataField AS result, m.dateObserved AS dateObserved\n    FROM measurements m\n    INNER JOIN (\n      SELECT demographicNo, MAX(dateObserved) AS dateObserved\n      FROM measurements\n      WHERE type = ${obs1Name}\n      GROUP BY demographicNo\n    ) mlast\n    ON m.demographicNo = mlast.demographicNo\n    WHERE m.type = ${obs1Name}\n    AND m.dateObserved = mlast.dateObserved\n    AND DATE(m.dateObserved) &gt; ${lowerLimit.labdatelast}\n    AND m.demographicNo &gt; 0\n  ) OBS1LAST\n  ON (d.demographic_no = OBS1LAST.demographic_no) \n  -- Fasting Glucose measurement\n  LEFT JOIN ( \n    SELECT m.demographicNo AS demographic_no, m.dataField AS result, m.dateObserved AS dateObserved\n    FROM measurements m\n    INNER JOIN (\n      SELECT demographicNo, MAX(dateObserved) AS dateObserved\n      FROM measurements\n      WHERE type = ${obs2Name}\n      GROUP BY demographicNo\n    ) mlast\n    ON m.demographicNo = mlast.demographicNo\n    WHERE m.type = ${obs2Name}\n    AND m.dateObserved = mlast.dateObserved\n    AND DATE(m.dateObserved) &gt; ${lowerLimit.labdatelast}\n    AND m.demographicNo &gt; 0\n  ) OBS2LAST\n  ON (d.demographic_no = OBS2LAST.demographic_no)\n  LEFT JOIN ( \n    SELECT m.demographicNo AS demographic_no, m.dataField, COUNT(m.dataField) AS countOBS\n    FROM measurements m\n    WHERE m.type = ${obs1Name}\n    AND m.dataField &gt;= ${obs1Result}\n    AND DATE(m.dateObserved) &gt; ${lowerLimit.labdateObs}\n    AND m.demographicNo &gt; 0\n    GROUP BY m.demographicNo HAVING COUNT(m.demographicNo) &gt; -1\n  ) OBS1CNT\n  ON (d.demographic_no = OBS1CNT.demographic_no)  \n  LEFT JOIN (\n    SELECT dr.demographic_no, COUNT(DISTINCT dr.atc) AS numOfMeds FROM drugs AS dr\n--    WHERE dr.archived = 0 AND LEFT(dr.ATC,4) IN ${drugs}\n    WHERE dr.archived = 0 AND dr.ATC LIKE \'A10%\'\n    AND (dr.long_term OR (dr.rx_date &lt;= NOW() AND (DATE_ADD(dr.rx_date, INTERVAL(DATEDIFF(dr.end_date,dr.rx_date)*1.0) DAY)) >= NOW()))\n    GROUP BY dr.demographic_no\n  ) MEDS\n  ON (d.demographic_no = MEDS.demographic_no)\n  WHERE d.patient_status LIKE ${active}\n  AND d.demographic_no &gt; 0\n  AND d.provider_no LIKE '${provider}'\n  AND d.demographic_no NOT IN ${excludedPatient}\n) fin\n		</query>\n	</indicatorQuery>\n	<drillDownQuery>\n		<version>03-13-2018</version>\n		<params>\n			<parameter id=\"provider\" name=\"Provider Number\" value=\"loggedInProvider\" />\n			<parameter id=\"active\" name=\"Active Patients\" value=\"\'AC\'\" />\n			<parameter id=\"dxcodesICD\" name=\"Dx Codes (ICD)\" value=\"250\" />\n                        <parameter id=\"dxcodesSysICD\" name=\"DX CodeSystem (ICD)\" value=\"\'icd9\'\" />\n                        <parameter id=\"dxcodesSnoMed\" name=\"Dx Codes (SnoMed)\" value=\"73211009\" />\n                        <parameter id=\"dxcodesSysSnoMed\" name=\"Dx CodeSystem (SnoMed)\" value=\"\'snomed%\'\" />\n			<parameter id=\"billingCode\" name=\"ICD9 Billing Code\" value=\"250\" />\n			<parameter id=\"numOfBills\" name=\"Number of ICD9 250 Bills\" value=\"2\" />\n			<parameter id=\"numOfVisits\" name=\"Number of Visit Encounters coded 250/ICD9\" value=\"2\" />\n                        <parameter id=\"obs1Name\" name=\"Obs 1 Name\" value=\"\'A1C\'\" />\n                        <parameter id=\"obs1Result\" name=\"Obs 1 Result\" value=\"6.5\" />\n                        <parameter id=\"obs2Name\" name=\"Obs 2 Name\" value=\"\'FBS\'\" />\n                        <parameter id=\"obs2Result\" name=\"Obs 2 Result\" value=\"7.0\" />\n                        <parameter id=\"numOfObs\" name=\"Number of measurements\" value=\"2\" />\n                        <parameter id=\"drugs\" name=\"ATC Code/Class\" value=\"A10A,A10B\" />\n                        <parameter id=\"numOfMeds\" name=\"Diabetes Medications\" value=\"1\" />\n			<parameter id=\"excludedPatient\" name=\"Excluded Patient\" value=\"excludedPatient\" />\n		</params>\n		\n		<range>\n			<lowerLimit id=\"billingdate\" label=\"From Date\" name=\"Date\" value=\"DATE_SUB( NOW(), INTERVAL 3 YEAR )\" />\n			<upperLimit id=\"billingdate\" label=\"Date Today\" name=\"Date\" value=\"NOW()\" />\n			<lowerLimit id=\"visitdate\" label=\"From Date\" name=\"Date\" value=\"DATE_SUB( NOW(), INTERVAL 3 YEAR )\" />\n                        <upperLimit id=\"visitdate\" label=\"Date Today\" name=\"Date\" value=\"NOW()\" />\n			<lowerLimit id=\"labdatelast\" label=\"From Date\" name=\"Date\" value=\"DATE_SUB( NOW(), INTERVAL 12 MONTH )\" />\n			<upperLimit id=\"labdatelast\" label=\"Date Today\" name=\"Date\" value=\"NOW()\" />\n			<lowerLimit id=\"labdateObs\" label=\"From Date\" name=\"Date\" value=\"DATE_SUB( NOW(), INTERVAL 5 YEAR )\" />\n			<upperLimit id=\"labdateObs\" label=\"Date Today\" name=\"Date\" value=\"NOW()\" />\n		</range>\n		\n		<displayColumns>\n			<column id=\"demographic\" name=\"d.demographic_no\" title=\"Patient Id\" primary=\"true\" />\n			<column id=\"name\" name=\"CONCAT( d.last_name, \', \', d.first_name )\" title=\"Patient Name\" primary=\"false\" />\n			<column id=\"dob\" name=\"DATE_FORMAT( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth), \'%m-%d-%Y\' )\" title=\"Date of Birth\" primary=\"false\" />\n			<column id=\"age\" name=\"TIMESTAMPDIFF(YEAR, DATE( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth) ), CURDATE() )\" title=\"Age\" primary=\"false\" />\n			<column id=\"billings\" name=\"IFNULL( BILLING.billcount, \'0\')\" title=\"Billings\" primary=\"false\" />\n			<column id=\"visits\" name=\"IFNULL( VISIT.visitcount, \'0\')\" title=\"Visits\" primary=\"false\" />\n			<column id=\"lastOBS1\" name=\"IFNULL( OBS1LAST.result, \'0\')\" title=\"HbA1C\" primary=\"false\" />\n			<column id=\"testDateOBS1\" name=\"IFNULL( DATE_FORMAT( OBS1LAST.dateObserved, \'%m-%d-%Y\' ), \'0\')\" title=\"HbA1C Test Date (mm-dd-yyyy)\" primary=\"false\" />\n			<column id=\"numOBS1s\" name=\"IFNULL( OBS1CNT.countOBS, \'0\')\" title=\"Num. HbA1C &gt;= 6.5% Last 5yrs\" primary=\"false\" />\n			<column id=\"lastOBS2\" name=\"IFNULL( OBS2LAST.result, \'0\')\" title=\"FBS\" primary=\"false\" />\n			<column id=\"testDateOBS2\" name=\"IFNULL( DATE_FORMAT( OBS2LAST.dateObserved, \'%m-%d-%Y\' ), \'0\')\" title=\"FBS Test Date (mm-dd-yyyy)\" primary=\"false\" />\n                        <column id=\"numOfMeds\" name=\"IFNULL( MEDS.numOfMeds, \'0\')\" title=\"Diabetes Medications\" primary=\"false\" />\n		</displayColumns>\n		\n		<exportColumns>\n			<column id=\"demographic\" name=\"d.demographic_no\" title=\"Patient Id\" primary=\"true\" />\n                        <column id=\"firstName\" name=\"d.first_name\" title=\"First Name\" primary=\"false\" />\n                        <column id=\"lastName\" name=\"d.last_name\" title=\"Last Name\" primary=\"false\" />\n			<column id=\"dob\" name=\"DATE_FORMAT( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth), \'%m-%d-%Y\' )\" title=\"Date of Birth\" primary=\"false\" />\n			<column id=\"age\" name=\"TIMESTAMPDIFF(YEAR, DATE( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth) ), CURDATE() )\" title=\"Age\" primary=\"false\" />\n			<column id=\"billings\" name=\"IFNULL( BILLING.billcount, \'0\')\" title=\"Billings\" primary=\"false\" />\n			<column id=\"visits\" name=\"IFNULL( VISIT.visitcount, \'0\')\" title=\"Visits\" primary=\"false\" />\n			<column id=\"lastOBS1\" name=\"IFNULL( OBS1LAST.result, \'0\')\" title=\"HbA1C\" primary=\"false\" />\n			<column id=\"testDateOBS1\" name=\"IFNULL( DATE_FORMAT( OBS1LAST.dateObserved, \'%m-%d-%Y\' ), \'0\')\" title=\"HbA1C Test Date (mm-dd-yyyy)\" primary=\"false\" />\n			<column id=\"numOBS1s\" name=\"IFNULL( OBS1CNT.countOBS, \'0\')\" title=\"Num. HbA1C &gt;= 6.5% Last 5yrs\" primary=\"false\" />\n			<column id=\"lastOBS2\" name=\"IFNULL( OBS2LAST.result, \'0\')\" title=\"FBS\" primary=\"false\" />\n			<column id=\"testDateOBS2\" name=\"IFNULL( DATE_FORMAT( OBS2LAST.dateObserved, \'%m-%d-%Y\' ), \'0\')\" title=\"FBS Test Date (mm-dd-yyyy)\" primary=\"false\" />\n                        <column id=\"numOfMeds\" name=\"IFNULL( MEDS.numOfMeds, \'0\')\" title=\"Diabetes Medications\" primary=\"false\" />\n		</exportColumns>\n		<drillDownActions>\n			<action id=\"tickler\" name=\"Tickler\"/>\n			<action id=\"dxUpdate\" name=\"Disease Registry Update\" value=\"250\"/>\n			<action id=\"demoExcl\" name=\"Patient Exclusion\"/>\n		</drillDownActions>\n		<query>\n  SELECT *	\n  FROM demographic d \n  LEFT JOIN (\n    SELECT demographic_no, dxresearch_code, coding_system, COUNT(dxresearch_code)\n    FROM dxresearch\n    WHERE status=\'A\' AND\n    (\n      (LEFT(dxresearch_code, 3) IN (\'250\') AND coding_system = ${dxcodesSysICD}) OR\n      (dxresearch_code IN (\'73211009\') AND coding_system LIKE ${dxcodesSysSnoMed})\n    )\n    GROUP BY demographic_no\n    ORDER BY demographic_no\n  ) dxr\n  ON d.demographic_no = dxr.demographic_no\n  -- Billed 250 in last 3 years\n  LEFT JOIN (\n    SELECT b.demographic_no AS demographic_no, b.dx_code1 AS dxcode1, b.dx_code2 AS dxcode2, b.dx_code3 AS dxcode3, COUNT(b.demographic_no) AS billcount\n    FROM billingmaster b\n    WHERE ( LEFT(b.dx_code1, 3) IN (\'250\') OR LEFT(b.dx_code2, 3) IN (\'250\') OR LEFT(b.dx_code3, 3) IN (\'250\') )\n    AND DATE(b.createdate) &gt; ${lowerLimit.billingdate}\n    AND b.demographic_no &gt; 0\n    GROUP BY b.demographic_no HAVING COUNT(b.demographic_no) &gt; -1\n  ) BILLING\n  ON (d.demographic_no = BILLING.demographic_no)  \n  -- Coded dx during visit encounter in last 3 years\n  LEFT JOIN(\n    SELECT ASSIGNED.demographic_no AS demographic_no, observation_date AS visitdate, uuid, COUNT(ASSIGNED.demographic_no) AS visitcount\n    FROM casemgmt_note note\n    INNER JOIN (\n      SELECT note_id, CODED.demo_no AS demographic_no FROM casemgmt_issue_notes\n      INNER JOIN (\n        SELECT ci.id AS id, ci.update_date, ci.demographic_no demo_no FROM issue i\n        INNER JOIN casemgmt_issue ci\n        ON i.issue_id = ci.issue_id\n        WHERE\n	(i.type LIKE ${dxcodesSysICD} AND LEFT(i.code, 3) IN (\'250\')) OR\n	(i.type LIKE ${dxcodesSysSnoMed} AND i.code IN (\'73211009\'))\n      ) CODED\n      ON (casemgmt_issue_notes.id = CODED.id)\n      ORDER BY CODED.demo_no\n    ) ASSIGNED\n    ON note.note_id = ASSIGNED.note_id AND\n    -- GET LAST REVISION OF NOTE\n    note.note_id= (SELECT MAX(cmn.note_id) FROM casemgmt_note cmn WHERE cmn.uuid = note.uuid) AND\n    DATE(note.observation_date) &gt; ${lowerLimit.visitdate}\n    GROUP BY demographic_no\n    ORDER BY demographic_no\n  ) VISIT\n  ON (d.demographic_no = VISIT.demographic_no)\n  -- HbA1C measurement\n  LEFT JOIN ( \n    SELECT m.demographicNo AS demographic_no, m.dataField AS result, m.dateObserved AS dateObserved\n    FROM measurements m\n    INNER JOIN (\n      SELECT demographicNo, MAX(dateObserved) AS dateObserved\n      FROM measurements\n      WHERE type = ${obs1Name}\n      GROUP BY demographicNo\n    ) mlast\n    ON m.demographicNo = mlast.demographicNo\n    WHERE m.type = ${obs1Name}\n    AND m.dateObserved = mlast.dateObserved\n    AND DATE(m.dateObserved) &gt; ${lowerLimit.labdatelast}\n    AND m.demographicNo &gt; 0\n  ) OBS1LAST\n  ON (d.demographic_no = OBS1LAST.demographic_no) \n  -- Fasting Glucose measurement\n  LEFT JOIN ( \n    SELECT m.demographicNo AS demographic_no, m.dataField AS result, m.dateObserved AS dateObserved\n    FROM measurements m\n    INNER JOIN (\n      SELECT demographicNo, MAX(dateObserved) AS dateObserved\n      FROM measurements\n      WHERE type = ${obs2Name}\n      GROUP BY demographicNo\n    ) mlast\n    ON m.demographicNo = mlast.demographicNo\n    WHERE m.type = ${obs2Name}\n    AND m.dateObserved = mlast.dateObserved\n    AND DATE(m.dateObserved) &gt; ${lowerLimit.labdatelast}\n    AND m.demographicNo &gt; 0\n  ) OBS2LAST\n  ON (d.demographic_no = OBS2LAST.demographic_no)\n  LEFT JOIN ( \n    SELECT m.demographicNo AS demographic_no, m.dataField, COUNT(m.dataField) AS countOBS\n    FROM measurements m\n    WHERE m.type = ${obs1Name}\n    AND m.dataField &gt;= ${obs1Result}\n    AND DATE(m.dateObserved) &gt; ${lowerLimit.labdateObs}\n    AND m.demographicNo &gt; 0\n    GROUP BY m.demographicNo HAVING COUNT(m.demographicNo) &gt; -1\n  ) OBS1CNT\n  ON (d.demographic_no = OBS1CNT.demographic_no)\n  LEFT JOIN (\n    SELECT dr.demographic_no, COUNT(DISTINCT dr.atc) AS numOfMeds FROM drugs AS dr\n--    WHERE dr.archived = 0 AND LEFT(dr.ATC,4) IN ${drugs}\n    WHERE dr.archived = 0 AND dr.ATC LIKE \'A10%\'\n    AND (dr.long_term OR (dr.rx_date &lt;= NOW() AND (DATE_ADD(dr.rx_date, INTERVAL(DATEDIFF(dr.end_date,dr.rx_date)*1.0) DAY)) >= NOW()))\n    GROUP BY dr.demographic_no\n  ) MEDS\n  ON (d.demographic_no = MEDS.demographic_no)\n  WHERE d.patient_status LIKE ${active}\n  AND d.demographic_no &gt; 0\n  AND (\n    dxr.dxresearch_code IS NULL AND\n    ( (BILLING.billcount IS NOT NULL AND BILLING.billcount &gt;= ${numOfBills})  OR\n      (VISIT.visitcount IS NOT NULL AND VISIT.visitcount &gt;= ${numOfVisits}) OR\n      ( (OBS1LAST.result IS NOT NULL AND (OBS1LAST.result &gt; ${obs1Result} OR (OBS1LAST.result &gt; 0.01*${obs1Result} AND OBS1LAST.result &lt; 1))) OR\n        (OBS2LAST.result IS NOT NULL AND OBS2LAST.result &gt; ${obs2Result} ) OR\n        (OBS1CNT.countOBS IS NOT NULL AND OBS1CNT.countOBS &gt;= ${numOfObs}) OR\n        (MEDS.numOfMeds IS NOT NULL AND MEDS.numOfMeds &gt;= ${numOfMeds})\n      )\n    )\n  )\n  AND d.provider_no LIKE ${provider}\n  AND d.demographic_no NOT IN ${excludedPatient}\n  ORDER BY d.demographic_no\n		</query>\n	</drillDownQuery>\n</indicatorTemplateXML>\n','','\0',0,NULL,NULL);
INSERT INTO `indicatorTemplate` (`dashboardId`, `name`, `category`, `subCategory`, `framework`, `frameworkVersion`, `definition`, `notes`, `template`, `active`, `locked`, `shared`, `metricSetName`, `metricLabel`) VALUES (@dashboardId2,'Heart Failure Prevalence','CDM (Click on the pie chart to see the patients included in the pie chart)','Heart Failure','DoBC CPQI PSP Panel','2017-08-02','Proportion of active patients in the practice coded with Heart Failure.','\ni) HF patients: \"Patient Status: Active\nAND has a Current Problem List/Profile item code of 84114007 OR starting with 428\"\nii) Other patients: \"Patient Status: Active\nAND does not have a Current Problem List/Profile item code of 84114007 OR starting with','<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<indicatorTemplateXML>\n	<author>OSCAR BC</author>\n	<uid></uid>\n	<heading>\n		<category>CDM (Click on the pie chart to see the patients included in the pie chart)</category>\n		<subCategory>Heart Failure</subCategory>\n		<name>Heart Failure Prevalence</name>\n		<definition>Proportion of active patients in the practice coded with Heart Failure.</definition>\n		<framework>DoBC CPQI PSP Panel</framework>\n		<frameworkVersion>08-02-2017</frameworkVersion>\n		<notes>\ni) HF patients: \"Patient Status: Active\nAND has a Current Problem List/Profile item code of 84114007 OR starting with 428\"\nii) Other patients: \"Patient Status: Active\nAND does not have a Current Problem List/Profile item code of 84114007 OR starting with 428\"\n		</notes>\n	</heading>\n	<indicatorQuery>\n		<version>03-13-2018</version>\n		<params>\n			<!-- \n				Required parameter provider. Value options are: \n					[ an individual providerNo, or provider range ] ie: value=\"370, 1001\" \n					\"all\" ie: value=\"all\" including null.\n					\"loggedInProvider\" ie:\n				Default is \"loggedInProvider\"\n				Use this parameter in the query as '${provider}'\n				This parameter should be used for fetching patient\'s assigned to a MRP.\n				ie: WHERE demographic.provider_no = ${provider}\n			-->\n			<parameter id=\"provider\" name=\"provider_no\" value=\"loggedInProvider\" />\n			<parameter id=\"pstatus\" name=\"Patient Status\" value=\"\'AC\'\" />\n			<parameter id=\"dxcodesICD\" name=\"Dx Codes (ICD)\" value=\"428\" />\n			<parameter id=\"dxcodesSysICD\" name=\"DX CodeSystem (ICD)\" value=\"\'icd9\'\" />\n			<parameter id=\"dxcodesSnoMed\" name=\"Dx Codes (SnoMed)\" value=\"84114007\" />\n			<parameter id=\"dxcodesSysSnoMed\" name=\"Dx CodeSystem (SnoMed)\" value=\"\'snomed%\'\" />\n			<parameter id=\"excludedPatient\" name=\"Excluded Patient\" value=\"excludedPatient\" />\n		</params>\n		<query>\n			<!-- Indicator SQL Query here -->\nSELECT\n -- TOTAL NUMBER OF PATIENTS NOTED AS ACTIVE WITH CURRENT HEART FAILURE\n IF( COUNT(fin.patient) &gt; 0, SUM( IF( dxresearch_code is not NULL , 1, 0) ) , 0 ) AS \"Number Active, Current Heart Failure\",\n -- TOTAL NUMBER OF PATIENTS NOTED AS ACTIVE WITHOUT CURRENT HEART FAILURE\n IF( COUNT(fin.patient) &gt; 0, SUM( IF( dxresearch_code is NULL , 1, 0) ) , 0 ) AS \"Number Active, No Current Heart Failure\"\nFROM (\n SELECT d.demographic_no AS patient, d.patient_status AS pt_status, dxresearch_code\n FROM demographic d\n LEFT JOIN (\n  SELECT demographic_no, dxresearch_code, COUNT(dxresearch_code)\n  FROM dxresearch\n  WHERE status=\'A\' AND\n  (\n   (LEFT(dxresearch_code, 3) IN (\'428\') AND coding_system = ${dxcodesSysICD}) OR\n   (dxresearch_code IN (\'84114007\') AND coding_system LIKE ${dxcodesSysSnoMed})\n   )\n  GROUP BY demographic_no\n  ORDER BY demographic_no\n ) problems\n ON d.demographic_no = problems.demographic_no\n WHERE d.provider_no LIKE ${provider} AND d.patient_status = ${pstatus}\n AND d.demographic_no NOT IN ${excludedPatient}\n) fin\n		</query>\n	</indicatorQuery>\n	<drillDownQuery>\n		<version>03-13-2018</version>\n		<params>\n			<parameter id=\"provider\" name=\"provider_no\" value=\"loggedInProvider\" />	\n			<parameter id=\"pstatus\" name=\"Patient Status\" value=\"\'AC\'\" />\n			<parameter id=\"dxcodesICD\" name=\"Dx Codes (ICD)\" value=\"428\" />\n			<parameter id=\"dxcodesSysICD\" name=\"DX CodeSystem (ICD)\" value=\"\'icd9\'\" />\n			<parameter id=\"dxcodesSnoMed\" name=\"Dx Codes (SnoMed)\" value=\"84114007\" />\n			<parameter id=\"dxcodesSysSnoMed\" name=\"Dx CodeSystem (SnoMed)\" value=\"\'snomed%\'\" />\n			<parameter id=\"excludedPatient\" name=\"Excluded Patient\" value=\"excludedPatient\" />\n		</params>\n		<displayColumns>\n			<column id=\"demographic\" name=\"d.demographic_no\" title=\"Patient Id\" primary=\"true\" />\n			<column id=\"name\" name=\"CONCAT( d.last_name, \', \', d.first_name )\" title=\"Patient Name\" primary=\"false\" />\n			<column id=\"dob\" name=\"DATE_FORMAT( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth), \'%m-%d-%Y\' )\" title=\"Date of Birth (mm-dd-yy)\" primary=\"false\" />\n			<column id=\"age\" name=\"TIMESTAMPDIFF(YEAR, DATE( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth) ), CURDATE() )\" title=\"Age\" primary=\"false\" />\n			<column id=\"dxcode\" title=\"Dx System/Code\" name=\"CONCAT(MIN(dxr.coding_system),\'/\',MIN(dxr.dxresearch_code),IF( MIN(dxr.dxresearch_code) != MAX(dxr.dxresearch_code),CONCAT( \' &amp; \', MAX(dxr.coding_system), \'/\', MAX(dxr.dxresearch_code) ),\'\') )\" primary=\"false\" />\n		</displayColumns>\n		<exportColumns>\n			<column id=\"demographic\" name=\"d.demographic_no\" title=\"Patient Id\" primary=\"true\" />\n			<column id=\"firstName\" name=\"d.first_name\" title=\"First Name\" primary=\"false\" />\n			<column id=\"lastName\" name=\"d.last_name\" title=\"Last Name\" primary=\"false\" />\n			<column id=\"dob\" name=\"DATE_FORMAT( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth), \'%m-%d-%Y\' )\" title=\"Date of Birth (mm-dd-yy)\" primary=\"false\" />\n			<column id=\"age\" name=\"TIMESTAMPDIFF(YEAR, DATE( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth) ), CURDATE() )\" title=\"Age\" primary=\"false\" />\n			<column id=\"dxcode\" title=\"Dx System/Code\" name=\"CONCAT(MIN(dxr.coding_system),\'/\',MIN(dxr.dxresearch_code),IF( MIN(dxr.dxresearch_code) != MAX(dxr.dxresearch_code),CONCAT( \' &amp; \', MAX(dxr.coding_system), \'/\', MAX(dxr.dxresearch_code) ),\'\') )\" primary=\"false\" />\n		</exportColumns>\n		<drillDownActions>\n			<action id=\"tickler\" name=\"Tickler\"/>\n			<action id=\"demoExcl\" name=\"Patient Exclusion\"/>\n		</drillDownActions>\n		<query>\n			<!-- Drilldown SQL Query here -->\nSELECT\n*			\nFROM demographic d\nLEFT JOIN dxresearch dxr\nON d.demographic_no = dxr.demographic_no\nWHERE d.patient_status=${pstatus}\nAND\n(\n (LEFT(dxr.dxresearch_code, 3) IN (\'428\') AND dxr.coding_system = ${dxcodesSysICD}) OR\n (dxr.dxresearch_code IN (\'84114007\') AND dxr.coding_system LIKE ${dxcodesSysSnoMed})\n )\nAND dxr.status = \'A\'\nAND d.provider_no LIKE ${provider}\nAND d.demographic_no NOT IN ${excludedPatient}\nGROUP BY d.demographic_no\nORDER BY COUNT(d.demographic_no) DESC, d.demographic_no\n		</query>\n	</drillDownQuery>\n</indicatorTemplateXML>\n','','\0',0,NULL,NULL);
INSERT INTO `indicatorTemplate` (`dashboardId`, `name`, `category`, `subCategory`, `framework`, `frameworkVersion`, `definition`, `notes`, `template`, `active`, `locked`, `shared`, `metricSetName`, `metricLabel`) VALUES (@dashboardId2,'Consider Heart Failure','CDM (Click on the pie chart to see the patients included in the pie chart)','Heart Failure','DoBC CPQI PSP Panel','2017-08-02','\n			Patients with heart failure dx in 2+ bills or 2+ visit encounters within last 3 years or LVEF < 40 OR BNP > 400 OR Current Digoxin OR Metolazone medication\n		','\n		  Created for Doctors of BC\n		  i) Heart Failure dx in 2+ bills: \"Patient Status: Active\nAND does not have a Current Problem List/Profile item code of 84114007 OR starting with 428\nAND with >= 2 Bills with the ICD9 diagnosis code starting with 428 in t','<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<indicatorTemplateXML>\n	<author>OSCAR BC</author>\n	<uid></uid>\n	<heading>\n		<category>CDM (Click on the pie chart to see the patients included in the pie chart)</category>\n		<subCategory>Heart Failure</subCategory>\n		<name>Consider Heart Failure</name>\n		<definition>\n			Patients with heart failure dx in 2+ bills or 2+ visit encounters within last 3 years or LVEF &lt; 40 OR BNP &gt; 400 OR Current Digoxin OR Metolazone medication\n		</definition>\n		<framework>DoBC CPQI PSP Panel</framework>\n		<frameworkVersion>08-02-2017</frameworkVersion>\n		<notes>\n		  Created for Doctors of BC\n		  i) Heart Failure dx in 2+ bills: \"Patient Status: Active\nAND does not have a Current Problem List/Profile item code of 84114007 OR starting with 428\nAND with >= 2 Bills with the ICD9 diagnosis code starting with 428 in the last 3 years\"\n		  ii) Heart Failure dx in 2+ visits: \"Patient Status: Active\nAND does not have a Current Problem List/Profile item code of 84114007 OR starting with 428\nAND with >= 2 Visit Encounters with Snomed diagnosis code 84114007 or the ICD9 diagnosis code starting with 428 in the last 3 years\"\n		  iii) LVEF &lt; 40 OR BNP &gt; 400 OR Current Digoxin OR Metolazone medication: \"Patient Status: Active\nAND does not have a Current Problem List/Profile item code of 84114007 OR starting with 428\nAND ( has an LVEF (Left ventricular ejection fraction) observation and value &lt; 40 OR a BNP observation and value &gt; 400 OR Current Digoxin medication (ATC code C01AA05) OR Current Metolazone medication (ATC code C03BA08)\"\n		</notes>\n	</heading>\n	<indicatorQuery>\n		<version>03-13-2018</version>\n		<params>\n			<parameter id=\"provider\" name=\"Provider Number\" value=\"loggedInProvider\" />\n			<parameter id=\"active\" name=\"Active Patients\" value=\"\'AC\'\" />\n			<parameter id=\"dxcodesICD\" name=\"Dx Codes (ICD)\" value=\"428\" />\n                        <parameter id=\"dxcodesSysICD\" name=\"DX CodeSystem (ICD)\" value=\"\'icd9\'\" />\n                        <parameter id=\"dxcodesSnoMed\" name=\"Dx Codes (SnoMed)\" value=\"84114007\" />\n                        <parameter id=\"dxcodesSysSnoMed\" name=\"Dx CodeSystem (SnoMed)\" value=\"\'snomed%\'\" />\n			<parameter id=\"billingCode\" name=\"ICD9 Billing Code\" value=\"428\" />\n			<parameter id=\"numOfBills\" name=\"Number of ICD9 428 Bills\" value=\"2\" />\n			<parameter id=\"numOfVisits\" name=\"Number of Visit Encounters coded 428/ICD9\" value=\"2\" />\n                        <parameter id=\"obs1Name\" name=\"Obs 1 Name\" value=\"\'LVEF\'\" />\n                        <parameter id=\"obs1Result\" name=\"Obs 1 Result\" value=\"40\" />\n                        <parameter id=\"obs2Name\" name=\"Obs 2 Name\" value=\"\'BNP\'\" />\n                        <parameter id=\"obs2Result\" name=\"Obs 2 Result\" value=\"400\" />\n			<parameter id=\"drugs\" name=\"ATC Code/Class\" value=\"C01AA05,C03BA08\" />\n			<parameter id=\"numOfMeds\" name=\"Heart Failure Medications\" value=\"1\" />\n			<parameter id=\"excludedPatient\" name=\"Excluded Patient\" value=\"excludedPatient\" />\n		</params>\n		<range>\n			<lowerLimit id=\"billingdate\" label=\"From Date\" name=\"Date\" value=\"DATE_SUB( NOW(), INTERVAL 3 YEAR )\" />\n			<upperLimit id=\"billingdate\" label=\"Date Today\" name=\"Date\" value=\"NOW()\" />\n			<lowerLimit id=\"visitdate\" label=\"From Date\" name=\"Date\" value=\"DATE_SUB( NOW(), INTERVAL 3 YEAR )\" />\n			<upperLimit id=\"visitdate\" label=\"Date Today\" name=\"Date\" value=\"NOW()\" />\n		</range>\n		<query>\nSELECT\n  IF ( COUNT(fin.patient) &gt; 0, SUM( CASE WHEN (fin.dx IS NULL AND fin.billcount &gt;= ${numOfBills}) THEN 1 ELSE 0 END ) , 0) AS \"Number with Heart Failure dx in ${numOfBills}+ bills\",\n  IF ( COUNT(fin.patient) &gt; 0, SUM( CASE WHEN (fin.dx IS NULL AND fin.visitcount &gt;= ${numOfVisits}) THEN 1 ELSE 0 END ) , 0) AS \"Number with Heart Failure dx in ${numOfVisits}+ visits\",\n  IF ( COUNT(fin.patient) &gt; 0, SUM( CASE WHEN (fin.dx IS NULL AND ( fin.result1 &lt; ${obs1Result} OR fin.result2 &gt; ${obs2Result} OR fin.medcount &gt;= ${numOfMeds})) THEN 1 ELSE 0 END ) , 0) AS \"Number with LVEF &lt; ${obs1Result} OR BNP &gt; ${obs2Result} OR current Digoxin/Metolazone\"\nFROM ( \n  SELECT \n    d.demographic_no AS patient, \n    BILLING.billcount AS billcount,\n    VISIT.visitcount AS visitcount,\n    OBS1LAST.result AS result1,\n    OBS2LAST.result AS result2,\n    MEDS.numOfMeds AS medcount,\n    dxr.dxresearch_code AS dx,\n    dxr.coding_system AS dxs\n  FROM demographic d \n  LEFT JOIN (\n    SELECT demographic_no, dxresearch_code, coding_system, COUNT(dxresearch_code)\n    FROM dxresearch\n    WHERE status=\'A\' AND\n    (\n      (LEFT(dxresearch_code, 3) IN (\'428\') AND coding_system = ${dxcodesSysICD}) OR\n      (dxresearch_code IN (\'84114007\') AND coding_system LIKE ${dxcodesSysSnoMed})\n    )\n    GROUP BY demographic_no\n    ORDER BY demographic_no\n  ) dxr\n  ON d.demographic_no = dxr.demographic_no\n  -- Billed 428 in last 3 years\n  LEFT JOIN (\n    SELECT b.demographic_no AS demographic_no, b.dx_code1 AS dxcode1, b.dx_code2 AS dxcode2, b.dx_code3 AS dxcode3, COUNT(b.demographic_no) AS billcount\n    FROM billingmaster b\n    WHERE ( LEFT(b.dx_code1, 3) IN (\'428\') OR LEFT(b.dx_code2, 3) IN (\'428\') OR LEFT(b.dx_code3, 3) IN (\'428\') )\n    AND DATE(b.createdate) &gt; ${lowerLimit.billingdate}\n    AND b.demographic_no &gt; 0\n    GROUP BY b.demographic_no HAVING COUNT(b.demographic_no) &gt; -1\n  ) BILLING\n  ON (d.demographic_no = BILLING.demographic_no)  \n  -- Coded dx during visit encounter in last 3 years\n  LEFT JOIN(\n    SELECT ASSIGNED.demographic_no AS demographic_no, observation_date AS visitdate, uuid, COUNT(ASSIGNED.demographic_no) AS visitcount\n    FROM casemgmt_note note\n    INNER JOIN (\n      SELECT note_id, CODED.demo_no AS demographic_no FROM casemgmt_issue_notes\n      INNER JOIN (\n        SELECT ci.id AS id, ci.update_date, ci.demographic_no demo_no FROM issue i\n        INNER JOIN casemgmt_issue ci\n        ON i.issue_id = ci.issue_id\n        WHERE\n	(i.type LIKE ${dxcodesSysICD} AND LEFT(i.code, 3) IN (\'428\')) OR\n	(i.type LIKE ${dxcodesSysSnoMed} AND i.code IN (\'84114007\'))\n      ) CODED\n      ON (casemgmt_issue_notes.id = CODED.id)\n      ORDER BY CODED.demo_no\n    ) ASSIGNED\n    ON note.note_id = ASSIGNED.note_id AND\n    -- GET LAST REVISION OF NOTE\n    note.note_id= (SELECT MAX(cmn.note_id) FROM casemgmt_note cmn WHERE cmn.uuid = note.uuid) AND\n    DATE(note.observation_date) &gt; ${lowerLimit.visitdate}\n    GROUP BY demographic_no\n    ORDER BY demographic_no\n  ) VISIT\n  ON (d.demographic_no = VISIT.demographic_no)  \n  -- LVEF measurement\n  LEFT JOIN ( \n    SELECT m.demographicNo AS demographic_no, m.dataField as result, m.dateObserved AS dateObserved\n    FROM measurements m\n    INNER JOIN (\n      SELECT demographicNo, MAX(dateObserved) AS dateObserved\n      FROM measurements\n      WHERE type = ${obs1Name}\n      GROUP BY demographicNo\n    ) mlast\n    ON m.demographicNo = mlast.demographicNo\n    WHERE m.type = ${obs1Name}\n    AND m.dataField NOT LIKE \'>%\'\n    AND m.dateObserved = mlast.dateObserved\n    AND m.demographicNo &gt; 0\n    GROUP BY m.demographicNo HAVING COUNT(m.demographicNo) &gt; -1\n  ) OBS1LAST\n  ON (d.demographic_no = OBS1LAST.demographic_no) \n  LEFT JOIN ( \n    SELECT m.demographicNo AS demographic_no, m.dataField as result, m.dateObserved AS dateObserved\n    FROM measurements m\n    INNER JOIN (\n      SELECT demographicNo, MAX(dateObserved) AS dateObserved\n      FROM measurements\n      WHERE type = ${obs2Name}\n      GROUP BY demographicNo\n    ) mlast\n    ON m.demographicNo = mlast.demographicNo\n    WHERE m.type = ${obs2Name}\n    AND m.dateObserved = mlast.dateObserved\n    AND m.demographicNo &gt; 0\n    GROUP BY m.demographicNo HAVING COUNT(m.demographicNo) &gt; -1\n  ) OBS2LAST\n  ON (d.demographic_no = OBS2LAST.demographic_no)\n  LEFT JOIN (\n    SELECT dr.demographic_no, count(DISTINCT dr.atc) AS numOfMeds FROM drugs AS dr\n    WHERE dr.archived = 0 AND dr.ATC IN ${drugs}\n    AND (dr.long_term OR (dr.rx_date &lt;= NOW() AND (DATE_ADD(dr.rx_date, INTERVAL(DATEDIFF(dr.end_date,dr.rx_date)*1.0) DAY)) >= NOW()))\n    GROUP BY dr.demographic_no\n  ) MEDS\n  ON (d.demographic_no = MEDS.demographic_no) \n  WHERE d.patient_status LIKE ${active}\n  AND d.demographic_no &gt; 0\n  AND d.provider_no LIKE '${provider}'\n  AND d.demographic_no NOT IN ${excludedPatient}\n) fin\n		</query>\n	</indicatorQuery>\n	<drillDownQuery>\n		<version>03-13-2018</version>\n		<params>\n			<parameter id=\"provider\" name=\"Provider Number\" value=\"loggedInProvider\" />\n			<parameter id=\"active\" name=\"Active Patients\" value=\"\'AC\'\" />\n			<parameter id=\"dxcodesICD\" name=\"Dx Codes (ICD)\" value=\"428\" />\n                        <parameter id=\"dxcodesSysICD\" name=\"DX CodeSystem (ICD)\" value=\"\'icd9\'\" />\n                        <parameter id=\"dxcodesSnoMed\" name=\"Dx Codes (SnoMed)\" value=\"84114007\" />\n                        <parameter id=\"dxcodesSysSnoMed\" name=\"Dx CodeSystem (SnoMed)\" value=\"\'snomed%\'\" />\n			<parameter id=\"billingCode\" name=\"ICD9 Billing Code\" value=\"428\" />\n			<parameter id=\"numOfBills\" name=\"Number of ICD9 428 Bills\" value=\"2\" />\n			<parameter id=\"numOfVisits\" name=\"Number of Visit Encounters coded 428/ICD9\" value=\"2\" />\n                        <parameter id=\"obs1Name\" name=\"Obs 1 Name\" value=\"\'LVEF\'\" />\n                        <parameter id=\"obs1Result\" name=\"Obs 1 Result\" value=\"40\" />\n                        <parameter id=\"obs2Name\" name=\"Obs 2 Name\" value=\"\'BNP\'\" />\n                        <parameter id=\"obs2Result\" name=\"Obs 2 Result\" value=\"400\" />\n			<parameter id=\"drugs\" name=\"ATC Code/Class\" value=\"C01AA05,C03BA08\" />\n			<parameter id=\"numOfMeds\" name=\"Heart Failure Medications\" value=\"1\" />\n			<parameter id=\"excludedPatient\" name=\"Excluded Patient\" value=\"excludedPatient\" />\n		</params>\n		\n		<range>\n			<lowerLimit id=\"billingdate\" label=\"From Date\" name=\"Date\" value=\"DATE_SUB( NOW(), INTERVAL 3 YEAR )\" />\n			<upperLimit id=\"billingdate\" label=\"Date Today\" name=\"Date\" value=\"NOW()\" />\n			<lowerLimit id=\"visitdate\" label=\"From Date\" name=\"Date\" value=\"DATE_SUB( NOW(), INTERVAL 3 YEAR )\" />\n			<upperLimit id=\"visitdate\" label=\"Date Today\" name=\"Date\" value=\"NOW()\" />\n		</range>\n		\n		<displayColumns>\n			<column id=\"demographic\" name=\"d.demographic_no\" title=\"Patient Id\" primary=\"true\" />\n			<column id=\"name\" name=\"CONCAT( d.last_name, \', \', d.first_name )\" title=\"Patient Name\" primary=\"false\" />\n			<column id=\"dob\" name=\"DATE_FORMAT( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth), \'%m-%d-%Y\' )\" title=\"Date of Birth\" primary=\"false\" />\n			<column id=\"age\" name=\"TIMESTAMPDIFF(YEAR, DATE( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth) ), CURDATE() )\" title=\"Age\" primary=\"false\" />\n			<column id=\"billings\" name=\"IFNULL( BILLING.billcount, \'0\')\" title=\"Billings\" primary=\"false\" />\n			<column id=\"visits\" name=\"IFNULL( VISIT.visitcount, \'0\')\" title=\"Visits\" primary=\"false\" />\n			<column id=\"obs1last\" name=\"IFNULL( OBS1LAST.result, \'NA\')\" title=\"LVEF\" primary=\"false\" />\n			<column id=\"obs2last\" name=\"IFNULL( OBS2LAST.result, \'NA\')\" title=\"BNP\" primary=\"false\" />\n			<column id=\"numOfMeds\" name=\"IFNULL( MEDS.numOfMeds, \'NA\')\" title=\"Digoxin/Metolazone Medications\" primary=\"false\" />\n		</displayColumns>\n		\n		<exportColumns>\n			<column id=\"demographic\" name=\"d.demographic_no\" title=\"Patient Id\" primary=\"true\" />\n                        <column id=\"firstName\" name=\"d.first_name\" title=\"First Name\" primary=\"false\" />\n                        <column id=\"lastName\" name=\"d.last_name\" title=\"Last Name\" primary=\"false\" />\n			<column id=\"dob\" name=\"DATE_FORMAT( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth), \'%m-%d-%Y\' )\" title=\"Date of Birth\" primary=\"false\" />\n			<column id=\"age\" name=\"TIMESTAMPDIFF(YEAR, DATE( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth) ), CURDATE() )\" title=\"Age\" primary=\"false\" />\n			<column id=\"billings\" name=\"IFNULL( BILLING.billcount, \'0\')\" title=\"Billings\" primary=\"false\" />\n			<column id=\"visits\" name=\"IFNULL( VISIT.visitcount, \'0\')\" title=\"Visits\" primary=\"false\" />\n			<column id=\"obs1last\" name=\"IFNULL( OBS1LAST.result, \'NA\')\" title=\"LVEF\" primary=\"false\" />\n			<column id=\"obs2last\" name=\"IFNULL( OBS2LAST.result, \'NA\')\" title=\"BNP\" primary=\"false\" />\n			<column id=\"numOfMeds\" name=\"IFNULL( MEDS.numOfMeds, \'NA\')\" title=\"Digoxin/Metolazone Medications\" primary=\"false\" />\n		</exportColumns>\n		<drillDownActions>\n			<action id=\"tickler\" name=\"Tickler\"/>\n			<action id=\"dxUpdate\" name=\"Disease Registry Update\" value=\"428\"/>\n			<action id=\"demoExcl\" name=\"Patient Exclusion\"/>\n		</drillDownActions>\n		<query>\n  SELECT *	\n  FROM demographic d \n  LEFT JOIN (\n    SELECT demographic_no, dxresearch_code, coding_system, COUNT(dxresearch_code)\n    FROM dxresearch\n    WHERE status=\'A\' AND\n    (\n      (LEFT(dxresearch_code, 3) IN (\'428\') AND coding_system = ${dxcodesSysICD}) OR\n      (dxresearch_code IN (\'84114007\') AND coding_system LIKE ${dxcodesSysSnoMed})\n    )\n    GROUP BY demographic_no\n    ORDER BY demographic_no\n  ) dxr\n  ON d.demographic_no = dxr.demographic_no\n  -- Billed 428 in last 3 years\n  LEFT JOIN (\n    SELECT b.demographic_no AS demographic_no, b.dx_code1 AS dxcode1, b.dx_code2 AS dxcode2, b.dx_code3 AS dxcode3, COUNT(b.demographic_no) AS billcount\n    FROM billingmaster b\n    WHERE ( LEFT(b.dx_code1, 3) IN (\'428\') OR LEFT(b.dx_code2, 3) IN (\'428\') OR LEFT(b.dx_code3, 3) IN (\'428\') )\n    AND DATE(b.createdate) &gt; ${lowerLimit.billingdate}\n    AND b.demographic_no &gt; 0\n    GROUP BY b.demographic_no HAVING COUNT(b.demographic_no) &gt; -1\n  ) BILLING\n  ON (d.demographic_no = BILLING.demographic_no)  \n  -- Coded dx during visit encounter in last 3 years\n  LEFT JOIN(\n    SELECT ASSIGNED.demographic_no AS demographic_no, observation_date AS visitdate, uuid, COUNT(ASSIGNED.demographic_no) AS visitcount\n    FROM casemgmt_note note\n    INNER JOIN (\n      SELECT note_id, CODED.demo_no AS demographic_no FROM casemgmt_issue_notes\n      INNER JOIN (\n        SELECT ci.id AS id, ci.update_date, ci.demographic_no demo_no FROM issue i\n        INNER JOIN casemgmt_issue ci\n        ON i.issue_id = ci.issue_id\n        WHERE\n	(i.type LIKE ${dxcodesSysICD} AND LEFT(i.code, 3) IN (\'428\')) OR\n	(i.type LIKE ${dxcodesSysSnoMed} AND i.code IN (\'84114007\'))\n      ) CODED\n      ON (casemgmt_issue_notes.id = CODED.id)\n      ORDER BY CODED.demo_no\n    ) ASSIGNED\n    ON note.note_id = ASSIGNED.note_id AND\n    -- GET LAST REVISION OF NOTE\n    note.note_id= (SELECT MAX(cmn.note_id) FROM casemgmt_note cmn WHERE cmn.uuid = note.uuid) AND\n    DATE(note.observation_date) &gt; ${lowerLimit.visitdate}\n    GROUP BY demographic_no\n    ORDER BY demographic_no\n  ) VISIT\n  ON (d.demographic_no = VISIT.demographic_no)  \n  -- LVEF measurement\n  LEFT JOIN ( \n    SELECT m.demographicNo AS demographic_no, m.dataField as result, m.dateObserved AS dateObserved\n    FROM measurements m\n    INNER JOIN (\n      SELECT demographicNo, MAX(dateObserved) AS dateObserved\n      FROM measurements\n      WHERE type = ${obs1Name}\n      GROUP BY demographicNo\n    ) mlast\n    ON m.demographicNo = mlast.demographicNo\n    WHERE m.type = ${obs1Name}\n    AND m.dataField NOT LIKE \'>%\'\n    AND m.dateObserved = mlast.dateObserved\n    AND m.demographicNo &gt; 0\n    GROUP BY m.demographicNo HAVING COUNT(m.demographicNo) &gt; -1\n  ) OBS1LAST\n  ON (d.demographic_no = OBS1LAST.demographic_no) \n  LEFT JOIN ( \n    SELECT m.demographicNo AS demographic_no, m.dataField as result, m.dateObserved AS dateObserved\n    FROM measurements m\n    INNER JOIN (\n      SELECT demographicNo, MAX(dateObserved) AS dateObserved\n      FROM measurements\n      WHERE type = ${obs2Name}\n      GROUP BY demographicNo\n    ) mlast\n    ON m.demographicNo = mlast.demographicNo\n    WHERE m.type = ${obs2Name}\n    AND m.dateObserved = mlast.dateObserved\n    AND m.demographicNo &gt; 0\n    GROUP BY m.demographicNo HAVING COUNT(m.demographicNo) &gt; -1\n  ) OBS2LAST\n  ON (d.demographic_no = OBS2LAST.demographic_no)\n    LEFT JOIN (\n    SELECT dr.demographic_no, count(DISTINCT dr.atc) AS numOfMeds FROM drugs AS dr\n    WHERE dr.archived = 0 AND dr.ATC IN ${drugs}\n    AND (dr.long_term OR (dr.rx_date &lt;= NOW() AND (DATE_ADD(dr.rx_date, INTERVAL(DATEDIFF(dr.end_date,dr.rx_date)*1.0) DAY)) >= NOW()))\n    GROUP BY dr.demographic_no\n  ) MEDS\n  ON (d.demographic_no = MEDS.demographic_no)\n  WHERE d.patient_status LIKE ${active}\n  AND d.demographic_no &gt; 0\n  AND dxr.dxresearch_code IS NULL AND (\n    (BILLING.billcount IS NOT NULL AND BILLING.billcount &gt;= ${numOfBills}) OR\n    (VISIT.visitcount IS NOT NULL AND VISIT.visitcount &gt;= ${numOfVisits}) OR\n    ((OBS1LAST.result IS NOT NULL AND OBS1LAST.result &lt; ${obs1Result}) OR (OBS2LAST.result IS NOT NULL AND OBS2LAST.result &gt; ${obs2Result}) OR (MEDS.numOfMeds IS NOT NULL AND MEDS.numOfMeds &gt;= ${numOfMeds}) ))\n  AND d.provider_no LIKE ${provider}\n  AND d.demographic_no NOT IN ${excludedPatient}\n  ORDER BY d.demographic_no\n		</query>\n	</drillDownQuery>\n</indicatorTemplateXML>\n','','\0',0,NULL,NULL);
INSERT INTO `indicatorTemplate` (`dashboardId`, `name`, `category`, `subCategory`, `framework`, `frameworkVersion`, `definition`, `notes`, `template`, `active`, `locked`, `shared`, `metricSetName`, `metricLabel`) VALUES (@dashboardId2,'Hypertension Prevalence','CDM (Click on the pie chart to see the patients included in the pie chart)','Hypertension','DoBC CPQI PSP Panel','2017-08-02','Proportion of active patients in the practice coded with Hypertension.','\ni) HTN patients: \"Patient Status: Active\nAND has a Current Problem List/Profile item code of 38341003 OR starting with 401\"\nii) Other patients: \"Patient Status: Active\nAND does not have a Current Problem List/Profile item code of 38341003 OR starting wit','<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<indicatorTemplateXML>\n	<author>OSCAR BC</author>\n	<uid></uid>\n	<heading>\n		<category>CDM (Click on the pie chart to see the patients included in the pie chart)</category>\n		<subCategory>Hypertension</subCategory>\n		<name>Hypertension Prevalence</name>\n		<definition>Proportion of active patients in the practice coded with Hypertension.</definition>\n		<framework>DoBC CPQI PSP Panel</framework>\n		<frameworkVersion>08-02-2017</frameworkVersion>\n		<notes>\ni) HTN patients: \"Patient Status: Active\nAND has a Current Problem List/Profile item code of 38341003 OR starting with 401\"\nii) Other patients: \"Patient Status: Active\nAND does not have a Current Problem List/Profile item code of 38341003 OR starting with 401\"\n		</notes>\n	</heading>\n	<indicatorQuery>\n		<version>03-13-2018</version>\n		<params>\n			<!-- \n				Required parameter provider. Value options are: \n					[ an individual providerNo, or provider range ] ie: value=\"370, 1001\" \n					\"all\" ie: value=\"all\" including null.\n					\"loggedInProvider\" ie:\n				Default is \"loggedInProvider\"\n				Use this parameter in the query as '${provider}'\n				This parameter should be used for fetching patient\'s assigned to a MRP.\n				ie: WHERE demographic.provider_no = ${provider}\n			-->\n			<parameter id=\"provider\" name=\"provider_no\" value=\"loggedInProvider\" />\n			<parameter id=\"pstatus\" name=\"Patient Status\" value=\"\'AC\'\" />\n			<parameter id=\"dxcodesICD\" name=\"Dx Codes (ICD)\" value=\"401\" />\n			<parameter id=\"dxcodesSysICD\" name=\"DX CodeSystem (ICD)\" value=\"\'icd9\'\" />\n			<parameter id=\"dxcodesSnoMed\" name=\"Dx Codes (SnoMed)\" value=\"38341003\" />\n			<parameter id=\"dxcodesSysSnoMed\" name=\"Dx CodeSystem (SnoMed)\" value=\"\'snomed%\'\" />\n			<parameter id=\"excludedPatient\" name=\"Excluded Patient\" value=\"excludedPatient\" />\n		</params>\n		<query>\n			<!-- Indicator SQL Query here -->\nSELECT\n -- TOTAL NUMBER OF PATIENTS NOTED AS ACTIVE WITH CURRENT HYPERTENSION\n IF( COUNT(fin.patient) &gt; 0, SUM( IF( dxresearch_code is not NULL , 1, 0) ) , 0 ) AS \"Number Active, Current Hypertension\",\n -- TOTAL NUMBER OF PATIENTS NOTED AS ACTIVE WITHOUT CURRENT HYPERTENSION\n IF( COUNT(fin.patient) &gt; 0, SUM( IF( dxresearch_code is NULL , 1, 0) ) , 0 ) AS \"Number Active, No Current Hypertension\"\nFROM (\n SELECT d.demographic_no AS patient, d.patient_status AS pt_status, dxresearch_code\n FROM demographic d\n LEFT JOIN (\n  SELECT demographic_no, dxresearch_code, COUNT(dxresearch_code)\n  FROM dxresearch\n  WHERE status=\'A\' AND\n  (\n   (LEFT(dxresearch_code, 3) IN (\'401\') AND coding_system = ${dxcodesSysICD}) OR\n   (dxresearch_code IN (\'38341003\') AND coding_system LIKE ${dxcodesSysSnoMed})\n   )\n  GROUP BY demographic_no\n  ORDER BY demographic_no\n ) problems\n ON d.demographic_no = problems.demographic_no\n WHERE d.provider_no LIKE ${provider} AND d.patient_status = ${pstatus}\n AND d.demographic_no NOT IN ${excludedPatient}\n) fin\n		</query>\n	</indicatorQuery>\n	<drillDownQuery>\n		<version>03-13-2018</version>\n		<params>\n			<parameter id=\"provider\" name=\"provider_no\" value=\"loggedInProvider\" />	\n			<parameter id=\"pstatus\" name=\"Patient Status\" value=\"\'AC\'\" />\n			<parameter id=\"dxcodesICD\" name=\"Dx Codes (ICD)\" value=\"401\" />\n			<parameter id=\"dxcodesSysICD\" name=\"DX CodeSystem (ICD)\" value=\"\'icd9\'\" />\n			<parameter id=\"dxcodesSnoMed\" name=\"Dx Codes (SnoMed)\" value=\"38341003\" />\n			<parameter id=\"dxcodesSysSnoMed\" name=\"Dx CodeSystem (SnoMed)\" value=\"\'snomed%\'\" />\n			<parameter id=\"excludedPatient\" name=\"Excluded Patient\" value=\"excludedPatient\" />\n		</params>\n		<displayColumns>\n			<column id=\"demographic\" name=\"d.demographic_no\" title=\"Patient Id\" primary=\"true\" />\n			<column id=\"name\" name=\"CONCAT( d.last_name, \', \', d.first_name )\" title=\"Patient Name\" primary=\"false\" />\n			<column id=\"dob\" name=\"DATE_FORMAT( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth), \'%m-%d-%Y\' )\" title=\"Date of Birth (mm-dd-yy)\" primary=\"false\" />\n			<column id=\"age\" name=\"TIMESTAMPDIFF(YEAR, DATE( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth) ), CURDATE() )\" title=\"Age\" primary=\"false\" />\n			<column id=\"dxcode\" title=\"Dx System/Code\" name=\"CONCAT(MIN(dxr.coding_system),\'/\',MIN(dxr.dxresearch_code),IF( MIN(dxr.dxresearch_code) != MAX(dxr.dxresearch_code),CONCAT( \' &amp; \', MAX(dxr.coding_system), \'/\', MAX(dxr.dxresearch_code) ),\'\') )\" primary=\"false\" />\n		</displayColumns>\n		<exportColumns>\n			<column id=\"demographic\" name=\"d.demographic_no\" title=\"Patient Id\" primary=\"true\" />\n			<column id=\"firstName\" name=\"d.first_name\" title=\"First Name\" primary=\"false\" />\n			<column id=\"lastName\" name=\"d.last_name\" title=\"Last Name\" primary=\"false\" />\n			<column id=\"dob\" name=\"DATE_FORMAT( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth), \'%m-%d-%Y\' )\" title=\"Date of Birth (mm-dd-yy)\" primary=\"false\" />\n			<column id=\"age\" name=\"TIMESTAMPDIFF(YEAR, DATE( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth) ), CURDATE() )\" title=\"Age\" primary=\"false\" />\n			<column id=\"dxcode\" title=\"Dx System/Code\" name=\"CONCAT(MIN(dxr.coding_system),\'/\',MIN(dxr.dxresearch_code),IF( MIN(dxr.dxresearch_code) != MAX(dxr.dxresearch_code),CONCAT( \' &amp; \', MAX(dxr.coding_system), \'/\', MAX(dxr.dxresearch_code) ),\'\') )\" primary=\"false\" />\n		</exportColumns>\n		<drillDownActions>\n			<action id=\"tickler\" name=\"Tickler\"/>\n			<action id=\"demoExcl\" name=\"Patient Exclusion\"/>\n		</drillDownActions>\n		<query>\n			<!-- Drilldown SQL Query here -->\nSELECT\n*			\nFROM demographic d\nLEFT JOIN dxresearch dxr\nON d.demographic_no = dxr.demographic_no\nWHERE d.patient_status=${pstatus}\nAND\n(\n (LEFT(dxr.dxresearch_code, 3) IN (\'401\') AND dxr.coding_system = ${dxcodesSysICD}) OR\n (dxr.dxresearch_code IN (\'38341003\') AND dxr.coding_system LIKE ${dxcodesSysSnoMed})\n )\nAND dxr.status = \'A\'\nAND d.provider_no LIKE ${provider}\nAND d.demographic_no NOT IN ${excludedPatient}\nGROUP BY d.demographic_no\nORDER BY COUNT(d.demographic_no) DESC, d.demographic_no\n		</query>\n	</drillDownQuery>\n</indicatorTemplateXML>\n','','\0',0,NULL,NULL);
INSERT INTO `indicatorTemplate` (`dashboardId`, `name`, `category`, `subCategory`, `framework`, `frameworkVersion`, `definition`, `notes`, `template`, `active`, `locked`, `shared`, `metricSetName`, `metricLabel`) VALUES (@dashboardId2,'Consider Hypertension','CDM (Click on the pie chart to see the patients included in the pie chart)','Hypertension','DoBC CPQI PSP Panel','2017-08-02','\n			Patients with hypertension dx in 2+ bills or 2+ visit encounters within last 3 years or most recent BP > 140/90 or 2+ BP > 140/90 in last 2 years\n		','\n		  Created for Doctors of BC\n		  i) Hypertension dx in 2+ bills: \"Patient Status: Active\nAND does not have a Current Problem List/Profile item code of 38341003 OR starting with 401\nAND with ?2 Bills with the ICD9 diagnosis code starting with 401 in the ','<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<indicatorTemplateXML>\n	<author>OSCAR BC</author>\n	<uid></uid>\n	<heading>\n		<category>CDM (Click on the pie chart to see the patients included in the pie chart)</category>\n		<subCategory>Hypertension</subCategory>\n		<name>Consider Hypertension</name>\n		<definition>\n			Patients with hypertension dx in 2+ bills or 2+ visit encounters within last 3 years or most recent BP &gt; 140/90 or 2+ BP > 140/90 in last 2 years\n		</definition>\n		<framework>DoBC CPQI PSP Panel</framework>\n		<frameworkVersion>08-02-2017</frameworkVersion>\n		<notes>\n		  Created for Doctors of BC\n		  i) Hypertension dx in 2+ bills: \"Patient Status: Active\nAND does not have a Current Problem List/Profile item code of 38341003 OR starting with 401\nAND with ?2 Bills with the ICD9 diagnosis code starting with 401 in the last 3 years\"\n		  ii) Hypertension dx in 2+ visits: \"Patient Status: Active\nAND does not have a Current Problem List/Profile item code of 38341003 OR starting with 401\nAND with ?2 Visit Encounters with Snomed diagnosis code 38341003 or the ICD9 diagnosis code starting with 401 in the last 3 years\"\n		  iii) BP &gt; 140/90: \"Patient Status: Active\nAND does not have a Current Problem List/Profile item code of 38341003 OR starting with 401\nAND WITH  the a most recent Blood Pressure observation > 140/90 OR 2+ Blood Pressures documented >140/90 in the last 2 years\"\n		</notes>\n	</heading>\n	<indicatorQuery>\n		<version>03-13-2018</version>\n		<params>\n			<parameter id=\"provider\" name=\"Provider Number\" value=\"loggedInProvider\" />\n			<parameter id=\"active\" name=\"Active Patients\" value=\"\'AC\'\" />\n			<parameter id=\"dxcodesICD\" name=\"Dx Codes (ICD)\" value=\"401\" />\n                        <parameter id=\"dxcodesSysICD\" name=\"DX CodeSystem (ICD)\" value=\"\'icd9\'\" />\n                        <parameter id=\"dxcodesSnoMed\" name=\"Dx Codes (SnoMed)\" value=\"38341003\" />\n                        <parameter id=\"dxcodesSysSnoMed\" name=\"Dx CodeSystem (SnoMed)\" value=\"\'snomed%\'\" />\n			<parameter id=\"billingCode\" name=\"ICD9 Billing Code\" value=\"401\" />\n			<parameter id=\"numOfBills\" name=\"Number of ICD9 401 Bills\" value=\"2\" />\n			<parameter id=\"numOfVisits\" name=\"Number of Visit Encounters coded 401/ICD9\" value=\"2\" />\n			<parameter id=\"numOfBPs\" name=\"Number of BP measurements\" value=\"2\" />\n			<parameter id=\"excludedPatient\" name=\"Excluded Patient\" value=\"excludedPatient\" />\n		</params>\n		<range>\n			<lowerLimit id=\"billingdate\" label=\"From Date\" name=\"Date\" value=\"DATE_SUB( NOW(), INTERVAL 3 YEAR )\" />\n			<upperLimit id=\"billingdate\" label=\"Date Today\" name=\"Date\" value=\"NOW()\" />\n			<lowerLimit id=\"visitdate\" label=\"From Date\" name=\"Date\" value=\"DATE_SUB( NOW(), INTERVAL 3 YEAR )\" />\n                        <upperLimit id=\"visitdate\" label=\"Date Today\" name=\"Date\" value=\"NOW()\" />\n			<lowerLimit id=\"labdate\" label=\"From Date\" name=\"Date\" value=\"DATE_SUB( NOW(), INTERVAL 2 YEAR )\" />\n			<upperLimit id=\"labdate\" label=\"Date Today\" name=\"Date\" value=\"NOW()\" />\n		</range>\n		<query>\nSELECT\n  IF ( COUNT(fin.patient) &gt; 0, SUM( CASE WHEN (fin.dx IS NULL AND fin.billcount &gt;= ${numOfBills}) THEN 1 ELSE 0 END ) , 0) AS \"Number with Hypertension dx in ${numOfBills}+ bills\",\n  IF ( COUNT(fin.patient) &gt; 0, SUM( CASE WHEN (fin.dx IS NULL AND fin.visitcount &gt;= ${numOfVisits}) THEN 1 ELSE 0 END ) , 0) AS \"Number with Hypertension dx in ${numOfVisits}+ visits\",\n\n  IF ( COUNT(fin.patient) &gt; 0, SUM( CASE WHEN (fin.dx IS NULL AND ((fin.lastBP REGEXP \'1[4-9][1-9]/[0-9]{1,3}\' OR fin.lastBP REGEXP \'2[0-9][0-9]/[0-9]{1,3}\' OR\n      fin.lastBP REGEXP \'[0-9]{1,3}/9[1-9]{1}\' OR fin.lastBP REGEXP \'[0-9]{1,3}/1[0-9]{2}\') OR (fin.countBP &gt;= ${numOfBPs})) ) THEN 1 ELSE 0 END ) , 0) AS \"BP &gt; 140/90\"\nFROM ( \n  SELECT \n    d.demographic_no AS patient, \n    BILLING.billcount AS billcount,\n    VISIT.visitcount AS visitcount,\n    LAST.bp AS lastBP,\n    BP2.countBP AS countBP,\n    dxr.dxresearch_code AS dx,\n    dxr.coding_system AS dxs\n  FROM demographic d \n  LEFT JOIN (\n    SELECT demographic_no, dxresearch_code, coding_system, COUNT(dxresearch_code)\n    FROM dxresearch\n    WHERE status=\'A\' AND\n    (\n      (LEFT(dxresearch_code, 3) IN (\'401\') AND coding_system = ${dxcodesSysICD}) OR\n      (dxresearch_code IN (\'38341003\') AND coding_system LIKE ${dxcodesSysSnoMed})\n    )\n    GROUP BY demographic_no\n    ORDER BY demographic_no\n  ) dxr\n  ON d.demographic_no = dxr.demographic_no\n  -- Billed 401 in last 3 years\n  LEFT JOIN (\n    SELECT b.demographic_no AS demographic_no, b.dx_code1 AS dxcode1, b.dx_code2 AS dxcode2, b.dx_code3 AS dxcode3, COUNT(b.demographic_no) AS billcount\n    FROM billingmaster b\n    WHERE ( LEFT(b.dx_code1, 3) IN (\'401\') OR LEFT(b.dx_code2, 3) IN (\'401\') OR LEFT(b.dx_code3, 3) IN (\'401\') )\n    AND DATE(b.createdate) &gt; ${lowerLimit.billingdate}\n    AND b.demographic_no &gt; 0\n    GROUP BY b.demographic_no HAVING COUNT(b.demographic_no) &gt; -1\n  ) BILLING\n  ON (d.demographic_no = BILLING.demographic_no)  \n  -- Coded dx during visit encounter in last 3 years\n  LEFT JOIN(\n    SELECT ASSIGNED.demographic_no AS demographic_no, observation_date AS visitdate, uuid, COUNT(ASSIGNED.demographic_no) AS visitcount\n    FROM casemgmt_note note\n    INNER JOIN (\n      SELECT note_id, CODED.demo_no AS demographic_no FROM casemgmt_issue_notes\n      INNER JOIN (\n        SELECT ci.id AS id, ci.update_date, ci.demographic_no demo_no FROM issue i\n        INNER JOIN casemgmt_issue ci\n        ON i.issue_id = ci.issue_id\n        WHERE\n	(i.type LIKE ${dxcodesSysICD} AND LEFT(i.code, 3) IN (\'401\')) OR\n	(i.type LIKE ${dxcodesSysSnoMed} AND i.code IN (\'38341003\'))\n      ) CODED\n      ON (casemgmt_issue_notes.id = CODED.id)\n      ORDER BY CODED.demo_no\n    ) ASSIGNED\n    ON note.note_id = ASSIGNED.note_id AND\n    -- GET LAST REVISION OF NOTE\n    note.note_id= (SELECT MAX(cmn.note_id) FROM casemgmt_note cmn WHERE cmn.uuid = note.uuid) AND\n    DATE(note.observation_date) &gt; ${lowerLimit.visitdate}\n    GROUP BY demographic_no\n    ORDER BY demographic_no\n  ) VISIT\n  ON (d.demographic_no = VISIT.demographic_no)\n  -- Last BP measurement\n  LEFT JOIN (\n    SELECT m.demographicNo AS demographic_no, m.dataField AS bp, m.dateObserved AS dateObserved\n    FROM measurements m\n    INNER JOIN (\n      SELECT demographicNo, MAX(dateObserved) AS dateObserved\n      FROM measurements\n      WHERE type = \'BP\'\n      GROUP BY demographicNo\n    ) mlast\n    ON m.demographicNo = mlast.demographicNo\n    WHERE m.type = \'BP\'\n    AND m.dateObserved = mlast.dateObserved\n    AND m.demographicNo &gt; 0\n    GROUP BY m.demographicNo\n  ) LAST\n  ON (d.demographic_no = LAST.demographic_no) \n  -- Two of more BP > 140/90 in last two years\n  LEFT JOIN ( \n    SELECT m.demographicNo as demographic_no, m.dataField as bp, count(m.dataField) as countBP\n    FROM measurements m\n    WHERE m.type = \'BP\'\n    AND DATE(m.dateObserved) > ${lowerLimit.labdate}\n    AND (m.dataField REGEXP \'1[4-9][1-9]/[0-9]{1,3}\' OR m.dataField REGEXP \'2[0-9][0-9]/[0-9]{1,3}\' OR\n      m.dataField REGEXP \'[0-9]{1,3}/9[1-9]{1}\' OR m.datafield REGEXP \'[0-9]{1,3}/1[0-9]{2}\')\n    AND m.demographicNo > 0\n    GROUP BY m.demographicNo HAVING COUNT(m.demographicNo) > -1\n  ) BP2\n  ON (d.demographic_no = BP2.demographic_no) \n  WHERE d.patient_status LIKE ${active}\n  AND d.demographic_no &gt; 0\n  AND d.provider_no LIKE '${provider}'\n  AND d.demographic_no NOT IN ${excludedPatient}\n) fin\n		</query>\n	</indicatorQuery>\n	<drillDownQuery>\n		<version>03-13-2018</version>\n		<params>\n			<parameter id=\"provider\" name=\"Provider Number\" value=\"loggedInProvider\" />\n			<parameter id=\"active\" name=\"Active Patients\" value=\"\'AC\'\" />\n			<parameter id=\"dxcodesICD\" name=\"Dx Codes (ICD)\" value=\"401\" />\n                        <parameter id=\"dxcodesSysICD\" name=\"DX CodeSystem (ICD)\" value=\"\'icd9\'\" />\n                        <parameter id=\"dxcodesSnoMed\" name=\"Dx Codes (SnoMed)\" value=\"38341003\" />\n                        <parameter id=\"dxcodesSysSnoMed\" name=\"Dx CodeSystem (SnoMed)\" value=\"\'snomed%\'\" />\n			<parameter id=\"billingCode\" name=\"ICD9 Billing Code\" value=\"401\" />\n			<parameter id=\"numOfBills\" name=\"Number of ICD9 401 Bills\" value=\"2\" />\n			<parameter id=\"numOfVisits\" name=\"Number of Visit Encounters coded 401/ICD9\" value=\"2\" />\n			<parameter id=\"numOfBPs\" name=\"Number of BP measurements\" value=\"2\" />\n\n			<parameter id=\"excludedPatient\" name=\"Excluded Patient\" value=\"excludedPatient\" />\n		</params>\n		\n		<range>\n			<lowerLimit id=\"billingdate\" label=\"From Date\" name=\"Date\" value=\"DATE_SUB( NOW(), INTERVAL 3 YEAR )\" />\n			<upperLimit id=\"billingdate\" label=\"Date Today\" name=\"Date\" value=\"NOW()\" />\n			<lowerLimit id=\"visitdate\" label=\"From Date\" name=\"Date\" value=\"DATE_SUB( NOW(), INTERVAL 3 YEAR )\" />\n                        <upperLimit id=\"visitdate\" label=\"Date Today\" name=\"Date\" value=\"NOW()\" />\n			<lowerLimit id=\"labdate\" label=\"From Date\" name=\"Date\" value=\"DATE_SUB( NOW(), INTERVAL 2 YEAR )\" />\n			<upperLimit id=\"labdate\" label=\"Date Today\" name=\"Date\" value=\"NOW()\" />\n		</range>\n		\n		<displayColumns>\n			<column id=\"demographic\" name=\"d.demographic_no\" title=\"Patient Id\" primary=\"true\" />\n			<column id=\"name\" name=\"CONCAT( d.last_name, \', \', d.first_name )\" title=\"Patient Name\" primary=\"false\" />\n			<column id=\"dob\" name=\"DATE_FORMAT( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth), \'%m-%d-%Y\' )\" title=\"Date of Birth\" primary=\"false\" />\n			<column id=\"age\" name=\"TIMESTAMPDIFF(YEAR, DATE( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth) ), CURDATE() )\" title=\"Age\" primary=\"false\" />\n			<column id=\"billings\" name=\"IFNULL( BILLING.billcount, \'0\')\" title=\"Billings\" primary=\"false\" />\n			<column id=\"visits\" name=\"IFNULL( VISIT.visitcount, \'0\')\" title=\"Visits\" primary=\"false\" />\n			<column id=\"lastBP\" name=\"IFNULL( LAST.bp, \'NA\')\" title=\"Last BP\" primary=\"false\" />\n			<column id=\"testDateBP\" name=\"IFNULL( DATE_FORMAT( LAST.dateObserved, \'%m-%d-%Y\' ), \'NA\')\" title=\"Date (yyyy-mm-dd)\" primary=\"false\" />\n			<column id=\"numBPs\" name=\"IFNULL( BP2.countBP, \'0\')\" title=\"BPs>140/90 Last 2yrs\" primary=\"false\" />\n		</displayColumns>\n		\n		<exportColumns>\n			<column id=\"demographic\" name=\"d.demographic_no\" title=\"Patient Id\" primary=\"true\" />\n                        <column id=\"firstName\" name=\"d.first_name\" title=\"First Name\" primary=\"false\" />\n                        <column id=\"lastName\" name=\"d.last_name\" title=\"Last Name\" primary=\"false\" />\n			<column id=\"dob\" name=\"DATE_FORMAT( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth), \'%m-%d-%Y\' )\" title=\"Date of Birth\" primary=\"false\" />\n			<column id=\"age\" name=\"TIMESTAMPDIFF(YEAR, DATE( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth) ), CURDATE() )\" title=\"Age\" primary=\"false\" />\n			<column id=\"billings\" name=\"IFNULL( BILLING.billcount, \'0\')\" title=\"Billings\" primary=\"false\" />\n			<column id=\"visits\" name=\"IFNULL( VISIT.visitcount, \'0\')\" title=\"Visits\" primary=\"false\" />\n			<column id=\"lastBP\" name=\"IFNULL( LAST.bp, \'\')\" title=\"Last BP\" primary=\"false\" />\n			<column id=\"testDateBP\" name=\"IFNULL( DATE_FORMAT( LAST.dateObserved, \'%m-%d-%Y\' ), \'\')\" title=\"Date (yyyy-mm-dd)\" primary=\"false\" />\n			<column id=\"numBPs\" name=\"IFNULL( BP2.countBP, \'0\')\" title=\"BPs>140/90 Last 2yrs\" primary=\"false\" />\n		</exportColumns>\n		<drillDownActions>\n			<action id=\"tickler\" name=\"Tickler\"/>\n			<action id=\"dxUpdate\" name=\"Disease Registry Update\" value=\"401\"/>\n			<action id=\"demoExcl\" name=\"Patient Exclusion\"/>\n		</drillDownActions>\n		<query>\n  SELECT *	\n  FROM demographic d \n  LEFT JOIN (\n    SELECT demographic_no, dxresearch_code, coding_system, COUNT(dxresearch_code)\n    FROM dxresearch\n    WHERE status=\'A\' AND\n    (\n      (LEFT(dxresearch_code, 3) IN (\'401\') AND coding_system = ${dxcodesSysICD}) OR\n      (dxresearch_code IN (\'38341003\') AND coding_system LIKE ${dxcodesSysSnoMed})\n    )\n    GROUP BY demographic_no\n    ORDER BY demographic_no\n  ) dxr\n  ON d.demographic_no = dxr.demographic_no\n  -- Billed 401 in last 3 years\n  LEFT JOIN (\n    SELECT b.demographic_no AS demographic_no, b.dx_code1 AS dxcode1, b.dx_code2 AS dxcode2, b.dx_code3 AS dxcode3, COUNT(b.demographic_no) AS billcount\n    FROM billingmaster b\n    WHERE ( LEFT(b.dx_code1, 3) IN (\'401\') OR LEFT(b.dx_code2, 3) IN (\'401\') OR LEFT(b.dx_code3, 3) IN (\'401\') )\n    AND DATE(b.createdate) &gt; ${lowerLimit.billingdate}\n    AND b.demographic_no &gt; 0\n    GROUP BY b.demographic_no HAVING COUNT(b.demographic_no) &gt; -1\n  ) BILLING\n  ON (d.demographic_no = BILLING.demographic_no)  \n  -- Coded dx during visit encounter in last 3 years\n  LEFT JOIN(\n    SELECT ASSIGNED.demographic_no AS demographic_no, observation_date AS visitdate, uuid, COUNT(ASSIGNED.demographic_no) AS visitcount\n    FROM casemgmt_note note\n    INNER JOIN (\n      SELECT note_id, CODED.demo_no AS demographic_no FROM casemgmt_issue_notes\n      INNER JOIN (\n        SELECT ci.id AS id, ci.update_date, ci.demographic_no demo_no FROM issue i\n        INNER JOIN casemgmt_issue ci\n        ON i.issue_id = ci.issue_id\n        WHERE\n	(i.type LIKE ${dxcodesSysICD} AND LEFT(i.code, 3) IN (\'401\')) OR\n	(i.type LIKE ${dxcodesSysSnoMed} AND i.code IN (\'38341003\'))\n      ) CODED\n      ON (casemgmt_issue_notes.id = CODED.id)\n      ORDER BY CODED.demo_no\n    ) ASSIGNED\n    ON note.note_id = ASSIGNED.note_id AND\n    -- GET LAST REVISION OF NOTE\n    note.note_id= (SELECT MAX(cmn.note_id) FROM casemgmt_note cmn WHERE cmn.uuid = note.uuid) AND\n    DATE(note.observation_date) &gt; ${lowerLimit.visitdate}\n    GROUP BY demographic_no\n    ORDER BY demographic_no\n  ) VISIT\n  ON (d.demographic_no = VISIT.demographic_no)\n  -- Last BP measurement\n  LEFT JOIN (\n    SELECT m.demographicNo AS demographic_no, m.dataField AS bp, m.dateObserved AS dateObserved\n    FROM measurements m\n    INNER JOIN (\n      SELECT demographicNo, MAX(dateObserved) AS dateObserved\n      FROM measurements\n      WHERE type = \'BP\'\n      GROUP BY demographicNo\n    ) mlast\n    ON m.demographicNo = mlast.demographicNo\n    WHERE m.type = \'BP\'\n    AND m.dateObserved = mlast.dateObserved\n    AND m.demographicNo &gt; 0\n    GROUP BY m.demographicNo\n  ) LAST\n  ON (d.demographic_no = LAST.demographic_no) \n  -- Two of more BP > 140/90 in last two years\n  LEFT JOIN ( \n    SELECT m.demographicNo as demographic_no, m.dataField as bp, count(m.dataField) as countBP\n    FROM measurements m\n    WHERE m.type = \'BP\'\n    AND DATE(m.dateObserved) > ${lowerLimit.labdate}\n    AND (m.dataField REGEXP \'1[4-9][1-9]/[0-9]{1,3}\' OR m.dataField REGEXP \'2[0-9][0-9]/[0-9]{1,3}\' OR\n      m.dataField REGEXP \'[0-9]{1,3}/9[1-9]{1}\' OR m.datafield REGEXP \'[0-9]{1,3}/1[0-9]{2}\')\n    AND m.demographicNo > 0\n    GROUP BY m.demographicNo HAVING COUNT(m.demographicNo) > -1\n  ) BP2\n  ON (d.demographic_no = BP2.demographic_no) \n  WHERE d.patient_status LIKE ${active}\n  AND d.demographic_no &gt; 0\n\n  AND ( dxr.dxresearch_code IS NULL AND (\n  (BILLING.billcount IS NOT NULL AND BILLING.billcount &gt;= ${numOfBills}) OR\n  (VISIT.visitcount IS NOT NULL AND VISIT.visitcount &gt;= ${numOfVisits}) OR\n  (LAST.bp REGEXP \'1[4-9][1-9]/[0-9]{1,3}\' OR LAST.bp REGEXP \'2[0-9][0-9]/[0-9]{1,3}\' OR\n  LAST.bp REGEXP \'[0-9]{1,3}/9[1-9]{1}\' OR LAST.bp REGEXP \'[0-9]{1,3}/1[0-9]{2}\') OR\n  (BP2.countBP IS NOT NULL AND BP2.countBP &gt; ${numOfBPs})))\n  AND d.provider_no LIKE ${provider}\n  AND d.demographic_no NOT IN ${excludedPatient}\n  ORDER BY d.demographic_no\n		</query>\n	</drillDownQuery>\n</indicatorTemplateXML>\n','','\0',0,NULL,NULL);
INSERT INTO `indicatorTemplate` (`dashboardId`, `name`, `category`, `subCategory`, `framework`, `frameworkVersion`, `definition`, `notes`, `template`, `active`, `locked`, `shared`, `metricSetName`, `metricLabel`) VALUES (@dashboardId2,'Ischemic Cerebrovascular Disease Prevalence','CDM (Click on the pie chart to see the patients included in the pie chart)','Ischemic Cerebrovascular Disease','DoBC CPQI PSP Panel','2017-08-02','Proportion of active patients in the practice coded with Ischemic Cerebrovascular Disease.','\ni) Ischemic cerebrovascular disease patients: \"Patient Status: Active\nAND has a Current Problem List/Profile item code of 62914000, 64009001, 195236009, 266257000, 288723005    OR starting with 430, 431, 433, 434, 435, 436\"\nii) Other patients: \"Patient S','<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<indicatorTemplateXML>\n	<author>OSCAR BC</author>\n	<uid></uid>\n	<heading>\n		<category>CDM (Click on the pie chart to see the patients included in the pie chart)</category>\n		<subCategory>Ischemic Cerebrovascular Disease</subCategory>\n		<name>Ischemic Cerebrovascular Disease Prevalence</name>\n		<definition>Proportion of active patients in the practice coded with Ischemic Cerebrovascular Disease.</definition>\n		<framework>DoBC CPQI PSP Panel</framework>\n		<frameworkVersion>08-02-2017</frameworkVersion>\n		<notes>\ni) Ischemic cerebrovascular disease patients: \"Patient Status: Active\nAND has a Current Problem List/Profile item code of 62914000, 64009001, 195236009, 266257000, 288723005    OR starting with 430, 431, 433, 434, 435, 436\"\nii) Other patients: \"Patient Status: Active\nAND does not have a Current Problem List/Profile item code of 62914000, 64009001, 195236009, 266257000, 288723005    OR starting with 430, 431, 433, 434, 435, 436\"\n		</notes>\n	</heading>\n	<indicatorQuery>\n		<version>03-13-2018</version>\n		<params>\n			<!-- \n				Required parameter provider. Value options are: \n					[ an individual providerNo, or provider range ] ie: value=\"370, 1001\" \n					\"all\" ie: value=\"all\" including null.\n					\"loggedInProvider\" ie:\n				Default is \"loggedInProvider\"\n				Use this parameter in the query as '${provider}'\n				This parameter should be used for fetching patient\'s assigned to a MRP.\n				ie: WHERE demographic.provider_no = ${provider}\n			-->\n			<parameter id=\"provider\" name=\"provider_no\" value=\"loggedInProvider\" />\n			<parameter id=\"pstatus\" name=\"Patient Status\" value=\"\'AC\'\" />\n			<parameter id=\"dxcodesICD\" name=\"Dx Codes (ICD)\" value=\"430,431,433,434,435,436\" />\n			<parameter id=\"dxcodesSysICD\" name=\"DX CodeSystem (ICD)\" value=\"\'icd9\'\" />\n			<parameter id=\"dxcodesSnoMed\" name=\"Dx Codes (SnoMed)\" value=\"62914000,64009001,195236009,266257000,288723005\" />\n			<parameter id=\"dxcodesSysSnoMed\" name=\"Dx CodeSystem (SnoMed)\" value=\"\'snomed%\'\" />\n			<parameter id=\"excludedPatient\" name=\"Excluded Patient\" value=\"excludedPatient\" />\n		</params>\n		<query>\n			<!-- Indicator SQL Query here -->\nSELECT\n -- TOTAL NUMBER OF PATIENTS NOTED AS ACTIVE WITH CURRENT ISCHEMIC CEREBROVASCULAR DISEASE\n IF( COUNT(fin.patient) &gt; 0, SUM( IF( dxresearch_code is not NULL , 1, 0) ) , 0 ) AS \"Number Active, Current Ischemic Cerebrovascular Disease\",\n -- TOTAL NUMBER OF PATIENTS NOTED AS ACTIVE WITHOUT CURRENT ISCHEMIC CEREBROVASCULAR DISEASE\n IF( COUNT(fin.patient) &gt; 0, SUM( IF( dxresearch_code is NULL , 1, 0) ) , 0 ) AS \"Number Active, No Current Ischemic Cerebrovascular Disease\"\nFROM (\n SELECT d.demographic_no AS patient, d.patient_status AS pt_status, dxresearch_code\n FROM demographic d\n LEFT JOIN (\n  SELECT demographic_no, dxresearch_code, COUNT(dxresearch_code)\n  FROM dxresearch\n  WHERE status=\'A\' AND\n  (\n   (LEFT(dxresearch_code, 3) IN (\'430\', \'431\', \'433\', \'434\', \'435\', \'436\') AND coding_system = ${dxcodesSysICD}) OR\n   (dxresearch_code IN (\'62914000\', \'64009001\', \'195236009\', \'266257000\', \'288723005\') AND coding_system LIKE ${dxcodesSysSnoMed})\n   )\n  GROUP BY demographic_no\n  ORDER BY demographic_no\n ) problems\n ON d.demographic_no = problems.demographic_no\n WHERE d.provider_no LIKE ${provider} AND d.patient_status = ${pstatus}\n AND d.demographic_no NOT IN ${excludedPatient}\n) fin\n		</query>\n	</indicatorQuery>\n	<drillDownQuery>\n		<version>03-13-2018</version>\n		<params>\n			<parameter id=\"provider\" name=\"provider_no\" value=\"loggedInProvider\" />	\n			<parameter id=\"pstatus\" name=\"Patient Status\" value=\"\'AC\'\" />\n			<parameter id=\"dxcodesICD\" name=\"Dx Codes (ICD)\" value=\"430,431,433,434,435,436\" />\n			<parameter id=\"dxcodesSysICD\" name=\"DX CodeSystem (ICD)\" value=\"\'icd9\'\" />\n			<parameter id=\"dxcodesSnoMed\" name=\"Dx Codes (SnoMed)\" value=\"62914000,64009001,195236009,266257000,288723005\" />\n			<parameter id=\"dxcodesSysSnoMed\" name=\"Dx CodeSystem (SnoMed)\" value=\"\'snomed%\'\" />\n			<parameter id=\"excludedPatient\" name=\"Excluded Patient\" value=\"excludedPatient\" />\n		</params>\n		<displayColumns>\n			<column id=\"demographic\" name=\"d.demographic_no\" title=\"Patient Id\" primary=\"true\" />\n			<column id=\"name\" name=\"CONCAT( d.last_name, \', \', d.first_name )\" title=\"Patient Name\" primary=\"false\" />\n			<column id=\"dob\" name=\"DATE_FORMAT( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth), \'%m-%d-%Y\' )\" title=\"Date of Birth (mm-dd-yy)\" primary=\"false\" />\n			<column id=\"age\" name=\"TIMESTAMPDIFF(YEAR, DATE( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth) ), CURDATE() )\" title=\"Age\" primary=\"false\" />\n			<column id=\"dxcode\" title=\"Dx System/Code\" name=\"CONCAT(MIN(dxr.coding_system),\'/\',MIN(dxr.dxresearch_code),IF( MIN(dxr.dxresearch_code) != MAX(dxr.dxresearch_code),CONCAT( \' &amp; \', MAX(dxr.coding_system), \'/\', MAX(dxr.dxresearch_code) ),\'\') )\" primary=\"false\" />\n		</displayColumns>\n		<exportColumns>\n			<column id=\"demographic\" name=\"d.demographic_no\" title=\"Patient Id\" primary=\"true\" />\n			<column id=\"firstName\" name=\"d.first_name\" title=\"First Name\" primary=\"false\" />\n			<column id=\"lastName\" name=\"d.last_name\" title=\"Last Name\" primary=\"false\" />\n			<column id=\"dob\" name=\"DATE_FORMAT( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth), \'%m-%d-%Y\' )\" title=\"Date of Birth (mm-dd-yy)\" primary=\"false\" />\n			<column id=\"age\" name=\"TIMESTAMPDIFF(YEAR, DATE( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth) ), CURDATE() )\" title=\"Age\" primary=\"false\" />\n			<column id=\"dxcode\" title=\"Dx System/Code\" name=\"CONCAT(MIN(dxr.coding_system),\'/\',MIN(dxr.dxresearch_code),IF( MIN(dxr.dxresearch_code) != MAX(dxr.dxresearch_code),CONCAT( \' &amp; \', MAX(dxr.coding_system), \'/\', MAX(dxr.dxresearch_code) ),\'\') )\" primary=\"false\" />\n		</exportColumns>\n		<drillDownActions>\n			<action id=\"tickler\" name=\"Tickler\"/>\n			<action id=\"demoExcl\" name=\"Patient Exclusion\"/>\n		</drillDownActions>\n		<query>\n			<!-- Drilldown SQL Query here -->\nSELECT\n*			\nFROM demographic d\nLEFT JOIN dxresearch dxr\nON d.demographic_no = dxr.demographic_no\nWHERE d.patient_status=${pstatus}\nAND\n(\n (LEFT(dxr.dxresearch_code, 3) IN (\'430\', \'431\', \'433\', \'434\', \'435\', \'436\') AND dxr.coding_system = ${dxcodesSysICD}) OR\n (dxr.dxresearch_code IN (\'62914000\', \'64009001\', \'195236009\', \'266257000\', \'288723005\') AND dxr.coding_system LIKE ${dxcodesSysSnoMed})\n )\nAND dxr.status = \'A\'\nAND d.provider_no LIKE ${provider}\nAND d.demographic_no NOT IN ${excludedPatient}\nGROUP BY d.demographic_no\nORDER BY COUNT(d.demographic_no) DESC, d.demographic_no\n		</query>\n	</drillDownQuery>\n</indicatorTemplateXML>\n','','\0',0,NULL,NULL);
INSERT INTO `indicatorTemplate` (`dashboardId`, `name`, `category`, `subCategory`, `framework`, `frameworkVersion`, `definition`, `notes`, `template`, `active`, `locked`, `shared`, `metricSetName`, `metricLabel`) VALUES (@dashboardId2,'Consider Ischemic Cerebrovascular Disease','CDM (Click on the pie chart to see the patients included in the pie chart)','Ischemic Cerebrovascular Disease','DoBC CPQI PSP Panel','2017-08-02','\n			Patients with ischemic cerebrovascular disease dx in 2+ bills or 2+ visit encounters within last 3 years\n		','\n		  Created for Doctors of BC\n		  i) Ischemic Cerebrovascular Disease dx in 2+ bills: \"Patient Status: Active\nAND does not have a Current Problem List/Profile item code of 62914000, 64009001, 195236009, 266257000, 288723005  OR starting with 430, 431, 43','<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<indicatorTemplateXML>\n	<author>OSCAR BC</author>\n	<uid></uid>\n	<heading>\n		<category>CDM (Click on the pie chart to see the patients included in the pie chart)</category>\n		<subCategory>Ischemic Cerebrovascular Disease</subCategory>\n		<name>Consider Ischemic Cerebrovascular Disease</name>\n		<definition>\n			Patients with ischemic cerebrovascular disease dx in 2+ bills or 2+ visit encounters within last 3 years\n		</definition>\n		<framework>DoBC CPQI PSP Panel</framework>\n		<frameworkVersion>08-02-2017</frameworkVersion>\n		<notes>\n		  Created for Doctors of BC\n		  i) Ischemic Cerebrovascular Disease dx in 2+ bills: \"Patient Status: Active\nAND does not have a Current Problem List/Profile item code of 62914000, 64009001, 195236009, 266257000, 288723005  OR starting with 430, 431, 433, 434, 435 OR 436\nAND with >= 2 Bills with the ICD9 diagnosis code starting with 430, 431, 433, 434, 435 OR 436 in the last 3 years\"\n		  ii) Ischemic Cerebrovascular Disease dx in 2+ visits: \"Patient Status: Active\nAND does not have a Current Problem List/Profile item code of 62914000, 64009001, 195236009, 266257000, 288723005  OR starting with 430, 431, 433, 434, 435 OR 436\nAND with >= 2 Visit Encounters with Snomed diagnosis code 62914000, 64009001, 195236009, 266257000, 288723005 or the ICD9 diagnosis code starting with  430, 431, 433, 434, 435 OR 436 in the last 3 years\"\n		</notes>\n	</heading>\n	<indicatorQuery>\n		<version>03-13-2018</version>\n		<params>\n			<parameter id=\"provider\" name=\"Provider Number\" value=\"loggedInProvider\" />\n			<parameter id=\"active\" name=\"Active Patients\" value=\"\'AC\'\" />\n			<parameter id=\"dxcodesICD\" name=\"Dx Codes (ICD)\" value=\"430,431,433,434,435,436\" />\n                        <parameter id=\"dxcodesSysICD\" name=\"DX CodeSystem (ICD)\" value=\"\'icd9\'\" />\n                        <parameter id=\"dxcodesSnoMed\" name=\"Dx Codes (SnoMed)\" value=\"62914000,64009001,195236009,266257000,288723005\" />\n                        <parameter id=\"dxcodesSysSnoMed\" name=\"Dx CodeSystem (SnoMed)\" value=\"\'snomed%\'\" />\n			<parameter id=\"billingCode\" name=\"ICD9 Billing Code\" value=\"430,431,433,434,435,436\" />\n			<parameter id=\"numOfBills\" name=\"Number of ICD9 430,431,433,434,435,436 Bills\" value=\"2\" />\n			<parameter id=\"numOfVisits\" name=\"Number of Visit Encounters coded 430,431,433,434,435,436/ICD9\" value=\"2\" />\n			<parameter id=\"excludedPatient\" name=\"Excluded Patient\" value=\"excludedPatient\" />\n		</params>\n		<range>\n			<lowerLimit id=\"billingdate\" label=\"From Date\" name=\"Date\" value=\"DATE_SUB( NOW(), INTERVAL 3 YEAR )\" />\n			<upperLimit id=\"billingdate\" label=\"Date Today\" name=\"Date\" value=\"NOW()\" />\n			<lowerLimit id=\"visitdate\" label=\"From Date\" name=\"Date\" value=\"DATE_SUB( NOW(), INTERVAL 3 YEAR )\" />\n			<upperLimit id=\"visitdate\" label=\"Date Today\" name=\"Date\" value=\"NOW()\" />\n		</range>\n		<query>\nSELECT\n  IF ( COUNT(fin.patient) &gt; 0, SUM( CASE WHEN (fin.dx IS NULL AND fin.billcount &gt;= ${numOfBills}) THEN 1 ELSE 0 END ) , 0) AS \"Number with Ischemic Cerebrovascular Disease dx in ${numOfBills}+ bills\",\n  IF ( COUNT(fin.patient) &gt; 0, SUM( CASE WHEN (fin.dx IS NULL AND fin.visitcount &gt;= ${numOfVisits}) THEN 1 ELSE 0 END ) , 0) AS \"Number with Ischemic Cerebrovascular Disease dx in ${numOfVisits}+ visits\"\n-- Consider OBS here if needed  \nFROM ( \n  SELECT \n    d.demographic_no AS patient, \n    BILLING.billcount AS billcount,\n    VISIT.visitcount AS visitcount,\n    dxr.dxresearch_code AS dx,\n    dxr.coding_system AS dxs\n  FROM demographic d \n  LEFT JOIN (\n    SELECT demographic_no, dxresearch_code, coding_system, COUNT(dxresearch_code)\n    FROM dxresearch\n    WHERE status=\'A\' AND\n    (\n      (LEFT(dxresearch_code, 3) IN (\'430\',\'431\',\'433\',\'434\',\'435\',\'436\') AND coding_system = ${dxcodesSysICD}) OR\n      (dxresearch_code IN (\'62914000\',\'64009001\',\'195236009\',\'266257000\',\'288723005\') AND coding_system LIKE ${dxcodesSysSnoMed})\n    )\n    GROUP BY demographic_no\n    ORDER BY demographic_no\n  ) dxr\n  ON d.demographic_no = dxr.demographic_no\n  -- Billed 430,431,433,434,435,436 in last 3 years\n  LEFT JOIN (\n    SELECT b.demographic_no AS demographic_no, b.dx_code1 AS dxcode1, b.dx_code2 AS dxcode2, b.dx_code3 AS dxcode3, COUNT(b.demographic_no) AS billcount\n    FROM billingmaster b\n    WHERE ( LEFT(b.dx_code1, 3) IN (\'430\',\'431\',\'433\',\'434\',\'435\',\'436\') OR LEFT(b.dx_code2, 3) IN (\'430\',\'431\',\'433\',\'434\',\'435\',\'436\') OR LEFT(b.dx_code3, 3) IN (\'430\',\'431\',\'433\',\'434\',\'435\',\'436\') )\n    AND DATE(b.createdate) &gt; ${lowerLimit.billingdate}\n    AND b.demographic_no &gt; 0\n    GROUP BY b.demographic_no HAVING COUNT(b.demographic_no) &gt; -1\n  ) BILLING\n  ON (d.demographic_no = BILLING.demographic_no)  \n  -- Coded dx during visit encounter in last 3 years\n  LEFT JOIN(\n    SELECT ASSIGNED.demographic_no AS demographic_no, observation_date AS visitdate, uuid, COUNT(ASSIGNED.demographic_no) AS visitcount\n    FROM casemgmt_note note\n    INNER JOIN (\n      SELECT note_id, CODED.demo_no AS demographic_no FROM casemgmt_issue_notes\n      INNER JOIN (\n        SELECT ci.id AS id, ci.update_date, ci.demographic_no demo_no FROM issue i\n        INNER JOIN casemgmt_issue ci\n        ON i.issue_id = ci.issue_id\n        WHERE\n	(i.type LIKE ${dxcodesSysICD} AND LEFT(i.code, 3) IN (\'430\',\'431\',\'433\',\'434\',\'435\',\'436\')) OR\n	(i.type LIKE ${dxcodesSysSnoMed} AND i.code IN (\'62914000\',\'64009001\',\'195236009\',\'266257000\',\'288723005\'))\n      ) CODED\n      ON (casemgmt_issue_notes.id = CODED.id)\n      ORDER BY CODED.demo_no\n    ) ASSIGNED\n    ON note.note_id = ASSIGNED.note_id AND\n    -- GET LAST REVISION OF NOTE\n    note.note_id= (SELECT MAX(cmn.note_id) FROM casemgmt_note cmn WHERE cmn.uuid = note.uuid) AND\n    DATE(note.observation_date) &gt; ${lowerLimit.visitdate}\n    GROUP BY demographic_no\n    ORDER BY demographic_no\n  ) VISIT\n  ON (d.demographic_no = VISIT.demographic_no)  \n  WHERE d.patient_status LIKE ${active}\n  AND d.demographic_no &gt; 0\n  AND d.provider_no LIKE '${provider}'\n  AND d.demographic_no NOT IN ${excludedPatient}\n) fin\n		</query>\n	</indicatorQuery>\n	<drillDownQuery>\n		<version>03-13-2018</version>\n		<params>\n			<parameter id=\"provider\" name=\"Provider Number\" value=\"loggedInProvider\" />\n			<parameter id=\"active\" name=\"Active Patients\" value=\"\'AC\'\" />\n			<parameter id=\"dxcodesICD\" name=\"Dx Codes (ICD)\" value=\"430,431,433,434,435,436\" />\n                        <parameter id=\"dxcodesSysICD\" name=\"DX CodeSystem (ICD)\" value=\"\'icd9\'\" />\n                        <parameter id=\"dxcodesSnoMed\" name=\"Dx Codes (SnoMed)\" value=\"62914000,64009001,195236009,266257000,288723005\" />\n                        <parameter id=\"dxcodesSysSnoMed\" name=\"Dx CodeSystem (SnoMed)\" value=\"\'snomed%\'\" />\n			<parameter id=\"billingCode\" name=\"ICD9 Billing Code\" value=\"430,431,433,434,435,436\" />\n			<parameter id=\"numOfBills\" name=\"Number of ICD9 430,431,433,434,435,436 Bills\" value=\"2\" />\n			<parameter id=\"numOfVisits\" name=\"Number of Visit Encounters coded 430,431,433,434,435,436/ICD9\" value=\"2\" />\n			<parameter id=\"excludedPatient\" name=\"Excluded Patient\" value=\"excludedPatient\" />\n		</params>\n		\n		<range>\n			<lowerLimit id=\"billingdate\" label=\"From Date\" name=\"Date\" value=\"DATE_SUB( NOW(), INTERVAL 3 YEAR )\" />\n			<upperLimit id=\"billingdate\" label=\"Date Today\" name=\"Date\" value=\"NOW()\" />\n			<lowerLimit id=\"visitdate\" label=\"From Date\" name=\"Date\" value=\"DATE_SUB( NOW(), INTERVAL 3 YEAR )\" />\n			<upperLimit id=\"visitdate\" label=\"Date Today\" name=\"Date\" value=\"NOW()\" />\n		</range>\n		\n		<displayColumns>\n			<column id=\"demographic\" name=\"d.demographic_no\" title=\"Patient Id\" primary=\"true\" />\n			<column id=\"name\" name=\"CONCAT( d.last_name, \', \', d.first_name )\" title=\"Patient Name\" primary=\"false\" />\n			<column id=\"dob\" name=\"DATE_FORMAT( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth), \'%m-%d-%Y\' )\" title=\"Date of Birth\" primary=\"false\" />\n			<column id=\"age\" name=\"TIMESTAMPDIFF(YEAR, DATE( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth) ), CURDATE() )\" title=\"Age\" primary=\"false\" />\n			<column id=\"billings\" name=\"IFNULL( BILLING.billcount, \'0\')\" title=\"Billings\" primary=\"false\" />\n			<column id=\"visits\" name=\"IFNULL( VISIT.visitcount, \'0\')\" title=\"Visits\" primary=\"false\" />\n		</displayColumns>\n		\n		<exportColumns>\n			<column id=\"demographic\" name=\"d.demographic_no\" title=\"Patient Id\" primary=\"true\" />\n                        <column id=\"firstName\" name=\"d.first_name\" title=\"First Name\" primary=\"false\" />\n                        <column id=\"lastName\" name=\"d.last_name\" title=\"Last Name\" primary=\"false\" />\n			<column id=\"dob\" name=\"DATE_FORMAT( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth), \'%m-%d-%Y\' )\" title=\"Date of Birth\" primary=\"false\" />\n			<column id=\"age\" name=\"TIMESTAMPDIFF(YEAR, DATE( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth) ), CURDATE() )\" title=\"Age\" primary=\"false\" />\n			<column id=\"billings\" name=\"IFNULL( BILLING.billcount, \'0\')\" title=\"Billings\" primary=\"false\" />\n			<column id=\"visits\" name=\"IFNULL( VISIT.visitcount, \'0\')\" title=\"Visits\" primary=\"false\" />\n		</exportColumns>\n		<drillDownActions>\n			<action id=\"tickler\" name=\"Tickler\"/>\n			<action id=\"dxUpdate\" name=\"Disease Registry Update\" value=\"436\"/>\n			<action id=\"demoExcl\" name=\"Patient Exclusion\"/>\n		</drillDownActions>\n		<query>\n  SELECT *	\n  FROM demographic d \n  LEFT JOIN (\n    SELECT demographic_no, dxresearch_code, coding_system, COUNT(dxresearch_code)\n    FROM dxresearch\n    WHERE status=\'A\' AND\n    (\n      (LEFT(dxresearch_code, 3) IN (\'430\',\'431\',\'433\',\'434\',\'435\',\'436\') AND coding_system = ${dxcodesSysICD}) OR\n      (dxresearch_code IN (\'62914000\',\'64009001\',\'195236009\',\'266257000\',\'288723005\') AND coding_system LIKE ${dxcodesSysSnoMed})\n    )\n    GROUP BY demographic_no\n    ORDER BY demographic_no\n  ) dxr\n  ON d.demographic_no = dxr.demographic_no\n  -- Billed 430,431,433,434,435,436 in last 3 years\n  LEFT JOIN (\n    SELECT b.demographic_no AS demographic_no, b.dx_code1 AS dxcode1, b.dx_code2 AS dxcode2, b.dx_code3 AS dxcode3, COUNT(b.demographic_no) AS billcount\n    FROM billingmaster b\n    WHERE ( LEFT(b.dx_code1, 3) IN (\'430\',\'431\',\'433\',\'434\',\'435\',\'436\') OR LEFT(b.dx_code2, 3) IN (\'430\',\'431\',\'433\',\'434\',\'435\',\'436\') OR LEFT(b.dx_code3, 3) IN (\'430\',\'431\',\'433\',\'434\',\'435\',\'436\') )\n    AND DATE(b.createdate) &gt; ${lowerLimit.billingdate}\n    AND b.demographic_no &gt; 0\n    GROUP BY b.demographic_no HAVING COUNT(b.demographic_no) &gt; -1\n  ) BILLING\n  ON (d.demographic_no = BILLING.demographic_no)  \n  -- Coded dx during visit encounter in last 3 years\n  LEFT JOIN(\n    SELECT ASSIGNED.demographic_no AS demographic_no, observation_date AS visitdate, uuid, COUNT(ASSIGNED.demographic_no) AS visitcount\n    FROM casemgmt_note note\n    INNER JOIN (\n      SELECT note_id, CODED.demo_no AS demographic_no FROM casemgmt_issue_notes\n      INNER JOIN (\n        SELECT ci.id AS id, ci.update_date, ci.demographic_no demo_no FROM issue i\n        INNER JOIN casemgmt_issue ci\n        ON i.issue_id = ci.issue_id\n        WHERE\n	(i.type LIKE ${dxcodesSysICD} AND LEFT(i.code, 3) IN (\'430\',\'431\',\'433\',\'434\',\'435\',\'436\')) OR\n	(i.type LIKE ${dxcodesSysSnoMed} AND i.code IN (\'62914000\',\'64009001\',\'195236009\',\'266257000\',\'288723005\'))\n      ) CODED\n      ON (casemgmt_issue_notes.id = CODED.id)\n      ORDER BY CODED.demo_no\n    ) ASSIGNED\n    ON note.note_id = ASSIGNED.note_id AND\n    -- GET LAST REVISION OF NOTE\n    note.note_id= (SELECT MAX(cmn.note_id) FROM casemgmt_note cmn WHERE cmn.uuid = note.uuid) AND\n    DATE(note.observation_date) &gt; ${lowerLimit.visitdate}\n    GROUP BY demographic_no\n    ORDER BY demographic_no\n  ) VISIT\n  ON (d.demographic_no = VISIT.demographic_no)  \n-- Consider OBS here if needed  \n  WHERE d.patient_status LIKE ${active}\n  AND d.demographic_no &gt; 0\n  AND ( dxr.dxresearch_code IS NULL AND ((BILLING.billcount IS NOT NULL AND BILLING.billcount &gt;= ${numOfBills}) OR (VISIT.visitcount IS NOT NULL AND VISIT.visitcount &gt;= ${numOfVisits})))\n  AND d.provider_no LIKE ${provider}\n  AND d.demographic_no NOT IN ${excludedPatient}\n  ORDER BY d.demographic_no\n		</query>\n	</drillDownQuery>\n</indicatorTemplateXML>\n','','\0',0,NULL,NULL);
INSERT INTO `indicatorTemplate` (`dashboardId`, `name`, `category`, `subCategory`, `framework`, `frameworkVersion`, `definition`, `notes`, `template`, `active`, `locked`, `shared`, `metricSetName`, `metricLabel`) VALUES (@dashboardId2,'Ischemic Heart Disease Prevalence','CDM (Click on the pie chart to see the patients included in the pie chart)','Ischemic Heart Disease','DoBC CPQI PSP Panel','2017-08-02','Proportion of active patients in the practice coded with Ischemic Heart Disease.','\ni) Heart disease patients: \"Patient Status: Active\nAND has a Current Problem List/Profile item code of 414545008  OR starting with 410-414\"\nii) Other patients: \"Patient Status: Active\nAND does not have a Current Problem List/Profile item code of 41454500','<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<indicatorTemplateXML>\n	<author>OSCAR BC</author>\n	<uid></uid>\n	<heading>\n		<category>CDM (Click on the pie chart to see the patients included in the pie chart)</category>\n		<subCategory>Ischemic Heart Disease</subCategory>\n		<name>Ischemic Heart Disease Prevalence</name>\n		<definition>Proportion of active patients in the practice coded with Ischemic Heart Disease.</definition>\n		<framework>DoBC CPQI PSP Panel</framework>\n		<frameworkVersion>08-02-2017</frameworkVersion>\n		<notes>\ni) Heart disease patients: \"Patient Status: Active\nAND has a Current Problem List/Profile item code of 414545008  OR starting with 410-414\"\nii) Other patients: \"Patient Status: Active\nAND does not have a Current Problem List/Profile item code of 414545008  OR starting with 410-414\"\n		</notes>\n	</heading>\n	<indicatorQuery>\n		<version>03-13-2018</version>\n		<params>\n			<!-- \n				Required parameter provider. Value options are: \n					[ an individual providerNo, or provider range ] ie: value=\"370, 1001\" \n					\"all\" ie: value=\"all\" including null.\n					\"loggedInProvider\" ie:\n				Default is \"loggedInProvider\"\n				Use this parameter in the query as '${provider}'\n				This parameter should be used for fetching patient\'s assigned to a MRP.\n				ie: WHERE demographic.provider_no = ${provider}\n			-->\n			<parameter id=\"provider\" name=\"provider_no\" value=\"loggedInProvider\" />\n			<parameter id=\"pstatus\" name=\"Patient Status\" value=\"\'AC\'\" />\n			<parameter id=\"dxcodesICD\" name=\"Dx Codes (ICD)\" value=\"410,411,412,413,414\" />\n			<parameter id=\"dxcodesSysICD\" name=\"DX CodeSystem (ICD)\" value=\"\'icd9\'\" />\n			<parameter id=\"dxcodesSnoMed\" name=\"Dx Codes (SnoMed)\" value=\"414545008\" />\n			<parameter id=\"dxcodesSysSnoMed\" name=\"Dx CodeSystem (SnoMed)\" value=\"\'snomed%\'\" />\n			<parameter id=\"excludedPatient\" name=\"Excluded Patient\" value=\"excludedPatient\" />\n		</params>\n		<query>\n			<!-- Indicator SQL Query here -->\nSELECT\n -- TOTAL NUMBER OF PATIENTS NOTED AS ACTIVE WITH CURRENT ISCHEMIC HEART DISEASE\n IF( COUNT(fin.patient) &gt; 0, SUM( IF( dxresearch_code is not NULL , 1, 0) ) , 0 ) AS \"Number Active, Current Ischemic Heart Disease\",\n -- TOTAL NUMBER OF PATIENTS NOTED AS ACTIVE WITHOUT CURRENT ISCHEMIC HEART DISEASE\n IF( COUNT(fin.patient) &gt; 0, SUM( IF( dxresearch_code is NULL , 1, 0) ) , 0 ) AS \"Number Active, No Current Ischemic Heart Disease\"\nFROM (\n SELECT d.demographic_no AS patient, d.patient_status AS pt_status, dxresearch_code\n FROM demographic d\n LEFT JOIN (\n  SELECT demographic_no, dxresearch_code, COUNT(dxresearch_code)\n  FROM dxresearch\n  WHERE status=\'A\' AND\n  (\n   (LEFT(dxresearch_code, 3) IN (\'410\', \'411\', \'412\', \'413\', \'414\') AND coding_system = ${dxcodesSysICD}) OR\n   (dxresearch_code IN (\'414545008\') AND coding_system LIKE ${dxcodesSysSnoMed})\n   )\n  GROUP BY demographic_no\n  ORDER BY demographic_no\n ) problems\n ON d.demographic_no = problems.demographic_no\n WHERE d.provider_no LIKE ${provider} AND d.patient_status = ${pstatus}\n AND d.demographic_no NOT IN ${excludedPatient}\n) fin\n		</query>\n	</indicatorQuery>\n	<drillDownQuery>\n		<version>03-13-2018</version>\n		<params>\n			<parameter id=\"provider\" name=\"provider_no\" value=\"loggedInProvider\" />	\n			<parameter id=\"pstatus\" name=\"Patient Status\" value=\"\'AC\'\" />\n			<parameter id=\"dxcodesICD\" name=\"Dx Codes (ICD)\" value=\"410,411,412,413,414\" />\n			<parameter id=\"dxcodesSysICD\" name=\"DX CodeSystem (ICD)\" value=\"\'icd9\'\" />\n			<parameter id=\"dxcodesSnoMed\" name=\"Dx Codes (SnoMed)\" value=\"414545008\" />\n			<parameter id=\"dxcodesSysSnoMed\" name=\"Dx CodeSystem (SnoMed)\" value=\"\'snomed%\'\" />\n			<parameter id=\"excludedPatient\" name=\"Excluded Patient\" value=\"excludedPatient\" />\n		</params>\n		<displayColumns>\n			<column id=\"demographic\" name=\"d.demographic_no\" title=\"Patient Id\" primary=\"true\" />\n			<column id=\"name\" name=\"CONCAT( d.last_name, \', \', d.first_name )\" title=\"Patient Name\" primary=\"false\" />\n			<column id=\"dob\" name=\"DATE_FORMAT( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth), \'%m-%d-%Y\' )\" title=\"Date of Birth (mm-dd-yy)\" primary=\"false\" />\n			<column id=\"age\" name=\"TIMESTAMPDIFF(YEAR, DATE( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth) ), CURDATE() )\" title=\"Age\" primary=\"false\" />\n			<column id=\"dxcode\" title=\"Dx System/Code\" name=\"CONCAT(MIN(dxr.coding_system),\'/\',MIN(dxr.dxresearch_code),IF( MIN(dxr.dxresearch_code) != MAX(dxr.dxresearch_code),CONCAT( \' &amp; \', MAX(dxr.coding_system), \'/\', MAX(dxr.dxresearch_code) ),\'\') )\" primary=\"false\" />\n		</displayColumns>\n		<exportColumns>\n			<column id=\"demographic\" name=\"d.demographic_no\" title=\"Patient Id\" primary=\"true\" />\n			<column id=\"firstName\" name=\"d.first_name\" title=\"First Name\" primary=\"false\" />\n			<column id=\"lastName\" name=\"d.last_name\" title=\"Last Name\" primary=\"false\" />\n			<column id=\"dob\" name=\"DATE_FORMAT( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth), \'%m-%d-%Y\' )\" title=\"Date of Birth (mm-dd-yy)\" primary=\"false\" />\n			<column id=\"age\" name=\"TIMESTAMPDIFF(YEAR, DATE( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth) ), CURDATE() )\" title=\"Age\" primary=\"false\" />\n			<column id=\"dxcode\" title=\"Dx System/Code\" name=\"CONCAT(MIN(dxr.coding_system),\'/\',MIN(dxr.dxresearch_code),IF( MIN(dxr.dxresearch_code) != MAX(dxr.dxresearch_code),CONCAT( \' &amp; \', MAX(dxr.coding_system), \'/\', MAX(dxr.dxresearch_code) ),\'\') )\" primary=\"false\" />\n		</exportColumns>\n		<drillDownActions>\n			<action id=\"tickler\" name=\"Tickler\"/>\n			<action id=\"demoExcl\" name=\"Patient Exclusion\"/>\n		</drillDownActions>\n		<query>\n			<!-- Drilldown SQL Query here -->\nSELECT\n*			\nFROM demographic d\nLEFT JOIN dxresearch dxr\nON d.demographic_no = dxr.demographic_no\nWHERE d.patient_status=${pstatus}\nAND\n(\n (LEFT(dxr.dxresearch_code, 3) IN (\'410\', \'411\', \'412\', \'413\', \'414\') AND dxr.coding_system = ${dxcodesSysICD}) OR\n (dxr.dxresearch_code IN (\'414545008\') AND dxr.coding_system LIKE ${dxcodesSysSnoMed})\n )\nAND dxr.status = \'A\'\nAND d.provider_no LIKE ${provider}\nAND d.demographic_no NOT IN ${excludedPatient}\nGROUP BY d.demographic_no\nORDER BY COUNT(d.demographic_no) DESC, d.demographic_no\n		</query>\n	</drillDownQuery>\n</indicatorTemplateXML>\n','','\0',0,NULL,NULL);
INSERT INTO `indicatorTemplate` (`dashboardId`, `name`, `category`, `subCategory`, `framework`, `frameworkVersion`, `definition`, `notes`, `template`, `active`, `locked`, `shared`, `metricSetName`, `metricLabel`) VALUES (@dashboardId2,'Consider Ischemic Heart Disease','CDM (Click on the pie chart to see the patients included in the pie chart)','Ischemic Heart Disease','DoBC CPQI PSP Panel','2017-08-02','\n			Patients with ischemic heart disease dx in 2+ bills or 2+ visit encounters within last 3 years\n		','\n		  Created for Doctors of BC\n		  i) Ischemic Heart Disease dx in 2+ bills: \"Patient Status: Active\nAND does not have a Current Problem List/Profile item code of 414545008  OR starting with 410-414\nAND with >= 2 Bills with the ICD9 diagnosis code startin','<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<indicatorTemplateXML>\n	<author>OSCAR BC</author>\n	<uid></uid>\n	<heading>\n		<category>CDM (Click on the pie chart to see the patients included in the pie chart)</category>\n		<subCategory>Ischemic Heart Disease</subCategory>\n		<name>Consider Ischemic Heart Disease</name>\n		<definition>\n			Patients with ischemic heart disease dx in 2+ bills or 2+ visit encounters within last 3 years\n		</definition>\n		<framework>DoBC CPQI PSP Panel</framework>\n		<frameworkVersion>08-02-2017</frameworkVersion>\n		<notes>\n		  Created for Doctors of BC\n		  i) Ischemic Heart Disease dx in 2+ bills: \"Patient Status: Active\nAND does not have a Current Problem List/Profile item code of 414545008  OR starting with 410-414\nAND with >= 2 Bills with the ICD9 diagnosis code starting with 410-414 in the last 3 years\"\n		  ii) Ischemic Heart Disease dx in 2+ visits: \"Patient Status: Active\nAND does not have a Current Problem List/Profile item code of 414545008  OR starting with 410-414\nAND with >= 2 Visit Encounters with Snomed diagnosis code of 414545008 or the ICD9 diagnosis code starting with 410-414 in the last 3 years\"\n		</notes>\n	</heading>\n	<indicatorQuery>\n		<version>03-13-2018</version>\n		<params>\n			<parameter id=\"provider\" name=\"Provider Number\" value=\"loggedInProvider\" />\n			<parameter id=\"active\" name=\"Active Patients\" value=\"\'AC\'\" />\n			<parameter id=\"dxcodesICD\" name=\"Dx Codes (ICD)\" value=\"410,411,412,413,414\" />\n                        <parameter id=\"dxcodesSysICD\" name=\"DX CodeSystem (ICD)\" value=\"\'icd9\'\" />\n                        <parameter id=\"dxcodesSnoMed\" name=\"Dx Codes (SnoMed)\" value=\"414545008\" />\n                        <parameter id=\"dxcodesSysSnoMed\" name=\"Dx CodeSystem (SnoMed)\" value=\"\'snomed%\'\" />\n			<parameter id=\"billingCode\" name=\"ICD9 Billing Code\" value=\"410,411,412,413,414\" />\n			<parameter id=\"numOfBills\" name=\"Number of ICD9 410,411,412,413,414 Bills\" value=\"2\" />\n			<parameter id=\"numOfVisits\" name=\"Number of Visit Encounters coded 410,411,412,413,414/ICD9\" value=\"2\" />\n			<parameter id=\"excludedPatient\" name=\"Excluded Patient\" value=\"excludedPatient\" />\n		</params>\n		<range>\n			<lowerLimit id=\"billingdate\" label=\"From Date\" name=\"Date\" value=\"DATE_SUB( NOW(), INTERVAL 3 YEAR )\" />\n			<upperLimit id=\"billingdate\" label=\"Date Today\" name=\"Date\" value=\"NOW()\" />\n			<lowerLimit id=\"visitdate\" label=\"From Date\" name=\"Date\" value=\"DATE_SUB( NOW(), INTERVAL 3 YEAR )\" />\n			<upperLimit id=\"visitdate\" label=\"Date Today\" name=\"Date\" value=\"NOW()\" />\n		</range>\n		<query>\nSELECT\n  IF ( COUNT(fin.patient) &gt; 0, SUM( CASE WHEN (fin.dx IS NULL AND fin.billcount &gt;= ${numOfBills}) THEN 1 ELSE 0 END ) , 0) AS \"Number with Ischemic Heart Disease dx in ${numOfBills}+ bills\",\n  IF ( COUNT(fin.patient) &gt; 0, SUM( CASE WHEN (fin.dx IS NULL AND fin.visitcount &gt;= ${numOfVisits}) THEN 1 ELSE 0 END ) , 0) AS \"Number with Ischemic Heart Disease dx in ${numOfVisits}+ visits\"\n-- Consider OBS here if needed  \nFROM ( \n  SELECT \n    d.demographic_no AS patient, \n    BILLING.billcount AS billcount,\n    VISIT.visitcount AS visitcount,\n    dxr.dxresearch_code AS dx,\n    dxr.coding_system AS dxs\n  FROM demographic d \n  LEFT JOIN (\n    SELECT demographic_no, dxresearch_code, coding_system, COUNT(dxresearch_code)\n    FROM dxresearch\n    WHERE status=\'A\' AND\n    (\n      (LEFT(dxresearch_code, 3) IN (\'410\',\'411\',\'412\',\'413\',\'414\') AND coding_system = ${dxcodesSysICD}) OR\n      (dxresearch_code IN (\'414545008\') AND coding_system LIKE ${dxcodesSysSnoMed})\n    )\n    GROUP BY demographic_no\n    ORDER BY demographic_no\n  ) dxr\n  ON d.demographic_no = dxr.demographic_no\n  -- Billed 410,411,412,413,414 in last 3 years\n  LEFT JOIN (\n    SELECT b.demographic_no AS demographic_no, b.dx_code1 AS dxcode1, b.dx_code2 AS dxcode2, b.dx_code3 AS dxcode3, COUNT(b.demographic_no) AS billcount\n    FROM billingmaster b\n    WHERE ( LEFT(b.dx_code1, 3) IN (\'410\',\'411\',\'412\',\'413\',\'414\') OR LEFT(b.dx_code2, 3) IN (\'410\',\'411\',\'412\',\'413\',\'414\') OR LEFT(b.dx_code3, 3) IN (\'410\',\'411\',\'412\',\'413\',\'414\') )\n    AND DATE(b.createdate) &gt; ${lowerLimit.billingdate}\n    AND b.demographic_no &gt; 0\n    GROUP BY b.demographic_no HAVING COUNT(b.demographic_no) &gt; -1\n  ) BILLING\n  ON (d.demographic_no = BILLING.demographic_no)  \n  -- Coded dx during visit encounter in last 3 years\n  LEFT JOIN(\n    SELECT ASSIGNED.demographic_no AS demographic_no, observation_date AS visitdate, uuid, COUNT(ASSIGNED.demographic_no) AS visitcount\n    FROM casemgmt_note note\n    INNER JOIN (\n      SELECT note_id, CODED.demo_no AS demographic_no FROM casemgmt_issue_notes\n      INNER JOIN (\n        SELECT ci.id AS id, ci.update_date, ci.demographic_no demo_no FROM issue i\n        INNER JOIN casemgmt_issue ci\n        ON i.issue_id = ci.issue_id\n        WHERE\n	(i.type LIKE ${dxcodesSysICD} AND LEFT(i.code, 3) IN (\'410\',\'411\',\'412\',\'413\',\'414\')) OR\n	(i.type LIKE ${dxcodesSysSnoMed} AND i.code IN (\'414545008\'))\n      ) CODED\n      ON (casemgmt_issue_notes.id = CODED.id)\n      ORDER BY CODED.demo_no\n    ) ASSIGNED\n    ON note.note_id = ASSIGNED.note_id AND\n    -- GET LAST REVISION OF NOTE\n    note.note_id= (SELECT MAX(cmn.note_id) FROM casemgmt_note cmn WHERE cmn.uuid = note.uuid) AND\n    DATE(note.observation_date) &gt; ${lowerLimit.visitdate}\n    GROUP BY demographic_no\n    ORDER BY demographic_no\n  ) VISIT\n  ON (d.demographic_no = VISIT.demographic_no)  \n  WHERE d.patient_status LIKE ${active}\n  AND d.demographic_no &gt; 0\n  AND d.provider_no LIKE '${provider}'\n  AND d.demographic_no NOT IN ${excludedPatient}\n) fin\n		</query>\n	</indicatorQuery>\n	<drillDownQuery>\n		<version>03-13-2018</version>\n		<params>\n			<parameter id=\"provider\" name=\"Provider Number\" value=\"loggedInProvider\" />\n			<parameter id=\"active\" name=\"Active Patients\" value=\"\'AC\'\" />\n			<parameter id=\"dxcodesICD\" name=\"Dx Codes (ICD)\" value=\"410,411,412,413,414\" />\n                        <parameter id=\"dxcodesSysICD\" name=\"DX CodeSystem (ICD)\" value=\"\'icd9\'\" />\n                        <parameter id=\"dxcodesSnoMed\" name=\"Dx Codes (SnoMed)\" value=\"414545008\" />\n                        <parameter id=\"dxcodesSysSnoMed\" name=\"Dx CodeSystem (SnoMed)\" value=\"\'snomed%\'\" />\n			<parameter id=\"billingCode\" name=\"ICD9 Billing Code\" value=\"410,411,412,413,414\" />\n			<parameter id=\"numOfBills\" name=\"Number of ICD9 410,411,412,413,414 Bills\" value=\"2\" />\n			<parameter id=\"numOfVisits\" name=\"Number of Visit Encounters coded 410,411,412,413,414/ICD9\" value=\"2\" />\n			<parameter id=\"excludedPatient\" name=\"Excluded Patient\" value=\"excludedPatient\" />\n		</params>\n		\n		<range>\n			<lowerLimit id=\"billingdate\" label=\"From Date\" name=\"Date\" value=\"DATE_SUB( NOW(), INTERVAL 3 YEAR )\" />\n			<upperLimit id=\"billingdate\" label=\"Date Today\" name=\"Date\" value=\"NOW()\" />\n			<lowerLimit id=\"visitdate\" label=\"From Date\" name=\"Date\" value=\"DATE_SUB( NOW(), INTERVAL 3 YEAR )\" />\n			<upperLimit id=\"visitdate\" label=\"Date Today\" name=\"Date\" value=\"NOW()\" />\n		</range>\n		\n		<displayColumns>\n			<column id=\"demographic\" name=\"d.demographic_no\" title=\"Patient Id\" primary=\"true\" />\n			<column id=\"name\" name=\"CONCAT( d.last_name, \', \', d.first_name )\" title=\"Patient Name\" primary=\"false\" />\n			<column id=\"dob\" name=\"DATE_FORMAT( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth), \'%m-%d-%Y\' )\" title=\"Date of Birth\" primary=\"false\" />\n			<column id=\"age\" name=\"TIMESTAMPDIFF(YEAR, DATE( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth) ), CURDATE() )\" title=\"Age\" primary=\"false\" />\n			<column id=\"billings\" name=\"IFNULL( BILLING.billcount, \'0\')\" title=\"Billings\" primary=\"false\" />\n			<column id=\"visits\" name=\"IFNULL( VISIT.visitcount, \'0\')\" title=\"Visits\" primary=\"false\" />\n		</displayColumns>\n		\n		<exportColumns>\n			<column id=\"demographic\" name=\"d.demographic_no\" title=\"Patient Id\" primary=\"true\" />\n                        <column id=\"firstName\" name=\"d.first_name\" title=\"First Name\" primary=\"false\" />\n                        <column id=\"lastName\" name=\"d.last_name\" title=\"Last Name\" primary=\"false\" />\n			<column id=\"dob\" name=\"DATE_FORMAT( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth), \'%m-%d-%Y\' )\" title=\"Date of Birth\" primary=\"false\" />\n			<column id=\"age\" name=\"TIMESTAMPDIFF(YEAR, DATE( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth) ), CURDATE() )\" title=\"Age\" primary=\"false\" />\n			<column id=\"billings\" name=\"IFNULL( BILLING.billcount, \'0\')\" title=\"Billings\" primary=\"false\" />\n			<column id=\"visits\" name=\"IFNULL( VISIT.visitcount, \'0\')\" title=\"Visits\" primary=\"false\" />\n		</exportColumns>\n		<drillDownActions>\n			<action id=\"tickler\" name=\"Tickler\"/>\n			<action id=\"dxUpdate\" name=\"Disease Registry Update\" value=\"414\"/>\n			<action id=\"demoExcl\" name=\"Patient Exclusion\"/>\n		</drillDownActions>\n		<query>\n  SELECT *	\n  FROM demographic d \n  LEFT JOIN (\n    SELECT demographic_no, dxresearch_code, coding_system, COUNT(dxresearch_code)\n    FROM dxresearch\n    WHERE status=\'A\' AND\n    (\n      (LEFT(dxresearch_code, 3) IN (\'410\',\'411\',\'412\',\'413\',\'414\') AND coding_system = ${dxcodesSysICD}) OR\n      (dxresearch_code IN (\'414545008\') AND coding_system LIKE ${dxcodesSysSnoMed})\n    )\n    GROUP BY demographic_no\n    ORDER BY demographic_no\n  ) dxr\n  ON d.demographic_no = dxr.demographic_no\n  -- Billed 410,411,412,413,414 in last 3 years\n  LEFT JOIN (\n    SELECT b.demographic_no AS demographic_no, b.dx_code1 AS dxcode1, b.dx_code2 AS dxcode2, b.dx_code3 AS dxcode3, COUNT(b.demographic_no) AS billcount\n    FROM billingmaster b\n    WHERE ( LEFT(b.dx_code1, 3) IN (\'410\',\'411\',\'412\',\'413\',\'414\') OR LEFT(b.dx_code2, 3) IN (\'410\',\'411\',\'412\',\'413\',\'414\') OR LEFT(b.dx_code3, 3) IN (\'410\',\'411\',\'412\',\'413\',\'414\') )\n    AND DATE(b.createdate) &gt; ${lowerLimit.billingdate}\n    AND b.demographic_no &gt; 0\n    GROUP BY b.demographic_no HAVING COUNT(b.demographic_no) &gt; -1\n  ) BILLING\n  ON (d.demographic_no = BILLING.demographic_no)  \n  -- Coded dx during visit encounter in last 3 years\n  LEFT JOIN(\n    SELECT ASSIGNED.demographic_no AS demographic_no, observation_date AS visitdate, uuid, COUNT(ASSIGNED.demographic_no) AS visitcount\n    FROM casemgmt_note note\n    INNER JOIN (\n      SELECT note_id, CODED.demo_no AS demographic_no FROM casemgmt_issue_notes\n      INNER JOIN (\n        SELECT ci.id AS id, ci.update_date, ci.demographic_no demo_no FROM issue i\n        INNER JOIN casemgmt_issue ci\n        ON i.issue_id = ci.issue_id\n        WHERE\n	(i.type LIKE ${dxcodesSysICD} AND LEFT(i.code, 3) IN (\'410\',\'411\',\'412\',\'413\',\'414\')) OR\n	(i.type LIKE ${dxcodesSysSnoMed} AND i.code IN (\'414545008\'))\n      ) CODED\n      ON (casemgmt_issue_notes.id = CODED.id)\n      ORDER BY CODED.demo_no\n    ) ASSIGNED\n    ON note.note_id = ASSIGNED.note_id AND\n    -- GET LAST REVISION OF NOTE\n    note.note_id= (SELECT MAX(cmn.note_id) FROM casemgmt_note cmn WHERE cmn.uuid = note.uuid) AND\n    DATE(note.observation_date) &gt; ${lowerLimit.visitdate}\n    GROUP BY demographic_no\n    ORDER BY demographic_no\n  ) VISIT\n  ON (d.demographic_no = VISIT.demographic_no)  \n-- Consider OBS here if needed  \n  WHERE d.patient_status LIKE ${active}\n  AND d.demographic_no &gt; 0\n  AND ( dxr.dxresearch_code IS NULL AND ((BILLING.billcount IS NOT NULL AND BILLING.billcount &gt;= ${numOfBills}) OR (VISIT.visitcount IS NOT NULL AND VISIT.visitcount &gt;= ${numOfVisits})))\n  AND d.provider_no LIKE ${provider}\n  AND d.demographic_no NOT IN ${excludedPatient}\n  ORDER BY d.demographic_no\n		</query>\n	</drillDownQuery>\n</indicatorTemplateXML>\n','','\0',0,NULL,NULL);
INSERT INTO `indicatorTemplate` (`dashboardId`, `name`, `category`, `subCategory`, `framework`, `frameworkVersion`, `definition`, `notes`, `template`, `active`, `locked`, `shared`, `metricSetName`, `metricLabel`) VALUES (@dashboardId3,'Alcohol Dependence Syndrome Prevalence','CDM (Click on the pie chart to see the patients included in the pie chart)','Alcohol Dependence Syndrome','DoBC CPQI PSP Panel','2017-08-02','Proportion of active patients in the practice coded with Alcohol Dependence Syndrome.','\ni) Alchohol dependence patients: \"Patient Status: Active\nAND has a Current Problem List/Profile item code of 66590003 OR starting with 303\"\nii) Other patients: \"Patient Status: Active\nAND does not have a Current Problem List/Profile item code of 66590003','<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<indicatorTemplateXML>\n	<author>OSCAR BC</author>\n	<uid></uid>\n	<heading>\n		<category>CDM (Click on the pie chart to see the patients included in the pie chart)</category>\n		<subCategory>Alcohol Dependence Syndrome</subCategory>\n		<name>Alcohol Dependence Syndrome Prevalence</name>\n		<definition>Proportion of active patients in the practice coded with Alcohol Dependence Syndrome.</definition>\n		<framework>DoBC CPQI PSP Panel</framework>\n		<frameworkVersion>08-02-2017</frameworkVersion>\n		<notes>\ni) Alchohol dependence patients: \"Patient Status: Active\nAND has a Current Problem List/Profile item code of 66590003 OR starting with 303\"\nii) Other patients: \"Patient Status: Active\nAND does not have a Current Problem List/Profile item code of 66590003 OR starting with 303\"\n		</notes>\n	</heading>\n	<indicatorQuery>\n		<version>03-13-2018</version>\n		<params>\n			<!-- \n				Required parameter provider. Value options are: \n					[ an individual providerNo, or provider range ] ie: value=\"370, 1001\" \n					\"all\" ie: value=\"all\" including null.\n					\"loggedInProvider\" ie:\n				Default is \"loggedInProvider\"\n				Use this parameter in the query as '${provider}'\n				This parameter should be used for fetching patient\'s assigned to a MRP.\n				ie: WHERE demographic.provider_no = ${provider}\n			-->\n			<parameter id=\"provider\" name=\"provider_no\" value=\"loggedInProvider\" />\n			<parameter id=\"pstatus\" name=\"Patient Status\" value=\"\'AC\'\" />\n			<parameter id=\"dxcodesICD\" name=\"Dx Codes (ICD)\" value=\"303\" />\n			<parameter id=\"dxcodesSysICD\" name=\"DX CodeSystem (ICD)\" value=\"\'icd9\'\" />\n			<parameter id=\"dxcodesSnoMed\" name=\"Dx Codes (SnoMed)\" value=\"66590003\" />\n			<parameter id=\"dxcodesSysSnoMed\" name=\"Dx CodeSystem (SnoMed)\" value=\"\'snomed%\'\" />\n			<parameter id=\"excludedPatient\" name=\"Excluded Patient\" value=\"excludedPatient\" />\n		</params>\n		<query>\n			<!-- Indicator SQL Query here -->\nSELECT\n -- TOTAL NUMBER OF PATIENTS NOTED AS ACTIVE WITH CURRENT ALCOHOL DEPENDENCE SYNDROME\n IF( COUNT(fin.patient) &gt; 0, SUM( IF( dxresearch_code is not NULL , 1, 0) ) , 0 ) AS \"Number Active, Current Alcohol Dependence Syndrome\",\n -- TOTAL NUMBER OF PATIENTS NOTED AS ACTIVE WITHOUT CURRENT ALCOHOL DEPENDENCE SYNDROME\n IF( COUNT(fin.patient) &gt; 0, SUM( IF( dxresearch_code is NULL , 1, 0) ) , 0 ) AS \"Number Active, No Current Alcohol Dependence Syndrome\"\nFROM (\n SELECT d.demographic_no AS patient, d.patient_status AS pt_status, dxresearch_code\n FROM demographic d\n LEFT JOIN (\n  SELECT demographic_no, dxresearch_code, COUNT(dxresearch_code)\n  FROM dxresearch\n  WHERE status=\'A\' AND\n  (\n   (LEFT(dxresearch_code, 3) IN (\'303\') AND coding_system = ${dxcodesSysICD}) OR\n   (dxresearch_code IN (\'66590003\') AND coding_system LIKE ${dxcodesSysSnoMed})\n   )\n  GROUP BY demographic_no\n  ORDER BY demographic_no\n ) problems\n ON d.demographic_no = problems.demographic_no\n WHERE d.provider_no LIKE ${provider} AND d.patient_status = ${pstatus}\n AND d.demographic_no NOT IN ${excludedPatient}\n) fin\n		</query>\n	</indicatorQuery>\n	<drillDownQuery>\n		<version>03-13-2018</version>\n		<params>\n			<parameter id=\"provider\" name=\"provider_no\" value=\"loggedInProvider\" />	\n			<parameter id=\"pstatus\" name=\"Patient Status\" value=\"\'AC\'\" />\n			<parameter id=\"dxcodesICD\" name=\"Dx Codes (ICD)\" value=\"303\" />\n			<parameter id=\"dxcodesSysICD\" name=\"DX CodeSystem (ICD)\" value=\"\'icd9\'\" />\n			<parameter id=\"dxcodesSnoMed\" name=\"Dx Codes (SnoMed)\" value=\"66590003\" />\n			<parameter id=\"dxcodesSysSnoMed\" name=\"Dx CodeSystem (SnoMed)\" value=\"\'snomed%\'\" />\n			<parameter id=\"excludedPatient\" name=\"Excluded Patient\" value=\"excludedPatient\" />\n		</params>\n		<displayColumns>\n			<column id=\"demographic\" name=\"d.demographic_no\" title=\"Patient Id\" primary=\"true\" />\n			<column id=\"name\" name=\"CONCAT( d.last_name, \', \', d.first_name )\" title=\"Patient Name\" primary=\"false\" />\n			<column id=\"dob\" name=\"DATE_FORMAT( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth), \'%m-%d-%Y\' )\" title=\"Date of Birth (mm-dd-yy)\" primary=\"false\" />\n			<column id=\"age\" name=\"TIMESTAMPDIFF(YEAR, DATE( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth) ), CURDATE() )\" title=\"Age\" primary=\"false\" />\n			<column id=\"dxcode\" title=\"Dx System/Code\" name=\"CONCAT(MIN(dxr.coding_system),\'/\',MIN(dxr.dxresearch_code),IF( MIN(dxr.dxresearch_code) != MAX(dxr.dxresearch_code),CONCAT( \' &amp; \', MAX(dxr.coding_system), \'/\', MAX(dxr.dxresearch_code) ),\'\') )\" primary=\"false\" />\n		</displayColumns>\n		<exportColumns>\n			<column id=\"demographic\" name=\"d.demographic_no\" title=\"Patient Id\" primary=\"true\" />\n			<column id=\"firstName\" name=\"d.first_name\" title=\"First Name\" primary=\"false\" />\n			<column id=\"lastName\" name=\"d.last_name\" title=\"Last Name\" primary=\"false\" />\n			<column id=\"dob\" name=\"DATE_FORMAT( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth), \'%m-%d-%Y\' )\" title=\"Date of Birth (mm-dd-yy)\" primary=\"false\" />\n			<column id=\"age\" name=\"TIMESTAMPDIFF(YEAR, DATE( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth) ), CURDATE() )\" title=\"Age\" primary=\"false\" />\n			<column id=\"dxcode\" title=\"Dx System/Code\" name=\"CONCAT(MIN(dxr.coding_system),\'/\',MIN(dxr.dxresearch_code),IF( MIN(dxr.dxresearch_code) != MAX(dxr.dxresearch_code),CONCAT( \' &amp; \', MAX(dxr.coding_system), \'/\', MAX(dxr.dxresearch_code) ),\'\') )\" primary=\"false\" />\n		</exportColumns>\n		<drillDownActions>\n			<action id=\"tickler\" name=\"Tickler\"/>\n			<action id=\"demoExcl\" name=\"Patient Exclusion\"/>\n		</drillDownActions>\n		<query>\n			<!-- Drilldown SQL Query here -->\nSELECT\n*			\nFROM demographic d\nLEFT JOIN dxresearch dxr\nON d.demographic_no = dxr.demographic_no\nWHERE d.patient_status=${pstatus}\nAND\n(\n (LEFT(dxr.dxresearch_code, 3) IN (\'303\') AND dxr.coding_system = ${dxcodesSysICD}) OR\n (dxr.dxresearch_code IN (\'66590003\') AND dxr.coding_system LIKE ${dxcodesSysSnoMed})\n )\nAND dxr.status = \'A\'\nAND d.provider_no LIKE ${provider}\nAND d.demographic_no NOT IN ${excludedPatient}\nGROUP BY d.demographic_no\nORDER BY COUNT(d.demographic_no) DESC, d.demographic_no\n		</query>\n	</drillDownQuery>\n</indicatorTemplateXML>\n','','\0',0,NULL,NULL);
INSERT INTO `indicatorTemplate` (`dashboardId`, `name`, `category`, `subCategory`, `framework`, `frameworkVersion`, `definition`, `notes`, `template`, `active`, `locked`, `shared`, `metricSetName`, `metricLabel`) VALUES (@dashboardId3,'Consider Alcohol Dependence Syndrome','CDM (Click on the pie chart to see the patients included in the pie chart)','Alcohol Dependence Syndrome','DoBC CPQI PSP Panel','2017-08-02','\n			Patients with alcohol dependence syndrome dx in 2+ bills or 2+ visit encounters within last 3 years\n		','\n		  Created for Doctors of BC\n		  i) Alcohol Dependence Syndrome dx in 2+ bills: \"Patient Status: Active\nAND does not have a Current Problem List/Profile item code of 66590003 OR starting with 303\nAND with >=2 Bills with the ICD9 diagnosis code starting ','<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<indicatorTemplateXML>\n	<author>OSCAR BC</author>\n	<uid></uid>\n	<heading>\n		<category>CDM (Click on the pie chart to see the patients included in the pie chart)</category>\n		<subCategory>Alcohol Dependence Syndrome</subCategory>\n		<name>Consider Alcohol Dependence Syndrome</name>\n		<definition>\n			Patients with alcohol dependence syndrome dx in 2+ bills or 2+ visit encounters within last 3 years\n		</definition>\n		<framework>DoBC CPQI PSP Panel</framework>\n		<frameworkVersion>08-02-2017</frameworkVersion>\n		<notes>\n		  Created for Doctors of BC\n		  i) Alcohol Dependence Syndrome dx in 2+ bills: \"Patient Status: Active\nAND does not have a Current Problem List/Profile item code of 66590003 OR starting with 303\nAND with >=2 Bills with the ICD9 diagnosis code starting with 303 in the last 3 years\"\n		  ii) Alcohol Dependence Syndrome dx in 2+ visits: \"Patient Status: Active\nAND does not have a Current Problem List/Profile item code of 66590003 OR starting with 303\nAND with >=2 Visit Encounters with Snomed diagnosis code 66590003 or the ICD9 diagnosis code starting with 303 in the last 3 years\"\n		</notes>\n	</heading>\n	<indicatorQuery>\n		<version>03-13-2018</version>\n		<params>\n			<parameter id=\"provider\" name=\"Provider Number\" value=\"loggedInProvider\" />\n			<parameter id=\"active\" name=\"Active Patients\" value=\"\'AC\'\" />\n			<parameter id=\"dxcodesICD\" name=\"Dx Codes (ICD)\" value=\"303\" />\n                        <parameter id=\"dxcodesSysICD\" name=\"DX CodeSystem (ICD)\" value=\"\'icd9\'\" />\n                        <parameter id=\"dxcodesSnoMed\" name=\"Dx Codes (SnoMed)\" value=\"66590003\" />\n                        <parameter id=\"dxcodesSysSnoMed\" name=\"Dx CodeSystem (SnoMed)\" value=\"\'snomed%\'\" />\n			<parameter id=\"billingCode\" name=\"ICD9 Billing Code\" value=\"303\" />\n			<parameter id=\"numOfBills\" name=\"Number of ICD9 303 Bills\" value=\"2\" />\n			<parameter id=\"numOfVisits\" name=\"Number of Visit Encounters coded 303/ICD9\" value=\"2\" />\n			<parameter id=\"excludedPatient\" name=\"Excluded Patient\" value=\"excludedPatient\" />\n		</params>\n		<range>\n			<lowerLimit id=\"billingdate\" label=\"From Date\" name=\"Date\" value=\"DATE_SUB( NOW(), INTERVAL 3 YEAR )\" />\n			<upperLimit id=\"billingdate\" label=\"Date Today\" name=\"Date\" value=\"NOW()\" />\n			<lowerLimit id=\"visitdate\" label=\"From Date\" name=\"Date\" value=\"DATE_SUB( NOW(), INTERVAL 3 YEAR )\" />\n			<upperLimit id=\"visitdate\" label=\"Date Today\" name=\"Date\" value=\"NOW()\" />\n		</range>\n		<query>\nSELECT\n  IF ( COUNT(fin.patient) &gt; 0, SUM( CASE WHEN (fin.dx IS NULL AND fin.billcount &gt;= ${numOfBills}) THEN 1 ELSE 0 END ) , 0) AS \"Number with Alcohol Dependence Syndrome dx in ${numOfBills}+ bills\",\n  IF ( COUNT(fin.patient) &gt; 0, SUM( CASE WHEN (fin.dx IS NULL AND fin.visitcount &gt;= ${numOfVisits}) THEN 1 ELSE 0 END ) , 0) AS \"Number with Alcohol Dependence Syndrome dx in ${numOfVisits}+ visits\"\n-- Consider OBS here if needed  \nFROM ( \n  SELECT \n    d.demographic_no AS patient, \n    BILLING.billcount AS billcount,\n    VISIT.visitcount AS visitcount,\n    dxr.dxresearch_code AS dx,\n    dxr.coding_system AS dxs\n  FROM demographic d \n  LEFT JOIN (\n    SELECT demographic_no, dxresearch_code, coding_system, COUNT(dxresearch_code)\n    FROM dxresearch\n    WHERE status=\'A\' AND\n    (\n      (LEFT(dxresearch_code, 3) IN (\'303\') AND coding_system = ${dxcodesSysICD}) OR\n      (dxresearch_code IN (\'66590003\') AND coding_system LIKE ${dxcodesSysSnoMed})\n    )\n    GROUP BY demographic_no\n    ORDER BY demographic_no\n  ) dxr\n  ON d.demographic_no = dxr.demographic_no\n  -- Billed 303 in last 3 years\n  LEFT JOIN (\n    SELECT b.demographic_no AS demographic_no, b.dx_code1 AS dxcode1, b.dx_code2 AS dxcode2, b.dx_code3 AS dxcode3, COUNT(b.demographic_no) AS billcount\n    FROM billingmaster b\n    WHERE ( LEFT(b.dx_code1, 3) IN (\'303\') OR LEFT(b.dx_code2, 3) IN (\'303\') OR LEFT(b.dx_code3, 3) IN (\'303\') )\n    AND DATE(b.createdate) &gt; ${lowerLimit.billingdate}\n    AND b.demographic_no &gt; 0\n    GROUP BY b.demographic_no HAVING COUNT(b.demographic_no) &gt; -1\n  ) BILLING\n  ON (d.demographic_no = BILLING.demographic_no)  \n  -- Coded dx during visit encounter in last 3 years\n  LEFT JOIN(\n    SELECT ASSIGNED.demographic_no AS demographic_no, observation_date AS visitdate, uuid, COUNT(ASSIGNED.demographic_no) AS visitcount\n    FROM casemgmt_note note\n    INNER JOIN (\n      SELECT note_id, CODED.demo_no AS demographic_no FROM casemgmt_issue_notes\n      INNER JOIN (\n        SELECT ci.id AS id, ci.update_date, ci.demographic_no demo_no FROM issue i\n        INNER JOIN casemgmt_issue ci\n        ON i.issue_id = ci.issue_id\n        WHERE\n	(i.type LIKE ${dxcodesSysICD} AND LEFT(i.code, 3) IN (\'303\')) OR\n	(i.type LIKE ${dxcodesSysSnoMed} AND i.code IN (\'66590003\'))\n      ) CODED\n      ON (casemgmt_issue_notes.id = CODED.id)\n      ORDER BY CODED.demo_no\n    ) ASSIGNED\n    ON note.note_id = ASSIGNED.note_id AND\n    -- GET LAST REVISION OF NOTE\n    note.note_id= (SELECT MAX(cmn.note_id) FROM casemgmt_note cmn WHERE cmn.uuid = note.uuid) AND\n    DATE(note.observation_date) &gt; ${lowerLimit.visitdate}\n    GROUP BY demographic_no\n    ORDER BY demographic_no\n  ) VISIT\n  ON (d.demographic_no = VISIT.demographic_no)  \n  WHERE d.patient_status LIKE ${active}\n  AND d.demographic_no &gt; 0\n  AND d.provider_no LIKE '${provider}'\n  AND d.demographic_no NOT IN ${excludedPatient}\n) fin\n		</query>\n	</indicatorQuery>\n	<drillDownQuery>\n		<version>03-13-2018</version>\n		<params>\n			<parameter id=\"provider\" name=\"Provider Number\" value=\"loggedInProvider\" />\n			<parameter id=\"active\" name=\"Active Patients\" value=\"\'AC\'\" />\n			<parameter id=\"dxcodesICD\" name=\"Dx Codes (ICD)\" value=\"303\" />\n                        <parameter id=\"dxcodesSysICD\" name=\"DX CodeSystem (ICD)\" value=\"\'icd9\'\" />\n                        <parameter id=\"dxcodesSnoMed\" name=\"Dx Codes (SnoMed)\" value=\"66590003\" />\n                        <parameter id=\"dxcodesSysSnoMed\" name=\"Dx CodeSystem (SnoMed)\" value=\"\'snomed%\'\" />\n			<parameter id=\"billingCode\" name=\"ICD9 Billing Code\" value=\"303\" />\n			<parameter id=\"numOfBills\" name=\"Number of ICD9 303 Bills\" value=\"2\" />\n			<parameter id=\"numOfVisits\" name=\"Number of Visit Encounters coded 303/ICD9\" value=\"2\" />\n			<parameter id=\"excludedPatient\" name=\"Excluded Patient\" value=\"excludedPatient\" />\n		</params>\n		\n		<range>\n			<lowerLimit id=\"billingdate\" label=\"From Date\" name=\"Date\" value=\"DATE_SUB( NOW(), INTERVAL 3 YEAR )\" />\n			<upperLimit id=\"billingdate\" label=\"Date Today\" name=\"Date\" value=\"NOW()\" />\n			<lowerLimit id=\"visitdate\" label=\"From Date\" name=\"Date\" value=\"DATE_SUB( NOW(), INTERVAL 3 YEAR )\" />\n			<upperLimit id=\"visitdate\" label=\"Date Today\" name=\"Date\" value=\"NOW()\" />\n		</range>\n		\n		<displayColumns>\n			<column id=\"demographic\" name=\"d.demographic_no\" title=\"Patient Id\" primary=\"true\" />\n			<column id=\"name\" name=\"CONCAT( d.last_name, \', \', d.first_name )\" title=\"Patient Name\" primary=\"false\" />\n			<column id=\"dob\" name=\"DATE_FORMAT( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth), \'%m-%d-%Y\' )\" title=\"Date of Birth\" primary=\"false\" />\n			<column id=\"age\" name=\"TIMESTAMPDIFF(YEAR, DATE( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth) ), CURDATE() )\" title=\"Age\" primary=\"false\" />\n			<column id=\"billings\" name=\"IFNULL( BILLING.billcount, \'0\')\" title=\"Billings\" primary=\"false\" />\n			<column id=\"visits\" name=\"IFNULL( VISIT.visitcount, \'0\')\" title=\"Visits\" primary=\"false\" />\n		</displayColumns>\n		\n		<exportColumns>\n			<column id=\"demographic\" name=\"d.demographic_no\" title=\"Patient Id\" primary=\"true\" />\n                        <column id=\"firstName\" name=\"d.first_name\" title=\"First Name\" primary=\"false\" />\n                        <column id=\"lastName\" name=\"d.last_name\" title=\"Last Name\" primary=\"false\" />\n			<column id=\"dob\" name=\"DATE_FORMAT( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth), \'%m-%d-%Y\' )\" title=\"Date of Birth\" primary=\"false\" />\n			<column id=\"age\" name=\"TIMESTAMPDIFF(YEAR, DATE( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth) ), CURDATE() )\" title=\"Age\" primary=\"false\" />\n			<column id=\"billings\" name=\"IFNULL( BILLING.billcount, \'0\')\" title=\"Billings\" primary=\"false\" />\n			<column id=\"visits\" name=\"IFNULL( VISIT.visitcount, \'0\')\" title=\"Visits\" primary=\"false\" />\n		</exportColumns>\n		<drillDownActions>\n			<action id=\"tickler\" name=\"Tickler\"/>\n			<action id=\"dxUpdate\" name=\"Disease Registry Update\" value=\"303\"/>\n			<action id=\"demoExcl\" name=\"Patient Exclusion\"/>\n		</drillDownActions>\n		<query>\n  SELECT *	\n  FROM demographic d \n  LEFT JOIN (\n    SELECT demographic_no, dxresearch_code, coding_system, COUNT(dxresearch_code)\n    FROM dxresearch\n    WHERE status=\'A\' AND\n    (\n      (LEFT(dxresearch_code, 3) IN (\'303\') AND coding_system = ${dxcodesSysICD}) OR\n      (dxresearch_code IN (\'66590003\') AND coding_system LIKE ${dxcodesSysSnoMed})\n    )\n    GROUP BY demographic_no\n    ORDER BY demographic_no\n  ) dxr\n  ON d.demographic_no = dxr.demographic_no\n  -- Billed 303 in last 3 years\n  LEFT JOIN (\n    SELECT b.demographic_no AS demographic_no, b.dx_code1 AS dxcode1, b.dx_code2 AS dxcode2, b.dx_code3 AS dxcode3, COUNT(b.demographic_no) AS billcount\n    FROM billingmaster b\n    WHERE ( LEFT(b.dx_code1, 3) IN (\'303\') OR LEFT(b.dx_code2, 3) IN (\'303\') OR LEFT(b.dx_code3, 3) IN (\'303\') )\n    AND DATE(b.createdate) &gt; ${lowerLimit.billingdate}\n    AND b.demographic_no &gt; 0\n    GROUP BY b.demographic_no HAVING COUNT(b.demographic_no) &gt; -1\n  ) BILLING\n  ON (d.demographic_no = BILLING.demographic_no)  \n  -- Coded dx during visit encounter in last 3 years\n  LEFT JOIN(\n    SELECT ASSIGNED.demographic_no AS demographic_no, observation_date AS visitdate, uuid, COUNT(ASSIGNED.demographic_no) AS visitcount\n    FROM casemgmt_note note\n    INNER JOIN (\n      SELECT note_id, CODED.demo_no AS demographic_no FROM casemgmt_issue_notes\n      INNER JOIN (\n        SELECT ci.id AS id, ci.update_date, ci.demographic_no demo_no FROM issue i\n        INNER JOIN casemgmt_issue ci\n        ON i.issue_id = ci.issue_id\n        WHERE\n	(i.type LIKE ${dxcodesSysICD} AND LEFT(i.code, 3) IN (\'303\')) OR\n	(i.type LIKE ${dxcodesSysSnoMed} AND i.code IN (\'66590003\'))\n      ) CODED\n      ON (casemgmt_issue_notes.id = CODED.id)\n      ORDER BY CODED.demo_no\n    ) ASSIGNED\n    ON note.note_id = ASSIGNED.note_id AND\n    -- GET LAST REVISION OF NOTE\n    note.note_id= (SELECT MAX(cmn.note_id) FROM casemgmt_note cmn WHERE cmn.uuid = note.uuid) AND\n    DATE(note.observation_date) &gt; ${lowerLimit.visitdate}\n    GROUP BY demographic_no\n    ORDER BY demographic_no\n  ) VISIT\n  ON (d.demographic_no = VISIT.demographic_no)  \n-- Consider OBS here if needed  \n  WHERE d.patient_status LIKE ${active}\n  AND d.demographic_no &gt; 0\n  AND ( dxr.dxresearch_code IS NULL AND ((BILLING.billcount IS NOT NULL AND BILLING.billcount &gt;= ${numOfBills}) OR (VISIT.visitcount IS NOT NULL AND VISIT.visitcount &gt;= ${numOfVisits})))\n  AND d.provider_no LIKE ${provider}\n  AND d.demographic_no NOT IN ${excludedPatient}\n  ORDER BY d.demographic_no\n		</query>\n	</drillDownQuery>\n</indicatorTemplateXML>\n','','\0',0,NULL,NULL);
INSERT INTO `indicatorTemplate` (`dashboardId`, `name`, `category`, `subCategory`, `framework`, `frameworkVersion`, `definition`, `notes`, `template`, `active`, `locked`, `shared`, `metricSetName`, `metricLabel`) VALUES (@dashboardId3,'Anxiety Prevalence','CDM (Click on the pie chart to see the patients included in the pie chart)','Anxiety','DoBC CPQI PSP Panel','2017-08-02','Proportion of active patients in the practice coded with Anxiety.','\ni) Anxiety patients: \"Patient Status: Active\nAND has a Current Problem List/Profile item code of 48694002, 197480006  OR starting with 300\"\nii) Other patients: \"Patient Status: Active\nAND does not have a Current Problem List/Profile item code of 48694002','<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<indicatorTemplateXML>\n	<author>OSCAR BC</author>\n	<uid></uid>\n	<heading>\n		<category>CDM (Click on the pie chart to see the patients included in the pie chart)</category>\n		<subCategory>Anxiety</subCategory>\n		<name>Anxiety Prevalence</name>\n		<definition>Proportion of active patients in the practice coded with Anxiety.</definition>\n		<framework>DoBC CPQI PSP Panel</framework>\n		<frameworkVersion>08-02-2017</frameworkVersion>\n		<notes>\ni) Anxiety patients: \"Patient Status: Active\nAND has a Current Problem List/Profile item code of 48694002, 197480006  OR starting with 300\"\nii) Other patients: \"Patient Status: Active\nAND does not have a Current Problem List/Profile item code of 48694002, 197480006  OR starting with 300\"\n		</notes>\n	</heading>\n	<indicatorQuery>\n		<version>03-13-2018</version>\n		<params>\n			<!-- \n				Required parameter provider. Value options are: \n					[ an individual providerNo, or provider range ] ie: value=\"370, 1001\" \n					\"all\" ie: value=\"all\" including null.\n					\"loggedInProvider\" ie:\n				Default is \"loggedInProvider\"\n				Use this parameter in the query as '${provider}'\n				This parameter should be used for fetching patient\'s assigned to a MRP.\n				ie: WHERE demographic.provider_no = ${provider}\n			-->\n			<parameter id=\"provider\" name=\"provider_no\" value=\"loggedInProvider\" />\n			<parameter id=\"pstatus\" name=\"Patient Status\" value=\"\'AC\'\" />\n			<parameter id=\"dxcodesICD\" name=\"Dx Codes (ICD)\" value=\"300\" />\n			<parameter id=\"dxcodesSysICD\" name=\"DX CodeSystem (ICD)\" value=\"\'icd9\'\" />\n			<parameter id=\"dxcodesSnoMed\" name=\"Dx Codes (SnoMed)\" value=\"48694002,197480006\" />\n			<parameter id=\"dxcodesSysSnoMed\" name=\"Dx CodeSystem (SnoMed)\" value=\"\'snomed%\'\" />\n			<parameter id=\"excludedPatient\" name=\"Excluded Patient\" value=\"excludedPatient\" />\n		</params>\n		<query>\n			<!-- Indicator SQL Query here -->\nSELECT\n -- TOTAL NUMBER OF PATIENTS NOTED AS ACTIVE WITH CURRENT ANXIETY\n IF( COUNT(fin.patient) &gt; 0, SUM( IF( dxresearch_code is not NULL , 1, 0) ) , 0 ) AS \"Number Active, Current Anxiety\",\n -- TOTAL NUMBER OF PATIENTS NOTED AS ACTIVE WITHOUT CURRENT ANXIETY\n IF( COUNT(fin.patient) &gt; 0, SUM( IF( dxresearch_code is NULL , 1, 0) ) , 0 ) AS \"Number Active, No Current Anxiety\"\nFROM (\n SELECT d.demographic_no AS patient, d.patient_status AS pt_status, dxresearch_code\n FROM demographic d\n LEFT JOIN (\n  SELECT demographic_no, dxresearch_code, COUNT(dxresearch_code)\n  FROM dxresearch\n  WHERE status=\'A\' AND\n  (\n   (LEFT(dxresearch_code, 3) IN (\'300\') AND coding_system = ${dxcodesSysICD}) OR\n   (dxresearch_code IN (\'48694002\', \'197480006\') AND coding_system LIKE ${dxcodesSysSnoMed})\n   )\n  GROUP BY demographic_no\n  ORDER BY demographic_no\n ) problems\n ON d.demographic_no = problems.demographic_no\n WHERE d.provider_no LIKE ${provider} AND d.patient_status = ${pstatus}\n AND d.demographic_no NOT IN ${excludedPatient}\n) fin\n		</query>\n	</indicatorQuery>\n	<drillDownQuery>\n		<version>03-13-2018</version>\n		<params>\n			<parameter id=\"provider\" name=\"provider_no\" value=\"loggedInProvider\" />	\n			<parameter id=\"pstatus\" name=\"Patient Status\" value=\"\'AC\'\" />\n			<parameter id=\"dxcodesICD\" name=\"Dx Codes (ICD)\" value=\"300\" />\n			<parameter id=\"dxcodesSysICD\" name=\"DX CodeSystem (ICD)\" value=\"\'icd9\'\" />\n			<parameter id=\"dxcodesSnoMed\" name=\"Dx Codes (SnoMed)\" value=\"48694002,197480006\" />\n			<parameter id=\"dxcodesSysSnoMed\" name=\"Dx CodeSystem (SnoMed)\" value=\"\'snomed%\'\" />\n			<parameter id=\"excludedPatient\" name=\"Excluded Patient\" value=\"excludedPatient\" />\n		</params>\n		<displayColumns>\n			<column id=\"demographic\" name=\"d.demographic_no\" title=\"Patient Id\" primary=\"true\" />\n			<column id=\"name\" name=\"CONCAT( d.last_name, \', \', d.first_name )\" title=\"Patient Name\" primary=\"false\" />\n			<column id=\"dob\" name=\"DATE_FORMAT( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth), \'%m-%d-%Y\' )\" title=\"Date of Birth (mm-dd-yy)\" primary=\"false\" />\n			<column id=\"age\" name=\"TIMESTAMPDIFF(YEAR, DATE( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth) ), CURDATE() )\" title=\"Age\" primary=\"false\" />\n			<column id=\"dxcode\" title=\"Dx System/Code\" name=\"CONCAT(MIN(dxr.coding_system),\'/\',MIN(dxr.dxresearch_code),IF( MIN(dxr.dxresearch_code) != MAX(dxr.dxresearch_code),CONCAT( \' &amp; \', MAX(dxr.coding_system), \'/\', MAX(dxr.dxresearch_code) ),\'\') )\" primary=\"false\" />\n		</displayColumns>\n		<exportColumns>\n			<column id=\"demographic\" name=\"d.demographic_no\" title=\"Patient Id\" primary=\"true\" />\n			<column id=\"firstName\" name=\"d.first_name\" title=\"First Name\" primary=\"false\" />\n			<column id=\"lastName\" name=\"d.last_name\" title=\"Last Name\" primary=\"false\" />\n			<column id=\"dob\" name=\"DATE_FORMAT( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth), \'%m-%d-%Y\' )\" title=\"Date of Birth (mm-dd-yy)\" primary=\"false\" />\n			<column id=\"age\" name=\"TIMESTAMPDIFF(YEAR, DATE( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth) ), CURDATE() )\" title=\"Age\" primary=\"false\" />\n			<column id=\"dxcode\" title=\"Dx System/Code\" name=\"CONCAT(MIN(dxr.coding_system),\'/\',MIN(dxr.dxresearch_code),IF( MIN(dxr.dxresearch_code) != MAX(dxr.dxresearch_code),CONCAT( \' &amp; \', MAX(dxr.coding_system), \'/\', MAX(dxr.dxresearch_code) ),\'\') )\" primary=\"false\" />\n		</exportColumns>\n		<drillDownActions>\n			<action id=\"tickler\" name=\"Tickler\"/>\n			<action id=\"demoExcl\" name=\"Patient Exclusion\"/>\n		</drillDownActions>\n		<query>\n			<!-- Drilldown SQL Query here -->\nSELECT\n*			\nFROM demographic d\nLEFT JOIN dxresearch dxr\nON d.demographic_no = dxr.demographic_no\nWHERE d.patient_status=${pstatus}\nAND\n(\n (LEFT(dxr.dxresearch_code, 3) IN (\'300\') AND dxr.coding_system = ${dxcodesSysICD}) OR\n (dxr.dxresearch_code IN (\'48694002\', \'197480006\') AND dxr.coding_system LIKE ${dxcodesSysSnoMed})\n )\nAND dxr.status = \'A\'\nAND d.provider_no LIKE ${provider}\nAND d.demographic_no NOT IN ${excludedPatient}\nGROUP BY d.demographic_no\nORDER BY COUNT(d.demographic_no) DESC, d.demographic_no\n		</query>\n	</drillDownQuery>\n</indicatorTemplateXML>\n','','\0',0,NULL,NULL);
INSERT INTO `indicatorTemplate` (`dashboardId`, `name`, `category`, `subCategory`, `framework`, `frameworkVersion`, `definition`, `notes`, `template`, `active`, `locked`, `shared`, `metricSetName`, `metricLabel`) VALUES (@dashboardId3,'Consider Anxiety','CDM (Click on the pie chart to see the patients included in the pie chart)','Anxiety','DoBC CPQI PSP Panel','2017-08-02','\n			Patients with anxiety dx in 2+ bills or 2+ visit encounters within last 3 years or most recent GAD-7 score >= 10 in the past 3 months\n		','\n		  Created for Doctors of BC\n		  i) Anxiety dx in 2+ bills: \"Patient Status: Active\nAND does not have a Current Problem List/Profile item code of 48694002 OR 197480006 OR starting with 300\nAND with >=2 Bills with the ICD9 diagnosis code starting with 30','<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<indicatorTemplateXML>\n	<author>OSCAR BC</author>\n	<uid></uid>\n	<heading>\n		<category>CDM (Click on the pie chart to see the patients included in the pie chart)</category>\n		<subCategory>Anxiety</subCategory>\n		<name>Consider Anxiety</name>\n		<definition>\n			Patients with anxiety dx in 2+ bills or 2+ visit encounters within last 3 years or most recent GAD-7 score &gt;= 10 in the past 3 months\n		</definition>\n		<framework>DoBC CPQI PSP Panel</framework>\n		<frameworkVersion>08-02-2017</frameworkVersion>\n		<notes>\n		  Created for Doctors of BC\n		  i) Anxiety dx in 2+ bills: \"Patient Status: Active\nAND does not have a Current Problem List/Profile item code of 48694002 OR 197480006 OR starting with 300\nAND with >=2 Bills with the ICD9 diagnosis code starting with 300 in the last 3 years\"\n		  ii) Anxiety dx in 2+ visits: \"Patient Status: Active\nAND does not have a Current Problem List/Profile item code of 48694002 OR 197480006 OR starting with 300\nAND with >=2 Visit Encounters with Snomed diagnosis code 48694002, 197480006 or the ICD9 diagnosis code starting with 300 in the last 3 years\"\n		  iii) GAD-7 score &gt;=10: \"Patient Status: Active\nAND does not have a Current Problem List/Profile item code of 48694002 OR 48694002 OR starting with 300\nAND WITH a most recent GAD-7 score &gt;=10 in the past 3 months\"\n		</notes>\n	</heading>\n	<indicatorQuery>\n		<version>03-13-2018</version>\n		<params>\n			<parameter id=\"provider\" name=\"Provider Number\" value=\"loggedInProvider\" />\n			<parameter id=\"active\" name=\"Active Patients\" value=\"\'AC\'\" />\n			<parameter id=\"dxcodesICD\" name=\"Dx Codes (ICD)\" value=\"300\" />\n                        <parameter id=\"dxcodesSysICD\" name=\"DX CodeSystem (ICD)\" value=\"\'icd9\'\" />\n                        <parameter id=\"dxcodesSnoMed\" name=\"Dx Codes (SnoMed)\" value=\"48694002,197480006\" />\n                        <parameter id=\"dxcodesSysSnoMed\" name=\"Dx CodeSystem (SnoMed)\" value=\"\'snomed%\'\" />\n			<parameter id=\"billingCode\" name=\"ICD9 Billing Code\" value=\"300\" />\n			<parameter id=\"numOfBills\" name=\"Number of ICD9 300 Bills\" value=\"2\" />\n			<parameter id=\"numOfVisits\" name=\"Number of Visit Encounters coded 300/ICD9\" value=\"2\" />\n			<parameter id=\"obsName\" name=\"Observation Name\" value=\"\'GAD7\'\" />\n			<parameter id=\"obsResult\" name=\"Observation Result\" value=\"10\" />\n			<parameter id=\"excludedPatient\" name=\"Excluded Patient\" value=\"excludedPatient\" />\n		</params>\n		<range>\n			<lowerLimit id=\"billingdate\" label=\"From Date\" name=\"Date\" value=\"DATE_SUB( NOW(), INTERVAL 3 YEAR )\" />\n			<upperLimit id=\"billingdate\" label=\"Date Today\" name=\"Date\" value=\"NOW()\" />\n			<lowerLimit id=\"visitdate\" label=\"From Date\" name=\"Date\" value=\"DATE_SUB( NOW(), INTERVAL 3 YEAR )\" />\n			<upperLimit id=\"visitdate\" label=\"Date Today\" name=\"Date\" value=\"NOW()\" />\n			<lowerLimit id=\"obsdate\" label=\"From Date\" name=\"Date\" value=\"DATE_SUB( NOW(), INTERVAL 3 MONTH )\" />\n			<upperLimit id=\"obsdate\" label=\"Date Today\" name=\"Date\" value=\"NOW()\" />\n		</range>\n		<query>\nSELECT\n  IF ( COUNT(fin.patient) &gt; 0, SUM( CASE WHEN (fin.dx IS NULL AND fin.billcount &gt;= ${numOfBills}) THEN 1 ELSE 0 END ) , 0) AS \"Number with Anxiety dx in ${numOfBills}+ bills\",\n  IF ( COUNT(fin.patient) &gt; 0, SUM( CASE WHEN (fin.dx IS NULL AND fin.visitcount &gt;= ${numOfVisits}) THEN 1 ELSE 0 END ) , 0) AS \"Number with Anxiety dx in ${numOfVisits}+ visits\",\n  IF ( COUNT(fin.patient) &gt; 0, SUM( CASE WHEN (fin.dx IS NULL AND fin.result &gt;= ${obsResult}) THEN 1 ELSE 0 END ) , 0) AS \"Number with GAD-7 &gt;= ${obsResult}\"\nFROM ( \n  SELECT \n    d.demographic_no AS patient, \n    BILLING.billcount AS billcount,\n    VISIT.visitcount AS visitcount,\n    OBSLAST.result AS result,\n    dxr.dxresearch_code AS dx,\n    dxr.coding_system AS dxs\n  FROM demographic d \n  LEFT JOIN (\n    SELECT demographic_no, dxresearch_code, coding_system, COUNT(dxresearch_code)\n    FROM dxresearch\n    WHERE status=\'A\' AND\n    (\n      (LEFT(dxresearch_code, 3) IN (\'300\') AND coding_system = ${dxcodesSysICD}) OR\n      (dxresearch_code IN (\'48694002\',\'197480006\') AND coding_system LIKE ${dxcodesSysSnoMed})\n    )\n    GROUP BY demographic_no\n    ORDER BY demographic_no\n  ) dxr\n  ON d.demographic_no = dxr.demographic_no\n  -- Billed 300 in last 3 years\n  LEFT JOIN (\n    SELECT b.demographic_no AS demographic_no, b.dx_code1 AS dxcode1, b.dx_code2 AS dxcode2, b.dx_code3 AS dxcode3, COUNT(b.demographic_no) AS billcount\n    FROM billingmaster b\n    WHERE ( LEFT(b.dx_code1, 3) IN (\'300\') OR LEFT(b.dx_code2, 3) IN (\'300\') OR LEFT(b.dx_code3, 3) IN (\'300\') )\n    AND DATE(b.createdate) &gt; ${lowerLimit.billingdate}\n    AND b.demographic_no &gt; 0\n    GROUP BY b.demographic_no HAVING COUNT(b.demographic_no) &gt; -1\n  ) BILLING\n  ON (d.demographic_no = BILLING.demographic_no)  \n  -- Coded dx during visit encounter in last 3 years\n  LEFT JOIN(\n    SELECT ASSIGNED.demographic_no AS demographic_no, observation_date AS visitdate, uuid, COUNT(ASSIGNED.demographic_no) AS visitcount\n    FROM casemgmt_note note\n    INNER JOIN (\n      SELECT note_id, CODED.demo_no AS demographic_no FROM casemgmt_issue_notes\n      INNER JOIN (\n        SELECT ci.id AS id, ci.update_date, ci.demographic_no demo_no FROM issue i\n        INNER JOIN casemgmt_issue ci\n        ON i.issue_id = ci.issue_id\n        WHERE\n	(i.type LIKE ${dxcodesSysICD} AND LEFT(i.code, 3) IN (\'300\')) OR\n	(i.type LIKE ${dxcodesSysSnoMed} AND i.code IN (\'48694002\',\'197480006\'))\n      ) CODED\n      ON (casemgmt_issue_notes.id = CODED.id)\n      ORDER BY CODED.demo_no\n    ) ASSIGNED\n    ON note.note_id = ASSIGNED.note_id AND\n    -- GET LAST REVISION OF NOTE\n    note.note_id= (SELECT MAX(cmn.note_id) FROM casemgmt_note cmn WHERE cmn.uuid = note.uuid) AND\n    DATE(note.observation_date) &gt; ${lowerLimit.visitdate}\n    GROUP BY demographic_no\n    ORDER BY demographic_no\n  ) VISIT\n  ON (d.demographic_no = VISIT.demographic_no)\n  -- Consider Observation\n  LEFT JOIN ( \n    SELECT m.demographicNo AS demographic_no, m.dataField AS result, m.dateObserved AS dateObserved\n    FROM measurements m\n    INNER JOIN (\n      SELECT demographicNo, MAX(dateObserved) AS dateObserved\n      FROM measurements\n      WHERE type = ${obsName}\n      GROUP BY demographicNo\n    ) mlast\n    ON m.demographicNo = mlast.demographicNo\n    WHERE m.type = ${obsName}\n    AND m.dateObserved = mlast.dateObserved\n    AND (DATE(m.dateObserved) &gt; ${lowerLimit.obsdate})\n    AND m.demographicNo &gt; 0\n    GROUP BY m.demographicNo HAVING COUNT(m.demographicNo) > -1\n  ) OBSLAST\n  ON (d.demographic_no = OBSLAST.demographic_no)  \n  WHERE d.patient_status LIKE ${active}\n  AND d.demographic_no &gt; 0\n  AND d.provider_no LIKE '${provider}'\n  AND d.demographic_no NOT IN ${excludedPatient}\n) fin\n		</query>\n	</indicatorQuery>\n	<drillDownQuery>\n		<version>03-13-2018</version>\n		<params>\n			<parameter id=\"provider\" name=\"Provider Number\" value=\"loggedInProvider\" />\n			<parameter id=\"active\" name=\"Active Patients\" value=\"\'AC\'\" />\n			<parameter id=\"dxcodesICD\" name=\"Dx Codes (ICD)\" value=\"300\" />\n                        <parameter id=\"dxcodesSysICD\" name=\"DX CodeSystem (ICD)\" value=\"\'icd9\'\" />\n                        <parameter id=\"dxcodesSnoMed\" name=\"Dx Codes (SnoMed)\" value=\"48694002,197480006\" />\n                        <parameter id=\"dxcodesSysSnoMed\" name=\"Dx CodeSystem (SnoMed)\" value=\"\'snomed%\'\" />\n			<parameter id=\"billingCode\" name=\"ICD9 Billing Code\" value=\"300\" />\n			<parameter id=\"numOfBills\" name=\"Number of ICD9 300 Bills\" value=\"2\" />\n			<parameter id=\"numOfVisits\" name=\"Number of Visit Encounters coded 300/ICD9\" value=\"2\" />\n			<parameter id=\"obsName\" name=\"Observation Name\" value=\"\'GAD7\'\" />\n			<parameter id=\"obsResult\" name=\"Observation Result\" value=\"10\" />\n			<parameter id=\"excludedPatient\" name=\"Excluded Patient\" value=\"excludedPatient\" />\n		</params>\n		\n		<range>\n			<lowerLimit id=\"billingdate\" label=\"From Date\" name=\"Date\" value=\"DATE_SUB( NOW(), INTERVAL 3 YEAR )\" />\n			<upperLimit id=\"billingdate\" label=\"Date Today\" name=\"Date\" value=\"NOW()\" />\n			<lowerLimit id=\"visitdate\" label=\"From Date\" name=\"Date\" value=\"DATE_SUB( NOW(), INTERVAL 3 YEAR )\" />\n			<upperLimit id=\"visitdate\" label=\"Date Today\" name=\"Date\" value=\"NOW()\" />\n			<lowerLimit id=\"obsdate\" label=\"From Date\" name=\"Date\" value=\"DATE_SUB( NOW(), INTERVAL 3 MONTH )\" />\n			<upperLimit id=\"obsdate\" label=\"Date Today\" name=\"Date\" value=\"NOW()\" />\n		</range>\n		\n		<displayColumns>\n			<column id=\"demographic\" name=\"d.demographic_no\" title=\"Patient Id\" primary=\"true\" />\n			<column id=\"name\" name=\"CONCAT( d.last_name, \', \', d.first_name )\" title=\"Patient Name\" primary=\"false\" />\n			<column id=\"dob\" name=\"DATE_FORMAT( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth), \'%m-%d-%Y\' )\" title=\"Date of Birth\" primary=\"false\" />\n			<column id=\"age\" name=\"TIMESTAMPDIFF(YEAR, DATE( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth) ), CURDATE() )\" title=\"Age\" primary=\"false\" />\n			<column id=\"billings\" name=\"IFNULL( BILLING.billcount, \'0\')\" title=\"Billings\" primary=\"false\" />\n			<column id=\"visits\" name=\"IFNULL( VISIT.visitcount, \'0\')\" title=\"Visits\" primary=\"false\" />\n			<column id=\"observation\" name=\"IFNULL( OBSLAST.result, \'NA\')\" title=\"GAD-7\" primary=\"false\" />\n			<column id=\"obsDate\" name=\"IFNULL( DATE_FORMAT( OBSLAST.dateObserved, \'%m-%d-%Y\' ), \'\')\" title=\"Test Date (mm-dd-yyyy)\" primary=\"false\" />\n\n		</displayColumns>\n		\n		<exportColumns>\n			<column id=\"demographic\" name=\"d.demographic_no\" title=\"Patient Id\" primary=\"true\" />\n                        <column id=\"firstName\" name=\"d.first_name\" title=\"First Name\" primary=\"false\" />\n                        <column id=\"lastName\" name=\"d.last_name\" title=\"Last Name\" primary=\"false\" />\n			<column id=\"dob\" name=\"DATE_FORMAT( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth), \'%m-%d-%Y\' )\" title=\"Date of Birth\" primary=\"false\" />\n			<column id=\"age\" name=\"TIMESTAMPDIFF(YEAR, DATE( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth) ), CURDATE() )\" title=\"Age\" primary=\"false\" />\n			<column id=\"billings\" name=\"IFNULL( BILLING.billcount, \'0\')\" title=\"Billings\" primary=\"false\" />\n			<column id=\"visits\" name=\"IFNULL( VISIT.visitcount, \'0\')\" title=\"Visits\" primary=\"false\" />\n			<column id=\"observation\" name=\"IFNULL( OBSLAST.result, \'NA\')\" title=\"GAD-7\" primary=\"false\" />\n			<column id=\"obsDate\" name=\"IFNULL( DATE_FORMAT( OBSLAST.dateObserved, \'%m-%d-%Y\' ), \'\')\" title=\"Test Date (mm-dd-yyyy)\" primary=\"false\" />\n		</exportColumns>\n		<drillDownActions>\n			<action id=\"tickler\" name=\"Tickler\"/>\n			<action id=\"dxUpdate\" name=\"Disease Registry Update\" value=\"300\"/>\n			<action id=\"demoExcl\" name=\"Patient Exclusion\"/>\n		</drillDownActions>\n		<query>\n  SELECT *	\n  FROM demographic d \n  LEFT JOIN (\n    SELECT demographic_no, dxresearch_code, coding_system, COUNT(dxresearch_code)\n    FROM dxresearch\n    WHERE status=\'A\' AND\n    (\n      (LEFT(dxresearch_code, 3) IN (\'300\') AND coding_system = ${dxcodesSysICD}) OR\n      (dxresearch_code IN (\'48694002\',\'197480006\') AND coding_system LIKE ${dxcodesSysSnoMed})\n    )\n    GROUP BY demographic_no\n    ORDER BY demographic_no\n  ) dxr\n  ON d.demographic_no = dxr.demographic_no\n  -- Billed 300 in last 3 years\n  LEFT JOIN (\n    SELECT b.demographic_no AS demographic_no, b.dx_code1 AS dxcode1, b.dx_code2 AS dxcode2, b.dx_code3 AS dxcode3, COUNT(b.demographic_no) AS billcount\n    FROM billingmaster b\n    WHERE ( LEFT(b.dx_code1, 3) IN (\'300\') OR LEFT(b.dx_code2, 3) IN (\'300\') OR LEFT(b.dx_code3, 3) IN (\'300\') )\n    AND DATE(b.createdate) &gt; ${lowerLimit.billingdate}\n    AND b.demographic_no &gt; 0\n    GROUP BY b.demographic_no HAVING COUNT(b.demographic_no) &gt; -1\n  ) BILLING\n  ON (d.demographic_no = BILLING.demographic_no)  \n  -- Coded dx during visit encounter in last 3 years\n  LEFT JOIN(\n    SELECT ASSIGNED.demographic_no AS demographic_no, observation_date AS visitdate, uuid, COUNT(ASSIGNED.demographic_no) AS visitcount\n    FROM casemgmt_note note\n    INNER JOIN (\n      SELECT note_id, CODED.demo_no AS demographic_no FROM casemgmt_issue_notes\n      INNER JOIN (\n        SELECT ci.id AS id, ci.update_date, ci.demographic_no demo_no FROM issue i\n        INNER JOIN casemgmt_issue ci\n        ON i.issue_id = ci.issue_id\n        WHERE\n	(i.type LIKE ${dxcodesSysICD} AND LEFT(i.code, 3) IN (\'300\')) OR\n	(i.type LIKE ${dxcodesSysSnoMed} AND i.code IN (\'48694002\',\'197480006\'))\n      ) CODED\n      ON (casemgmt_issue_notes.id = CODED.id)\n      ORDER BY CODED.demo_no\n    ) ASSIGNED\n    ON note.note_id = ASSIGNED.note_id AND\n    -- GET LAST REVISION OF NOTE\n    note.note_id= (SELECT MAX(cmn.note_id) FROM casemgmt_note cmn WHERE cmn.uuid = note.uuid) AND\n    DATE(note.observation_date) &gt; ${lowerLimit.visitdate}\n    GROUP BY demographic_no\n    ORDER BY demographic_no\n  ) VISIT\n  ON (d.demographic_no = VISIT.demographic_no)  \n  -- Consider OBS here if needed\n  LEFT JOIN ( \n    SELECT m.demographicNo AS demographic_no, m.dataField AS result, m.dateObserved AS dateObserved\n    FROM measurements m\n    INNER JOIN (\n      SELECT demographicNo, MAX(dateObserved) AS dateObserved\n      FROM measurements\n      WHERE type = ${obsName}\n      GROUP BY demographicNo\n    ) mlast\n    ON m.demographicNo = mlast.demographicNo\n    WHERE m.type = ${obsName}\n    AND m.dateObserved = mlast.dateObserved\n    AND (DATE(m.dateObserved) &gt; ${lowerLimit.obsdate})\n    AND m.demographicNo &gt; 0\n    GROUP BY m.demographicNo HAVING COUNT(m.demographicNo) > -1\n  ) OBSLAST\n  ON (d.demographic_no = OBSLAST.demographic_no)  \n  WHERE d.patient_status LIKE ${active}\n  AND d.demographic_no &gt; 0\n  AND ( dxr.dxresearch_code IS NULL AND ((BILLING.billcount IS NOT NULL AND BILLING.billcount &gt;= ${numOfBills}) OR (VISIT.visitcount IS NOT NULL AND VISIT.visitcount &gt;= ${numOfVisits})))\n  AND d.provider_no LIKE ${provider}\n  AND d.demographic_no NOT IN ${excludedPatient}\n  ORDER BY d.demographic_no\n		</query>\n	</drillDownQuery>\n</indicatorTemplateXML>\n','','\0',0,NULL,NULL);
INSERT INTO `indicatorTemplate` (`dashboardId`, `name`, `category`, `subCategory`, `framework`, `frameworkVersion`, `definition`, `notes`, `template`, `active`, `locked`, `shared`, `metricSetName`, `metricLabel`) VALUES (@dashboardId3,'Chronic Pain Prevalence','CDM (Click on the pie chart to see the patients included in the pie chart)','Chronic Pain','DoBC CPQI PSP Panel','2017-08-02','Proportion of active patients in the practice coded with Chronic Pain.','\ni) Pain patients: \"Patient Status: Active\nAND has a Current Problem List/Profile item code of 82423001, 373621006 OR starting with 338.4, 338.2, 3384, 3382\"\nii) Other patients: \"Patient Status: Active\nAND does not have a Current Problem List/Profile item','<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<indicatorTemplateXML>\n	<author>OSCAR BC</author>\n	<uid></uid>\n	<heading>\n		<category>CDM (Click on the pie chart to see the patients included in the pie chart)</category>\n		<subCategory>Chronic Pain</subCategory>\n		<name>Chronic Pain Prevalence</name>\n		<definition>Proportion of active patients in the practice coded with Chronic Pain.</definition>\n		<framework>DoBC CPQI PSP Panel</framework>\n		<frameworkVersion>08-02-2017</frameworkVersion>\n		<notes>\ni) Pain patients: \"Patient Status: Active\nAND has a Current Problem List/Profile item code of 82423001, 373621006 OR starting with 338.4, 338.2, 3384, 3382\"\nii) Other patients: \"Patient Status: Active\nAND does not have a Current Problem List/Profile item code of 82423001, 373621006 OR starting with 338.4, 338.2, 3384, 3382\"\n		</notes>\n	</heading>\n	<indicatorQuery>\n		<version>03-13-2018</version>\n		<params>\n			<!-- \n				Required parameter provider. Value options are: \n					[ an individual providerNo, or provider range ] ie: value=\"370, 1001\" \n					\"all\" ie: value=\"all\" including null.\n					\"loggedInProvider\" ie:\n				Default is \"loggedInProvider\"\n				Use this parameter in the query as '${provider}'\n				This parameter should be used for fetching patient\'s assigned to a MRP.\n				ie: WHERE demographic.provider_no = ${provider}\n			-->\n			<parameter id=\"provider\" name=\"provider_no\" value=\"loggedInProvider\" />\n			<parameter id=\"pstatus\" name=\"Patient Status\" value=\"\'AC\'\" />\n			<parameter id=\"dxcodesICD\" name=\"Dx Codes (ICD)\" value=\"338.4,338.2,3384,3382\" />\n			<parameter id=\"dxcodesSysICD\" name=\"DX CodeSystem (ICD)\" value=\"\'icd9\'\" />\n			<parameter id=\"dxcodesSnoMed\" name=\"Dx Codes (SnoMed)\" value=\"82423001,373621006\" />\n			<parameter id=\"dxcodesSysSnoMed\" name=\"Dx CodeSystem (SnoMed)\" value=\"\'snomed%\'\" />\n			<parameter id=\"excludedPatient\" name=\"Excluded Patient\" value=\"excludedPatient\" />\n		</params>\n		<query>\n			<!-- Indicator SQL Query here -->\nSELECT\n -- TOTAL NUMBER OF PATIENTS NOTED AS ACTIVE WITH CURRENT CHRONIC PAIN\n IF( COUNT(fin.patient) &gt; 0, SUM( IF( dxresearch_code is not NULL , 1, 0) ) , 0 ) AS \"Number Active, Current Chronic Pain\",\n -- TOTAL NUMBER OF PATIENTS NOTED AS ACTIVE WITHOUT CURRENT CHRONIC PAIN\n IF( COUNT(fin.patient) &gt; 0, SUM( IF( dxresearch_code is NULL , 1, 0) ) , 0 ) AS \"Number Active, No Current Chronic Pain\"\nFROM (\n SELECT d.demographic_no AS patient, d.patient_status AS pt_status, dxresearch_code\n FROM demographic d\n LEFT JOIN (\n  SELECT demographic_no, dxresearch_code, COUNT(dxresearch_code)\n  FROM dxresearch\n  WHERE status=\'A\' AND\n  (\n   (LEFT(dxresearch_code, 5) IN (\'338.4\', \'338.2\') AND coding_system = ${dxcodesSysICD}) OR\n   (LEFT(dxresearch_code, 4) IN (\'3384\', \'3382\') AND coding_system = ${dxcodesSysICD}) OR\n   (dxresearch_code IN (\'82423001\', \'373621006\') AND coding_system LIKE ${dxcodesSysSnoMed})\n   )\n  GROUP BY demographic_no\n  ORDER BY demographic_no\n ) problems\n ON d.demographic_no = problems.demographic_no\n WHERE d.provider_no LIKE ${provider} AND d.patient_status = ${pstatus}\n AND d.demographic_no NOT IN ${excludedPatient}\n) fin\n		</query>\n	</indicatorQuery>\n	<drillDownQuery>\n		<version>03-13-2018</version>\n		<params>\n			<parameter id=\"provider\" name=\"provider_no\" value=\"loggedInProvider\" />	\n			<parameter id=\"pstatus\" name=\"Patient Status\" value=\"\'AC\'\" />\n			<parameter id=\"dxcodesICD\" name=\"Dx Codes (ICD)\" value=\"338.4,338.2,3384,3382\" />\n			<parameter id=\"dxcodesSysICD\" name=\"DX CodeSystem (ICD)\" value=\"\'icd9\'\" />\n			<parameter id=\"dxcodesSnoMed\" name=\"Dx Codes (SnoMed)\" value=\"82423001,373621006\" />\n			<parameter id=\"dxcodesSysSnoMed\" name=\"Dx CodeSystem (SnoMed)\" value=\"\'snomed%\'\" />\n			<parameter id=\"excludedPatient\" name=\"Excluded Patient\" value=\"excludedPatient\" />\n		</params>\n		<displayColumns>\n			<column id=\"demographic\" name=\"d.demographic_no\" title=\"Patient Id\" primary=\"true\" />\n			<column id=\"name\" name=\"CONCAT( d.last_name, \', \', d.first_name )\" title=\"Patient Name\" primary=\"false\" />\n			<column id=\"dob\" name=\"DATE_FORMAT( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth), \'%m-%d-%Y\' )\" title=\"Date of Birth (mm-dd-yy)\" primary=\"false\" />\n			<column id=\"age\" name=\"TIMESTAMPDIFF(YEAR, DATE( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth) ), CURDATE() )\" title=\"Age\" primary=\"false\" />\n			<column id=\"dxcode\" title=\"Dx System/Code\" name=\"CONCAT(MIN(dxr.coding_system),\'/\',MIN(dxr.dxresearch_code),IF( MIN(dxr.dxresearch_code) != MAX(dxr.dxresearch_code),CONCAT( \' &amp; \', MAX(dxr.coding_system), \'/\', MAX(dxr.dxresearch_code) ),\'\') )\" primary=\"false\" />\n		</displayColumns>\n		<exportColumns>\n			<column id=\"demographic\" name=\"d.demographic_no\" title=\"Patient Id\" primary=\"true\" />\n			<column id=\"firstName\" name=\"d.first_name\" title=\"First Name\" primary=\"false\" />\n			<column id=\"lastName\" name=\"d.last_name\" title=\"Last Name\" primary=\"false\" />\n			<column id=\"dob\" name=\"DATE_FORMAT( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth), \'%m-%d-%Y\' )\" title=\"Date of Birth (mm-dd-yy)\" primary=\"false\" />\n			<column id=\"age\" name=\"TIMESTAMPDIFF(YEAR, DATE( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth) ), CURDATE() )\" title=\"Age\" primary=\"false\" />\n			<column id=\"dxcode\" title=\"Dx System/Code\" name=\"CONCAT(MIN(dxr.coding_system),\'/\',MIN(dxr.dxresearch_code),IF( MIN(dxr.dxresearch_code) != MAX(dxr.dxresearch_code),CONCAT( \' &amp; \', MAX(dxr.coding_system), \'/\', MAX(dxr.dxresearch_code) ),\'\') )\" primary=\"false\" />\n		</exportColumns>\n		<drillDownActions>\n			<action id=\"tickler\" name=\"Tickler\"/>\n			<action id=\"demoExcl\" name=\"Patient Exclusion\"/>\n		</drillDownActions>\n		<query>\n			<!-- Drilldown SQL Query here -->\nSELECT\n*			\nFROM demographic d\nLEFT JOIN dxresearch dxr\nON d.demographic_no = dxr.demographic_no\nWHERE d.patient_status=${pstatus}\nAND\n(\n (LEFT(dxr.dxresearch_code, 5) IN (\'338.4\', \'338.2\') AND dxr.coding_system = ${dxcodesSysICD}) OR\n (LEFT(dxr.dxresearch_code, 4) IN (\'3384\', \'3382\') AND dxr.coding_system = ${dxcodesSysICD}) OR\n (dxr.dxresearch_code IN (\'82423001\', \'373621006\') AND dxr.coding_system LIKE ${dxcodesSysSnoMed})\n )\nAND dxr.status = \'A\'\nAND d.provider_no LIKE ${provider}\nAND d.demographic_no NOT IN ${excludedPatient}\nGROUP BY d.demographic_no\nORDER BY COUNT(d.demographic_no) DESC, d.demographic_no\n		</query>\n	</drillDownQuery>\n</indicatorTemplateXML>\n','','\0',0,NULL,NULL);
INSERT INTO `indicatorTemplate` (`dashboardId`, `name`, `category`, `subCategory`, `framework`, `frameworkVersion`, `definition`, `notes`, `template`, `active`, `locked`, `shared`, `metricSetName`, `metricLabel`) VALUES (@dashboardId3,'Consider Chronic Pain','CDM (Click on the pie chart to see the patients included in the pie chart)','Chronic Pain','DoBC CPQI PSP Panel','2017-08-02','\n			Patients with chronic pain dx in 2+ bills or 2+ visit encounters within last 3 years or on Opioid medication\n		','\n		  Created for Doctors of BC\n		  i) Chronic Pain dx in 2+ bills: \"Patient Status: Active\nAND does not have a Current Problem List/Profile item code of 82423001, 373621006 OR starting with 338.4, 338.2, 3384, 3382\nAND with >= 2 Bills with the ICD9 diagno','<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<indicatorTemplateXML>\n	<author>OSCAR BC</author>\n	<uid></uid>\n	<heading>\n		<category>CDM (Click on the pie chart to see the patients included in the pie chart)</category>\n		<subCategory>Chronic Pain</subCategory>\n		<name>Consider Chronic Pain</name>\n		<definition>\n			Patients with chronic pain dx in 2+ bills or 2+ visit encounters within last 3 years or on Opioid medication\n		</definition>\n		<framework>DoBC CPQI PSP Panel</framework>\n		<frameworkVersion>08-02-2017</frameworkVersion>\n		<notes>\n		  Created for Doctors of BC\n		  i) Chronic Pain dx in 2+ bills: \"Patient Status: Active\nAND does not have a Current Problem List/Profile item code of 82423001, 373621006 OR starting with 338.4, 338.2, 3384, 3382\nAND with >= 2 Bills with the ICD9 diagnosis code starting with 338.4, 338.2, 3384 OR 3382 in the last 3 years\"\n		  ii) Chronic Pain dx in 2+ visits: \"Patient Status: Active\nAND does not have a Current Problem List/Profile item code of 82423001, 373621006 OR starting with 338.4, 338.2, 3384, 3382\nAND with >= 2 Visit Encounters with Snomed diagnosis code 82423001, 373621006 or the ICD9 diagnosis code starting with 338.4, 338.2, 3384 OR 3382 in the last 3 years\"\n		  iii) Opioid medication: \"Patient Status: Active\nAND does not have a Current Problem List/Profile item code of 82423001, 373621006 OR starting with 338.4, 338.2, 3384, 3382\nAND with >= 1 Active Current Medication with ATC Code/Class of N02A\"\n		</notes>\n	</heading>\n	<indicatorQuery>\n		<version>03-13-2018</version>\n		<params>\n			<parameter id=\"provider\" name=\"Provider Number\" value=\"loggedInProvider\" />\n			<parameter id=\"active\" name=\"Active Patients\" value=\"\'AC\'\" />\n			<parameter id=\"dxcodesICD\" name=\"Dx Codes (ICD)\" value=\"338.4,338.2\" />\n			<parameter id=\"dxcodesICD2\" name=\"Dx Codes (ICD)\" value=\"3384,3382\" />\n                        <parameter id=\"dxcodesSysICD\" name=\"DX CodeSystem (ICD)\" value=\"\'icd9\'\" />\n                        <parameter id=\"dxcodesSnoMed\" name=\"Dx Codes (SnoMed)\" value=\"82423001,373621006\" />\n                        <parameter id=\"dxcodesSysSnoMed\" name=\"Dx CodeSystem (SnoMed)\" value=\"\'snomed%\'\" />\n			<parameter id=\"billingCode\" name=\"ICD9 Billing Code\" value=\"338.4,338.2\" />\n			<parameter id=\"billingCode2\" name=\"ICD9 Billing Code\" value=\"3384,3382\" />\n			<parameter id=\"numOfBills\" name=\"Number of ICD9 338.4,338.2 Bills\" value=\"2\" />\n			<parameter id=\"numOfVisits\" name=\"Number of Visit Encounters coded 338.4,338.2/ICD9\" value=\"2\" />\n			<parameter id=\"drug\" name=\"ATC Code/Class\" value=\"\'N02A%\'\" />\n			<parameter id=\"numOfMeds\" name=\"Opioid Medications\" value=\"1\" />\n			<parameter id=\"excludedPatient\" name=\"Excluded Patient\" value=\"excludedPatient\" />\n		</params>\n		<range>\n			<lowerLimit id=\"billingdate\" label=\"From Date\" name=\"Date\" value=\"DATE_SUB( NOW(), INTERVAL 3 YEAR )\" />\n			<upperLimit id=\"billingdate\" label=\"Date Today\" name=\"Date\" value=\"NOW()\" />\n			<lowerLimit id=\"visitdate\" label=\"From Date\" name=\"Date\" value=\"DATE_SUB( NOW(), INTERVAL 3 YEAR )\" />\n			<upperLimit id=\"visitdate\" label=\"Date Today\" name=\"Date\" value=\"NOW()\" />\n		</range>\n		<query>\nSELECT\n  IF ( COUNT(fin.patient) &gt; 0, SUM( CASE WHEN (fin.dx IS NULL AND fin.billcount &gt;= ${numOfBills}) THEN 1 ELSE 0 END ) , 0) AS \"Number with Chronic Pain dx in ${numOfBills}+ bills\",\n  IF ( COUNT(fin.patient) &gt; 0, SUM( CASE WHEN (fin.dx IS NULL AND fin.visitcount &gt;= ${numOfVisits}) THEN 1 ELSE 0 END ) , 0) AS \"Number with Chronic Pain dx in ${numOfVisits}+ visits\",\n  IF ( COUNT(fin.patient) &gt; 0, SUM( CASE WHEN (fin.dx IS NULL AND fin.medcount &gt;= ${numOfMeds} ) THEN 1 ELSE 0 END ) , 0) AS \"Number with Opioid Meds &gt;= ${numOfMeds}\"\nFROM ( \n  SELECT \n    d.demographic_no AS patient, \n    BILLING.billcount AS billcount,\n    VISIT.visitcount AS visitcount,\n    MEDS.numOfMeds AS medcount,\n    dxr.dxresearch_code AS dx,\n    dxr.coding_system AS dxs\n  FROM demographic d \n  LEFT JOIN (\n    SELECT demographic_no, dxresearch_code, coding_system, COUNT(dxresearch_code)\n    FROM dxresearch\n    WHERE status=\'A\' AND\n    (\n      (LEFT(dxresearch_code,5) IN ${dxcodesICD} AND coding_system = ${dxcodesSysICD}) OR\n      (LEFT(dxresearch_code,4) IN ${dxcodesICD2} AND coding_system = ${dxcodesSysICD}) OR\n      (dxresearch_code IN ${dxcodesSnoMed} AND coding_system LIKE ${dxcodesSysSnoMed})\n    )\n    GROUP BY demographic_no\n    ORDER BY demographic_no\n  ) dxr\n  ON d.demographic_no = dxr.demographic_no\n  -- Billed 338.4,338.2 in last 3 years\n  LEFT JOIN (\n    SELECT b.demographic_no AS demographic_no, b.dx_code1 AS dxcode1, b.dx_code2 AS dxcode2, b.dx_code3 AS dxcode3, COUNT(b.demographic_no) AS billcount\n    FROM billingmaster b\n    WHERE LEFT(b.dx_code1,5) IN ${billingCode} OR LEFT(b.dx_code2,5) IN ${billingCode} OR LEFT(b.dx_code3,5) IN ${billingCode}\n    OR LEFT(b.dx_code1,4) IN ${billingCode2} OR LEFT(b.dx_code2,4) IN ${billingCode2} OR LEFT(b.dx_code3,4) IN ${billingCode2}\n    AND DATE(b.createdate) &gt; ${lowerLimit.billingdate}\n    AND b.demographic_no &gt; 0\n    GROUP BY b.demographic_no HAVING COUNT(b.demographic_no) &gt; -1\n  ) BILLING\n  ON (d.demographic_no = BILLING.demographic_no)  \n  -- Coded dx during visit encounter in last 3 years\n  LEFT JOIN(\n    SELECT ASSIGNED.demographic_no AS demographic_no, observation_date AS visitdate, uuid, COUNT(ASSIGNED.demographic_no) AS visitcount\n    FROM casemgmt_note note\n    INNER JOIN (\n      SELECT note_id, CODED.demo_no AS demographic_no FROM casemgmt_issue_notes\n      INNER JOIN (\n        SELECT ci.id AS id, ci.update_date, ci.demographic_no demo_no FROM issue i\n        INNER JOIN casemgmt_issue ci\n        ON i.issue_id = ci.issue_id\n        WHERE\n	(i.type like ${dxcodesSysICD} and LEFT(i.code,5) IN ${dxcodesICD}) OR\n	(i.type like ${dxcodesSysICD} and LEFT(i.code,4) IN ${dxcodesICD2}) OR\n	(i.type like ${dxcodesSysSnoMed} and i.code IN ${dxcodesSnoMed})\n      ) CODED\n      ON (casemgmt_issue_notes.id = CODED.id)\n      ORDER BY CODED.demo_no\n    ) ASSIGNED\n    ON note.note_id = ASSIGNED.note_id AND\n    -- GET LAST REVISION OF NOTE\n    note.note_id= (SELECT MAX(cmn.note_id) FROM casemgmt_note cmn WHERE cmn.uuid = note.uuid) AND\n    DATE(note.observation_date) &gt; ${lowerLimit.visitdate}\n    GROUP BY demographic_no\n    ORDER BY demographic_no\n  ) VISIT\n  ON (d.demographic_no = VISIT.demographic_no)  \n  -- Opioid medications\n  LEFT JOIN (\n    SELECT dr.demographic_no, count(DISTINCT dr.atc) AS numOfMeds FROM drugs AS dr\n    WHERE dr.archived = 0 AND dr.ATC LIKE ${drug}\n    AND (dr.long_term OR (dr.rx_date &lt;= NOW() AND (DATE_ADD(dr.rx_date, INTERVAL(DATEDIFF(dr.end_date,dr.rx_date)*1.0) DAY)) >= NOW()))\n    GROUP BY dr.demographic_no\n  ) MEDS\n  ON (d.demographic_no = MEDS.demographic_no) \n  WHERE d.patient_status LIKE ${active}\n  AND d.demographic_no &gt; 0\n  AND d.provider_no LIKE '${provider}'\n  AND d.demographic_no NOT IN ${excludedPatient}\n) fin\n		</query>\n	</indicatorQuery>\n	<drillDownQuery>\n		<version>03-13-2018</version>\n		<params>\n			<parameter id=\"provider\" name=\"Provider Number\" value=\"loggedInProvider\" />\n			<parameter id=\"active\" name=\"Active Patients\" value=\"\'AC\'\" />\n			<parameter id=\"dxcodesICD\" name=\"Dx Codes (ICD)\" value=\"338.4,338.2\" />\n			<parameter id=\"dxcodesICD2\" name=\"Dx Codes (ICD)\" value=\"3384,3382\" />\n                        <parameter id=\"dxcodesSysICD\" name=\"DX CodeSystem (ICD)\" value=\"\'icd9\'\" />\n                        <parameter id=\"dxcodesSnoMed\" name=\"Dx Codes (SnoMed)\" value=\"82423001,373621006\" />\n                        <parameter id=\"dxcodesSysSnoMed\" name=\"Dx CodeSystem (SnoMed)\" value=\"\'snomed%\'\" />\n			<parameter id=\"billingCode\" name=\"ICD9 Billing Code\" value=\"338.4,338.2\" />\n			<parameter id=\"billingCode2\" name=\"ICD9 Billing Code\" value=\"3384,3382\" />\n			<parameter id=\"numOfBills\" name=\"Number of ICD9 338.4,338.2 Bills\" value=\"2\" />\n			<parameter id=\"numOfVisits\" name=\"Number of Visit Encounters coded 338.4,338.2/ICD9\" value=\"2\" />\n			<parameter id=\"drug\" name=\"ATC Code/Class\" value=\"\'N02A%\'\" />\n			<parameter id=\"numOfMeds\" name=\"Opioid Medications\" value=\"1\" />\n			<parameter id=\"excludedPatient\" name=\"Excluded Patient\" value=\"excludedPatient\" />\n		</params>\n		\n		<range>\n			<lowerLimit id=\"billingdate\" label=\"From Date\" name=\"Date\" value=\"DATE_SUB( NOW(), INTERVAL 3 YEAR )\" />\n			<upperLimit id=\"billingdate\" label=\"Date Today\" name=\"Date\" value=\"NOW()\" />\n			<lowerLimit id=\"visitdate\" label=\"From Date\" name=\"Date\" value=\"DATE_SUB( NOW(), INTERVAL 3 YEAR )\" />\n			<upperLimit id=\"visitdate\" label=\"Date Today\" name=\"Date\" value=\"NOW()\" />\n		</range>\n		\n		<displayColumns>\n			<column id=\"demographic\" name=\"d.demographic_no\" title=\"Patient Id\" primary=\"true\" />\n			<column id=\"name\" name=\"CONCAT( d.last_name, \', \', d.first_name )\" title=\"Patient Name\" primary=\"false\" />\n			<column id=\"dob\" name=\"DATE_FORMAT( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth), \'%m-%d-%Y\' )\" title=\"Date of Birth\" primary=\"false\" />\n			<column id=\"age\" name=\"TIMESTAMPDIFF(YEAR, DATE( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth) ), CURDATE() )\" title=\"Age\" primary=\"false\" />\n			<column id=\"billings\" name=\"IFNULL( BILLING.billcount, \'0\')\" title=\"Billings\" primary=\"false\" />\n			<column id=\"visits\" name=\"IFNULL( VISIT.visitcount, \'0\')\" title=\"Visits\" primary=\"false\" />\n			<column id=\"numOfMeds\" name=\"IFNULL( MEDS.numOfMeds, \'NA\')\" title=\"Opioid Medications\" primary=\"false\" />\n		</displayColumns>\n		\n		<exportColumns>\n			<column id=\"demographic\" name=\"d.demographic_no\" title=\"Patient Id\" primary=\"true\" />\n                        <column id=\"firstName\" name=\"d.first_name\" title=\"First Name\" primary=\"false\" />\n                        <column id=\"lastName\" name=\"d.last_name\" title=\"Last Name\" primary=\"false\" />\n			<column id=\"dob\" name=\"DATE_FORMAT( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth), \'%m-%d-%Y\' )\" title=\"Date of Birth\" primary=\"false\" />\n			<column id=\"age\" name=\"TIMESTAMPDIFF(YEAR, DATE( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth) ), CURDATE() )\" title=\"Age\" primary=\"false\" />\n			<column id=\"billings\" name=\"IFNULL( BILLING.billcount, \'0\')\" title=\"Billings\" primary=\"false\" />\n			<column id=\"visits\" name=\"IFNULL( VISIT.visitcount, \'0\')\" title=\"Visits\" primary=\"false\" />\n			<column id=\"numOfMeds\" name=\"IFNULL( MEDS.numOfMeds, \'NA\')\" title=\"Opioid Medications\" primary=\"false\" />\n		</exportColumns>\n		<drillDownActions>\n			<action id=\"tickler\" name=\"Tickler\"/>\n			<action id=\"dxUpdate\" name=\"Disease Registry Update\" value=\"338.2\"/>\n			<action id=\"demoExcl\" name=\"Patient Exclusion\"/>\n		</drillDownActions>\n		<query>\n  SELECT *	\n  FROM demographic d \n  LEFT JOIN (\n    SELECT demographic_no, dxresearch_code, coding_system, COUNT(dxresearch_code)\n    FROM dxresearch\n    WHERE status=\'A\' AND\n    (\n      (LEFT(dxresearch_code,5) IN ${dxcodesICD} AND coding_system = ${dxcodesSysICD}) OR\n      (LEFT(dxresearch_code,4) IN ${dxcodesICD2} AND coding_system = ${dxcodesSysICD}) OR\n      (dxresearch_code IN ${dxcodesSnoMed} AND coding_system LIKE ${dxcodesSysSnoMed})\n    )\n    GROUP BY demographic_no\n    ORDER BY demographic_no\n  ) dxr\n  ON d.demographic_no = dxr.demographic_no\n  -- Billed 338.4,338.2 in last 3 years\n  LEFT JOIN (\n    SELECT b.demographic_no AS demographic_no, b.dx_code1 AS dxcode1, b.dx_code2 AS dxcode2, b.dx_code3 AS dxcode3, COUNT(b.demographic_no) AS billcount\n    FROM billingmaster b\n    WHERE LEFT(b.dx_code1,5) IN ${billingCode} OR LEFT(b.dx_code2,5) IN ${billingCode} OR LEFT(b.dx_code3,5) IN ${billingCode}\n    OR LEFT(b.dx_code1,4) IN ${billingCode2} OR LEFT(b.dx_code2,4) IN ${billingCode2} OR LEFT(b.dx_code3,4) IN ${billingCode2}\n    AND DATE(b.createdate) &gt; ${lowerLimit.billingdate}\n    AND b.demographic_no &gt; 0\n    GROUP BY b.demographic_no HAVING COUNT(b.demographic_no) &gt; -1\n  ) BILLING\n  ON (d.demographic_no = BILLING.demographic_no)  \n  -- Coded dx during visit encounter in last 3 years\n  LEFT JOIN(\n    SELECT ASSIGNED.demographic_no AS demographic_no, observation_date AS visitdate, uuid, COUNT(ASSIGNED.demographic_no) AS visitcount\n    FROM casemgmt_note note\n    INNER JOIN (\n      SELECT note_id, CODED.demo_no AS demographic_no FROM casemgmt_issue_notes\n      INNER JOIN (\n        SELECT ci.id AS id, ci.update_date, ci.demographic_no demo_no FROM issue i\n        INNER JOIN casemgmt_issue ci\n        ON i.issue_id = ci.issue_id\n        WHERE\n	(i.type LIKE ${dxcodesSysICD} AND LEFT(i.code,5) IN ${dxcodesICD}) OR\n	(i.type LIKE ${dxcodesSysICD} AND LEFT(i.code,4) IN ${dxcodesICD2}) OR\n	(i.type LIKE ${dxcodesSysSnoMed} AND i.code IN ${dxcodesSnoMed})\n      ) CODED\n      ON (casemgmt_issue_notes.id = CODED.id)\n      ORDER BY CODED.demo_no\n    ) ASSIGNED\n    ON note.note_id = ASSIGNED.note_id AND\n    -- GET LAST REVISION OF NOTE\n    note.note_id= (SELECT MAX(cmn.note_id) FROM casemgmt_note cmn WHERE cmn.uuid = note.uuid) AND\n    DATE(note.observation_date) &gt; ${lowerLimit.visitdate}\n    GROUP BY demographic_no\n    ORDER BY demographic_no\n  ) VISIT\n  ON (d.demographic_no = VISIT.demographic_no)  \n  -- Opioid medications\n  LEFT JOIN (\n    SELECT dr.demographic_no, count(DISTINCT dr.atc) AS numOfMeds FROM drugs AS dr\n    WHERE dr.archived = 0 AND dr.ATC LIKE ${drug}\n    AND (dr.long_term OR (dr.rx_date &lt;= NOW() AND (DATE_ADD(dr.rx_date, INTERVAL(DATEDIFF(dr.end_date,dr.rx_date)*1.0) DAY)) >= NOW()))\n    GROUP BY dr.demographic_no\n  ) MEDS\n  ON (d.demographic_no = MEDS.demographic_no) \n  WHERE d.patient_status LIKE ${active}\n  AND d.demographic_no &gt; 0\n  AND ( dxr.dxresearch_code IS NULL AND ((BILLING.billcount IS NOT NULL AND BILLING.billcount &gt;= ${numOfBills}) OR (VISIT.visitcount IS NOT NULL AND VISIT.visitcount &gt;= ${numOfVisits}) OR (MEDS.numOfMeds IS NOT NULL AND MEDS.numOfMeds &gt;= ${numOfMeds})))\n  AND d.provider_no LIKE ${provider}\n  AND d.demographic_no NOT IN ${excludedPatient}\n  ORDER BY d.demographic_no\n		</query>\n	</drillDownQuery>\n</indicatorTemplateXML>\n','','\0',0,NULL,NULL);
INSERT INTO `indicatorTemplate` (`dashboardId`, `name`, `category`, `subCategory`, `framework`, `frameworkVersion`, `definition`, `notes`, `template`, `active`, `locked`, `shared`, `metricSetName`, `metricLabel`) VALUES (@dashboardId3,'Dementia Prevalence','CDM (Click on the pie chart to see the patients included in the pie chart)','Dementia','DoBC CPQI PSP Panel','2017-08-02','Proportion of active patients in the practice coded with Dementia.','\ni) Dementia patients: \"Patient Status: Active\nAND has a Current Problem List/Profile item code of 52448006 OR starting with 331 OR 290\"\nii) Other patients: \"Patient Status: Active\nAND does not have a Current Problem List/Profile item code of 52448006 OR ','<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<indicatorTemplateXML>\n	<author>OSCAR BC</author>\n	<uid></uid>\n	<heading>\n		<category>CDM (Click on the pie chart to see the patients included in the pie chart)</category>\n		<subCategory>Dementia</subCategory>\n		<name>Dementia Prevalence</name>\n		<definition>Proportion of active patients in the practice coded with Dementia.</definition>\n		<framework>DoBC CPQI PSP Panel</framework>\n		<frameworkVersion>08-02-2017</frameworkVersion>\n		<notes>\ni) Dementia patients: \"Patient Status: Active\nAND has a Current Problem List/Profile item code of 52448006 OR starting with 331 OR 290\"\nii) Other patients: \"Patient Status: Active\nAND does not have a Current Problem List/Profile item code of 52448006 OR starting with 331 OR 290\"\n		</notes>\n	</heading>\n	<indicatorQuery>\n		<version>03-13-2018</version>\n		<params>\n			<!-- \n				Required parameter provider. Value options are: \n					[ an individual providerNo, or provider range ] ie: value=\"370, 1001\" \n					\"all\" ie: value=\"all\" including null.\n					\"loggedInProvider\" ie:\n				Default is \"loggedInProvider\"\n				Use this parameter in the query as '${provider}'\n				This parameter should be used for fetching patient\'s assigned to a MRP.\n				ie: WHERE demographic.provider_no = ${provider}\n			-->\n			<parameter id=\"provider\" name=\"provider_no\" value=\"loggedInProvider\" />\n			<parameter id=\"pstatus\" name=\"Patient Status\" value=\"\'AC\'\" />\n			<parameter id=\"dxcodesICD\" name=\"Dx Codes (ICD)\" value=\"331,290\" />\n			<parameter id=\"dxcodesSysICD\" name=\"DX CodeSystem (ICD)\" value=\"\'icd9\'\" />\n			<parameter id=\"dxcodesSnoMed\" name=\"Dx Codes (SnoMed)\" value=\"52448006\" />\n			<parameter id=\"dxcodesSysSnoMed\" name=\"Dx CodeSystem (SnoMed)\" value=\"\'snomed%\'\" />\n			<parameter id=\"excludedPatient\" name=\"Excluded Patient\" value=\"excludedPatient\" />\n		</params>\n		<query>\n			<!-- Indicator SQL Query here -->\nSELECT\n -- TOTAL NUMBER OF PATIENTS NOTED AS ACTIVE WITH CURRENT DEMENTIA\n IF( COUNT(fin.patient) &gt; 0, SUM( IF( dxresearch_code is not NULL , 1, 0) ) , 0 ) AS \"Number Active, Current Dementia\",\n -- TOTAL NUMBER OF PATIENTS NOTED AS ACTIVE WITHOUT CURRENT DEMENTIA\n IF( COUNT(fin.patient) &gt; 0, SUM( IF( dxresearch_code is NULL , 1, 0) ) , 0 ) AS \"Number Active, No Current Dementia\"\nFROM (\n SELECT d.demographic_no AS patient, d.patient_status AS pt_status, dxresearch_code\n FROM demographic d\n LEFT JOIN (\n  SELECT demographic_no, dxresearch_code, COUNT(dxresearch_code)\n  FROM dxresearch\n  WHERE status=\'A\' AND\n  (\n   (LEFT(dxresearch_code, 3) IN (\'331\', \'290\') AND coding_system = ${dxcodesSysICD}) OR\n   (dxresearch_code IN (\'52448006\') AND coding_system LIKE ${dxcodesSysSnoMed})\n   )\n  GROUP BY demographic_no\n  ORDER BY demographic_no\n ) problems\n ON d.demographic_no = problems.demographic_no\n WHERE d.provider_no LIKE ${provider} AND d.patient_status = ${pstatus}\n AND d.demographic_no NOT IN ${excludedPatient}\n) fin\n		</query>\n	</indicatorQuery>\n	<drillDownQuery>\n		<version>03-13-2018</version>\n		<params>\n			<parameter id=\"provider\" name=\"provider_no\" value=\"loggedInProvider\" />	\n			<parameter id=\"pstatus\" name=\"Patient Status\" value=\"\'AC\'\" />\n			<parameter id=\"dxcodesICD\" name=\"Dx Codes (ICD)\" value=\"331,290\" />\n			<parameter id=\"dxcodesSysICD\" name=\"DX CodeSystem (ICD)\" value=\"\'icd9\'\" />\n			<parameter id=\"dxcodesSnoMed\" name=\"Dx Codes (SnoMed)\" value=\"52448006\" />\n			<parameter id=\"dxcodesSysSnoMed\" name=\"Dx CodeSystem (SnoMed)\" value=\"\'snomed%\'\" />\n			<parameter id=\"excludedPatient\" name=\"Excluded Patient\" value=\"excludedPatient\" />\n		</params>\n		<displayColumns>\n			<column id=\"demographic\" name=\"d.demographic_no\" title=\"Patient Id\" primary=\"true\" />\n			<column id=\"name\" name=\"CONCAT( d.last_name, \', \', d.first_name )\" title=\"Patient Name\" primary=\"false\" />\n			<column id=\"dob\" name=\"DATE_FORMAT( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth), \'%m-%d-%Y\' )\" title=\"Date of Birth (mm-dd-yy)\" primary=\"false\" />\n			<column id=\"age\" name=\"TIMESTAMPDIFF(YEAR, DATE( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth) ), CURDATE() )\" title=\"Age\" primary=\"false\" />\n			<column id=\"dxcode\" title=\"Dx System/Code\" name=\"CONCAT(MIN(dxr.coding_system),\'/\',MIN(dxr.dxresearch_code),IF( MIN(dxr.dxresearch_code) != MAX(dxr.dxresearch_code),CONCAT( \' &amp; \', MAX(dxr.coding_system), \'/\', MAX(dxr.dxresearch_code) ),\'\') )\" primary=\"false\" />\n		</displayColumns>\n		<exportColumns>\n			<column id=\"demographic\" name=\"d.demographic_no\" title=\"Patient Id\" primary=\"true\" />\n			<column id=\"firstName\" name=\"d.first_name\" title=\"First Name\" primary=\"false\" />\n			<column id=\"lastName\" name=\"d.last_name\" title=\"Last Name\" primary=\"false\" />\n			<column id=\"dob\" name=\"DATE_FORMAT( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth), \'%m-%d-%Y\' )\" title=\"Date of Birth (mm-dd-yy)\" primary=\"false\" />\n			<column id=\"age\" name=\"TIMESTAMPDIFF(YEAR, DATE( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth) ), CURDATE() )\" title=\"Age\" primary=\"false\" />\n			<column id=\"dxcode\" title=\"Dx System/Code\" name=\"CONCAT(MIN(dxr.coding_system),\'/\',MIN(dxr.dxresearch_code),IF( MIN(dxr.dxresearch_code) != MAX(dxr.dxresearch_code),CONCAT( \' &amp; \', MAX(dxr.coding_system), \'/\', MAX(dxr.dxresearch_code) ),\'\') )\" primary=\"false\" />\n		</exportColumns>\n		<drillDownActions>\n			<action id=\"tickler\" name=\"Tickler\"/>\n			<action id=\"demoExcl\" name=\"Patient Exclusion\"/>\n		</drillDownActions>\n		<query>\n			<!-- Drilldown SQL Query here -->\nSELECT\n*			\nFROM demographic d\nLEFT JOIN dxresearch dxr\nON d.demographic_no = dxr.demographic_no\nWHERE d.patient_status=${pstatus}\nAND\n(\n (LEFT(dxr.dxresearch_code, 3) IN (\'331\', \'290\') AND dxr.coding_system = ${dxcodesSysICD}) OR\n (dxr.dxresearch_code IN (\'52448006\') AND dxr.coding_system LIKE ${dxcodesSysSnoMed})\n )\nAND dxr.status = \'A\'\nAND d.provider_no LIKE ${provider}\nAND d.demographic_no NOT IN ${excludedPatient}\nGROUP BY d.demographic_no\nORDER BY COUNT(d.demographic_no) DESC, d.demographic_no\n		</query>\n	</drillDownQuery>\n</indicatorTemplateXML>\n','','\0',0,NULL,NULL);
INSERT INTO `indicatorTemplate` (`dashboardId`, `name`, `category`, `subCategory`, `framework`, `frameworkVersion`, `definition`, `notes`, `template`, `active`, `locked`, `shared`, `metricSetName`, `metricLabel`) VALUES (@dashboardId3,'Consider Dementia','CDM (Click on the pie chart to see the patients included in the pie chart)','Dementia','DoBC CPQI PSP Panel','2017-08-02','\n			Patients with dementia dx in 2+ bills or 2+ visit encounters or sMMSE < 21 or MOCA < 21 within last 3 years\n		','\n		  Created for Doctors of BC\n		  i) Dementia dx in 2+ bills: \"Patient Status: Active\nAnd does not have a Current Problem List/Profile item code of 52448006 OR starting with 331 OR with 290\nAND with >=2 Bills with the ICD9 diagnosis code starting with 33','<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<indicatorTemplateXML>\n	<author>OSCAR BC</author>\n	<uid></uid>\n	<heading>\n		<category>CDM (Click on the pie chart to see the patients included in the pie chart)</category>\n		<subCategory>Dementia</subCategory>\n		<name>Consider Dementia</name>\n		<definition>\n			Patients with dementia dx in 2+ bills or 2+ visit encounters or sMMSE &lt; 21 or MOCA &lt; 21 within last 3 years\n		</definition>\n		<framework>DoBC CPQI PSP Panel</framework>\n		<frameworkVersion>08-02-2017</frameworkVersion>\n		<notes>\n		  Created for Doctors of BC\n		  i) Dementia dx in 2+ bills: \"Patient Status: Active\nAnd does not have a Current Problem List/Profile item code of 52448006 OR starting with 331 OR with 290\nAND with >=2 Bills with the ICD9 diagnosis code starting with 331, 290 in the last 3 years\"\n		  ii) Dementia dx in 2+ visits: \"Patient Status: Active\nAnd does not have a Current Problem List/Profile item code of 52448006 OR starting with 331 OR with 290\nAND with >=2 Visit Encounters with Snomed code 52448006 or the ICD9 diagnosis code starting with 331, 290 in the last 3 years\"\n		  iii) sMMSE score &lt; 21 OR MOCA &lt; 21: \"Patient Status: Active\nAnd does not have a Current Problem List/Profile item code of 52448006 OR starting with 331 OR with 290\nAnd has an observation of \"Total sMMSE Score\" with a value &lt; 21 OR MOCA &lt; 21 within the last 3 years\"\n		</notes>\n	</heading>\n	<indicatorQuery>\n		<version>03-13-2018</version>\n		<params>\n			<parameter id=\"provider\" name=\"Provider Number\" value=\"loggedInProvider\" />\n			<parameter id=\"active\" name=\"Active Patients\" value=\"\'AC\'\" />\n			<parameter id=\"dxcodesICD\" name=\"Dx Codes (ICD)\" value=\"331,290\" />\n                        <parameter id=\"dxcodesSysICD\" name=\"DX CodeSystem (ICD)\" value=\"\'icd9\'\" />\n                        <parameter id=\"dxcodesSnoMed\" name=\"Dx Codes (SnoMed)\" value=\"52448006\" />\n                        <parameter id=\"dxcodesSysSnoMed\" name=\"Dx CodeSystem (SnoMed)\" value=\"\'snomed%\'\" />\n			<parameter id=\"billingCode\" name=\"ICD9 Billing Code\" value=\"331,290\" />\n			<parameter id=\"numOfBills\" name=\"Number of ICD9 331,290 Bills\" value=\"2\" />\n			<parameter id=\"numOfVisits\" name=\"Number of Visit Encounters coded 331,290/ICD9\" value=\"2\" />\n			<parameter id=\"obsName\" name=\"Observation Name\" value=\"MMSE,MOCA\" />\n			<parameter id=\"obsResult\" name=\"Observation Result\" value=\"21\" />\n			<parameter id=\"excludedPatient\" name=\"Excluded Patient\" value=\"excludedPatient\" />\n		</params>\n		<range>\n			<lowerLimit id=\"billingdate\" label=\"From Date\" name=\"Date\" value=\"DATE_SUB( NOW(), INTERVAL 3 YEAR )\" />\n			<upperLimit id=\"billingdate\" label=\"Date Today\" name=\"Date\" value=\"NOW()\" />\n			<lowerLimit id=\"visitdate\" label=\"From Date\" name=\"Date\" value=\"DATE_SUB( NOW(), INTERVAL 3 YEAR )\" />\n			<upperLimit id=\"visitdate\" label=\"Date Today\" name=\"Date\" value=\"NOW()\" />\n			<lowerLimit id=\"obsdate\" label=\"From Date\" name=\"Date\" value=\"DATE_SUB( NOW(), INTERVAL 3 YEAR )\" />\n			<upperLimit id=\"obsdate\" label=\"Date Today\" name=\"Date\" value=\"NOW()\" />\n		</range>\n		<query>\nSELECT\n  IF ( COUNT(fin.patient) &gt; 0, SUM( CASE WHEN (fin.dx IS NULL AND fin.billcount &gt;= ${numOfBills}) THEN 1 ELSE 0 END ) , 0) AS \"Number with Dementia dx in ${numOfBills}+ bills\",\n  IF ( COUNT(fin.patient) &gt; 0, SUM( CASE WHEN (fin.dx IS NULL AND fin.visitcount &gt;= ${numOfVisits}) THEN 1 ELSE 0 END ) , 0) AS \"Number with Dementia dx in ${numOfVisits}+ visits\",\n  IF ( COUNT(fin.patient) &gt; 0, SUM( CASE WHEN (fin.dx IS NULL AND fin.result &lt; ${obsResult}) THEN 1 ELSE 0 END ) , 0) AS \"Number with sMMSE/MOCA &lt; ${obsResult}\"\nFROM ( \n  SELECT \n    d.demographic_no AS patient, \n    BILLING.billcount AS billcount,\n    VISIT.visitcount AS visitcount,\n    OBSLAST.result AS result,\n    dxr.dxresearch_code AS dx,\n    dxr.coding_system AS dxs\n  FROM demographic d \n  LEFT JOIN (\n    SELECT demographic_no, dxresearch_code, coding_system, COUNT(dxresearch_code)\n    FROM dxresearch\n    WHERE status=\'A\' AND\n    (\n      (LEFT(dxresearch_code, 3) IN (\'331\',\'290\') AND coding_system = ${dxcodesSysICD}) OR\n      (dxresearch_code IN (\'52448006\') AND coding_system LIKE ${dxcodesSysSnoMed})\n    )\n    GROUP BY demographic_no\n    ORDER BY demographic_no\n  ) dxr\n  ON d.demographic_no = dxr.demographic_no\n  -- Billed 331,290 in last 3 years\n  LEFT JOIN (\n    SELECT b.demographic_no AS demographic_no, b.dx_code1 AS dxcode1, b.dx_code2 AS dxcode2, b.dx_code3 AS dxcode3, COUNT(b.demographic_no) AS billcount\n    FROM billingmaster b\n    WHERE ( LEFT(b.dx_code1, 3) IN (\'331\',\'290\') OR LEFT(b.dx_code2, 3) IN (\'331\',\'290\') OR LEFT(b.dx_code3, 3) IN (\'331\',\'290\') )\n    AND DATE(b.createdate) &gt; ${lowerLimit.billingdate}\n    AND b.demographic_no &gt; 0\n    GROUP BY b.demographic_no HAVING COUNT(b.demographic_no) &gt; -1\n  ) BILLING\n  ON (d.demographic_no = BILLING.demographic_no)  \n  -- Coded dx during visit encounter in last 3 years\n  LEFT JOIN(\n    SELECT ASSIGNED.demographic_no AS demographic_no, observation_date AS visitdate, uuid, COUNT(ASSIGNED.demographic_no) AS visitcount\n    FROM casemgmt_note note\n    INNER JOIN (\n      SELECT note_id, CODED.demo_no AS demographic_no FROM casemgmt_issue_notes\n      INNER JOIN (\n        SELECT ci.id AS id, ci.update_date, ci.demographic_no demo_no FROM issue i\n        INNER JOIN casemgmt_issue ci\n        ON i.issue_id = ci.issue_id\n        WHERE\n	(i.type LIKE ${dxcodesSysICD} AND LEFT(i.code, 3) IN (\'331\',\'290\')) OR\n	(i.type LIKE ${dxcodesSysSnoMed} AND i.code IN (\'52448006\'))\n      ) CODED\n      ON (casemgmt_issue_notes.id = CODED.id)\n      ORDER BY CODED.demo_no\n    ) ASSIGNED\n    ON note.note_id = ASSIGNED.note_id AND\n    -- GET LAST REVISION OF NOTE\n    note.note_id= (SELECT MAX(cmn.note_id) FROM casemgmt_note cmn WHERE cmn.uuid = note.uuid) AND\n    DATE(note.observation_date) &gt; ${lowerLimit.visitdate}\n    GROUP BY demographic_no\n    ORDER BY demographic_no\n  ) VISIT\n  ON (d.demographic_no = VISIT.demographic_no)\n  -- Consider Observation\n  LEFT JOIN ( \n    SELECT m.demographicNo AS demographic_no, m.dataField AS result, m.dateObserved AS dateObserved\n    FROM measurements m\n    INNER JOIN (\n      SELECT demographicNo, MAX(dateObserved) AS dateObserved\n      FROM measurements\n      WHERE type IN ${obsName}\n      GROUP BY demographicNo\n    ) mlast\n    ON m.demographicNo = mlast.demographicNo\n    WHERE m.type IN ${obsName}\n    AND m.dateObserved = mlast.dateObserved\n    AND (DATE(m.dateObserved) &gt; ${lowerLimit.obsdate})\n    AND m.demographicNo &gt; 0\n    GROUP BY m.demographicNo\n  ) OBSLAST\n  ON (d.demographic_no = OBSLAST.demographic_no) \n  WHERE d.patient_status LIKE ${active}\n  AND d.demographic_no &gt; 0\n  AND d.provider_no LIKE '${provider}'\n  AND d.demographic_no NOT IN ${excludedPatient}\n) fin\n		</query>\n	</indicatorQuery>\n	<drillDownQuery>\n		<version>03-13-2018</version>\n		<params>\n			<parameter id=\"provider\" name=\"Provider Number\" value=\"loggedInProvider\" />\n			<parameter id=\"active\" name=\"Active Patients\" value=\"\'AC\'\" />\n			<parameter id=\"dxcodesICD\" name=\"Dx Codes (ICD)\" value=\"331,290\" />\n                        <parameter id=\"dxcodesSysICD\" name=\"DX CodeSystem (ICD)\" value=\"\'icd9\'\" />\n                        <parameter id=\"dxcodesSnoMed\" name=\"Dx Codes (SnoMed)\" value=\"52448006\" />\n                        <parameter id=\"dxcodesSysSnoMed\" name=\"Dx CodeSystem (SnoMed)\" value=\"\'snomed%\'\" />\n			<parameter id=\"billingCode\" name=\"ICD9 Billing Code\" value=\"331,290\" />\n			<parameter id=\"numOfBills\" name=\"Number of ICD9 331,290 Bills\" value=\"2\" />\n			<parameter id=\"numOfVisits\" name=\"Number of Visit Encounters coded 331,290/ICD9\" value=\"2\" />\n			<parameter id=\"obsName\" name=\"Observation Name\" value=\"MMSE,MOCA\" />\n			<parameter id=\"obsResult\" name=\"Observation Result\" value=\"21\" />\n			<parameter id=\"excludedPatient\" name=\"Excluded Patient\" value=\"excludedPatient\" />\n		</params>\n		\n		<range>\n			<lowerLimit id=\"billingdate\" label=\"From Date\" name=\"Date\" value=\"DATE_SUB( NOW(), INTERVAL 3 YEAR )\" />\n			<upperLimit id=\"billingdate\" label=\"Date Today\" name=\"Date\" value=\"NOW()\" />\n			<lowerLimit id=\"visitdate\" label=\"From Date\" name=\"Date\" value=\"DATE_SUB( NOW(), INTERVAL 3 YEAR )\" />\n			<upperLimit id=\"visitdate\" label=\"Date Today\" name=\"Date\" value=\"NOW()\" />\n			<lowerLimit id=\"obsdate\" label=\"From Date\" name=\"Date\" value=\"DATE_SUB( NOW(), INTERVAL 3 YEAR )\" />\n			<upperLimit id=\"obsdate\" label=\"Date Today\" name=\"Date\" value=\"NOW()\" />\n		</range>\n		\n		<displayColumns>\n			<column id=\"demographic\" name=\"d.demographic_no\" title=\"Patient Id\" primary=\"true\" />\n			<column id=\"name\" name=\"CONCAT( d.last_name, \', \', d.first_name )\" title=\"Patient Name\" primary=\"false\" />\n			<column id=\"dob\" name=\"DATE_FORMAT( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth), \'%m-%d-%Y\' )\" title=\"Date of Birth\" primary=\"false\" />\n			<column id=\"age\" name=\"TIMESTAMPDIFF(YEAR, DATE( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth) ), CURDATE() )\" title=\"Age\" primary=\"false\" />\n			<column id=\"billings\" name=\"IFNULL( BILLING.billcount, \'0\')\" title=\"Billings\" primary=\"false\" />\n			<column id=\"visits\" name=\"IFNULL( VISIT.visitcount, \'0\')\" title=\"Visits\" primary=\"false\" />\n			<column id=\"observation\" name=\"IFNULL( OBSLAST.result, \'NA\')\" title=\"sMMSE/MOCA\" primary=\"false\" />\n			<column id=\"obsDate\" name=\"IFNULL( DATE_FORMAT( OBSLAST.dateObserved, \'%m-%d-%Y\' ), \'\')\" title=\"Test Date (mm-dd-yyyy)\" primary=\"false\" />\n\n		</displayColumns>\n		\n		<exportColumns>\n			<column id=\"demographic\" name=\"d.demographic_no\" title=\"Patient Id\" primary=\"true\" />\n                        <column id=\"firstName\" name=\"d.first_name\" title=\"First Name\" primary=\"false\" />\n                        <column id=\"lastName\" name=\"d.last_name\" title=\"Last Name\" primary=\"false\" />\n			<column id=\"dob\" name=\"DATE_FORMAT( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth), \'%m-%d-%Y\' )\" title=\"Date of Birth\" primary=\"false\" />\n			<column id=\"age\" name=\"TIMESTAMPDIFF(YEAR, DATE( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth) ), CURDATE() )\" title=\"Age\" primary=\"false\" />\n			<column id=\"billings\" name=\"IFNULL( BILLING.billcount, \'0\')\" title=\"Billings\" primary=\"false\" />\n			<column id=\"visits\" name=\"IFNULL( VISIT.visitcount, \'0\')\" title=\"Visits\" primary=\"false\" />\n			<column id=\"observation\" name=\"IFNULL( OBSLAST.result, \'NA\')\" title=\"sMMSE/MOCA\" primary=\"false\" />\n			<column id=\"obsDate\" name=\"IFNULL( DATE_FORMAT( OBSLAST.dateObserved, \'%m-%d-%Y\' ), \'\')\" title=\"Test Date (mm-dd-yyyy)\" primary=\"false\" />\n		</exportColumns>\n		<drillDownActions>\n			<action id=\"tickler\" name=\"Tickler\"/>\n			<action id=\"dxUpdate\" name=\"Disease Registry Update\" value=\"331\"/>\n			<action id=\"demoExcl\" name=\"Patient Exclusion\"/>\n		</drillDownActions>\n		<query>\n  SELECT *	\n  FROM demographic d \n  LEFT JOIN (\n    SELECT demographic_no, dxresearch_code, coding_system, COUNT(dxresearch_code)\n    FROM dxresearch\n    WHERE status=\'A\' AND\n    (\n      (LEFT(dxresearch_code, 3) IN (\'331\',\'290\') AND coding_system = ${dxcodesSysICD}) OR\n      (dxresearch_code IN (\'52448006\') AND coding_system LIKE ${dxcodesSysSnoMed})\n    )\n    GROUP BY demographic_no\n    ORDER BY demographic_no\n  ) dxr\n  ON d.demographic_no = dxr.demographic_no\n  -- Billed 331,290 in last 3 years\n  LEFT JOIN (\n    SELECT b.demographic_no AS demographic_no, b.dx_code1 AS dxcode1, b.dx_code2 AS dxcode2, b.dx_code3 AS dxcode3, COUNT(b.demographic_no) AS billcount\n    FROM billingmaster b\n    WHERE ( LEFT(b.dx_code1, 3) IN (\'331\',\'290\') OR LEFT(b.dx_code2, 3) IN (\'331\',\'290\') OR LEFT(b.dx_code3, 3) IN (\'331\',\'290\') )\n    AND DATE(b.createdate) &gt; ${lowerLimit.billingdate}\n    AND b.demographic_no &gt; 0\n    GROUP BY b.demographic_no HAVING COUNT(b.demographic_no) &gt; -1\n  ) BILLING\n  ON (d.demographic_no = BILLING.demographic_no)  \n  -- Coded dx during visit encounter in last 3 years\n  LEFT JOIN(\n    SELECT ASSIGNED.demographic_no AS demographic_no, observation_date AS visitdate, uuid, COUNT(ASSIGNED.demographic_no) AS visitcount\n    FROM casemgmt_note note\n    INNER JOIN (\n      SELECT note_id, CODED.demo_no AS demographic_no FROM casemgmt_issue_notes\n      INNER JOIN (\n        SELECT ci.id AS id, ci.update_date, ci.demographic_no demo_no FROM issue i\n        INNER JOIN casemgmt_issue ci\n        ON i.issue_id = ci.issue_id\n        WHERE\n	(i.type LIKE ${dxcodesSysICD} AND LEFT(i.code, 3) IN (\'331\',\'290\')) OR\n	(i.type LIKE ${dxcodesSysSnoMed} AND i.code IN (\'52448006\'))\n      ) CODED\n      ON (casemgmt_issue_notes.id = CODED.id)\n      ORDER BY CODED.demo_no\n    ) ASSIGNED\n    ON note.note_id = ASSIGNED.note_id AND\n    -- GET LAST REVISION OF NOTE\n    note.note_id= (SELECT MAX(cmn.note_id) FROM casemgmt_note cmn WHERE cmn.uuid = note.uuid) AND\n    DATE(note.observation_date) &gt; ${lowerLimit.visitdate}\n    GROUP BY demographic_no\n    ORDER BY demographic_no\n  ) VISIT\n  ON (d.demographic_no = VISIT.demographic_no)  \n  -- Consider OBS here if needed\n  LEFT JOIN ( \n    SELECT m.demographicNo AS demographic_no, m.dataField AS result, m.dateObserved AS dateObserved\n    FROM measurements m\n    INNER JOIN (\n      SELECT demographicNo, MAX(dateObserved) AS dateObserved\n      FROM measurements\n      WHERE type IN ${obsName}\n      GROUP BY demographicNo\n    ) mlast\n    ON m.demographicNo = mlast.demographicNo\n    WHERE m.type IN ${obsName}\n    AND m.dateObserved = mlast.dateObserved\n    AND (DATE(m.dateObserved) &gt; ${lowerLimit.obsdate})\n    AND m.demographicNo &gt; 0\n    GROUP BY m.demographicNo\n  ) OBSLAST\n  ON (d.demographic_no = OBSLAST.demographic_no) \n  WHERE d.patient_status LIKE ${active}\n  AND d.demographic_no &gt; 0\n  AND ( dxr.dxresearch_code IS NULL AND ((BILLING.billcount IS NOT NULL AND BILLING.billcount &gt;= ${numOfBills}) OR (VISIT.visitcount IS NOT NULL AND VISIT.visitcount &gt;= ${numOfVisits}) OR (OBSLAST.result IS NOT NULL AND OBSLAST.result &lt; ${obsResult})) )\n  AND d.provider_no LIKE ${provider}\n  AND d.demographic_no NOT IN ${excludedPatient}\n  ORDER BY d.demographic_no\n		</query>\n	</drillDownQuery>\n</indicatorTemplateXML>\n','','\0',0,NULL,NULL);
INSERT INTO `indicatorTemplate` (`dashboardId`, `name`, `category`, `subCategory`, `framework`, `frameworkVersion`, `definition`, `notes`, `template`, `active`, `locked`, `shared`, `metricSetName`, `metricLabel`) VALUES (@dashboardId3,'Depression Prevalence','CDM (Click on the pie chart to see the patients included in the pie chart)','Depression','DoBC CPQI PSP Panel','2017-08-02','Proportion of active patients in the practice coded with Depression.','\ni) Depression patients: \"Patient Status: Active\nAND has a Current Problem List/Profile item code of 35489007, 366979004 OR starting with 311, 296\"\nii) Other patients: \"Patient Status: Active\nAND does not have a Current Problem List/Profile item code of 3','<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<indicatorTemplateXML>\n	<author>OSCAR BC</author>\n	<uid></uid>\n	<heading>\n		<category>CDM (Click on the pie chart to see the patients included in the pie chart)</category>\n		<subCategory>Depression</subCategory>\n		<name>Depression Prevalence</name>\n		<definition>Proportion of active patients in the practice coded with Depression.</definition>\n		<framework>DoBC CPQI PSP Panel</framework>\n		<frameworkVersion>08-02-2017</frameworkVersion>\n		<notes>\ni) Depression patients: \"Patient Status: Active\nAND has a Current Problem List/Profile item code of 35489007, 366979004 OR starting with 311, 296\"\nii) Other patients: \"Patient Status: Active\nAND does not have a Current Problem List/Profile item code of 35489007, 366979004 OR starting with 311, 296\"\n		</notes>\n	</heading>\n	<indicatorQuery>\n		<version>03-13-2018</version>\n		<params>\n			<!-- \n				Required parameter provider. Value options are: \n					[ an individual providerNo, or provider range ] ie: value=\"370, 1001\" \n					\"all\" ie: value=\"all\" including null.\n					\"loggedInProvider\" ie:\n				Default is \"loggedInProvider\"\n				Use this parameter in the query as '${provider}'\n				This parameter should be used for fetching patient\'s assigned to a MRP.\n				ie: WHERE demographic.provider_no = ${provider}\n			-->\n			<parameter id=\"provider\" name=\"provider_no\" value=\"loggedInProvider\" />\n			<parameter id=\"pstatus\" name=\"Patient Status\" value=\"\'AC\'\" />\n			<parameter id=\"dxcodesICD\" name=\"Dx Codes (ICD)\" value=\"311,296\" />\n			<parameter id=\"dxcodesSysICD\" name=\"DX CodeSystem (ICD)\" value=\"\'icd9\'\" />\n			<parameter id=\"dxcodesSnoMed\" name=\"Dx Codes (SnoMed)\" value=\"35489007,366979004\" />\n			<parameter id=\"dxcodesSysSnoMed\" name=\"Dx CodeSystem (SnoMed)\" value=\"\'snomed%\'\" />\n			<parameter id=\"excludedPatient\" name=\"Excluded Patient\" value=\"excludedPatient\" />\n		</params>\n		<query>\n			<!-- Indicator SQL Query here -->\nSELECT\n -- TOTAL NUMBER OF PATIENTS NOTED AS ACTIVE WITH CURRENT DEPRESSION\n IF( COUNT(fin.patient) &gt; 0, SUM( IF( dxresearch_code is not NULL , 1, 0) ) , 0 ) AS \"Number Active, Current Depression\",\n -- TOTAL NUMBER OF PATIENTS NOTED AS ACTIVE WITHOUT CURRENT DEPRESSION\n IF( COUNT(fin.patient) &gt; 0, SUM( IF( dxresearch_code is NULL , 1, 0) ) , 0 ) AS \"Number Active, No Current Depression\"\nFROM (\n SELECT d.demographic_no AS patient, d.patient_status AS pt_status, dxresearch_code\n FROM demographic d\n LEFT JOIN (\n  SELECT demographic_no, dxresearch_code, COUNT(dxresearch_code)\n  FROM dxresearch\n  WHERE status=\'A\' AND\n  (\n   (LEFT(dxresearch_code, 3) IN (\'311\', \'296\') AND coding_system = ${dxcodesSysICD}) OR\n   (dxresearch_code IN (\'35489007\', \'366979004\') AND coding_system LIKE ${dxcodesSysSnoMed})\n   )\n  GROUP BY demographic_no\n  ORDER BY demographic_no\n ) problems\n ON d.demographic_no = problems.demographic_no\n WHERE d.provider_no LIKE ${provider} AND d.patient_status = ${pstatus}\n AND d.demographic_no NOT IN ${excludedPatient}\n) fin\n		</query>\n	</indicatorQuery>\n	<drillDownQuery>\n		<version>03-13-2018</version>\n		<params>\n			<parameter id=\"provider\" name=\"provider_no\" value=\"loggedInProvider\" />	\n			<parameter id=\"pstatus\" name=\"Patient Status\" value=\"\'AC\'\" />\n			<parameter id=\"dxcodesICD\" name=\"Dx Codes (ICD)\" value=\"311,296\" />\n			<parameter id=\"dxcodesSysICD\" name=\"DX CodeSystem (ICD)\" value=\"\'icd9\'\" />\n			<parameter id=\"dxcodesSnoMed\" name=\"Dx Codes (SnoMed)\" value=\"35489007,366979004\" />\n			<parameter id=\"dxcodesSysSnoMed\" name=\"Dx CodeSystem (SnoMed)\" value=\"\'snomed%\'\" />\n			<parameter id=\"excludedPatient\" name=\"Excluded Patient\" value=\"excludedPatient\" />\n		</params>\n		<displayColumns>\n			<column id=\"demographic\" name=\"d.demographic_no\" title=\"Patient Id\" primary=\"true\" />\n			<column id=\"name\" name=\"CONCAT( d.last_name, \', \', d.first_name )\" title=\"Patient Name\" primary=\"false\" />\n			<column id=\"dob\" name=\"DATE_FORMAT( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth), \'%m-%d-%Y\' )\" title=\"Date of Birth (mm-dd-yy)\" primary=\"false\" />\n			<column id=\"age\" name=\"TIMESTAMPDIFF(YEAR, DATE( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth) ), CURDATE() )\" title=\"Age\" primary=\"false\" />\n			<column id=\"dxcode\" title=\"Dx System/Code\" name=\"CONCAT(MIN(dxr.coding_system),\'/\',MIN(dxr.dxresearch_code),IF( MIN(dxr.dxresearch_code) != MAX(dxr.dxresearch_code),CONCAT( \' &amp; \', MAX(dxr.coding_system), \'/\', MAX(dxr.dxresearch_code) ),\'\') )\" primary=\"false\" />\n		</displayColumns>\n		<exportColumns>\n			<column id=\"demographic\" name=\"d.demographic_no\" title=\"Patient Id\" primary=\"true\" />\n			<column id=\"firstName\" name=\"d.first_name\" title=\"First Name\" primary=\"false\" />\n			<column id=\"lastName\" name=\"d.last_name\" title=\"Last Name\" primary=\"false\" />\n			<column id=\"dob\" name=\"DATE_FORMAT( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth), \'%m-%d-%Y\' )\" title=\"Date of Birth (mm-dd-yy)\" primary=\"false\" />\n			<column id=\"age\" name=\"TIMESTAMPDIFF(YEAR, DATE( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth) ), CURDATE() )\" title=\"Age\" primary=\"false\" />\n			<column id=\"dxcode\" title=\"Dx System/Code\" name=\"CONCAT(MIN(dxr.coding_system),\'/\',MIN(dxr.dxresearch_code),IF( MIN(dxr.dxresearch_code) != MAX(dxr.dxresearch_code),CONCAT( \' &amp; \', MAX(dxr.coding_system), \'/\', MAX(dxr.dxresearch_code) ),\'\') )\" primary=\"false\" />\n		</exportColumns>\n		<drillDownActions>\n			<action id=\"tickler\" name=\"Tickler\"/>\n			<action id=\"demoExcl\" name=\"Patient Exclusion\"/>\n		</drillDownActions>\n		<query>\n			<!-- Drilldown SQL Query here -->\nSELECT\n*			\nFROM demographic d\nLEFT JOIN dxresearch dxr\nON d.demographic_no = dxr.demographic_no\nWHERE d.patient_status=${pstatus}\nAND\n(\n (LEFT(dxr.dxresearch_code, 3) IN (\'311\', \'296\') AND dxr.coding_system = ${dxcodesSysICD}) OR\n (dxr.dxresearch_code IN (\'35489007\', \'366979004\') AND dxr.coding_system LIKE ${dxcodesSysSnoMed})\n )\nAND dxr.status = \'A\'\nAND d.provider_no LIKE ${provider}\nAND d.demographic_no NOT IN ${excludedPatient}\nGROUP BY d.demographic_no\nORDER BY COUNT(d.demographic_no) DESC, d.demographic_no\n		</query>\n	</drillDownQuery>\n</indicatorTemplateXML>\n','','\0',0,NULL,NULL);
INSERT INTO `indicatorTemplate` (`dashboardId`, `name`, `category`, `subCategory`, `framework`, `frameworkVersion`, `definition`, `notes`, `template`, `active`, `locked`, `shared`, `metricSetName`, `metricLabel`) VALUES (@dashboardId3,'Consider Depression','CDM (Click on the pie chart to see the patients included in the pie chart)','Depression','DoBC CPQI PSP Panel','2017-08-02','\n			Patients with depression dx in 2+ bills or 2+ visit encounters within last 3 years or most recent PHQ-9 >= 15 in past 3 months\n		','\n		  Created for Doctors of BC\n		  i) Depression dx in 2+ bills: \"Patient Status: Active\nAND does not have a Current Problem List/Profile item code of 35489007, 366979004 OR starting with 311 OR 296\nAND with >=2 Bills with the ICD9 diagnosis code of 50B o','<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<indicatorTemplateXML>\n	<author>OSCAR BC</author>\n	<uid></uid>\n	<heading>\n		<category>CDM (Click on the pie chart to see the patients included in the pie chart)</category>\n		<subCategory>Depression</subCategory>\n		<name>Consider Depression</name>\n		<definition>\n			Patients with depression dx in 2+ bills or 2+ visit encounters within last 3 years or most recent PHQ-9 &gt;= 15 in past 3 months\n		</definition>\n		<framework>DoBC CPQI PSP Panel</framework>\n		<frameworkVersion>08-02-2017</frameworkVersion>\n		<notes>\n		  Created for Doctors of BC\n		  i) Depression dx in 2+ bills: \"Patient Status: Active\nAND does not have a Current Problem List/Profile item code of 35489007, 366979004 OR starting with 311 OR 296\nAND with >=2 Bills with the ICD9 diagnosis code of 50B or starting with 311, 296 in the last 3 years\"\n		  ii) Depression dx in 2+ visits: \"Patient Status: Active\nAND does not have a Current Problem List/Profile item code of 35489007, 366979004 OR starting with 311 OR 296\nAND with >= 2 Visit Encounters with Snomed diagnosis code 35489007 or the ICD9 diagnosis code starting with 311 OR 296 in the last 3 years\"\n                  iii) Depression PHQ-9 score &gt;= 15: \"Patient Status: Active\nAND does not have a Current Problem List/Profile item code of 35489007 OR starting with 311 OR 296\nAND WITH a most recent PHQ-9 score &gt;=15 in the past 3 months\"\n		</notes>\n	</heading>\n	<indicatorQuery>\n		<version>03-13-2018</version>\n		<params>\n			<parameter id=\"provider\" name=\"Provider Number\" value=\"loggedInProvider\" />\n			<parameter id=\"active\" name=\"Active Patients\" value=\"\'AC\'\" />\n			<parameter id=\"dxcodesICD\" name=\"Dx Codes (ICD)\" value=\"311,296\" />\n                        <parameter id=\"dxcodesSysICD\" name=\"DX CodeSystem (ICD)\" value=\"\'icd9\'\" />\n                        <parameter id=\"dxcodesSnoMed\" name=\"Dx Codes (SnoMed)\" value=\"35489007,366979004\" />\n                        <parameter id=\"dxcodesSysSnoMed\" name=\"Dx CodeSystem (SnoMed)\" value=\"\'snomed%\'\" />\n			<parameter id=\"billingCode\" name=\"ICD9 Billing Code\" value=\"311,296\" />\n			<parameter id=\"numOfBills\" name=\"Number of ICD9 311,296 Bills\" value=\"2\" />\n			<parameter id=\"numOfVisits\" name=\"Number of Visit Encounters coded 311,296/ICD9\" value=\"2\" />\n			<parameter id=\"obsName\" name=\"Observation Name\" value=\"\'PHQ9\'\" />\n			<parameter id=\"obsResult\" name=\"Observation Result\" value=\"15\" />\n			<parameter id=\"excludedPatient\" name=\"Excluded Patient\" value=\"excludedPatient\" />\n		</params>\n		<range>\n			<lowerLimit id=\"billingdate\" label=\"From Date\" name=\"Date\" value=\"DATE_SUB( NOW(), INTERVAL 3 YEAR )\" />\n			<upperLimit id=\"billingdate\" label=\"Date Today\" name=\"Date\" value=\"NOW()\" />\n			<lowerLimit id=\"visitdate\" label=\"From Date\" name=\"Date\" value=\"DATE_SUB( NOW(), INTERVAL 3 YEAR )\" />\n			<upperLimit id=\"visitdate\" label=\"Date Today\" name=\"Date\" value=\"NOW()\" />\n			<lowerLimit id=\"obsdate\" label=\"From Date\" name=\"Date\" value=\"DATE_SUB( NOW(), INTERVAL 3 MONTH )\" />\n			<upperLimit id=\"obsdate\" label=\"Date Today\" name=\"Date\" value=\"NOW()\" />\n		</range>\n		<query>\nSELECT\n  IF ( COUNT(fin.patient) &gt; 0, SUM( CASE WHEN (fin.dx IS NULL AND fin.billcount &gt;= ${numOfBills}) THEN 1 ELSE 0 END ) , 0) AS \"Number with Depression dx in ${numOfBills}+ bills\",\n  IF ( COUNT(fin.patient) &gt; 0, SUM( CASE WHEN (fin.dx IS NULL AND fin.visitcount &gt;= ${numOfVisits}) THEN 1 ELSE 0 END ) , 0) AS \"Number with Depression dx in ${numOfVisits}+ visits\",\n  IF ( COUNT(fin.patient) &gt; 0, SUM( CASE WHEN (fin.dx IS NULL AND fin.result &gt;= ${obsResult}) THEN 1 ELSE 0 END ) , 0) AS \"Number with PHQ-9 &gt;= ${obsResult}\"\nFROM ( \n  SELECT \n    d.demographic_no AS patient, \n    BILLING.billcount AS billcount,\n    VISIT.visitcount AS visitcount,\n    OBSLAST.result AS result,\n    dxr.dxresearch_code AS dx,\n    dxr.coding_system AS dxs\n  FROM demographic d \n  LEFT JOIN (\n    SELECT demographic_no, dxresearch_code, coding_system, COUNT(dxresearch_code)\n    FROM dxresearch\n    WHERE status=\'A\' AND\n    (\n      (LEFT(dxresearch_code, 3) IN (\'311\',\'296\') AND coding_system = ${dxcodesSysICD}) OR\n      (dxresearch_code IN (\'35489007\',\'366979004\') AND coding_system LIKE ${dxcodesSysSnoMed})\n    )\n    GROUP BY demographic_no\n    ORDER BY demographic_no\n  ) dxr\n  ON d.demographic_no = dxr.demographic_no\n  -- Billed 311,296 in last 3 years\n  LEFT JOIN (\n    SELECT b.demographic_no AS demographic_no, b.dx_code1 AS dxcode1, b.dx_code2 AS dxcode2, b.dx_code3 AS dxcode3, COUNT(b.demographic_no) AS billcount\n    FROM billingmaster b\n    WHERE ( LEFT(b.dx_code1, 3) IN (\'311\',\'296\') OR LEFT(b.dx_code2, 3) IN (\'311\',\'296\') OR LEFT(b.dx_code3, 3) IN (\'311\',\'296\')\n     OR b.dx_code1 IN (\'50B\') OR b.dx_code2 IN (\'50B\') OR b.dx_code3 IN (\'50B\') )\n    AND DATE(b.createdate) &gt; ${lowerLimit.billingdate}\n    AND b.demographic_no &gt; 0\n    GROUP BY b.demographic_no HAVING COUNT(b.demographic_no) &gt; -1\n  ) BILLING\n  ON (d.demographic_no = BILLING.demographic_no)  \n  -- Coded dx during visit encounter in last 3 years\n  LEFT JOIN(\n    SELECT ASSIGNED.demographic_no AS demographic_no, observation_date AS visitdate, uuid, COUNT(ASSIGNED.demographic_no) AS visitcount\n    FROM casemgmt_note note\n    INNER JOIN (\n      SELECT note_id, CODED.demo_no AS demographic_no FROM casemgmt_issue_notes\n      INNER JOIN (\n        SELECT ci.id AS id, ci.update_date, ci.demographic_no demo_no FROM issue i\n        INNER JOIN casemgmt_issue ci\n        ON i.issue_id = ci.issue_id\n        WHERE\n	(i.type LIKE ${dxcodesSysICD} AND LEFT(i.code, 3) IN (\'311\',\'296\')) OR\n	(i.type LIKE ${dxcodesSysSnoMed} AND i.code IN (\'35489007\',\'366979004\'))\n      ) CODED\n      ON (casemgmt_issue_notes.id = CODED.id)\n      ORDER BY CODED.demo_no\n    ) ASSIGNED\n    ON note.note_id = ASSIGNED.note_id AND\n    -- GET LAST REVISION OF NOTE\n    note.note_id= (SELECT MAX(cmn.note_id) FROM casemgmt_note cmn WHERE cmn.uuid = note.uuid) AND\n    DATE(note.observation_date) &gt; ${lowerLimit.visitdate}\n    GROUP BY demographic_no\n    ORDER BY demographic_no\n  ) VISIT\n  ON (d.demographic_no = VISIT.demographic_no)\n  -- Consider Observation\n  LEFT JOIN ( \n    SELECT m.demographicNo AS demographic_no, m.dataField AS result, m.dateObserved AS dateObserved\n    FROM measurements m\n    INNER JOIN (\n      SELECT demographicNo, MAX(dateObserved) AS dateObserved\n      FROM measurements\n      WHERE type = ${obsName}\n      GROUP BY demographicNo\n    ) mlast\n    ON m.demographicNo = mlast.demographicNo\n    WHERE m.type = ${obsName}\n    AND m.dateObserved = mlast.dateObserved\n    AND (DATE(m.dateObserved) &gt; ${lowerLimit.obsdate})\n    AND m.demographicNo &gt; 0\n    GROUP BY m.demographicNo HAVING COUNT(m.demographicNo) > -1\n  ) OBSLAST\n  ON (d.demographic_no = OBSLAST.demographic_no)  \n  WHERE d.patient_status LIKE ${active}\n  AND d.demographic_no &gt; 0\n  AND d.provider_no LIKE '${provider}'\n  AND d.demographic_no NOT IN ${excludedPatient}\n) fin\n		</query>\n	</indicatorQuery>\n	<drillDownQuery>\n		<version>03-13-2018</version>\n		<params>\n			<parameter id=\"provider\" name=\"Provider Number\" value=\"loggedInProvider\" />\n			<parameter id=\"active\" name=\"Active Patients\" value=\"\'AC\'\" />\n			<parameter id=\"dxcodesICD\" name=\"Dx Codes (ICD)\" value=\"311,296\" />\n                        <parameter id=\"dxcodesSysICD\" name=\"DX CodeSystem (ICD)\" value=\"\'icd9\'\" />\n                        <parameter id=\"dxcodesSnoMed\" name=\"Dx Codes (SnoMed)\" value=\"35489007,366979004\" />\n                        <parameter id=\"dxcodesSysSnoMed\" name=\"Dx CodeSystem (SnoMed)\" value=\"\'snomed%\'\" />\n			<parameter id=\"billingCode\" name=\"ICD9 Billing Code\" value=\"311,296\" />\n			<parameter id=\"numOfBills\" name=\"Number of ICD9 311,296 Bills\" value=\"2\" />\n			<parameter id=\"numOfVisits\" name=\"Number of Visit Encounters coded 311,296/ICD9\" value=\"2\" />\n			<parameter id=\"obsName\" name=\"Observation Name\" value=\"\'PHQ9\'\" />\n			<parameter id=\"obsResult\" name=\"Observation Result\" value=\"15\" />\n			<parameter id=\"excludedPatient\" name=\"Excluded Patient\" value=\"excludedPatient\" />\n		</params>\n		\n		<range>\n			<lowerLimit id=\"billingdate\" label=\"From Date\" name=\"Date\" value=\"DATE_SUB( NOW(), INTERVAL 3 YEAR )\" />\n			<upperLimit id=\"billingdate\" label=\"Date Today\" name=\"Date\" value=\"NOW()\" />\n			<lowerLimit id=\"visitdate\" label=\"From Date\" name=\"Date\" value=\"DATE_SUB( NOW(), INTERVAL 3 YEAR )\" />\n			<upperLimit id=\"visitdate\" label=\"Date Today\" name=\"Date\" value=\"NOW()\" />\n			<lowerLimit id=\"obsdate\" label=\"From Date\" name=\"Date\" value=\"DATE_SUB( NOW(), INTERVAL 3 MONTH )\" />\n			<upperLimit id=\"obsdate\" label=\"Date Today\" name=\"Date\" value=\"NOW()\" />\n		</range>\n		\n		<displayColumns>\n			<column id=\"demographic\" name=\"d.demographic_no\" title=\"Patient Id\" primary=\"true\" />\n			<column id=\"name\" name=\"CONCAT( d.last_name, \', \', d.first_name )\" title=\"Patient Name\" primary=\"false\" />\n			<column id=\"dob\" name=\"DATE_FORMAT( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth), \'%m-%d-%Y\' )\" title=\"Date of Birth\" primary=\"false\" />\n			<column id=\"age\" name=\"TIMESTAMPDIFF(YEAR, DATE( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth) ), CURDATE() )\" title=\"Age\" primary=\"false\" />\n			<column id=\"billings\" name=\"IFNULL( BILLING.billcount, \'0\')\" title=\"Billings\" primary=\"false\" />\n			<column id=\"visits\" name=\"IFNULL( VISIT.visitcount, \'0\')\" title=\"Visits\" primary=\"false\" />\n			<column id=\"observation\" name=\"IFNULL( OBSLAST.result, \'NA\')\" title=\"PHQ9\" primary=\"false\" />\n			<column id=\"obsDate\" name=\"IFNULL( DATE_FORMAT( OBSLAST.dateObserved, \'%m-%d-%Y\' ), \'\')\" title=\"Test Date (mm-dd-yyyy)\" primary=\"false\" />\n\n		</displayColumns>\n		\n		<exportColumns>\n			<column id=\"demographic\" name=\"d.demographic_no\" title=\"Patient Id\" primary=\"true\" />\n                        <column id=\"firstName\" name=\"d.first_name\" title=\"First Name\" primary=\"false\" />\n                        <column id=\"lastName\" name=\"d.last_name\" title=\"Last Name\" primary=\"false\" />\n			<column id=\"dob\" name=\"DATE_FORMAT( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth), \'%m-%d-%Y\' )\" title=\"Date of Birth\" primary=\"false\" />\n			<column id=\"age\" name=\"TIMESTAMPDIFF(YEAR, DATE( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth) ), CURDATE() )\" title=\"Age\" primary=\"false\" />\n			<column id=\"billings\" name=\"IFNULL( BILLING.billcount, \'0\')\" title=\"Billings\" primary=\"false\" />\n			<column id=\"visits\" name=\"IFNULL( VISIT.visitcount, \'0\')\" title=\"Visits\" primary=\"false\" />\n			<column id=\"observation\" name=\"IFNULL( OBSLAST.result, \'NA\')\" title=\"PHQ9\" primary=\"false\" />\n			<column id=\"obsDate\" name=\"IFNULL( DATE_FORMAT( OBSLAST.dateObserved, \'%m-%d-%Y\' ), \'\')\" title=\"Test Date (mm-dd-yyyy)\" primary=\"false\" />\n		</exportColumns>\n		<drillDownActions>\n			<action id=\"tickler\" name=\"Tickler\"/>\n			<action id=\"dxUpdate\" name=\"Disease Registry Update\" value=\"311\"/>\n			<action id=\"demoExcl\" name=\"Patient Exclusion\"/>\n		</drillDownActions>\n		<query>\n  SELECT *	\n  FROM demographic d \n  LEFT JOIN (\n    SELECT demographic_no, dxresearch_code, coding_system, COUNT(dxresearch_code)\n    FROM dxresearch\n    WHERE status=\'A\' AND\n    (\n      (LEFT(dxresearch_code, 3) IN (\'311\',\'296\') AND coding_system = ${dxcodesSysICD}) OR\n      (dxresearch_code IN (\'35489007\',\'366979004\') AND coding_system LIKE ${dxcodesSysSnoMed})\n    )\n    GROUP BY demographic_no\n    ORDER BY demographic_no\n  ) dxr\n  ON d.demographic_no = dxr.demographic_no\n  -- Billed 311,296 in last 3 years\n  LEFT JOIN (\n    SELECT b.demographic_no AS demographic_no, b.dx_code1 AS dxcode1, b.dx_code2 AS dxcode2, b.dx_code3 AS dxcode3, COUNT(b.demographic_no) AS billcount\n    FROM billingmaster b\n    WHERE ( LEFT(b.dx_code1, 3) IN (\'311\',\'296\') OR LEFT(b.dx_code2, 3) IN (\'311\',\'296\') OR LEFT(b.dx_code3, 3) IN (\'311\',\'296\')\n     OR b.dx_code1 IN (\'50B\') OR b.dx_code2 IN (\'50B\') OR b.dx_code3 IN (\'50B\') )\n    AND DATE(b.createdate) &gt; ${lowerLimit.billingdate}\n    AND b.demographic_no &gt; 0\n    GROUP BY b.demographic_no HAVING COUNT(b.demographic_no) &gt; -1\n  ) BILLING\n  ON (d.demographic_no = BILLING.demographic_no)  \n  -- Coded dx during visit encounter in last 3 years\n  LEFT JOIN(\n    SELECT ASSIGNED.demographic_no AS demographic_no, observation_date AS visitdate, uuid, COUNT(ASSIGNED.demographic_no) AS visitcount\n    FROM casemgmt_note note\n    INNER JOIN (\n      SELECT note_id, CODED.demo_no AS demographic_no FROM casemgmt_issue_notes\n      INNER JOIN (\n        SELECT ci.id AS id, ci.update_date, ci.demographic_no demo_no FROM issue i\n        INNER JOIN casemgmt_issue ci\n        ON i.issue_id = ci.issue_id\n        WHERE\n	(i.type LIKE ${dxcodesSysICD} AND LEFT(i.code, 3) IN (\'311\',\'296\')) OR\n	(i.type LIKE ${dxcodesSysSnoMed} AND i.code IN (\'35489007\',\'366979004\'))\n      ) CODED\n      ON (casemgmt_issue_notes.id = CODED.id)\n      ORDER BY CODED.demo_no\n    ) ASSIGNED\n    ON note.note_id = ASSIGNED.note_id AND\n    -- GET LAST REVISION OF NOTE\n    note.note_id= (SELECT MAX(cmn.note_id) FROM casemgmt_note cmn WHERE cmn.uuid = note.uuid) AND\n    DATE(note.observation_date) &gt; ${lowerLimit.visitdate}\n    GROUP BY demographic_no\n    ORDER BY demographic_no\n  ) VISIT\n  ON (d.demographic_no = VISIT.demographic_no)  \n  -- Consider OBS here if needed\n  LEFT JOIN ( \n    SELECT m.demographicNo AS demographic_no, m.dataField AS result, m.dateObserved AS dateObserved\n    FROM measurements m\n    INNER JOIN (\n      SELECT demographicNo, MAX(dateObserved) AS dateObserved\n      FROM measurements\n      WHERE type = ${obsName}\n      GROUP BY demographicNo\n    ) mlast\n    ON m.demographicNo = mlast.demographicNo\n    WHERE m.type = ${obsName}\n    AND m.dateObserved = mlast.dateObserved\n    AND (DATE(m.dateObserved) &gt; ${lowerLimit.obsdate})\n    AND m.demographicNo &gt; 0\n    GROUP BY m.demographicNo HAVING COUNT(m.demographicNo) > -1\n  ) OBSLAST\n  ON (d.demographic_no = OBSLAST.demographic_no)  \n  WHERE d.patient_status LIKE ${active}\n  AND d.demographic_no &gt; 0\n  AND ( dxr.dxresearch_code IS NULL AND ((BILLING.billcount IS NOT NULL AND BILLING.billcount &gt;= ${numOfBills}) OR (VISIT.visitcount IS NOT NULL AND VISIT.visitcount &gt;= ${numOfVisits}) OR (OBSLAST.result IS NOT NULL AND OBSLAST.result &gt;= ${obsResult})) )\n  AND d.provider_no LIKE ${provider}\n  AND d.demographic_no NOT IN ${excludedPatient}\n  ORDER BY d.demographic_no\n		</query>\n	</drillDownQuery>\n</indicatorTemplateXML>\n','','\0',0,NULL,NULL);
INSERT INTO `indicatorTemplate` (`dashboardId`, `name`, `category`, `subCategory`, `framework`, `frameworkVersion`, `definition`, `notes`, `template`, `active`, `locked`, `shared`, `metricSetName`, `metricLabel`) VALUES (@dashboardId3,'Drug Dependence Syndrome Prevalence','CDM (Click on the pie chart to see the patients included in the pie chart)','Drug Dependence Syndrome','DoBC CPQI PSP Panel','2017-08-02','Proportion of active patients in the practice coded with Drug Dependence Syndrome.','\ni) Drug dependence patients: \"Patient Status: Active\nAND has a Current Problem List/Profile item code of 191816009 OR starting with 304\"\nii) Other patients: \"Patient Status: Active\nAND does not have a Current Problem List/Profile item code of 191816009 O','<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<indicatorTemplateXML>\n	<author>OSCAR BC</author>\n	<uid></uid>\n	<heading>\n		<category>CDM (Click on the pie chart to see the patients included in the pie chart)</category>\n		<subCategory>Drug Dependence Syndrome</subCategory>\n		<name>Drug Dependence Syndrome Prevalence</name>\n		<definition>Proportion of active patients in the practice coded with Drug Dependence Syndrome.</definition>\n		<framework>DoBC CPQI PSP Panel</framework>\n		<frameworkVersion>08-02-2017</frameworkVersion>\n		<notes>\ni) Drug dependence patients: \"Patient Status: Active\nAND has a Current Problem List/Profile item code of 191816009 OR starting with 304\"\nii) Other patients: \"Patient Status: Active\nAND does not have a Current Problem List/Profile item code of 191816009 OR starting with 304\"\n		</notes>\n	</heading>\n	<indicatorQuery>\n		<version>03-13-2018</version>\n		<params>\n			<!-- \n				Required parameter provider. Value options are: \n					[ an individual providerNo, or provider range ] ie: value=\"370, 1001\" \n					\"all\" ie: value=\"all\" including null.\n					\"loggedInProvider\" ie:\n				Default is \"loggedInProvider\"\n				Use this parameter in the query as '${provider}'\n				This parameter should be used for fetching patient\'s assigned to a MRP.\n				ie: WHERE demographic.provider_no = ${provider}\n			-->\n			<parameter id=\"provider\" name=\"provider_no\" value=\"loggedInProvider\" />\n			<parameter id=\"pstatus\" name=\"Patient Status\" value=\"\'AC\'\" />\n			<parameter id=\"dxcodesICD\" name=\"Dx Codes (ICD)\" value=\"304\" />\n			<parameter id=\"dxcodesSysICD\" name=\"DX CodeSystem (ICD)\" value=\"\'icd9\'\" />\n			<parameter id=\"dxcodesSnoMed\" name=\"Dx Codes (SnoMed)\" value=\"191816009\" />\n			<parameter id=\"dxcodesSysSnoMed\" name=\"Dx CodeSystem (SnoMed)\" value=\"\'snomed%\'\" />\n			<parameter id=\"excludedPatient\" name=\"Excluded Patient\" value=\"excludedPatient\" />\n		</params>\n		<query>\n			<!-- Indicator SQL Query here -->\nSELECT\n -- TOTAL NUMBER OF PATIENTS NOTED AS ACTIVE WITH CURRENT DRUG DEPENDENCE SYNDROME\n IF( COUNT(fin.patient) &gt; 0, SUM( IF( dxresearch_code is not NULL , 1, 0) ) , 0 ) AS \"Number Active, Current Drug Dependence Syndrome\",\n -- TOTAL NUMBER OF PATIENTS NOTED AS ACTIVE WITHOUT CURRENT DRUG DEPENDENCE SYNDROME\n IF( COUNT(fin.patient) &gt; 0, SUM( IF( dxresearch_code is NULL , 1, 0) ) , 0 ) AS \"Number Active, No Current Drug Dependence Syndrome\"\nFROM (\n SELECT d.demographic_no AS patient, d.patient_status AS pt_status, dxresearch_code\n FROM demographic d\n LEFT JOIN (\n  SELECT demographic_no, dxresearch_code, COUNT(dxresearch_code)\n  FROM dxresearch\n  WHERE status=\'A\' AND\n  (\n   (LEFT(dxresearch_code, 3) IN (\'304\') AND coding_system = ${dxcodesSysICD}) OR\n   (dxresearch_code IN (\'191816009\') AND coding_system LIKE ${dxcodesSysSnoMed})\n   )\n  GROUP BY demographic_no\n  ORDER BY demographic_no\n ) problems\n ON d.demographic_no = problems.demographic_no\n WHERE d.provider_no LIKE ${provider} AND d.patient_status = ${pstatus}\n AND d.demographic_no NOT IN ${excludedPatient}\n) fin\n		</query>\n	</indicatorQuery>\n	<drillDownQuery>\n		<version>03-13-2018</version>\n		<params>\n			<parameter id=\"provider\" name=\"provider_no\" value=\"loggedInProvider\" />	\n			<parameter id=\"pstatus\" name=\"Patient Status\" value=\"\'AC\'\" />\n			<parameter id=\"dxcodesICD\" name=\"Dx Codes (ICD)\" value=\"304\" />\n			<parameter id=\"dxcodesSysICD\" name=\"DX CodeSystem (ICD)\" value=\"\'icd9\'\" />\n			<parameter id=\"dxcodesSnoMed\" name=\"Dx Codes (SnoMed)\" value=\"191816009\" />\n			<parameter id=\"dxcodesSysSnoMed\" name=\"Dx CodeSystem (SnoMed)\" value=\"\'snomed%\'\" />\n			<parameter id=\"excludedPatient\" name=\"Excluded Patient\" value=\"excludedPatient\" />\n		</params>\n		<displayColumns>\n			<column id=\"demographic\" name=\"d.demographic_no\" title=\"Patient Id\" primary=\"true\" />\n			<column id=\"name\" name=\"CONCAT( d.last_name, \', \', d.first_name )\" title=\"Patient Name\" primary=\"false\" />\n			<column id=\"dob\" name=\"DATE_FORMAT( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth), \'%m-%d-%Y\' )\" title=\"Date of Birth (mm-dd-yy)\" primary=\"false\" />\n			<column id=\"age\" name=\"TIMESTAMPDIFF(YEAR, DATE( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth) ), CURDATE() )\" title=\"Age\" primary=\"false\" />\n			<column id=\"dxcode\" title=\"Dx System/Code\" name=\"CONCAT(MIN(dxr.coding_system),\'/\',MIN(dxr.dxresearch_code),IF( MIN(dxr.dxresearch_code) != MAX(dxr.dxresearch_code),CONCAT( \' &amp; \', MAX(dxr.coding_system), \'/\', MAX(dxr.dxresearch_code) ),\'\') )\" primary=\"false\" />\n		</displayColumns>\n		<exportColumns>\n			<column id=\"demographic\" name=\"d.demographic_no\" title=\"Patient Id\" primary=\"true\" />\n			<column id=\"firstName\" name=\"d.first_name\" title=\"First Name\" primary=\"false\" />\n			<column id=\"lastName\" name=\"d.last_name\" title=\"Last Name\" primary=\"false\" />\n			<column id=\"dob\" name=\"DATE_FORMAT( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth), \'%m-%d-%Y\' )\" title=\"Date of Birth (mm-dd-yy)\" primary=\"false\" />\n			<column id=\"age\" name=\"TIMESTAMPDIFF(YEAR, DATE( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth) ), CURDATE() )\" title=\"Age\" primary=\"false\" />\n			<column id=\"dxcode\" title=\"Dx System/Code\" name=\"CONCAT(MIN(dxr.coding_system),\'/\',MIN(dxr.dxresearch_code),IF( MIN(dxr.dxresearch_code) != MAX(dxr.dxresearch_code),CONCAT( \' &amp; \', MAX(dxr.coding_system), \'/\', MAX(dxr.dxresearch_code) ),\'\') )\" primary=\"false\" />\n		</exportColumns>\n		<drillDownActions>\n			<action id=\"tickler\" name=\"Tickler\"/>\n			<action id=\"demoExcl\" name=\"Patient Exclusion\"/>\n		</drillDownActions>\n		<query>\n			<!-- Drilldown SQL Query here -->\nSELECT\n*			\nFROM demographic d\nLEFT JOIN dxresearch dxr\nON d.demographic_no = dxr.demographic_no\nWHERE d.patient_status=${pstatus}\nAND\n(\n (LEFT(dxr.dxresearch_code, 3) IN (\'304\') AND dxr.coding_system = ${dxcodesSysICD}) OR\n (dxr.dxresearch_code IN (\'191816009\') AND dxr.coding_system LIKE ${dxcodesSysSnoMed})\n )\nAND dxr.status = \'A\'\nAND d.provider_no LIKE ${provider}\nAND d.demographic_no NOT IN ${excludedPatient}\nGROUP BY d.demographic_no\nORDER BY COUNT(d.demographic_no) DESC, d.demographic_no\n		</query>\n	</drillDownQuery>\n</indicatorTemplateXML>\n','','\0',0,NULL,NULL);
INSERT INTO `indicatorTemplate` (`dashboardId`, `name`, `category`, `subCategory`, `framework`, `frameworkVersion`, `definition`, `notes`, `template`, `active`, `locked`, `shared`, `metricSetName`, `metricLabel`) VALUES (@dashboardId3,'Consider Drug Dependence Syndrome','CDM (Click on the pie chart to see the patients included in the pie chart)','Drug Dependence Syndrome','DoBC CPQI PSP Panel','2017-08-02','\n			Patients with drug dependence syndrome dx in 2+ bills or 2+ visit encounters within last 3 years\n		','\n		  Created for Doctors of BC\n		  i) Drug Dependence Syndrome dx in 2+ bills: \"Patient Status: Active\nAND does not have a Current Problem List/Profile item code of 191816009 OR starting with 304\nAND with >=2 Bills with the ICD9 diagnosis code starting wi','<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<indicatorTemplateXML>\n	<author>OSCAR BC</author>\n	<uid></uid>\n	<heading>\n		<category>CDM (Click on the pie chart to see the patients included in the pie chart)</category>\n		<subCategory>Drug Dependence Syndrome</subCategory>\n		<name>Consider Drug Dependence Syndrome</name>\n		<definition>\n			Patients with drug dependence syndrome dx in 2+ bills or 2+ visit encounters within last 3 years\n		</definition>\n		<framework>DoBC CPQI PSP Panel</framework>\n		<frameworkVersion>08-02-2017</frameworkVersion>\n		<notes>\n		  Created for Doctors of BC\n		  i) Drug Dependence Syndrome dx in 2+ bills: \"Patient Status: Active\nAND does not have a Current Problem List/Profile item code of 191816009 OR starting with 304\nAND with >=2 Bills with the ICD9 diagnosis code starting with 304 in the last 3 years\"\n		  ii) Drug Dependence Syndrome dx in 2+ visits: \"Patient Status: Active\nAND does not have a Current Problem List/Profile item code of 191816009 OR starting with 304\nAND with >=2 Visit Encounters with Snomed diagnosis code 191816009 or the ICD9 diagnosis code starting with 304 in the last 3 years\"\n		</notes>\n	</heading>\n	<indicatorQuery>\n		<version>03-13-2018</version>\n		<params>\n			<parameter id=\"provider\" name=\"Provider Number\" value=\"loggedInProvider\" />\n			<parameter id=\"active\" name=\"Active Patients\" value=\"\'AC\'\" />\n			<parameter id=\"dxcodesICD\" name=\"Dx Codes (ICD)\" value=\"304\" />\n                        <parameter id=\"dxcodesSysICD\" name=\"DX CodeSystem (ICD)\" value=\"\'icd9\'\" />\n                        <parameter id=\"dxcodesSnoMed\" name=\"Dx Codes (SnoMed)\" value=\"191816009\" />\n                        <parameter id=\"dxcodesSysSnoMed\" name=\"Dx CodeSystem (SnoMed)\" value=\"\'snomed%\'\" />\n			<parameter id=\"billingCode\" name=\"ICD9 Billing Code\" value=\"304\" />\n			<parameter id=\"numOfBills\" name=\"Number of ICD9 304 Bills\" value=\"2\" />\n			<parameter id=\"numOfVisits\" name=\"Number of Visit Encounters coded 304/ICD9\" value=\"2\" />\n			<parameter id=\"excludedPatient\" name=\"Excluded Patient\" value=\"excludedPatient\" />\n		</params>\n		<range>\n			<lowerLimit id=\"billingdate\" label=\"From Date\" name=\"Date\" value=\"DATE_SUB( NOW(), INTERVAL 3 YEAR )\" />\n			<upperLimit id=\"billingdate\" label=\"Date Today\" name=\"Date\" value=\"NOW()\" />\n			<lowerLimit id=\"visitdate\" label=\"From Date\" name=\"Date\" value=\"DATE_SUB( NOW(), INTERVAL 3 YEAR )\" />\n			<upperLimit id=\"visitdate\" label=\"Date Today\" name=\"Date\" value=\"NOW()\" />\n		</range>\n		<query>\nSELECT\n  IF ( COUNT(fin.patient) &gt; 0, SUM( CASE WHEN (fin.dx IS NULL AND fin.billcount &gt;= ${numOfBills}) THEN 1 ELSE 0 END ) , 0) AS \"Number with Drug Dependence Syndrome dx in ${numOfBills}+ bills\",\n  IF ( COUNT(fin.patient) &gt; 0, SUM( CASE WHEN (fin.dx IS NULL AND fin.visitcount &gt;= ${numOfVisits}) THEN 1 ELSE 0 END ) , 0) AS \"Number with Drug Dependence Syndrome dx in ${numOfVisits}+ visits\"\n-- Consider OBS here if needed  \nFROM ( \n  SELECT \n    d.demographic_no AS patient, \n    BILLING.billcount AS billcount,\n    VISIT.visitcount AS visitcount,\n    dxr.dxresearch_code AS dx,\n    dxr.coding_system AS dxs\n  FROM demographic d \n  LEFT JOIN (\n    SELECT demographic_no, dxresearch_code, coding_system, COUNT(dxresearch_code)\n    FROM dxresearch\n    WHERE status=\'A\' AND\n    (\n      (LEFT(dxresearch_code, 3) IN (\'304\') AND coding_system = ${dxcodesSysICD}) OR\n      (dxresearch_code IN (\'191816009\') AND coding_system LIKE ${dxcodesSysSnoMed})\n    )\n    GROUP BY demographic_no\n    ORDER BY demographic_no\n  ) dxr\n  ON d.demographic_no = dxr.demographic_no\n  -- Billed 304 in last 3 years\n  LEFT JOIN (\n    SELECT b.demographic_no AS demographic_no, b.dx_code1 AS dxcode1, b.dx_code2 AS dxcode2, b.dx_code3 AS dxcode3, COUNT(b.demographic_no) AS billcount\n    FROM billingmaster b\n    WHERE ( LEFT(b.dx_code1, 3) IN (\'304\') OR LEFT(b.dx_code2, 3) IN (\'304\') OR LEFT(b.dx_code3, 3) IN (\'304\') )\n    AND DATE(b.createdate) &gt; ${lowerLimit.billingdate}\n    AND b.demographic_no &gt; 0\n    GROUP BY b.demographic_no HAVING COUNT(b.demographic_no) &gt; -1\n  ) BILLING\n  ON (d.demographic_no = BILLING.demographic_no)  \n  -- Coded dx during visit encounter in last 3 years\n  LEFT JOIN(\n    SELECT ASSIGNED.demographic_no AS demographic_no, observation_date AS visitdate, uuid, COUNT(ASSIGNED.demographic_no) AS visitcount\n    FROM casemgmt_note note\n    INNER JOIN (\n      SELECT note_id, CODED.demo_no AS demographic_no FROM casemgmt_issue_notes\n      INNER JOIN (\n        SELECT ci.id AS id, ci.update_date, ci.demographic_no demo_no FROM issue i\n        INNER JOIN casemgmt_issue ci\n        ON i.issue_id = ci.issue_id\n        WHERE\n	(i.type LIKE ${dxcodesSysICD} AND LEFT(i.code, 3) IN (\'304\')) OR\n	(i.type LIKE ${dxcodesSysSnoMed} AND i.code IN (\'191816009\'))\n      ) CODED\n      ON (casemgmt_issue_notes.id = CODED.id)\n      ORDER BY CODED.demo_no\n    ) ASSIGNED\n    ON note.note_id = ASSIGNED.note_id AND\n    -- GET LAST REVISION OF NOTE\n    note.note_id= (SELECT MAX(cmn.note_id) FROM casemgmt_note cmn WHERE cmn.uuid = note.uuid) AND\n    DATE(note.observation_date) &gt; ${lowerLimit.visitdate}\n    GROUP BY demographic_no\n    ORDER BY demographic_no\n  ) VISIT\n  ON (d.demographic_no = VISIT.demographic_no)  \n  WHERE d.patient_status LIKE ${active}\n  AND d.demographic_no &gt; 0\n  AND d.provider_no LIKE '${provider}'\n  AND d.demographic_no NOT IN ${excludedPatient}\n) fin\n		</query>\n	</indicatorQuery>\n	<drillDownQuery>\n		<version>03-13-2018</version>\n		<params>\n			<parameter id=\"provider\" name=\"Provider Number\" value=\"loggedInProvider\" />\n			<parameter id=\"active\" name=\"Active Patients\" value=\"\'AC\'\" />\n			<parameter id=\"dxcodesICD\" name=\"Dx Codes (ICD)\" value=\"304\" />\n                        <parameter id=\"dxcodesSysICD\" name=\"DX CodeSystem (ICD)\" value=\"\'icd9\'\" />\n                        <parameter id=\"dxcodesSnoMed\" name=\"Dx Codes (SnoMed)\" value=\"191816009\" />\n                        <parameter id=\"dxcodesSysSnoMed\" name=\"Dx CodeSystem (SnoMed)\" value=\"\'snomed%\'\" />\n			<parameter id=\"billingCode\" name=\"ICD9 Billing Code\" value=\"304\" />\n			<parameter id=\"numOfBills\" name=\"Number of ICD9 304 Bills\" value=\"2\" />\n			<parameter id=\"numOfVisits\" name=\"Number of Visit Encounters coded 304/ICD9\" value=\"2\" />\n			<parameter id=\"excludedPatient\" name=\"Excluded Patient\" value=\"excludedPatient\" />\n		</params>\n		\n		<range>\n			<lowerLimit id=\"billingdate\" label=\"From Date\" name=\"Date\" value=\"DATE_SUB( NOW(), INTERVAL 3 YEAR )\" />\n			<upperLimit id=\"billingdate\" label=\"Date Today\" name=\"Date\" value=\"NOW()\" />\n			<lowerLimit id=\"visitdate\" label=\"From Date\" name=\"Date\" value=\"DATE_SUB( NOW(), INTERVAL 3 YEAR )\" />\n			<upperLimit id=\"visitdate\" label=\"Date Today\" name=\"Date\" value=\"NOW()\" />\n		</range>\n		\n		<displayColumns>\n			<column id=\"demographic\" name=\"d.demographic_no\" title=\"Patient Id\" primary=\"true\" />\n			<column id=\"name\" name=