INSERT INTO `indicatorTemplate` (`dashboardId`, `name`, `category`, `subCategory`, `framework`, `frameworkVersion`, `definition`, `notes`, `template`, `active`, `locked`, `shared`, `metricSetName`, `metricLabel`) VALUES (@dashboardId4,'Aggregated Active Patients Report','General','Patient Population','DoBC CPQI PSP Panel','2017-08-02','# of patients that have been seen in the last 3 years in comparison to # all active patients','\n		  i) # of active patients that have been seen in the last 3 year\n		  ii) # of active patients not seen in the last 3 years\n		','<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<indicatorTemplateXML>\n	<author>OSCAR BC</author>\n	<uid></uid>\n	<heading>\n		<category>General</category>\n		<subCategory>Patient Population</subCategory>\n		<name>Aggregated Active Patients Report</name>\n		<definition># of patients that have been seen in the last 3 years in comparison to # all active patients</definition>\n		<framework>DoBC CPQI PSP Panel</framework>\n		<frameworkVersion>08-02-2017</frameworkVersion>\n		<notes>\n		  i) # of active patients that have been seen in the last 3 year\n		  ii) # of active patients not seen in the last 3 years\n		</notes>\n	</heading>\n	<indicatorQuery>\n		<version>03-13-2018</version>\n		<params>\n			<!-- \n				Use this parameter in the query as '${provider}'\n				This parameter should be used for fetching patient\'s assigned to a MRP.\n				ie: WHERE demographic.provider_no = ${provider}\n			-->\n			<parameter id=\"provider\" name=\"provider_no\" value=\"loggedInProvider\" />\n			<parameter id=\"pstatus\" name=\"Patient Status\" value=\"\'AC\'\" />\n		</params>\n		<query>\n		  <!-- Indicator SQL Query here -->\nSELECT\n	SUM(fin.THREE) AS \"% Active, Seen within 3 years\",\n	SUM(fin.ALL_PATIENTS)-SUM(fin.THREE) AS \"% Active, Not seen within 3 years\"\nFROM (\n	SELECT\n		IF( DATE_SUB(CURDATE() , INTERVAL 3 YEAR) &lt;= DATE( MAX(cmn.observation_date) ) ,1 ,0 ) AS THREE, \n		IF( TRUE, 1, 0) AS ALL_PATIENTS\n	FROM demographic d\n	LEFT JOIN casemgmt_note cmn\n	ON (  d.demographic_no = cmn.demographic_no AND cmn.signed = 1 AND cmn.provider_no != \'-1\'  )\n	WHERE d.provider_no LIKE ${provider}\n	AND d.patient_status LIKE ${pstatus}\n	GROUP BY d.demographic_no HAVING count(d.demographic_no) > -1\n) fin;\n		</query>\n	</indicatorQuery>\n	<drillDownQuery>\n		<version>03-13-2018</version>\n		<params>\n			<parameter id=\"provider\" name=\"provider_no\" value=\"loggedInProvider\" />	\n			<parameter id=\"pstatus\" name=\"Patient Status\" value=\"\'AC\'\" />\n		</params>\n		<displayColumns>\n		        <column id=\"seen3yrs\" name=\"SUM(fin.THREE)\" title=\"Active Seen Within Last 3 Years\" primary=\"false\" />\n		  	<column id=\"allActive\" name=\"SUM(fin.ALL_PATIENTS)\" title=\"Active Patient Total\" primary=\"false\" />\n		</displayColumns>\n		<exportColumns>\n		        <column id=\"seen3yrs\" name=\"SUM(fin.THREE)\" title=\"Active Seen Within Last 3 Years\" primary=\"false\" />\n		  	<column id=\"allActive\" name=\"SUM(fin.ALL_PATIENTS)\" title=\"Active Patient Total\" primary=\"false\" />\n		</exportColumns>\n		<query>\n			<!-- Drilldown SQL Query here -->\nSELECT\nfin.*\nFROM (\n	SELECT\n		IF( DATE_SUB(CURDATE() , INTERVAL 3 YEAR) &lt;= DATE( MAX(cmn.observation_date) ) ,1 ,0 ) AS THREE, \n		IF( TRUE, 1, 0) AS ALL_PATIENTS\n	FROM demographic d\n	LEFT JOIN casemgmt_note cmn\n	ON (  d.demographic_no = cmn.demographic_no AND cmn.signed = 1 AND cmn.provider_no != \'-1\'  )\n	WHERE d.provider_no LIKE ${provider}\n	AND d.patient_status LIKE ${pstatus}\n	GROUP BY d.demographic_no HAVING count(d.demographic_no) > -1\n) fin;\n		</query>\n	</drillDownQuery>\n</indicatorTemplateXML>\n','','\0',0,NULL,NULL);
INSERT INTO `indicatorTemplate` (`dashboardId`, `name`, `category`, `subCategory`, `framework`, `frameworkVersion`, `definition`, `notes`, `template`, `active`, `locked`, `shared`, `metricSetName`, `metricLabel`) VALUES (@dashboardId4,'Aggregated No Assigned Primary','General','Patient Population','DoBC CPQI PSP Panel','2017-08-02','# of active patients without an assigned primary\n		','\n		  i)  # of active patients without an assigned primary\n		  ii) # all active patients for all providers\n		','<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<indicatorTemplateXML>\n	<author>OSCAR BC</author>\n	<uid></uid>\n	<heading>\n		<category>General</category>\n		<subCategory>Patient Population</subCategory>\n		<name>Aggregated No Assigned Primary</name>\n		<definition># of active patients without an assigned primary\n		</definition>\n		<framework>DoBC CPQI PSP Panel</framework>\n		<frameworkVersion>08-02-2017</frameworkVersion>\n		<notes>\n		  i)  # of active patients without an assigned primary\n		  ii) # all active patients for all providers\n		</notes>\n	</heading>\n	<indicatorQuery>\n		<version>03-13-2018</version>\n		<params>\n			<!-- \n				Use this parameter in the query as '${provider}'\n				This parameter should be used for fetching patient\'s assigned to a MRP.\n				ie: WHERE demographic.provider_no = ${provider}\n			-->\n			<parameter id=\"provider\" name=\"provider_no\" value=\"loggedInProvider\" />\n			<parameter id=\"pstatus\" name=\"Patient Status\" value=\"\'AC\'\" />\n		</params>\n		<query>\n		  <!-- Indicator SQL Query here -->\nSELECT\n	SUM(fin.NO_ASSIGNED_PRIMARY) AS \"% Active, No Assigned Primary\",\n	SUM(fin.OTHER_PATIENTS) AS \"% Active, Assigned Primary\"\nFROM (\n	SELECT\n		IF( d.provider_no IS NULL OR d.provider_no = \'\' OR p.provider_no IS NULL ,1 ,0 ) AS NO_ASSIGNED_PRIMARY,\n		IF( d.provider_no IS NOT NULL AND d.provider_no != \'\' AND p.provider_no is NOT NULL,1 ,0 ) AS OTHER_PATIENTS\n	FROM demographic d\n	LEFT JOIN provider p\n	ON d.provider_no = p.provider_no\n	WHERE d.patient_status LIKE ${pstatus}\n) fin;\n		</query>\n	</indicatorQuery>\n	<drillDownQuery>\n		<version>03-13-2018</version>\n		<params>\n			<parameter id=\"provider\" name=\"provider_no\" value=\"loggedInProvider\" />	\n			<parameter id=\"pstatus\" name=\"Patient Status\" value=\"\'AC\'\" />\n		</params>\n		<displayColumns>\n		        <column id=\"noProvider\" name=\"SUM(fin.NO_ASSIGNED_PRIMARY)\" title=\"Active Without Assigned Primary\" primary=\"false\" />\n		  	<column id=\"allActive\" name=\"SUM(fin.ALLACTIVE)\" title=\"Active Patient Total\" primary=\"false\" />\n		</displayColumns>\n		<exportColumns>\n		        <column id=\"noProvider\" name=\"SUM(fin.NO_ASSIGNED_PRIMARY)\" title=\"Active Without Contact Info\" primary=\"false\" />\n		  	<column id=\"allActive\" name=\"SUM(fin.ALLACTIVE)\" title=\"Active Patient Total\" primary=\"false\" />\n		</exportColumns>\n		<query>\n			<!-- Drilldown SQL Query here -->\nSELECT\nfin.*\nFROM (\n	SELECT\n		IF( d.provider_no IS NULL OR d.provider_no = \'\' OR p.provider_no IS NULL ,1 ,0 ) AS NO_ASSIGNED_PRIMARY,\n		IF( true, 1, 0 ) AS ALLACTIVE\n	FROM demographic d\n	LEFT JOIN provider p\n	ON d.provider_no = p.provider_no\n	WHERE d.patient_status LIKE ${pstatus}\n) fin;\n		</query>\n	</drillDownQuery>\n</indicatorTemplateXML>\n','','\0',0,NULL,NULL);
INSERT INTO `indicatorTemplate` (`dashboardId`, `name`, `category`, `subCategory`, `framework`, `frameworkVersion`, `definition`, `notes`, `template`, `active`, `locked`, `shared`, `metricSetName`, `metricLabel`) VALUES (@dashboardId4,'Aggregated Patient Contact','General','Patient Population','DoBC CPQI PSP Panel','2017-08-02','# of active patients with incomplete contact information in comparison to # all active patients\n		','\n		  i)  # of active patients with incomplete contact information\n		  ii) # of active patients with contact information\n		','<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<indicatorTemplateXML>\n	<author>OSCAR BC</author>\n	<uid></uid>\n	<heading>\n		<category>General</category>\n		<subCategory>Patient Population</subCategory>\n		<name>Aggregated Patient Contact</name>\n		<definition># of active patients with incomplete contact information in comparison to # all active patients\n		</definition>\n		<framework>DoBC CPQI PSP Panel</framework>\n		<frameworkVersion>08-02-2017</frameworkVersion>\n		<notes>\n		  i)  # of active patients with incomplete contact information\n		  ii) # of active patients with contact information\n		</notes>\n	</heading>\n	<indicatorQuery>\n		<version>03-13-2018</version>\n		<params>\n			<!-- \n				Use this parameter in the query as '${provider}'\n				This parameter should be used for fetching patient\'s assigned to a MRP.\n				ie: WHERE demographic.provider_no = ${provider}\n			-->\n			<parameter id=\"provider\" name=\"provider_no\" value=\"loggedInProvider\" />\n			<parameter id=\"pstatus\" name=\"Patient Status\" value=\"\'AC\'\" />\n		</params>\n		<query>\n		  <!-- Indicator SQL Query here -->\nSELECT\n	SUM(fin.INCOMPLETE_CONTACT_INFO) AS \"Active, Incomplete Contact Information\",\n	SUM(fin.HAS_CONTACT_INFO) AS \"Active, With Contact Information\"\nFROM (\n	SELECT\n		IF( (LENGTH(phone) &lt; LENGTH(\"###-###-####\") AND LENGTH(phone2) &lt; LENGTH(\"###-###-####\") AND LENGTH(e.value) &lt; LENGTH(\"###-###-####\")) OR (address is NULL OR address=\"\") OR (city is NULL OR city=\"\") ,1 ,0 ) AS INCOMPLETE_CONTACT_INFO,\n		IF( (LENGTH(phone) &lt; LENGTH(\"###-###-####\") AND LENGTH(phone2) &lt; LENGTH(\"###-###-####\") AND LENGTH(e.value) &lt; LENGTH(\"###-###-####\")) OR (address is NULL OR address=\"\") OR (city is NULL OR city=\"\") ,0 ,1 ) AS HAS_CONTACT_INFO\n	FROM demographic d\n	LEFT JOIN demographicExt e ON d.demographic_no=e.demographic_no AND\n	  e.key_val=\'demo_cell\' AND e.date_time=(SELECT MAX(de.date_time) FROM demographicExt de WHERE e.demographic_no=de.demographic_no)\n	WHERE d.provider_no LIKE ${provider}\n	AND d.patient_status LIKE ${pstatus}\n) fin;\n		</query>\n	</indicatorQuery>\n	<drillDownQuery>\n		<version>03-13-2018</version>\n		<params>\n			<parameter id=\"provider\" name=\"provider_no\" value=\"loggedInProvider\" />	\n			<parameter id=\"pstatus\" name=\"Patient Status\" value=\"\'AC\'\" />\n		</params>\n		<displayColumns>\n		        <column id=\"incompleteContact\" name=\"SUM(fin.INCOMPLETE_CONTACT_INFO)\" title=\"Active, Incomplete Contact Info\" primary=\"false\" />\n		  	<column id=\"allActive\" name=\"SUM(fin.ALLACTIVE)\" title=\"Active Patient Total\" primary=\"false\" />\n		</displayColumns>\n		<exportColumns>\n		        <column id=\"incompleteContact\" name=\"SUM(fin.INCOMPLETE_CONTACT_INFO)\" title=\"Active, Incomplete Contact Info\" primary=\"false\" />\n		  	<column id=\"allActive\" name=\"SUM(fin.ALLACTIVE)\" title=\"Active Patient Total\" primary=\"false\" />\n		</exportColumns>\n		<query>\n			<!-- Drilldown SQL Query here -->\nSELECT\nfin.*\nFROM (\n	SELECT\n		IF( (LENGTH(phone) &lt; LENGTH(\"###-###-####\") AND LENGTH(phone2) &lt; LENGTH(\"###-###-####\") AND LENGTH(e.value) &lt; LENGTH(\"###-###-####\")) OR (address is NULL OR address=\"\") OR (city is NULL OR city=\"\") ,1 ,0 ) AS INCOMPLETE_CONTACT_INFO,\n		IF( true, 1, 0 ) AS ALLACTIVE\n	FROM demographic d\n	LEFT JOIN demographicExt e ON d.demographic_no=e.demographic_no AND e.key_val=\'demo_cell\'\n	  AND e.date_time=(SELECT MAX(de.date_time) FROM demographicExt de WHERE e.demographic_no=de.demographic_no)\n	WHERE d.provider_no LIKE ${provider}\n	AND d.patient_status LIKE ${pstatus}\n	) fin\n		</query>\n	</drillDownQuery>\n</indicatorTemplateXML>\n','','\0',0,NULL,NULL);
INSERT INTO `indicatorTemplate` (`dashboardId`, `name`, `category`, `subCategory`, `framework`, `frameworkVersion`, `definition`, `notes`, `template`, `active`, `locked`, `shared`, `metricSetName`, `metricLabel`) VALUES (@dashboardId5,'Active Patients Excluded from One or More Indicators','Utility','Patient Population','DoBC CPQI PSP Panel','2019-09-08','Active patients that are currently excluded from one or more indicators','\n		  Patients that are excluded together with the indicator from which they have been excluded.\n		','<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<indicatorTemplateXML>\n	<author>OSCAR BC</author>\n	<uid></uid>\n	<heading>\n		<category>Utility</category>\n		<subCategory>Patient Population</subCategory>\n		<name>Active Patients Excluded from One or More Indicators</name>\n		<definition>Active patients that are currently excluded from one or more indicators</definition>\n		<framework>DoBC CPQI PSP Panel</framework>\n		<frameworkVersion>21-08-2018</frameworkVersion>\n		<notes>\n		  Patients that are excluded together with the indicator from which they have been excluded.\n		</notes>\n	</heading>\n	<indicatorQuery>\n		<version>08-02-2017</version>\n		<params>\n			<!-- \n				Use this parameter in the query as '${provider}'\n				This parameter should be used for fetching patient\'s assigned to a MRP.\n				ie: WHERE demographic.provider_no = ${provider}\n			-->\n			<parameter id=\"provider\" name=\"provider_no\" value=\"loggedInProvider\" />\n			<parameter id=\"pstatus\" name=\"Patient Status\" value=\"\'AC\'\" />\n		</params>\n		<query>\n		  <!-- Indicator SQL Query here -->\nSELECT\n	SUM(fin.CURRENT) AS \"Current Exclusions\",\n	SUM(fin.NOT_CURRENT) AS \"Expired Exclusions\"\nFROM (\n	SELECT\n		IF( DATE_SUB(CURDATE() , INTERVAL 1 YEAR) &lt;= DATE( dext.date_time ) ,1 ,0 ) AS CURRENT, \n		IF( DATE_SUB(CURDATE() , INTERVAL 1 YEAR) &lt;= DATE( dext.date_time ) ,0 ,1 ) AS NOT_CURRENT\n	FROM demographic d\n	LEFT JOIN demographicExt dext\n	ON (  d.demographic_no = dext.demographic_no )\n	WHERE d.provider_no LIKE ${provider}\n	AND dext.provider_no LIKE ${provider}\n	AND d.patient_status LIKE ${pstatus}\n	AND dext.key_val = \"excludeIndicator\"\n) fin;\n		</query>\n	</indicatorQuery>\n	<drillDownQuery>\n		<version>03-13-2018</version>\n		<params>\n			<parameter id=\"provider\" name=\"provider_no\" value=\"loggedInProvider\" />	\n			<parameter id=\"pstatus\" name=\"Patient Status\" value=\"\'AC\'\" />\n		</params>\n		<displayColumns>\n			<column id=\"demographic\" name=\"d.demographic_no\" title=\"Patient Id\" primary=\"true\" />\n			<column id=\"name\" name=\"CONCAT( d.last_name, \', \', d.first_name )\" title=\"Patient Name\" primary=\"false\" />\n			<column id=\"dob\" name=\"DATE_FORMAT( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth), \'%m-%d-%Y\' )\" title=\"DOB (mm-dd-yy)\" primary=\"false\" />\n			<column id=\"age\" name=\"TIMESTAMPDIFF(YEAR, DATE( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth) ), CURDATE() )\" title=\"Age\" primary=\"false\" />	\n			<column id=\"indicatorId\" name=\"value\" title=\"Indicator (Name|Category|SubCategory)\" primary=\"false\" />\n			<column id=\"exclusionStatus\" name=\"IF(DATE_SUB(CURDATE(),INTERVAL 1 YEAR) &lt;= DATE(dext.date_time) ,\'Active\' ,\'Expired\')\" title=\"Exclusion\" primary=\"false\" />\n			<column id=\"dateExcluded\" name=\"dext.date_time\" title=\"Date Excluded\" primary=\"false\" />\n		</displayColumns>\n		<exportColumns>\n                        <column id=\"demographic\" name=\"d.demographic_no\" title=\"Patient Id\" primary=\"true\" />\n			<column id=\"name\" name=\"CONCAT( d.last_name, \', \', d.first_name )\" title=\"Patient Name\" primary=\"false\" />\n			<column id=\"dob\" name=\"DATE_FORMAT( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth), \'%m-%d-%Y\' )\" title=\"DOB (mm-dd-yy)\" primary=\"false\" />\n			<column id=\"age\" name=\"TIMESTAMPDIFF(YEAR, DATE( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth) ), CURDATE() )\" title=\"Age\" primary=\"false\" />	\n			<column id=\"indicatorId\" name=\"value\" title=\"Indicator (Name|Category|SubCategory)\" primary=\"false\" />\n			<column id=\"exclusionStatus\" name=\"IF(DATE_SUB(CURDATE(),INTERVAL 1 YEAR) &lt;= DATE(dext.date_time) ,\'Active\' ,\'Expired\')\" title=\"Exclusion\" primary=\"false\" />\n			<column id=\"dateExcluded\" name=\"dext.date_time\" title=\"Date Excluded\" primary=\"false\" />\n		</exportColumns>\n		<query>\n			<!-- Drilldown SQL Query here -->\nSELECT\n    d.*,\n    d.patient_status AS pt_status,\n    dext.date_time as exclusion_date\nFROM demographic d\nLEFT JOIN demographicExt dext\nON (  d.demographic_no = dext.demographic_no )\nWHERE d.provider_no LIKE ${provider}\nAND dext.provider_no like ${provider}\nAND d.patient_status LIKE ${pstatus}\nAND dext.key_val = \"excludeIndicator\"\nORDER BY dext.date_time DESC, d.demographic_no ASC\n		</query>\n	</drillDownQuery>\n</indicatorTemplateXML>\n','','\0',0,NULL,NULL);
